import{_ as o,c as e,o as t,ag as a}from"./chunks/framework.4QX5UEtQ.js";const p=JSON.parse('{"title":"Custom Domains - Disclosurely DNS Configuration Guide","description":"Configure custom domains for your whistleblowing portal (reports.yourcompany.com). SSL certificates, DNS setup, and domain verification included automatically.","frontmatter":{"title":"Custom Domains - Disclosurely DNS Configuration Guide","description":"Configure custom domains for your whistleblowing portal (reports.yourcompany.com). SSL certificates, DNS setup, and domain verification included automatically.","head":[["meta",{"property":"og:title","content":"Custom Domain Setup for Whistleblowing - Disclosurely"}],["meta",{"property":"og:description","content":"Configure custom domains for your whistleblowing portal (reports.yourcompany.com). SSL certificates, DNS setup, and domain verification included automatically."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Custom Domains - Disclosurely"}],["meta",{"name":"twitter:description","content":"Configure custom domains for your whistleblowing portal (reports.yourcompany.com). SSL certificates, DNS setup, and domain verification included automatically."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"admin/custom-domains.md","filePath":"admin/custom-domains.md"}'),r={name:"admin/custom-domains.md"};function n(s,i,l,c,m,u){return t(),e("div",null,[...i[0]||(i[0]=[a('<h1 id="custom-domains" tabindex="-1">Custom Domains <a class="header-anchor" href="#custom-domains" aria-label="Permalink to &quot;Custom Domains&quot;">​</a></h1><p>Use your own domain for your whistleblowing reporting portal. Create a professional, branded experience like reports.yourcompany.com.</p><p><strong>Available on</strong>: Pro and Enterprise plans</p><h2 id="benefits-of-custom-domains" tabindex="-1">Benefits of Custom Domains <a class="header-anchor" href="#benefits-of-custom-domains" aria-label="Permalink to &quot;Benefits of Custom Domains&quot;">​</a></h2><p><strong>Professional Appearance</strong></p><ul><li>Use your own domain (reports.yourcompany.com)</li><li>Builds trust with employees</li><li>Reinforces brand identity</li><li>Shows organizational commitment</li></ul><p><strong>Increased Trust</strong></p><ul><li>Employees recognize your domain</li><li>Reduces phishing concerns</li><li>Higher submission rates</li><li>Better engagement</li></ul><p><strong>SEO &amp; Analytics</strong></p><ul><li>Own the web presence</li><li>Track traffic in your analytics</li><li>Control SEO for compliance pages</li></ul><h2 id="requirements" tabindex="-1">Requirements <a class="header-anchor" href="#requirements" aria-label="Permalink to &quot;Requirements&quot;">​</a></h2><p>Before setting up a custom domain:</p><p>✅ <strong>Subscription</strong>: Pro or Enterprise plan ✅ <strong>Domain Ownership</strong>: You must own the domain ✅ <strong>DNS Access</strong>: Ability to modify DNS records ✅ <strong>SSL Certificate</strong>: Automatically provided by Disclosurely</p><h2 id="setup-process" tabindex="-1">Setup Process <a class="header-anchor" href="#setup-process" aria-label="Permalink to &quot;Setup Process&quot;">​</a></h2><h3 id="step-1-add-domain-in-disclosurely" tabindex="-1">Step 1: Add Domain in Disclosurely <a class="header-anchor" href="#step-1-add-domain-in-disclosurely" aria-label="Permalink to &quot;Step 1: Add Domain in Disclosurely&quot;">​</a></h3><ol><li>Navigate to <strong>Settings &gt; Custom Domains</strong></li><li>Click <strong>&quot;Add Custom Domain&quot;</strong></li><li>Enter your desired subdomain: <ul><li>reports.yourcompany.com</li><li>speakup.yourcompany.com</li><li>ethics.yourcompany.com</li></ul></li><li>Click <strong>&quot;Add Domain&quot;</strong></li></ol><h3 id="step-2-create-dns-records" tabindex="-1">Step 2: Create DNS Records <a class="header-anchor" href="#step-2-create-dns-records" aria-label="Permalink to &quot;Step 2: Create DNS Records&quot;">​</a></h3><p>You&#39;ll receive DNS configuration instructions:</p><p><strong>CNAME Record</strong>:</p><ul><li><strong>Name/Host</strong>: reports (or your chosen subdomain)</li><li><strong>Value/Points to</strong>: cname.disclosurely.com</li><li><strong>TTL</strong>: 3600 (or automatic)</li></ul><p><strong>Verification TXT Record</strong> (temporary):</p><ul><li><strong>Name/Host</strong>: _disclosurely-verify.reports</li><li><strong>Value</strong>: [unique verification token]</li><li><strong>TTL</strong>: 3600</li></ul><h3 id="step-3-configure-dns-provider" tabindex="-1">Step 3: Configure DNS Provider <a class="header-anchor" href="#step-3-configure-dns-provider" aria-label="Permalink to &quot;Step 3: Configure DNS Provider&quot;">​</a></h3><p>Log into your DNS provider (examples):</p><p><strong>Cloudflare</strong>:</p><ol><li>Go to DNS settings</li><li>Add CNAME record</li><li>Add TXT record for verification</li><li>Save changes</li></ol><p><strong>GoDaddy</strong>:</p><ol><li>Navigate to DNS Management</li><li>Add new CNAME record</li><li>Add TXT record</li><li>Save</li></ol><p><strong>Google Domains</strong>:</p><ol><li>Go to DNS settings</li><li>Add Custom Resource Records</li><li>Type: CNAME, TXT</li><li>Save</li></ol><h3 id="step-4-verify-domain" tabindex="-1">Step 4: Verify Domain <a class="header-anchor" href="#step-4-verify-domain" aria-label="Permalink to &quot;Step 4: Verify Domain&quot;">​</a></h3><p>Back in Disclosurely:</p><ol><li>Click <strong>&quot;Verify Domain&quot;</strong></li><li>System checks DNS records</li><li>Verification usually completes within minutes</li><li>May take up to 48 hours for DNS propagation</li></ol><h3 id="step-5-ssl-certificate-provisioning" tabindex="-1">Step 5: SSL Certificate Provisioning <a class="header-anchor" href="#step-5-ssl-certificate-provisioning" aria-label="Permalink to &quot;Step 5: SSL Certificate Provisioning&quot;">​</a></h3><p>Once verified:</p><ol><li>SSL certificate automatically provisioned</li><li>Takes 5-15 minutes</li><li>HTTPS automatically enabled</li><li>Domain goes live</li></ol><h2 id="domain-status" tabindex="-1">Domain Status <a class="header-anchor" href="#domain-status" aria-label="Permalink to &quot;Domain Status&quot;">​</a></h2><p><strong>Pending Verification</strong></p><ul><li>DNS records not detected yet</li><li>Check DNS configuration</li><li>Wait for DNS propagation</li></ul><p><strong>Verified</strong></p><ul><li>DNS records confirmed</li><li>SSL provisioning in progress</li></ul><p><strong>Active</strong></p><ul><li>SSL certificate issued</li><li>Domain is live</li><li>Reports can be submitted</li></ul><p><strong>Failed</strong></p><ul><li>Verification failed</li><li>Check DNS records</li><li>Re-verify or contact support</li></ul><h2 id="managing-domains" tabindex="-1">Managing Domains <a class="header-anchor" href="#managing-domains" aria-label="Permalink to &quot;Managing Domains&quot;">​</a></h2><h3 id="set-primary-domain" tabindex="-1">Set Primary Domain <a class="header-anchor" href="#set-primary-domain" aria-label="Permalink to &quot;Set Primary Domain&quot;">​</a></h3><p>If you have multiple domains:</p><ol><li>Go to <strong>Settings &gt; Custom Domains</strong></li><li>Click <strong>&quot;Set as Primary&quot;</strong> next to desired domain</li><li>This becomes the default reporting URL</li></ol><h3 id="remove-domain" tabindex="-1">Remove Domain <a class="header-anchor" href="#remove-domain" aria-label="Permalink to &quot;Remove Domain&quot;">​</a></h3><p>To remove a custom domain:</p><ol><li>Navigate to <strong>Settings &gt; Custom Domains</strong></li><li>Click <strong>&quot;Remove&quot;</strong> next to domain</li><li>Confirm removal</li><li>Domain stops working immediately</li><li>Original disclosurely.com link remains active</li></ol><h3 id="domain-analytics" tabindex="-1">Domain Analytics <a class="header-anchor" href="#domain-analytics" aria-label="Permalink to &quot;Domain Analytics&quot;">​</a></h3><p>View domain-specific analytics:</p><ul><li>Visits to custom domain</li><li>Reports submitted via domain</li><li>Geographic distribution</li><li>Referral sources</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><p><strong>Domain not verifying</strong>:</p><ul><li>Check DNS records are correct</li><li>Wait for DNS propagation (up to 48 hours)</li><li>Ensure no conflicting records</li><li>Verify subdomain is correct</li></ul><p><strong>SSL certificate not provisioning</strong>:</p><ul><li>Ensure DNS is fully propagated</li><li>Check for CAA records blocking issuance</li><li>Wait 15-30 minutes</li><li>Contact support if issue persists</li></ul><p><strong>Domain shows error</strong>:</p><ul><li>Check domain status in dashboard</li><li>Verify SSL certificate is active</li><li>Test from different network</li><li>Clear browser cache</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><p>✅ <strong>Use descriptive subdomain</strong>: reports, speakup, ethics, whistleblow ✅ <strong>Communicate the URL</strong>: Share in employee communications ✅ <strong>Update materials</strong>: Change posters, handbooks, emails ✅ <strong>Test thoroughly</strong>: Submit test reports before announcement ✅ <strong>Monitor analytics</strong>: Track usage and engagement</p><h2 id="multi-domain-setup-enterprise" tabindex="-1">Multi-Domain Setup (Enterprise) <a class="header-anchor" href="#multi-domain-setup-enterprise" aria-label="Permalink to &quot;Multi-Domain Setup (Enterprise)&quot;">​</a></h2><p>Enterprise customers can configure multiple domains:</p><p><strong>Use Cases</strong>:</p><ul><li>Different domains per region</li><li>Separate domains per business unit</li><li>Language-specific domains</li><li>Acquisition integration</li></ul><p><strong>Setup</strong>:</p><ol><li>Add multiple domains via Settings</li><li>Each gets unique DNS configuration</li><li>All point to same organization</li><li>Shared reporting and case management</li></ol><hr><p><strong>Next Steps:</strong></p><ul><li><a href="/docs/admin/custom-branding">Custom Branding</a></li><li><a href="/docs/admin/organization-settings">Organization Settings</a></li><li><a href="/docs/admin/subscription-billing">Subscription &amp; Billing</a></li></ul>',73)])])}const g=o(r,[["render",n]]);export{p as __pageData,g as default};
