import{_ as l,c as r,o as s,ag as o,j as i,a as t,t as n}from"./chunks/framework.4QX5UEtQ.js";const f=JSON.parse('{"title":"Initial Setup - Disclosurely Configuration Guide","description":"Set up your whistleblowing platform in minutes. Configure organization settings, customize branding, invite team members, and launch your secure reporting portal.","frontmatter":{"title":"Initial Setup - Disclosurely Configuration Guide","description":"Set up your whistleblowing platform in minutes. Configure organization settings, customize branding, invite team members, and launch your secure reporting portal.","head":[["meta",{"property":"og:title","content":"Initial Setup & Configuration - Disclosurely"}],["meta",{"property":"og:description","content":"Set up your whistleblowing platform in minutes. Configure organization settings, customize branding, invite team members, and launch your secure reporting portal."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Initial Setup - Disclosurely"}],["meta",{"name":"twitter:description","content":"Set up your whistleblowing platform in minutes. Configure organization settings, customize branding, invite team members, and launch your secure reporting portal."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"admin/initial-setup.md","filePath":"admin/initial-setup.md"}'),p={name:"admin/initial-setup.md"};function u(a,e,c,d,g,m){return s(),r("div",null,[e[10]||(e[10]=o('<h1 id="initial-setup" tabindex="-1">Initial Setup <a class="header-anchor" href="#initial-setup" aria-label="Permalink to &quot;Initial Setup&quot;">​</a></h1><p>This guide will walk you through setting up your Disclosurely organization from scratch. Follow these steps to get your whistleblowing and compliance platform up and running quickly.</p><h2 id="step-1-create-your-account" tabindex="-1">Step 1: Create Your Account <a class="header-anchor" href="#step-1-create-your-account" aria-label="Permalink to &quot;Step 1: Create Your Account&quot;">​</a></h2><h3 id="sign-up" tabindex="-1">Sign Up <a class="header-anchor" href="#sign-up" aria-label="Permalink to &quot;Sign Up&quot;">​</a></h3><ol><li>Visit <a href="https://disclosurely.com" target="_blank" rel="noreferrer">disclosurely.com</a></li><li>Click <strong>&quot;Sign Up&quot;</strong> or <strong>&quot;Get Started&quot;</strong></li><li>Enter your email address</li><li>Create a secure password (minimum 8 characters)</li><li>Accept the Terms of Service and Privacy Policy</li><li>Click <strong>&quot;Create Account&quot;</strong></li></ol><h3 id="email-verification" tabindex="-1">Email Verification <a class="header-anchor" href="#email-verification" aria-label="Permalink to &quot;Email Verification&quot;">​</a></h3><p>After creating your account:</p><ol><li>Check your email inbox for a verification email</li><li>Click the verification link in the email</li><li>Your email is now verified and you can log in</li></ol><p><strong>Tip</strong>: Check your spam folder if you don&#39;t receive the verification email within 5 minutes.</p><h2 id="step-2-organization-setup" tabindex="-1">Step 2: Organization Setup <a class="header-anchor" href="#step-2-organization-setup" aria-label="Permalink to &quot;Step 2: Organization Setup&quot;">​</a></h2><p>Upon first login, you&#39;ll be prompted to set up your organization.</p><h3 id="organization-details" tabindex="-1">Organization Details <a class="header-anchor" href="#organization-details" aria-label="Permalink to &quot;Organization Details&quot;">​</a></h3><p><strong>Organization Name</strong></p><ul><li>Enter your company or organization&#39;s official name</li><li>This will appear on your reporting portal and emails</li><li>Can be changed later in settings</li></ul><p><strong>Organization Domain</strong></p><ul><li>Your organization&#39;s primary website (e.g., yourcompany.com)</li><li>Used for email domain verification</li><li>Helps prevent spoofing and unauthorized access</li></ul><p><strong>Industry/Sector</strong></p><ul><li>Select your organization&#39;s primary industry</li><li>Helps us provide relevant compliance guidance</li><li>Options include: Financial Services, Healthcare, Technology, Manufacturing, Government, Non-Profit, etc.</li></ul><p><strong>Organization Size</strong></p><ul><li>Select your employee count range</li><li>Helps with compliance benchmarking</li><li>Options: 1-50, 51-200, 201-1000, 1000+</li></ul><h3 id="contact-information" tabindex="-1">Contact Information <a class="header-anchor" href="#contact-information" aria-label="Permalink to &quot;Contact Information&quot;">​</a></h3><p><strong>Primary Contact Name</strong></p><ul><li>Your name as the primary administrator</li><li>Used for account-related communications</li></ul><p><strong>Primary Contact Email</strong></p><ul><li>Your email for system notifications</li><li>Should be monitored regularly</li><li>You can add additional contacts later</li></ul><p><strong>Phone Number</strong> (Optional)</p><ul><li>Support contact number</li><li>Displayed on reporting portal for questions</li></ul><h2 id="step-3-configure-your-reporting-portal" tabindex="-1">Step 3: Configure Your Reporting Portal <a class="header-anchor" href="#step-3-configure-your-reporting-portal" aria-label="Permalink to &quot;Step 3: Configure Your Reporting Portal&quot;">​</a></h2><h3 id="secure-link-generation" tabindex="-1">Secure Link Generation <a class="header-anchor" href="#secure-link-generation" aria-label="Permalink to &quot;Secure Link Generation&quot;">​</a></h3><p>Every organization gets a unique secure reporting link:</p><p><strong>Format</strong>: <code>https://disclosurely.com/secure/tool/submit/[your-unique-token]</code></p><p>This link is your organization&#39;s anonymous reporting portal where whistleblowers can <a href="/docs/reporting/how-to-submit">submit anonymous reports</a>:</p><ul><li>Submit anonymous reports</li><li>Track report status</li><li>Communicate securely with your team</li></ul><p><strong>How to Find Your Link</strong>:</p><ol><li>Navigate to <strong>Dashboard &gt; Secure Link</strong></li><li>Copy your unique secure submission link</li><li>Share this link with employees via: <ul><li>Internal website/intranet</li><li>Email signatures</li><li>Employee handbooks</li><li>Posters in workplace</li><li>Training materials</li></ul></li></ol><h3 id="reporting-form-configuration" tabindex="-1">Reporting Form Configuration <a class="header-anchor" href="#reporting-form-configuration" aria-label="Permalink to &quot;Reporting Form Configuration&quot;">​</a></h3><p>Customize what information is collected in reports:</p><p><strong>Required Fields</strong>:</p><ul><li>Report description (always required)</li><li>Incident date</li><li>Category selection</li></ul><p><strong>Optional Fields</strong> (can be enabled/disabled):</p><ul><li>Location/Department</li><li>Witnesses</li><li>File uploads</li><li>Reporter contact (for confidential reports)</li></ul><p><strong>To Configure</strong>:</p><ol><li>Go to <strong>Settings &gt; Reporting Form</strong></li><li>Toggle optional fields on/off</li><li>Customize field labels if needed</li><li>Set category options</li><li>Click <strong>Save Changes</strong></li></ol><h3 id="report-categories" tabindex="-1">Report Categories <a class="header-anchor" href="#report-categories" aria-label="Permalink to &quot;Report Categories&quot;">​</a></h3><p>Set up categories to organize reports:</p><p><strong>Default Categories</strong>:</p><ul><li>Harassment &amp; Discrimination</li><li>Financial Misconduct</li><li>Health &amp; Safety</li><li>Data Privacy</li><li>Conflicts of Interest</li><li>Other</li></ul><p><strong>To Customize Categories</strong>:</p><ol><li>Navigate to <strong>Settings &gt; Categories</strong></li><li>Click <strong>&quot;Add Category&quot;</strong></li><li>Enter category name and description</li><li>Add sub-categories if needed</li><li>Set category color for visual organization</li><li>Click <strong>Save</strong></li></ol><p><strong>Best Practice</strong>: Keep categories broad enough to capture all report types, but specific enough to be useful for routing and analysis.</p><h2 id="step-4-invite-your-team" tabindex="-1">Step 4: Invite Your Team <a class="header-anchor" href="#step-4-invite-your-team" aria-label="Permalink to &quot;Step 4: Invite Your Team&quot;">​</a></h2><h3 id="understanding-roles" tabindex="-1">Understanding Roles <a class="header-anchor" href="#understanding-roles" aria-label="Permalink to &quot;Understanding Roles&quot;">​</a></h3><p>Before inviting team members, understand the four user roles:</p><table tabindex="0"><thead><tr><th>Role</th><th>Access Level</th><th>Best For</th></tr></thead><tbody><tr><td><strong>Admin</strong></td><td>Full system access</td><td>IT admins, compliance officers</td></tr><tr><td><strong>Org Admin</strong></td><td>Organization management</td><td>HR directors, legal team</td></tr><tr><td><strong>Case Handler</strong></td><td>Case management</td><td>Investigators, compliance team</td></tr><tr><td><strong>Reviewer</strong></td><td>View-only</td><td>Auditors, oversight roles</td></tr></tbody></table><p>See <a href="/docs/admin/team-management">Team Management</a> for detailed role descriptions.</p><h3 id="sending-invitations" tabindex="-1">Sending Invitations <a class="header-anchor" href="#sending-invitations" aria-label="Permalink to &quot;Sending Invitations&quot;">​</a></h3><p>To invite team members:</p><ol><li>Navigate to <strong>Dashboard &gt; Team</strong></li><li>Click <strong>&quot;Invite Team Member&quot;</strong></li><li>Enter invitee&#39;s email address</li><li>Select their role</li><li>Add optional welcome message</li><li>Click <strong>&quot;Send Invitation&quot;</strong></li></ol><p>The invitee will receive an email with:</p><ul><li>Invitation to join your organization</li><li>Link to create their account</li><li>Role assignment information</li><li>Expiration notice (7 days)</li></ul><p><strong>Important</strong>:</p><ul><li>Invitations expire after 7 days</li><li>You can resend expired invitations</li><li>Invitees must use the email address the invitation was sent to</li></ul><h3 id="initial-team-recommendations" tabindex="-1">Initial Team Recommendations <a class="header-anchor" href="#initial-team-recommendations" aria-label="Permalink to &quot;Initial Team Recommendations&quot;">​</a></h3><p>For most organizations, we recommend starting with:</p><ul><li><strong>1-2 Admins</strong>: Overall system responsibility</li><li><strong>2-3 Case Handlers</strong>: Primary investigation team</li><li><strong>1 Reviewer</strong>: Oversight or audit role (if needed)</li></ul><p>You can always add more team members as your program grows.</p><h2 id="step-5-customize-branding-pro-enterprise" tabindex="-1">Step 5: Customize Branding (Pro/Enterprise) <a class="header-anchor" href="#step-5-customize-branding-pro-enterprise" aria-label="Permalink to &quot;Step 5: Customize Branding (Pro/Enterprise)&quot;">​</a></h2><p>If you&#39;re on a Pro or Enterprise plan, <a href="/docs/admin/custom-branding">customize your reporting portal</a> to match your brand.</p><h3 id="upload-your-logo" tabindex="-1">Upload Your Logo <a class="header-anchor" href="#upload-your-logo" aria-label="Permalink to &quot;Upload Your Logo&quot;">​</a></h3><ol><li>Navigate to <strong>Settings &gt; Branding</strong></li><li>Click <strong>&quot;Upload Logo&quot;</strong></li><li>Select your logo file (PNG, JPG, or SVG)</li><li>Ensure file is under 2MB</li><li>Preview how it looks</li><li>Click <strong>&quot;Save&quot;</strong></li></ol><p><strong>Logo Guidelines</strong>:</p><ul><li>Recommended size: 200x80 pixels</li><li>Transparent background (PNG) works best</li><li>Horizontal orientation preferred</li><li>Should be clearly visible on light and dark backgrounds</li></ul><h3 id="set-brand-color" tabindex="-1">Set Brand Color <a class="header-anchor" href="#set-brand-color" aria-label="Permalink to &quot;Set Brand Color&quot;">​</a></h3><ol><li>In <strong>Settings &gt; Branding</strong></li><li>Click the color picker</li><li>Enter your brand&#39;s hex color code</li><li>Preview how it appears on the portal</li><li>Click <strong>&quot;Save&quot;</strong></li></ol><p>Your brand color will be used for:</p><ul><li>Primary buttons and links</li><li>Header accents</li><li>Form highlights</li><li>Email templates</li></ul><h3 id="custom-messaging" tabindex="-1">Custom Messaging <a class="header-anchor" href="#custom-messaging" aria-label="Permalink to &quot;Custom Messaging&quot;">​</a></h3><p>Personalize messages shown to reporters:</p><p><strong>Welcome Message</strong>:</p><ul><li>Shown at the top of the reporting form</li><li>Use to reassure reporters about confidentiality</li><li>Example: &quot;Your voice matters. Report concerns securely and anonymously.&quot;</li></ul><p><strong>Confirmation Message</strong>:</p><ul><li>Shown after successful report submission</li><li>Include next steps and expected timeline</li><li>Example: &quot;Thank you for speaking up. Your report has been received and will be reviewed within 24 hours.&quot;</li></ul><p><strong>Status Page Message</strong>:</p><ul><li>Shown on the tracking page</li><li>Explain how to use the tracking system</li><li>Example: &quot;Use your tracking ID to check the status of your report anytime.&quot;</li></ul><h2 id="step-6-configure-notifications" tabindex="-1">Step 6: Configure Notifications <a class="header-anchor" href="#step-6-configure-notifications" aria-label="Permalink to &quot;Step 6: Configure Notifications&quot;">​</a></h2><p>Set up email notifications to keep your team informed.</p><h3 id="notification-settings" tabindex="-1">Notification Settings <a class="header-anchor" href="#notification-settings" aria-label="Permalink to &quot;Notification Settings&quot;">​</a></h3><p>Navigate to <strong>Settings &gt; Notifications</strong> and configure:</p><p><strong>New Report Notifications</strong>:</p><ul><li>✅ Enable email alerts for new reports</li><li>Choose who receives notifications: <ul><li>All Admins</li><li>All Case Handlers</li><li>Specific users</li></ul></li><li>Set notification frequency: <ul><li>Immediate (recommended)</li><li>Daily digest</li><li>Weekly digest</li></ul></li></ul><p><strong>Status Change Notifications</strong>:</p><ul><li>✅ Notify reporters when report status changes</li><li>Automated emails sent to reporter via tracking ID</li><li>Customize email template if desired</li></ul><p><strong>Assignment Notifications</strong>:</p><ul><li>✅ Notify team members when <a href="/docs/cases/assignment">assigned to a case</a></li><li>Immediate email notification</li><li>Include case summary in email</li></ul><p><strong>Compliance Reminders</strong>:</p><ul><li>✅ Enable reminders for upcoming deadlines</li><li>Set reminder timing (7 days, 3 days, 1 day before)</li><li>Choose recipients</li></ul><h3 id="email-templates" tabindex="-1">Email Templates <a class="header-anchor" href="#email-templates" aria-label="Permalink to &quot;Email Templates&quot;">​</a></h3><p>Customize email templates for:</p><ul><li>New report acknowledgments</li><li>Status update notifications</li><li>Assignment notifications</li><li>Compliance reminders</li></ul><p><strong>To Customize</strong>:</p>',100)),i("ol",null,[e[5]||(e[5]=i("li",null,[t("Go to "),i("strong",null,"Settings > Email Templates")],-1)),e[6]||(e[6]=i("li",null,"Select template to edit",-1)),e[7]||(e[7]=i("li",null,"Modify subject line and body",-1)),i("li",null,[e[4]||(e[4]=t("Use merge tags for dynamic content: ",-1)),i("ul",null,[i("li",null,[i("code",null,n(a.TRACKING_ID),1),e[0]||(e[0]=t(" - Report tracking ID",-1))]),i("li",null,[i("code",null,n(a.STATUS),1),e[1]||(e[1]=t(" - Current status",-1))]),i("li",null,[i("code",null,n(a.ORGANIZATION),1),e[2]||(e[2]=t(" - Your organization name",-1))]),i("li",null,[i("code",null,n(a.DATE),1),e[3]||(e[3]=t(" - Report date",-1))])])]),e[8]||(e[8]=i("li",null,"Preview template",-1)),e[9]||(e[9]=i("li",null,"Save changes",-1))]),e[11]||(e[11]=o(`<h2 id="step-7-set-up-compliance-modules-optional" tabindex="-1">Step 7: Set Up Compliance Modules (Optional) <a class="header-anchor" href="#step-7-set-up-compliance-modules-optional" aria-label="Permalink to &quot;Step 7: Set Up Compliance Modules (Optional)&quot;">​</a></h2><p>If you plan to use Disclosurely for broader compliance management, set up these modules:</p><h3 id="policy-management" tabindex="-1">Policy Management <a class="header-anchor" href="#policy-management" aria-label="Permalink to &quot;Policy Management&quot;">​</a></h3><ol><li>Navigate to <strong>Compliance &gt; Policies</strong></li><li>Click <strong>&quot;Add Policy&quot;</strong></li><li>Enter policy details: <ul><li>Title</li><li>Policy type</li><li>Description</li><li>Effective date</li></ul></li><li>Upload policy document (PDF)</li><li>Assign policy owner</li><li>Set next review date</li><li>Click <strong>&quot;Save&quot;</strong></li></ol><p>See <a href="/docs/compliance/policies">Policy Management</a> for details.</p><h3 id="risk-register" tabindex="-1">Risk Register <a class="header-anchor" href="#risk-register" aria-label="Permalink to &quot;Risk Register&quot;">​</a></h3><ol><li>Go to <strong>Compliance &gt; Risks</strong></li><li>Click <strong>&quot;Add Risk&quot;</strong></li><li>Enter risk details: <ul><li>Risk description</li><li>Likelihood (1-5)</li><li>Impact (1-5)</li><li>Risk category</li></ul></li><li>Add mitigation plan</li><li>Assign risk owner</li><li>Set review date</li><li>Click <strong>&quot;Save&quot;</strong></li></ol><p>See <a href="/docs/compliance/risks">Risk Management</a> for details.</p><h3 id="compliance-calendar" tabindex="-1">Compliance Calendar <a class="header-anchor" href="#compliance-calendar" aria-label="Permalink to &quot;Compliance Calendar&quot;">​</a></h3><ol><li>Navigate to <strong>Compliance &gt; Calendar</strong></li><li>Click <strong>&quot;Add Event&quot;</strong></li><li>Enter event details: <ul><li>Event name</li><li>Event type (deadline, review, audit, etc.)</li><li>Due date</li><li>Description</li></ul></li><li>Assign responsible party</li><li>Set reminder preferences</li><li>Click <strong>&quot;Save&quot;</strong></li></ol><p>See <a href="/docs/compliance/calendar">Compliance Calendar</a> for details.</p><h2 id="step-8-test-your-setup" tabindex="-1">Step 8: Test Your Setup <a class="header-anchor" href="#step-8-test-your-setup" aria-label="Permalink to &quot;Step 8: Test Your Setup&quot;">​</a></h2><p>Before going live, test your configuration:</p><h3 id="test-report-submission" tabindex="-1">Test Report Submission <a class="header-anchor" href="#test-report-submission" aria-label="Permalink to &quot;Test Report Submission&quot;">​</a></h3><ol><li>Open your secure reporting link</li><li>Submit a test report</li><li>Verify you receive notification</li><li>Check report appears in dashboard</li><li>Test status tracking with tracking ID</li><li>Test secure messaging feature</li></ol><h3 id="test-team-access" tabindex="-1">Test Team Access <a class="header-anchor" href="#test-team-access" aria-label="Permalink to &quot;Test Team Access&quot;">​</a></h3><ol><li>Verify invited team members can log in</li><li>Confirm role permissions work correctly</li><li>Test assignment workflow</li><li>Verify notifications are received</li></ol><h3 id="test-branding" tabindex="-1">Test Branding <a class="header-anchor" href="#test-branding" aria-label="Permalink to &quot;Test Branding&quot;">​</a></h3><ol><li>Review reporting portal appearance</li><li>Check logo displays correctly</li><li>Verify brand colors are applied</li><li>Test custom messages appear</li></ol><h2 id="step-9-launch-communications" tabindex="-1">Step 9: Launch Communications <a class="header-anchor" href="#step-9-launch-communications" aria-label="Permalink to &quot;Step 9: Launch Communications&quot;">​</a></h2><p>Once you&#39;ve tested everything, announce the new reporting channel to your organization.</p><h3 id="internal-communications" tabindex="-1">Internal Communications <a class="header-anchor" href="#internal-communications" aria-label="Permalink to &quot;Internal Communications&quot;">​</a></h3><p><strong>Announce via</strong>:</p><ul><li>Company-wide email</li><li>Intranet/internal website</li><li>Team meetings and town halls</li><li>New employee onboarding</li><li>Manager training sessions</li><li>Digital signage</li><li>Employee handbook</li></ul><p><strong>Key Messages to Include</strong>:</p><ul><li>Purpose of the reporting channel</li><li>Types of issues to report</li><li>How anonymity is protected</li><li>How to submit a report (link and instructions)</li><li>What happens after submission</li><li>Expected response times</li><li>Who can see reports (reassure about confidentiality)</li></ul><h3 id="sample-announcement-email" tabindex="-1">Sample Announcement Email <a class="header-anchor" href="#sample-announcement-email" aria-label="Permalink to &quot;Sample Announcement Email&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Subject: New Secure Reporting Channel Now Available</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Dear Team,</span></span>
<span class="line"><span></span></span>
<span class="line"><span>We are committed to maintaining a safe, ethical workplace where everyone feels empowered to speak up. Today, we&#39;re launching a new secure reporting channel powered by Disclosurely.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>This platform allows you to report concerns about:</span></span>
<span class="line"><span>- Harassment or discrimination</span></span>
<span class="line"><span>- Financial irregularities</span></span>
<span class="line"><span>- Safety issues</span></span>
<span class="line"><span>- Policy violations</span></span>
<span class="line"><span>- Ethical concerns</span></span>
<span class="line"><span>- Any other workplace issues</span></span>
<span class="line"><span></span></span>
<span class="line"><span>KEY FEATURES:</span></span>
<span class="line"><span>✓ Submit reports anonymously</span></span>
<span class="line"><span>✓ Track status with your tracking ID</span></span>
<span class="line"><span>✓ Communicate securely with our team</span></span>
<span class="line"><span>✓ End-to-end encryption protects your privacy</span></span>
<span class="line"><span></span></span>
<span class="line"><span>HOW TO REPORT:</span></span>
<span class="line"><span>Visit [your secure reporting link] or find the link on our intranet.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Your voice matters. Together, we can maintain the highest standards of ethics and compliance.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Best regards,</span></span>
<span class="line"><span>[Your Name/Team]</span></span></code></pre></div><h3 id="ongoing-communications" tabindex="-1">Ongoing Communications <a class="header-anchor" href="#ongoing-communications" aria-label="Permalink to &quot;Ongoing Communications&quot;">​</a></h3><p>Regularly remind employees about the reporting channel:</p><ul><li>Quarterly reminders via email</li><li>Include in compliance training</li><li>Reference in employee onboarding</li><li>Promote during Ethics &amp; Compliance Week</li><li>Display posters in common areas</li></ul><h2 id="step-10-ongoing-administration" tabindex="-1">Step 10: Ongoing Administration <a class="header-anchor" href="#step-10-ongoing-administration" aria-label="Permalink to &quot;Step 10: Ongoing Administration&quot;">​</a></h2><p>After launch, maintain your system:</p><h3 id="weekly-tasks" tabindex="-1">Weekly Tasks <a class="header-anchor" href="#weekly-tasks" aria-label="Permalink to &quot;Weekly Tasks&quot;">​</a></h3><ul><li>Review new reports</li><li>Check pending assignments</li><li>Monitor compliance calendar</li><li>Review analytics dashboard</li></ul><h3 id="monthly-tasks" tabindex="-1">Monthly Tasks <a class="header-anchor" href="#monthly-tasks" aria-label="Permalink to &quot;Monthly Tasks&quot;">​</a></h3><ul><li>Review team access and roles</li><li>Check notification settings</li><li>Review and update categories if needed</li><li>Assess reporting trends</li></ul><h3 id="quarterly-tasks" tabindex="-1">Quarterly Tasks <a class="header-anchor" href="#quarterly-tasks" aria-label="Permalink to &quot;Quarterly Tasks&quot;">​</a></h3><ul><li>Review policies and update as needed</li><li>Audit risk register</li><li>Review team training needs</li><li>Assess program effectiveness</li></ul><h3 id="annual-tasks" tabindex="-1">Annual Tasks <a class="header-anchor" href="#annual-tasks" aria-label="Permalink to &quot;Annual Tasks&quot;">​</a></h3><ul><li>Conduct comprehensive program review</li><li>Update compliance documentation</li><li>Review and renew subscription</li><li>Assess need for additional features or training</li></ul><h2 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-label="Permalink to &quot;Getting Help&quot;">​</a></h2><p>Need assistance during setup?</p><ul><li><strong>Email Support</strong>: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li><strong>In-App Chat</strong>: Click chat icon in bottom right</li><li><strong>Documentation</strong>: Browse our comprehensive guides</li><li><strong>Video Tutorials</strong>: [Coming soon]</li><li><strong>Setup Call</strong>: Enterprise customers can schedule onboarding call</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><p>Now that you&#39;ve completed initial setup:</p><ol><li><strong><a href="/docs/admin/custom-branding">Customize Your Branding</a></strong> - Make it yours</li><li><strong><a href="/docs/admin/custom-domains">Add Custom Domain</a></strong> - Use your own domain (Pro/Enterprise)</li><li><strong><a href="/docs/compliance/gdpr">Configure GDPR Settings</a></strong> - Ensure data compliance</li><li><strong><a href="/docs/ai/case-helper">Explore AI Features</a></strong> - Leverage AI for case analysis</li><li><strong><a href="/docs/compliance/overview">Set Up Compliance Modules</a></strong> - Expand beyond whistleblowing</li></ol><p>Welcome to Disclosurely! You&#39;re all set to run an effective, compliant whistleblowing and ethics program.</p>`,48))])}const y=l(p,[["render",u]]);export{f as __pageData,y as default};
