import{_ as e,c as t,o as a,ag as n}from"./chunks/framework.4QX5UEtQ.js";const d=JSON.parse('{"title":"Link Generator & Management - Disclosurely Admin Guide","description":"Create and manage secure reporting portal links with custom settings, language options, usage limits, expiration dates, and department-specific configurations.","frontmatter":{"title":"Link Generator & Management - Disclosurely Admin Guide","description":"Create and manage secure reporting portal links with custom settings, language options, usage limits, expiration dates, and department-specific configurations.","head":[["meta",{"property":"og:title","content":"Link Generator & Management - Disclosurely"}],["meta",{"property":"og:description","content":"Create and manage secure reporting portal links with custom settings, language options, usage limits, expiration dates, and department-specific configurations."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Link Generator - Disclosurely"}],["meta",{"property":"og:description","content":"Create and manage secure reporting portal links with custom settings, language options, usage limits, expiration dates, and department-specific configurations."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"admin/link-generator.md","filePath":"admin/link-generator.md"}'),l={name:"admin/link-generator.md"};function s(o,i,r,u,g,p){return a(),t("div",null,[...i[0]||(i[0]=[n(`<h1 id="link-generator-management" tabindex="-1">Link Generator &amp; Management <a class="header-anchor" href="#link-generator-management" aria-label="Permalink to &quot;Link Generator &amp; Management&quot;">​</a></h1><p>Create customized, secure reporting portal links tailored to specific departments, languages, and use cases within your organization.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The Link Generator allows you to create multiple reporting portal URLs, each with its own configuration. This enables department-specific portals, multi-language submissions, limited-use links for specific campaigns, and temporary access for contractors or third parties.</p><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Link Generator</p><h2 id="default-reporting-url" tabindex="-1">Default Reporting URL <a class="header-anchor" href="#default-reporting-url" aria-label="Permalink to &quot;Default Reporting URL&quot;">​</a></h2><p>Every organization receives a default reporting URL automatically:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://[your-org-name].disclosurely.com/report</span></span></code></pre></div><p><strong>Characteristics:</strong></p><ul><li>Automatically generated from your organization name</li><li>Cannot be changed or deleted</li><li>Always active</li><li>Supports all languages</li><li>No usage limits</li><li>Default landing page for all reporting</li></ul><p><strong>Custom Branding:</strong> Upgrade to a custom branded domain for professional appearance:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://reports.yourcompany.com</span></span></code></pre></div><p>Learn more: <a href="/docs/admin/custom-domains">Custom Domains</a></p><h2 id="creating-custom-links" tabindex="-1">Creating Custom Links <a class="header-anchor" href="#creating-custom-links" aria-label="Permalink to &quot;Creating Custom Links&quot;">​</a></h2><h3 id="why-create-custom-links" tabindex="-1">Why Create Custom Links? <a class="header-anchor" href="#why-create-custom-links" aria-label="Permalink to &quot;Why Create Custom Links?&quot;">​</a></h3><p><strong>Department-Specific Portals:</strong></p><ul><li>Pre-fill department information</li><li>Route automatically to specific investigators</li><li>Track submissions by department</li><li>Example: <code>https://yourorg.disclosurely.com/report?dept=finance</code></li></ul><p><strong>Language-Specific Links:</strong></p><ul><li>Restrict to specific languages</li><li>Set default language for region</li><li>Example: Spanish portal for Latin American operations</li></ul><p><strong>Limited-Use Links:</strong></p><ul><li>One-time access for specific events</li><li>Temporary contractor access</li><li>Campaign-specific reporting</li><li>Controlled capacity management</li></ul><p><strong>Testing and Training:</strong></p><ul><li>Create test links for demos</li><li>Training environment links</li><li>QA and staging portals</li></ul><h3 id="how-to-create-a-link" tabindex="-1">How to Create a Link <a class="header-anchor" href="#how-to-create-a-link" aria-label="Permalink to &quot;How to Create a Link&quot;">​</a></h3><p><strong>Step-by-Step:</strong></p><ol><li>Navigate to <strong>Dashboard &gt; Settings &gt; Link Generator</strong></li><li>Click <strong>Create New Link</strong></li><li>Configure link settings (see below)</li><li>Click <strong>Generate Link</strong></li><li>Copy and distribute link</li></ol><h3 id="link-configuration-options" tabindex="-1">Link Configuration Options <a class="header-anchor" href="#link-configuration-options" aria-label="Permalink to &quot;Link Configuration Options&quot;">​</a></h3><h4 id="basic-settings" tabindex="-1">Basic Settings <a class="header-anchor" href="#basic-settings" aria-label="Permalink to &quot;Basic Settings&quot;">​</a></h4><p><strong>Link Name</strong> (Required)</p><ul><li>Internal identifier for your reference</li><li>Not visible to reporters</li><li>Examples: &quot;HR Department&quot;, &quot;Spanish Portal&quot;, &quot;Q1 Campaign&quot;</li></ul><p><strong>Description</strong> (Optional)</p><ul><li>Internal notes about link purpose</li><li>Usage instructions for your team</li><li>Track campaigns or initiatives</li></ul><p><strong>Department/Location</strong> (Optional)</p><ul><li>Pre-fill department field in submission form</li><li>Auto-route to specific investigators</li><li>Helpful for tracking and analytics</li></ul><h4 id="language-settings" tabindex="-1">Language Settings <a class="header-anchor" href="#language-settings" aria-label="Permalink to &quot;Language Settings&quot;">​</a></h4><p><strong>Available Languages:</strong> Choose which languages reporters can use with this link.</p><p><strong>Default Language:</strong> Set the language displayed when link is first opened.</p><p><strong>Supported Languages:</strong></p><ul><li>English</li><li>Spanish (Espa�ol)</li><li>French (Fran�ais)</li><li>German (Deutsch)</li><li>Polish (Polski)</li><li>Swedish (Svenska)</li><li>Norwegian (Norsk)</li><li>Portuguese (Portugu�s)</li><li>Italian (Italiano)</li><li>Dutch (Nederlands)</li><li>Danish (Dansk)</li><li>Greek (��������)</li></ul><p><strong>Language Restriction:</strong></p><ul><li> Allow all languages</li><li> Restrict to specific languages only</li></ul><p><strong>Example Use Cases:</strong></p><ul><li>European office: Enable English, French, German only</li><li>Latin America office: Spanish and Portuguese only</li><li>Global hotline: All languages enabled</li></ul><h4 id="usage-limits" tabindex="-1">Usage Limits <a class="header-anchor" href="#usage-limits" aria-label="Permalink to &quot;Usage Limits&quot;">​</a></h4><p><strong>Maximum Submissions:</strong> Limit how many reports can be submitted through this link.</p><p><strong>Options:</strong></p><ul><li>Unlimited (default)</li><li>1 submission (one-time use)</li><li>10 submissions (limited campaign)</li><li>50 submissions (department quota)</li><li>100 submissions (large initiative)</li><li>Custom number</li></ul><p><strong>Use Cases:</strong></p><ul><li>One-time whistleblower access</li><li>Specific investigation follow-ups</li><li>Budget-controlled campaigns</li><li>Test link with limited capacity</li></ul><h4 id="expiration-settings" tabindex="-1">Expiration Settings <a class="header-anchor" href="#expiration-settings" aria-label="Permalink to &quot;Expiration Settings&quot;">​</a></h4><p><strong>Link Expiration Date:</strong> Automatically disable link after specific date.</p><p><strong>Options:</strong></p><ul><li>No expiration (default)</li><li>7 days</li><li>30 days</li><li>90 days</li><li>Custom date</li></ul><p><strong>Use Cases:</strong></p><ul><li>Temporary contractor access</li><li>Time-limited campaigns</li><li>Seasonal reporting periods</li><li>Event-specific reporting</li></ul><p><strong>Expiration Behavior:</strong></p><ul><li>Link displays &quot;Expired&quot; message when accessed</li><li>Past submissions remain accessible</li><li>Can be manually reactivated if needed</li><li>Email notification to admins 7 days before expiration</li></ul><h4 id="advanced-settings" tabindex="-1">Advanced Settings <a class="header-anchor" href="#advanced-settings" aria-label="Permalink to &quot;Advanced Settings&quot;">​</a></h4><p><strong>Auto-Assignment Rules:</strong></p><ul><li>Automatically assign reports from this link to specific investigators</li><li>Bypass default assignment rules</li><li>Department-specific routing</li></ul><p><strong>Category Restrictions:</strong></p><ul><li>Limit available categories for this link</li><li>Pre-select default category</li><li>Simplify form for specific use cases</li></ul><p><strong>Custom Welcome Message:</strong></p><ul><li>Override default welcome text</li><li>Department-specific instructions</li><li>Language-specific guidance</li></ul><p><strong>Analytics Tracking:</strong></p><ul><li>Track link performance</li><li>Measure campaign effectiveness</li><li>Source attribution</li></ul><h2 id="managing-existing-links" tabindex="-1">Managing Existing Links <a class="header-anchor" href="#managing-existing-links" aria-label="Permalink to &quot;Managing Existing Links&quot;">​</a></h2><h3 id="link-dashboard" tabindex="-1">Link Dashboard <a class="header-anchor" href="#link-dashboard" aria-label="Permalink to &quot;Link Dashboard&quot;">​</a></h3><p><strong>View All Links:</strong> Access all created links in one centralized location.</p><p><strong>Information Displayed:</strong></p><ul><li>Link name and description</li><li>Current status (Active, Paused, Expired)</li><li>Usage count (submissions received)</li><li>Usage limit (if set)</li><li>Expiration date (if set)</li><li>Creation date</li><li>Last used date</li></ul><h3 id="link-actions" tabindex="-1">Link Actions <a class="header-anchor" href="#link-actions" aria-label="Permalink to &quot;Link Actions&quot;">​</a></h3><p><strong>Copy Link:</strong></p><ul><li>Click clipboard icon to copy URL</li><li>Share via email, intranet, posters, QR codes</li></ul><p><strong>Edit Link:</strong></p><ul><li>Modify settings after creation</li><li>Update expiration dates</li><li>Change usage limits</li><li>Adjust language options</li></ul><p><strong>Pause Link:</strong></p><ul><li>Temporarily disable without deleting</li><li>Preserve analytics and history</li><li>Re-enable anytime</li></ul><p><strong>Delete Link:</strong></p><ul><li>Permanently remove link</li><li>Past submissions remain in system</li><li>Cannot be undone</li><li>Confirmation required</li></ul><p><strong>View Analytics:</strong></p><ul><li>Submissions received through this link</li><li>Usage trends over time</li><li>Geographic/department breakdown</li><li>Conversion rates</li></ul><h3 id="link-status-indicators" tabindex="-1">Link Status Indicators <a class="header-anchor" href="#link-status-indicators" aria-label="Permalink to &quot;Link Status Indicators&quot;">​</a></h3><p><strong>=� Active:</strong></p><ul><li>Link is live and accepting submissions</li><li>Within usage limits</li><li>Not expired</li></ul><p><strong>=� Paused:</strong></p><ul><li>Temporarily disabled by admin</li><li>Can be reactivated</li><li>Displays &quot;Temporarily Unavailable&quot; message</li></ul><p><strong>=4 Expired:</strong></p><ul><li>Past expiration date</li><li>Can be manually reactivated</li><li>Past submissions accessible</li></ul><p><strong>� Limit Reached:</strong></p><ul><li>Maximum submissions received</li><li>Auto-disabled</li><li>Can increase limit to reactivate</li></ul><h2 id="testing-links" tabindex="-1">Testing Links <a class="header-anchor" href="#testing-links" aria-label="Permalink to &quot;Testing Links&quot;">​</a></h2><h3 id="link-tester-tool" tabindex="-1">Link Tester Tool <a class="header-anchor" href="#link-tester-tool" aria-label="Permalink to &quot;Link Tester Tool&quot;">​</a></h3><p><strong>Built-In Testing:</strong> Before sharing links widely, test functionality.</p><p><strong>How to Test:</strong></p><ol><li>Navigate to <strong>Link Generator</strong></li><li>Find your link in the list</li><li>Click <strong>Test Link</strong> button</li><li>Opens in new tab/window</li><li>Verify: <ul><li>Correct language displays</li><li>Department pre-filled (if configured)</li><li>Welcome message appears correctly</li><li>Form loads properly</li><li>Branding is correct</li></ul></li></ol><p><strong>Test Submission:</strong></p><ul><li>Can make test submissions</li><li>Flagged as &quot;Test&quot; in system</li><li>Easy to identify and delete</li><li>Don&#39;t count toward usage limits</li></ul><h2 id="distribution-methods" tabindex="-1">Distribution Methods <a class="header-anchor" href="#distribution-methods" aria-label="Permalink to &quot;Distribution Methods&quot;">​</a></h2><h3 id="digital-distribution" tabindex="-1">Digital Distribution <a class="header-anchor" href="#digital-distribution" aria-label="Permalink to &quot;Digital Distribution&quot;">​</a></h3><p><strong>Email:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Subject: New Anonymous Reporting Portal</span></span>
<span class="line"><span></span></span>
<span class="line"><span>We&#39;ve launched a secure, anonymous whistleblowing portal.</span></span>
<span class="line"><span>Report concerns at: https://yourorg.disclosurely.com/report</span></span>
<span class="line"><span></span></span>
<span class="line"><span>All reports are confidential and investigated promptly.</span></span></code></pre></div><p><strong>Intranet:</strong></p><ul><li>Add link to company intranet homepage</li><li>Create dedicated ethics page</li><li>Link from HR resources</li></ul><p><strong>Employee Portal:</strong></p><ul><li>Integrate into HRIS or employee self-service</li><li>Add to digital onboarding materials</li></ul><p><strong>Instant Messaging:</strong></p><ul><li>Slack workspace announcement</li><li>Microsoft Teams message</li><li>Internal chat platforms</li></ul><h3 id="physical-distribution" tabindex="-1">Physical Distribution <a class="header-anchor" href="#physical-distribution" aria-label="Permalink to &quot;Physical Distribution&quot;">​</a></h3><p><strong>QR Codes:</strong></p><ol><li>Click <strong>Generate QR Code</strong> next to link</li><li>Download high-resolution PNG</li><li>Print on: <ul><li>Posters (breakrooms, hallways)</li><li>Employee badges</li><li>Desk placards</li><li>Bathroom stalls</li></ul></li></ol><p><strong>Printed Materials:</strong></p><ul><li>Employee handbooks</li><li>Code of conduct booklets</li><li>Policy manuals</li><li>Orientation packets</li><li>Payroll inserts</li></ul><p><strong>Signage:</strong></p><ul><li>Office posters</li><li>Factory floor signs</li><li>Warehouse bulletin boards</li><li>Reception areas</li></ul><h3 id="multi-channel-strategy" tabindex="-1">Multi-Channel Strategy <a class="header-anchor" href="#multi-channel-strategy" aria-label="Permalink to &quot;Multi-Channel Strategy&quot;">​</a></h3><p><strong>Best Practice:</strong> Distribute link through multiple channels for maximum awareness.</p><p><strong>Recommended Channels:</strong></p><ol><li>Email announcement (primary)</li><li>Intranet/portal integration (permanent access)</li><li>QR code posters (physical locations)</li><li>Manager communication (team meetings)</li><li>Training sessions (onboarding, annual ethics training)</li></ol><h2 id="use-case-examples" tabindex="-1">Use Case Examples <a class="header-anchor" href="#use-case-examples" aria-label="Permalink to &quot;Use Case Examples&quot;">​</a></h2><h3 id="example-1-department-specific-portal" tabindex="-1">Example 1: Department-Specific Portal <a class="header-anchor" href="#example-1-department-specific-portal" aria-label="Permalink to &quot;Example 1: Department-Specific Portal&quot;">​</a></h3><p><strong>Scenario:</strong> Finance department wants dedicated reporting channel.</p><p><strong>Configuration:</strong></p><ul><li><strong>Name:</strong> &quot;Finance Department Hotline&quot;</li><li><strong>Department:</strong> Pre-filled as &quot;Finance&quot;</li><li><strong>Auto-Assignment:</strong> CFO or Audit Committee</li><li><strong>Categories:</strong> Limit to Financial Misconduct, Conflicts of Interest</li><li><strong>Languages:</strong> English, Spanish (for diverse workforce)</li></ul><p><strong>URL:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://yourorg.disclosurely.com/report?dept=finance</span></span></code></pre></div><p><strong>Benefits:</strong></p><ul><li>Auto-routes to appropriate investigators</li><li>Tracks finance-specific trends</li><li>Simplified category selection</li><li>Department ownership of ethics</li></ul><h3 id="example-2-time-limited-campaign" tabindex="-1">Example 2: Time-Limited Campaign <a class="header-anchor" href="#example-2-time-limited-campaign" aria-label="Permalink to &quot;Example 2: Time-Limited Campaign&quot;">​</a></h3><p><strong>Scenario:</strong> Annual ethics awareness week.</p><p><strong>Configuration:</strong></p><ul><li><strong>Name:</strong> &quot;Ethics Week 2024&quot;</li><li><strong>Expiration:</strong> 7 days (duration of campaign)</li><li><strong>Custom Message:</strong> &quot;During Ethics Week, we encourage all employees to speak up...&quot;</li><li><strong>Tracking:</strong> Campaign analytics enabled</li></ul><p><strong>Benefits:</strong></p><ul><li>Measures campaign effectiveness</li><li>Temporary spike handling</li><li>Auto-expires after event</li><li>Dedicated tracking</li></ul><h3 id="example-3-multi-language-regional-portals" tabindex="-1">Example 3: Multi-Language Regional Portals <a class="header-anchor" href="#example-3-multi-language-regional-portals" aria-label="Permalink to &quot;Example 3: Multi-Language Regional Portals&quot;">​</a></h3><p><strong>Scenario:</strong> Global company with regional operations.</p><p><strong>Configuration:</strong></p><p><strong>EMEA Portal:</strong></p><ul><li>Languages: English, French, German, Polish</li><li>Default: English</li><li>Department: Pre-filled as &quot;EMEA&quot;</li></ul><p><strong>LATAM Portal:</strong></p><ul><li>Languages: Spanish, Portuguese</li><li>Default: Spanish</li><li>Department: Pre-filled as &quot;LATAM&quot;</li></ul><p><strong>APAC Portal:</strong></p><ul><li>Languages: English, simplified translations</li><li>Default: English</li><li>Department: Pre-filled as &quot;APAC&quot;</li></ul><p><strong>Benefits:</strong></p><ul><li>Region-appropriate languages</li><li>Cultural sensitivity</li><li>Geographic tracking</li><li>Localized compliance</li></ul><h3 id="example-4-third-party-contractor-access" tabindex="-1">Example 4: Third-Party Contractor Access <a class="header-anchor" href="#example-4-third-party-contractor-access" aria-label="Permalink to &quot;Example 4: Third-Party Contractor Access&quot;">​</a></h3><p><strong>Scenario:</strong> Temporary contractors need reporting access.</p><p><strong>Configuration:</strong></p><ul><li><strong>Name:</strong> &quot;Contractor Portal - Q1 2024&quot;</li><li><strong>Expiration:</strong> 90 days (contract duration)</li><li><strong>Usage Limit:</strong> 50 submissions</li><li><strong>Custom Message:</strong> &quot;For contractors and temporary workers...&quot;</li><li><strong>Assignment:</strong> Vendor management team</li></ul><p><strong>Benefits:</strong></p><ul><li>Automatic expiration</li><li>Limited capacity</li><li>Separate from employee portal</li><li>Vendor management oversight</li></ul><h2 id="analytics-and-reporting" tabindex="-1">Analytics and Reporting <a class="header-anchor" href="#analytics-and-reporting" aria-label="Permalink to &quot;Analytics and Reporting&quot;">​</a></h2><h3 id="link-performance-metrics" tabindex="-1">Link Performance Metrics <a class="header-anchor" href="#link-performance-metrics" aria-label="Permalink to &quot;Link Performance Metrics&quot;">​</a></h3><p><strong>Submissions by Link:</strong></p><ul><li>Track which links receive most submissions</li><li>Measure campaign effectiveness</li><li>Identify popular channels</li></ul><p><strong>Conversion Rates:</strong></p><ul><li>Link views vs. submissions</li><li>Engagement measurement</li><li>Optimize distribution strategy</li></ul><p><strong>Geographic Distribution:</strong></p><ul><li>Where submissions originate</li><li>Regional trends</li><li>Location-based patterns</li></ul><p><strong>Time-Based Analysis:</strong></p><ul><li>Peak submission times by link</li><li>Seasonal patterns</li><li>Campaign timing optimization</li></ul><h3 id="export-link-analytics" tabindex="-1">Export Link Analytics <a class="header-anchor" href="#export-link-analytics" aria-label="Permalink to &quot;Export Link Analytics&quot;">​</a></h3><p><strong>Available Exports:</strong></p><ul><li>CSV: Raw data for analysis</li><li>PDF: Formatted report with charts</li><li>Excel: Detailed breakdown with pivot tables</li></ul><p><strong>Metrics Included:</strong></p><ul><li>Link name and configuration</li><li>Total submissions</li><li>Submissions over time</li><li>Department breakdown</li><li>Category distribution</li><li>Language usage</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="link-naming-conventions" tabindex="-1">Link Naming Conventions <a class="header-anchor" href="#link-naming-conventions" aria-label="Permalink to &quot;Link Naming Conventions&quot;">​</a></h3><p> <strong>Use descriptive names:</strong></p><ul><li>L Bad: &quot;Link 1&quot;, &quot;New Link&quot;, &quot;Test&quot;</li><li> Good: &quot;HR Department Portal&quot;, &quot;Spanish LATAM&quot;, &quot;Q4 Ethics Campaign&quot;</li></ul><p> <strong>Include purpose or audience:</strong></p><ul><li>&quot;Finance - SOX Reporting&quot;</li><li>&quot;Contractors - Vendor Ethics&quot;</li><li>&quot;All Staff - General Hotline&quot;</li></ul><p> <strong>Add date for time-limited links:</strong></p><ul><li>&quot;Annual Training - 2024&quot;</li><li>&quot;Q1 Campaign - Jan-Mar&quot;</li><li>&quot;Contractor Access - Spring 2024&quot;</li></ul><h3 id="security-considerations" tabindex="-1">Security Considerations <a class="header-anchor" href="#security-considerations" aria-label="Permalink to &quot;Security Considerations&quot;">​</a></h3><p> <strong>Limit link sharing:</strong></p><ul><li>Only distribute to intended audience</li><li>Avoid public posting</li><li>Use password protection if needed</li></ul><p> <strong>Regular audits:</strong></p><ul><li>Review active links quarterly</li><li>Delete unused links</li><li>Update expiration dates</li><li>Monitor for misuse</li></ul><p> <strong>Access controls:</strong></p><ul><li>Limit who can create links (Admin+ role)</li><li>Log all link creation and modifications</li><li>Alert on suspicious link usage</li></ul><h3 id="link-hygiene" tabindex="-1">Link Hygiene <a class="header-anchor" href="#link-hygiene" aria-label="Permalink to &quot;Link Hygiene&quot;">​</a></h3><p> <strong>Clean up old links:</strong></p><ul><li>Delete expired links regularly</li><li>Archive campaign links post-completion</li><li>Keep active list manageable</li></ul><p> <strong>Monitor usage:</strong></p><ul><li>Set email alerts for unusual activity</li><li>Track zero-submission links (not being used)</li><li>Adjust limits based on actual usage</li></ul><p> <strong>Update configurations:</strong></p><ul><li>Refresh expiration dates for ongoing links</li><li>Adjust language options as needed</li><li>Update auto-assignment as team changes</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><p><strong>Link not working?</strong></p><ul><li>Check if link is paused or expired</li><li>Verify usage limit not reached</li><li>Ensure correct URL copied</li><li>Test in incognito/private browsing mode</li></ul><p><strong>Wrong language displaying?</strong></p><ul><li>Verify language is enabled in link settings</li><li>Check browser language settings (may auto-detect)</li><li>Update default language in configuration</li></ul><p><strong>Reports not auto-assigning?</strong></p><ul><li>Confirm auto-assignment rule is configured</li><li>Verify assigned user has appropriate role</li><li>Check for conflicting assignment rules</li><li>Review assignment rule logs</li></ul><p><strong>QR code not scanning?</strong></p><ul><li>Ensure sufficient size (minimum 2x2 inches)</li><li>Check printer quality</li><li>Verify adequate contrast</li><li>Test with multiple QR reader apps</li></ul><p><strong>Analytics not showing submissions?</strong></p><ul><li>Allow 24 hours for data processing</li><li>Verify date range selection</li><li>Check filter settings</li><li>Ensure you have analytics permissions</li></ul><h2 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">​</a></h2><ul><li><a href="/docs/admin/organization-settings">Organization Settings</a> - General org configuration</li><li><a href="/docs/admin/custom-domains">Custom Domains</a> - Branded domain setup</li><li><a href="/docs/admin/custom-branding">Custom Branding</a> - Visual customization</li><li><a href="/docs/admin/team-management">Team Management</a> - User assignments</li><li><a href="/docs/features/assignment-rules">Assignment Rules</a> - Auto-routing configuration</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Questions about link management?</p><ul><li>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="https://disclosurely.com/tutorials/link-generator" target="_blank" rel="noreferrer">Video Tutorial</a></li></ul>`,204)])])}const m=e(l,[["render",s]]);export{d as __pageData,m as default};
