import{_ as t,c as e,o as a,ag as n}from"./chunks/framework.4QX5UEtQ.js";const u=JSON.parse('{"title":"Organization Settings - Disclosurely Admin Configuration","description":"Manage organization settings including branding, notifications, retention policies, and automation rules. Centralized configuration for administrators.","frontmatter":{"title":"Organization Settings - Disclosurely Admin Configuration","description":"Manage organization settings including branding, notifications, retention policies, and automation rules. Centralized configuration for administrators.","head":[["meta",{"property":"og:title","content":"Organization Settings & Configuration - Disclosurely"}],["meta",{"property":"og:description","content":"Manage organization settings including branding, notifications, retention policies, and automation rules. Centralized configuration for administrators."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Organization Settings - Disclosurely"}],["meta",{"name":"twitter:description","content":"Manage organization settings including branding, notifications, retention policies, and automation rules. Centralized configuration for administrators."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"admin/organization-settings.md","filePath":"admin/organization-settings.md"}'),o={name:"admin/organization-settings.md"};function l(r,i,s,c,g,p){return a(),e("div",null,[...i[0]||(i[0]=[n('<h1 id="organization-settings" tabindex="-1">Organization Settings <a class="header-anchor" href="#organization-settings" aria-label="Permalink to &quot;Organization Settings&quot;">​</a></h1><p>Manage your organization&#39;s core settings, branding, and configuration in one centralized location.</p><h2 id="accessing-organization-settings" tabindex="-1">Accessing Organization Settings <a class="header-anchor" href="#accessing-organization-settings" aria-label="Permalink to &quot;Accessing Organization Settings&quot;">​</a></h2><ol><li>Navigate to <strong>Dashboard &gt; Settings</strong></li><li>Click <strong>Organization</strong> in the left sidebar</li><li>Make desired changes</li><li>Click <strong>Save Changes</strong></li></ol><h2 id="organization-profile" tabindex="-1">Organization Profile <a class="header-anchor" href="#organization-profile" aria-label="Permalink to &quot;Organization Profile&quot;">​</a></h2><h3 id="basic-information" tabindex="-1">Basic Information <a class="header-anchor" href="#basic-information" aria-label="Permalink to &quot;Basic Information&quot;">​</a></h3><p><strong>Organization Name</strong></p><ul><li>Your company&#39;s official name</li><li>Appears on reporting portal and emails</li><li>Can be changed anytime</li><li>Best practice: Use full legal name</li></ul><p><strong>Organization Domain</strong></p><ul><li>Your company&#39;s website domain</li><li>Used for email verification</li><li>Helps prevent unauthorized access</li><li>Format: yourcompany.com (no http://)</li></ul><p><strong>Industry/Sector</strong></p><ul><li>Select your primary industry</li><li>Helps with compliance guidance</li><li>Used for benchmarking</li><li>Can update as organization changes</li></ul><p><strong>Organization Size</strong></p><ul><li>Employee count range</li><li>Updates automatically with team growth</li><li>Used for analytics and pricing</li><li>Options: 1-50, 51-200, 201-1000, 1000+</li></ul><h3 id="contact-information" tabindex="-1">Contact Information <a class="header-anchor" href="#contact-information" aria-label="Permalink to &quot;Contact Information&quot;">​</a></h3><p><strong>Primary Contact</strong></p><ul><li>Main administrator&#39;s name</li><li>Receives important system notifications</li><li>Account-related communications</li><li>Can be updated anytime</li></ul><p><strong>Contact Email</strong></p><ul><li>Primary email for organization</li><li>System notifications sent here</li><li>Should be monitored regularly</li><li>Can set multiple notification emails</li></ul><p><strong>Support Phone</strong> (Optional)</p><ul><li>Displayed on reporting portal</li><li>For reporter questions</li><li>Not required but recommended</li><li>Helps build trust with reporters</li></ul><h2 id="reporting-configuration" tabindex="-1">Reporting Configuration <a class="header-anchor" href="#reporting-configuration" aria-label="Permalink to &quot;Reporting Configuration&quot;">​</a></h2><h3 id="secure-reporting-link" tabindex="-1">Secure Reporting Link <a class="header-anchor" href="#secure-reporting-link" aria-label="Permalink to &quot;Secure Reporting Link&quot;">​</a></h3><p>Your organization has two types of reporting URLs:</p><p><strong>Default Subdomain URL</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://[your-org-name].disclosurely.com/report</span></span></code></pre></div><p><strong>Custom Branded Domain</strong> (Optional):</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://reports.yourcompany.com</span></span></code></pre></div><p><strong>Managing Your Links</strong>:</p><ul><li>Default subdomain is automatically generated from your organization name</li><li>Set up custom branded domains in <a href="/docs/admin/custom-domains">Custom Domains</a> settings</li><li>Create and manage multiple reporting links in the Link Generator</li><li>QR codes available for printing and distribution</li><li>See <a href="/docs/admin/link-generator">Link Generator &amp; Management</a> for detailed link configuration</li></ul><h3 id="report-form-settings" tabindex="-1">Report Form Settings <a class="header-anchor" href="#report-form-settings" aria-label="Permalink to &quot;Report Form Settings&quot;">​</a></h3><p><strong>Required Fields</strong>:</p><ul><li>Report description (always required)</li><li>Incident date</li><li>Category selection</li></ul><p><strong>Optional Fields</strong> (toggle on/off):</p><ul><li>☑ Location/Department</li><li>☑ Witness information</li><li>☑ File uploads</li><li>☑ Reporter contact (confidential reports)</li><li>☑ Incident time</li><li>☑ Additional context</li></ul><p><strong>Custom Fields</strong>:</p><ul><li>Add organization-specific fields</li><li>Text, dropdown, or checkbox</li><li>Mark as required or optional</li><li>Up to 5 custom fields (Pro plan)</li></ul><h3 id="category-management" tabindex="-1">Category Management <a class="header-anchor" href="#category-management" aria-label="Permalink to &quot;Category Management&quot;">​</a></h3><p>Customize report categories:</p><p><strong>Default Categories</strong>:</p><ul><li>Harassment &amp; Discrimination</li><li>Financial Misconduct</li><li>Health &amp; Safety</li><li>Data Privacy</li><li>Conflicts of Interest</li><li>Other</li></ul><p><strong>Add Custom Category</strong>:</p><ol><li>Click <strong>Add Category</strong></li><li>Enter category name</li><li>Add description</li><li>Set routing rules (optional)</li><li>Choose category color</li><li>Save</li></ol><p><strong>Sub-Categories</strong>:</p><ul><li>Add sub-categories under main categories</li><li>Helps with detailed classification</li><li>Up to 3 levels deep</li><li>Improves analytics and routing</li></ul><h2 id="data-privacy-settings" tabindex="-1">Data &amp; Privacy Settings <a class="header-anchor" href="#data-privacy-settings" aria-label="Permalink to &quot;Data &amp; Privacy Settings&quot;">​</a></h2><h3 id="data-retention" tabindex="-1">Data Retention <a class="header-anchor" href="#data-retention" aria-label="Permalink to &quot;Data Retention&quot;">​</a></h3><p><strong>Report Retention</strong>:</p><ul><li>How long to keep completed reports</li><li>Options: 1 year, 3 years, 5 years, 7 years, indefinitely</li><li>Consider regulatory requirements</li><li>Archived reports follow same retention</li></ul><p><strong>Audit Log Retention</strong>:</p><ul><li>How long to keep audit logs</li><li>Minimum: 1 year (recommended: 7 years)</li><li>Critical for compliance</li><li>Cannot be deleted before retention period</li></ul><h3 id="gdpr-settings" tabindex="-1">GDPR Settings <a class="header-anchor" href="#gdpr-settings" aria-label="Permalink to &quot;GDPR Settings&quot;">​</a></h3><p><strong>Data Processing Region</strong>:</p><ul><li>Choose where data is stored</li><li>Options: EU, US, UK</li><li>Affects data sovereignty</li><li>Set based on your jurisdiction</li></ul><p><strong>Automatic Data Requests</strong>:</p><ul><li>Enable automatic GDPR processing</li><li>Safety period before deletion (24 hours)</li><li>Email notifications at each stage</li><li>Requires Pro or Enterprise plan</li></ul><p>See <a href="/docs/compliance/gdpr">GDPR Compliance</a> for details.</p><h2 id="notification-preferences" tabindex="-1">Notification Preferences <a class="header-anchor" href="#notification-preferences" aria-label="Permalink to &quot;Notification Preferences&quot;">​</a></h2><h3 id="email-notifications" tabindex="-1">Email Notifications <a class="header-anchor" href="#email-notifications" aria-label="Permalink to &quot;Email Notifications&quot;">​</a></h3><p><strong>New Report Notifications</strong>:</p><ul><li>Who receives alerts: All Admins | All Handlers | Specific Users</li><li>Frequency: Immediate | Daily Digest | Weekly Digest</li><li>Email template customization</li><li>Test notification feature</li></ul><p><strong>Status Update Notifications</strong>:</p><ul><li>Notify reporters of status changes</li><li>Automatic via tracking ID</li><li>Customizable templates</li><li>Can include custom messages</li></ul><p><strong>Assignment Notifications</strong>:</p><ul><li>Alert team members when assigned</li><li>Include case summary</li><li>Immediate delivery</li><li>Cannot be disabled</li></ul><p><strong>Compliance Reminders</strong>:</p><ul><li>Upcoming deadline alerts</li><li>Configurable reminder timing</li><li>Multiple reminder schedule</li><li>Sent to responsible parties</li></ul><h3 id="in-app-notifications" tabindex="-1">In-App Notifications <a class="header-anchor" href="#in-app-notifications" aria-label="Permalink to &quot;In-App Notifications&quot;">​</a></h3><p><strong>Real-Time Alerts</strong>:</p><ul><li>New reports</li><li>New messages</li><li>Assignments</li><li>Status changes</li><li>GDPR requests</li></ul><p><strong>Notification Settings</strong>:</p><ul><li>Enable/disable per type</li><li>Sound alerts</li><li>Desktop notifications (browser permission required)</li><li>Badge counters</li></ul><h2 id="subscription-billing" tabindex="-1">Subscription &amp; Billing <a class="header-anchor" href="#subscription-billing" aria-label="Permalink to &quot;Subscription &amp; Billing&quot;">​</a></h2><p>Subscription details are managed separately.</p><p>See <a href="/docs/admin/subscription-billing">Subscription &amp; Billing</a> for:</p><ul><li>Current plan details</li><li>Billing information</li><li>Upgrade/downgrade options</li><li>Invoice history</li><li>Usage statistics</li></ul><h2 id="advanced-settings" tabindex="-1">Advanced Settings <a class="header-anchor" href="#advanced-settings" aria-label="Permalink to &quot;Advanced Settings&quot;">​</a></h2><h3 id="security-settings" tabindex="-1">Security Settings <a class="header-anchor" href="#security-settings" aria-label="Permalink to &quot;Security Settings&quot;">​</a></h3><p><strong>Session Timeout</strong>:</p><ul><li>Automatic logout after inactivity</li><li>Options: 15 min, 30 min, 1 hour, 4 hours, 8 hours</li><li>Recommended: 30 minutes for security</li><li>Applies to all users</li></ul><p><strong>Password Requirements</strong>:</p><ul><li>Minimum length (8-16 characters)</li><li>Require special characters</li><li>Require numbers</li><li>Require uppercase/lowercase</li><li>Password expiration (optional)</li></ul><p><strong>Multi-Factor Authentication</strong>:</p><ul><li>Require MFA for all users</li><li>Require MFA for admins only</li><li>Optional MFA</li><li>MFA methods: Email OTP, Authenticator app</li></ul><h3 id="api-access-enterprise" tabindex="-1">API Access (Enterprise) <a class="header-anchor" href="#api-access-enterprise" aria-label="Permalink to &quot;API Access (Enterprise)&quot;">​</a></h3><p><strong>API Keys</strong>:</p><ul><li>Generate API keys for integrations</li><li>Set expiration dates</li><li>Revoke keys</li><li>Monitor API usage</li></ul><p><strong>Webhooks</strong> (Coming Soon):</p><ul><li>Configure webhook endpoints</li><li>Choose events to trigger webhooks</li><li>Test webhook delivery</li><li>View webhook logs</li></ul><h2 id="compliance-settings" tabindex="-1">Compliance Settings <a class="header-anchor" href="#compliance-settings" aria-label="Permalink to &quot;Compliance Settings&quot;">​</a></h2><h3 id="audit-configuration" tabindex="-1">Audit Configuration <a class="header-anchor" href="#audit-configuration" aria-label="Permalink to &quot;Audit Configuration&quot;">​</a></h3><p><strong>Audit Detail Level</strong>:</p><ul><li>Basic: Login/logout, major actions</li><li>Standard: All user actions</li><li>Detailed: Every action and data change</li></ul><p><strong>Audit Export</strong>:</p><ul><li>Export format: CSV, JSON</li><li>Date range selection</li><li>Filtered by user or action type</li></ul><h3 id="policy-acknowledgment" tabindex="-1">Policy Acknowledgment <a class="header-anchor" href="#policy-acknowledgment" aria-label="Permalink to &quot;Policy Acknowledgment&quot;">​</a></h3><p><strong>Require Acknowledgment</strong>:</p><ul><li>Policies requiring employee acknowledgment</li><li>Acknowledgment tracking</li><li>Reminder schedule for unacknowledged</li><li>Reporting on compliance</li></ul><h2 id="organization-deletion" tabindex="-1">Organization Deletion <a class="header-anchor" href="#organization-deletion" aria-label="Permalink to &quot;Organization Deletion&quot;">​</a></h2><p><strong>Permanently Delete Organization</strong>:</p><p>⚠️ <strong>Warning</strong>: This action is irreversible!</p><p>Deleting your organization:</p><ul><li>Removes all reports and data</li><li>Deletes all team member access</li><li>Cancels subscription</li><li>Cannot be undone</li></ul><p><strong>24-hour safety period before deletion</strong></p><p>To delete:</p><ol><li>Scroll to bottom of settings</li><li>Click <strong>Delete Organization</strong></li><li>Type organization name to confirm</li><li>Enter password</li><li>Click <strong>Confirm Deletion</strong></li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ol><li><strong>Review Regularly</strong>: Check settings quarterly</li><li><strong>Test Features</strong>: Use test reports to verify configuration</li><li><strong>Document Changes</strong>: Keep log of setting changes</li><li><strong>Train Team</strong>: Ensure team understands notification system</li><li><strong>Monitor Usage</strong>: Review analytics to optimize settings</li></ol><h2 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-label="Permalink to &quot;Getting Help&quot;">​</a></h2><p><strong>Questions about settings?</strong></p><ul><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="/docs/support/faqs">FAQs</a></li><li>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li></ul><hr><p><strong>Next Steps:</strong></p><ul><li><a href="/docs/admin/team-management">Team Management</a></li><li><a href="/docs/admin/custom-branding">Custom Branding</a></li><li><a href="/docs/admin/subscription-billing">Subscription &amp; Billing</a></li></ul>',114)])])}const m=t(o,[["render",l]]);export{u as __pageData,m as default};
