import{_ as a,c as e,o as t,ag as l}from"./chunks/framework.4QX5UEtQ.js";const p=JSON.parse(`{"title":"Subscription & Billing - Disclosurely Plans & Pricing","description":"Manage subscriptions, upgrade plans, update billing information, and track usage. Compare Basic, Pro, and Enterprise plans for your organization's needs.","frontmatter":{"title":"Subscription & Billing - Disclosurely Plans & Pricing","description":"Manage subscriptions, upgrade plans, update billing information, and track usage. Compare Basic, Pro, and Enterprise plans for your organization's needs.","head":[["meta",{"property":"og:title","content":"Subscription & Billing Management - Disclosurely"}],["meta",{"property":"og:description","content":"Manage subscriptions, upgrade plans, update billing information, and track usage. Compare Basic, Pro, and Enterprise plans for your organization's needs."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Subscription & Billing - Disclosurely"}],["meta",{"name":"twitter:description","content":"Manage subscriptions, upgrade plans, update billing information, and track usage. Compare Basic, Pro, and Enterprise plans for your organization's needs."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"admin/subscription-billing.md","filePath":"admin/subscription-billing.md"}`),n={name:"admin/subscription-billing.md"};function o(r,i,s,c,d,u){return t(),e("div",null,[...i[0]||(i[0]=[l('<h1 id="subscription-billing" tabindex="-1">Subscription &amp; Billing <a class="header-anchor" href="#subscription-billing" aria-label="Permalink to &quot;Subscription &amp; Billing&quot;">​</a></h1><p>Manage your Disclosurely subscription, billing information, and usage in one place.</p><h2 id="subscription-plans" tabindex="-1">Subscription Plans <a class="header-anchor" href="#subscription-plans" aria-label="Permalink to &quot;Subscription Plans&quot;">​</a></h2><h3 id="basic-free" tabindex="-1">Basic (Free) <a class="header-anchor" href="#basic-free" aria-label="Permalink to &quot;Basic (Free)&quot;">​</a></h3><ul><li>10 reports per month</li><li>Core reporting features</li><li>Basic case management</li><li>Email notifications</li><li>30-day data retention</li></ul><h3 id="pro-£39-99-month" tabindex="-1">Pro (£39.99/month) <a class="header-anchor" href="#pro-£39-99-month" aria-label="Permalink to &quot;Pro (£39.99/month)&quot;">​</a></h3><ul><li>Unlimited reports</li><li>Advanced case management</li><li>AI-powered analysis</li><li>Custom branding</li><li>Custom domains</li><li>Compliance management</li><li>GDPR automation</li><li>Priority support</li></ul><h3 id="enterprise-custom" tabindex="-1">Enterprise (Custom) <a class="header-anchor" href="#enterprise-custom" aria-label="Permalink to &quot;Enterprise (Custom)&quot;">​</a></h3><ul><li>Everything in Pro</li><li>Dedicated support</li><li>Custom integrations</li><li>SLA guarantees</li><li>Volume discounts</li><li>Annual billing</li><li>Training included</li></ul><h2 id="viewing-your-subscription" tabindex="-1">Viewing Your Subscription <a class="header-anchor" href="#viewing-your-subscription" aria-label="Permalink to &quot;Viewing Your Subscription&quot;">​</a></h2><p>Navigate to <strong>Settings &gt; Billing</strong> to view:</p><ul><li>Current plan</li><li>Billing cycle</li><li>Next billing date</li><li>Usage statistics</li><li>Invoice history</li><li>Payment method</li></ul><h2 id="upgrading-your-plan" tabindex="-1">Upgrading Your Plan <a class="header-anchor" href="#upgrading-your-plan" aria-label="Permalink to &quot;Upgrading Your Plan&quot;">​</a></h2><h3 id="upgrade-to-pro" tabindex="-1">Upgrade to Pro <a class="header-anchor" href="#upgrade-to-pro" aria-label="Permalink to &quot;Upgrade to Pro&quot;">​</a></h3><ol><li>Go to <strong>Settings &gt; Billing</strong></li><li>Click <strong>&quot;Upgrade to Pro&quot;</strong></li><li>Enter payment information</li><li>Review pricing (£39.99/month)</li><li>Click <strong>&quot;Confirm Upgrade&quot;</strong></li></ol><p><strong>What Happens</strong>:</p><ul><li>Immediate access to Pro features</li><li>Prorated charge for current month</li><li>Billing cycle starts from upgrade date</li></ul><h3 id="upgrade-to-enterprise" tabindex="-1">Upgrade to Enterprise <a class="header-anchor" href="#upgrade-to-enterprise" aria-label="Permalink to &quot;Upgrade to Enterprise&quot;">​</a></h3><ol><li>Click <strong>&quot;Contact Sales&quot;</strong> in billing settings</li><li>Complete inquiry form</li><li>Schedule consultation call</li><li>Receive custom quote</li><li>Sign agreement</li><li>Account upgraded</li></ol><h2 id="downgrading-your-plan" tabindex="-1">Downgrading Your Plan <a class="header-anchor" href="#downgrading-your-plan" aria-label="Permalink to &quot;Downgrading Your Plan&quot;">​</a></h2><p>To downgrade from Pro to Basic:</p><ol><li>Navigate to <strong>Settings &gt; Billing</strong></li><li>Click <strong>&quot;Change Plan&quot;</strong></li><li>Select <strong>Basic</strong></li><li>Confirm downgrade</li><li>Takes effect at end of billing cycle</li></ol><p><strong>Important</strong>:</p><ul><li>Access to Pro features until cycle ends</li><li>Custom branding removed</li><li>Custom domains disabled</li><li>AI features locked</li><li>Report history retained</li></ul><h2 id="managing-payment" tabindex="-1">Managing Payment <a class="header-anchor" href="#managing-payment" aria-label="Permalink to &quot;Managing Payment&quot;">​</a></h2><h3 id="update-payment-method" tabindex="-1">Update Payment Method <a class="header-anchor" href="#update-payment-method" aria-label="Permalink to &quot;Update Payment Method&quot;">​</a></h3><ol><li>Go to <strong>Settings &gt; Billing</strong></li><li>Click <strong>&quot;Update Payment Method&quot;</strong></li><li>Enter new card details</li><li>Click <strong>&quot;Save&quot;</strong></li><li>New method used for next billing</li></ol><h3 id="payment-methods-accepted" tabindex="-1">Payment Methods Accepted <a class="header-anchor" href="#payment-methods-accepted" aria-label="Permalink to &quot;Payment Methods Accepted&quot;">​</a></h3><ul><li>Credit/Debit Cards (Visa, Mastercard, Amex)</li><li>Apple Pay</li><li>Google Pay</li><li>Bank Transfer (Enterprise, annual billing)</li></ul><h2 id="billing-invoices" tabindex="-1">Billing &amp; Invoices <a class="header-anchor" href="#billing-invoices" aria-label="Permalink to &quot;Billing &amp; Invoices&quot;">​</a></h2><h3 id="view-invoices" tabindex="-1">View Invoices <a class="header-anchor" href="#view-invoices" aria-label="Permalink to &quot;View Invoices&quot;">​</a></h3><p>Access all invoices:</p><ol><li>Navigate to <strong>Settings &gt; Billing &gt; Invoices</strong></li><li>View invoice list with: <ul><li>Invoice number</li><li>Date</li><li>Amount</li><li>Status (Paid, Pending, Failed)</li></ul></li><li>Download PDF for accounting</li></ol><h3 id="failed-payments" tabindex="-1">Failed Payments <a class="header-anchor" href="#failed-payments" aria-label="Permalink to &quot;Failed Payments&quot;">​</a></h3><p>If payment fails:</p><ol><li>Email notification sent</li><li>Retry attempt after 3 days</li><li>Second retry after 7 days</li><li>Account suspended if still failing</li><li>Update payment method to resolve</li></ol><p><strong>Account Suspended</strong>:</p><ul><li>Reporting portal disabled</li><li>Cannot access dashboard</li><li>Data preserved</li><li>Reactivated upon payment</li></ul><h2 id="usage-limits" tabindex="-1">Usage &amp; Limits <a class="header-anchor" href="#usage-limits" aria-label="Permalink to &quot;Usage &amp; Limits&quot;">​</a></h2><h3 id="current-usage" tabindex="-1">Current Usage <a class="header-anchor" href="#current-usage" aria-label="Permalink to &quot;Current Usage&quot;">​</a></h3><p>View usage statistics:</p><p><strong>Reports</strong>:</p><ul><li>Total reports this month</li><li>Remaining allowance (Basic plan)</li><li>Historical usage trends</li></ul><p><strong>Team Members</strong>:</p><ul><li>Active team members</li><li>Available seats</li><li>Additional seat costs</li></ul><p><strong>AI Conversations</strong>:</p><ul><li>AI conversations used (Pro plan: 100/month)</li><li>Remaining allowance</li><li>Usage history</li></ul><p><strong>Storage</strong>:</p><ul><li>Total storage used</li><li>File upload volumes</li><li>Retention periods</li></ul><h3 id="exceeding-limits" tabindex="-1">Exceeding Limits <a class="header-anchor" href="#exceeding-limits" aria-label="Permalink to &quot;Exceeding Limits&quot;">​</a></h3><p><strong>Basic Plan - Report Limit</strong>:</p><ul><li>10 reports per month included</li><li>Upgrade to Pro for unlimited</li><li>Cannot submit reports once limit reached</li></ul><p><strong>Pro Plan - Team Seats</strong>:</p><ul><li>5 seats included</li><li>Additional seats: £5/user/month</li><li>Add/remove seats anytime</li><li>Prorated billing</li></ul><h2 id="canceling-your-subscription" tabindex="-1">Canceling Your Subscription <a class="header-anchor" href="#canceling-your-subscription" aria-label="Permalink to &quot;Canceling Your Subscription&quot;">​</a></h2><p>To cancel your Pro subscription:</p><ol><li>Navigate to <strong>Settings &gt; Billing</strong></li><li>Click <strong>&quot;Cancel Subscription&quot;</strong></li><li>Provide feedback (optional)</li><li>Confirm cancellation</li></ol><p><strong>What Happens</strong>:</p><ul><li>Access continues until cycle ends</li><li>No further charges</li><li>Downgraded to Basic at renewal</li><li>Data preserved for 90 days</li></ul><h3 id="data-after-cancellation" tabindex="-1">Data After Cancellation <a class="header-anchor" href="#data-after-cancellation" aria-label="Permalink to &quot;Data After Cancellation&quot;">​</a></h3><p><strong>90-Day Grace Period</strong>:</p><ul><li>All data accessible (read-only)</li><li>Can export data</li><li>Can reactivate subscription</li><li>No charges during grace period</li></ul><p><strong>After 90 Days</strong>:</p><ul><li>Data permanently deleted</li><li>Cannot be recovered</li><li>Export data before deletion</li></ul><h2 id="billing-faq" tabindex="-1">Billing FAQ <a class="header-anchor" href="#billing-faq" aria-label="Permalink to &quot;Billing FAQ&quot;">​</a></h2><p><strong>Q: Can I get a refund?</strong> A: 14-day money-back guarantee on first Pro subscription. Contact support within 14 days.</p><p><strong>Q: Do you offer discounts?</strong> A: Annual billing: 20% discount. Non-profits: Contact sales for special pricing.</p><p><strong>Q: Can I pause my subscription?</strong> A: No, but you can cancel and reactivate anytime within 90 days.</p><p><strong>Q: What happens if I don&#39;t pay?</strong> A: Account suspended after failed retry attempts. Data preserved for 90 days.</p><p><strong>Q: Can I pay annually?</strong> A: Yes! Annual billing available with 20% discount. Contact sales.</p><hr><p><strong>Questions?</strong></p><ul><li>Email: <a href="mailto:billing@disclosurely.com" target="_blank" rel="noreferrer">billing@disclosurely.com</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="https://disclosurely.com/pricing" target="_blank" rel="noreferrer">Pricing Page</a></li></ul>',73)])])}const m=a(n,[["render",o]]);export{p as __pageData,m as default};
