import{_ as t,c as i,o as a,ag as n}from"./chunks/framework.4QX5UEtQ.js";const u=JSON.parse('{"title":"Team Management - Disclosurely User Roles & Access","description":"Invite team members, assign roles (Admin, Case Handler, Reviewer), and manage permissions. Configure role-based access control for your investigation team.","frontmatter":{"title":"Team Management - Disclosurely User Roles & Access","description":"Invite team members, assign roles (Admin, Case Handler, Reviewer), and manage permissions. Configure role-based access control for your investigation team.","head":[["meta",{"property":"og:title","content":"Team Management & User Roles - Disclosurely"}],["meta",{"property":"og:description","content":"Invite team members, assign roles (Admin, Case Handler, Reviewer), and manage permissions. Configure role-based access control for your investigation team."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Team Management - Disclosurely"}],["meta",{"name":"twitter:description","content":"Invite team members, assign roles (Admin, Case Handler, Reviewer), and manage permissions. Configure role-based access control for your investigation team."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"admin/team-management.md","filePath":"admin/team-management.md"}'),r={name:"admin/team-management.md"};function s(o,e,l,d,m,g){return a(),i("div",null,[...e[0]||(e[0]=[n('<h1 id="team-management" tabindex="-1">Team Management <a class="header-anchor" href="#team-management" aria-label="Permalink to &quot;Team Management&quot;">​</a></h1><p>Manage your team members, assign roles, send invitations, and control access to your Disclosurely organization.</p><h2 id="user-roles-overview" tabindex="-1">User Roles Overview <a class="header-anchor" href="#user-roles-overview" aria-label="Permalink to &quot;User Roles Overview&quot;">​</a></h2><p>Disclosurely uses role-based access control with four primary roles:</p><h3 id="admin" tabindex="-1">Admin <a class="header-anchor" href="#admin" aria-label="Permalink to &quot;Admin&quot;">​</a></h3><p><strong>Full system access</strong> including:</p><ul><li>All reports and cases</li><li>Team management (add/remove users)</li><li>Organization settings</li><li>Billing and subscription management</li><li>Custom domain configuration</li><li>All compliance modules</li><li>System configuration</li></ul><p><strong>Best for</strong>: IT administrators, compliance officers, system owners</p><h3 id="org-admin" tabindex="-1">Org Admin <a class="header-anchor" href="#org-admin" aria-label="Permalink to &quot;Org Admin&quot;">​</a></h3><p><strong>Organization-level administration</strong>:</p><ul><li>View and manage all reports</li><li>Team management (invite/remove members)</li><li>Organization settings</li><li>Custom branding</li><li>Compliance management</li><li>Analytics and reporting</li><li><strong>Cannot modify billing</strong></li></ul><p><strong>Best for</strong>: HR directors, legal team, department heads</p><h3 id="case-handler" tabindex="-1">Case Handler <a class="header-anchor" href="#case-handler" aria-label="Permalink to &quot;Case Handler&quot;">​</a></h3><p><strong>Day-to-day case management</strong>:</p><ul><li>View assigned reports</li><li>Update report status</li><li>Add case notes</li><li>Send messages to whistleblowers</li><li>Assign reports to team members</li><li>Access analytics</li><li><strong>Cannot modify organization settings</strong></li></ul><p><strong>Best for</strong>: Investigators, compliance team, case managers</p><h3 id="reviewer" tabindex="-1">Reviewer <a class="header-anchor" href="#reviewer" aria-label="Permalink to &quot;Reviewer&quot;">​</a></h3><p><strong>Read-only access</strong>:</p><ul><li>View assigned reports</li><li>Read messages and notes</li><li>Access analytics</li><li><strong>Cannot modify cases or settings</strong></li></ul><p><strong>Best for</strong>: Auditors, oversight roles, board members</p><h2 id="inviting-team-members" tabindex="-1">Inviting Team Members <a class="header-anchor" href="#inviting-team-members" aria-label="Permalink to &quot;Inviting Team Members&quot;">​</a></h2><h3 id="send-an-invitation" tabindex="-1">Send an Invitation <a class="header-anchor" href="#send-an-invitation" aria-label="Permalink to &quot;Send an Invitation&quot;">​</a></h3><ol><li>Navigate to <strong>Dashboard &gt; Team</strong></li><li>Click <strong>&quot;Invite Team Member&quot;</strong> button</li><li>Fill in invitation form: <ul><li><strong>Email Address</strong>: Invitee&#39;s work email</li><li><strong>Role</strong>: Select appropriate role</li><li><strong>First Name</strong>: Optional, personalizes invitation</li><li><strong>Last Name</strong>: Optional</li><li><strong>Custom Message</strong>: Optional welcome message</li></ul></li><li>Click <strong>&quot;Send Invitation&quot;</strong></li></ol><h3 id="what-happens-next" tabindex="-1">What Happens Next <a class="header-anchor" href="#what-happens-next" aria-label="Permalink to &quot;What Happens Next&quot;">​</a></h3><p><strong>Invitee Receives</strong>:</p><ul><li>Email with invitation link</li><li>Information about your organization</li><li>Role assignment details</li><li>Expiration notice (7 days)</li><li>Instructions to create account</li></ul><p><strong>Invitation Process</strong>:</p><ol><li>Invitee clicks link in email</li><li>Creates Disclosurely account</li><li>Sets up password</li><li>Automatically added to your organization</li><li>Role is assigned</li></ol><h3 id="managing-invitations" tabindex="-1">Managing Invitations <a class="header-anchor" href="#managing-invitations" aria-label="Permalink to &quot;Managing Invitations&quot;">​</a></h3><p><strong>View Pending Invitations</strong>:</p><ul><li>Navigate to <strong>Team &gt; Invitations</strong></li><li>See all pending invitations</li><li>Check status and expiration</li></ul><p><strong>Resend Invitation</strong>:</p><ul><li>Click <strong>Resend</strong> next to expired invitation</li><li>Generates new invitation link</li><li>Resets 7-day expiration timer</li></ul><p><strong>Cancel Invitation</strong>:</p><ul><li>Click <strong>Cancel</strong> next to pending invitation</li><li>Invitee can no longer accept</li><li>Can send new invitation if needed</li></ul><h2 id="managing-team-members" tabindex="-1">Managing Team Members <a class="header-anchor" href="#managing-team-members" aria-label="Permalink to &quot;Managing Team Members&quot;">​</a></h2><h3 id="view-team-members" tabindex="-1">View Team Members <a class="header-anchor" href="#view-team-members" aria-label="Permalink to &quot;View Team Members&quot;">​</a></h3><p>Navigate to <strong>Dashboard &gt; Team</strong> to see:</p><ul><li>All active team members</li><li>Their roles</li><li>Last login date</li><li>Account status</li><li>Contact information</li></ul><h3 id="edit-team-member" tabindex="-1">Edit Team Member <a class="header-anchor" href="#edit-team-member" aria-label="Permalink to &quot;Edit Team Member&quot;">​</a></h3><p><strong>Change Role</strong>:</p><ol><li>Click on team member</li><li>Select <strong>&quot;Edit Role&quot;</strong></li><li>Choose new role</li><li>Click <strong>&quot;Save&quot;</strong></li><li>Changes take effect immediately</li></ol><p><strong>Update Information</strong>:</p><ul><li>Edit name, email (requires verification)</li><li>Update phone number</li><li>Modify department or title</li></ul><h3 id="deactivate-team-member" tabindex="-1">Deactivate Team Member <a class="header-anchor" href="#deactivate-team-member" aria-label="Permalink to &quot;Deactivate Team Member&quot;">​</a></h3><p><strong>When employee leaves</strong>:</p><ol><li>Click on team member</li><li>Select <strong>&quot;Deactivate Account&quot;</strong></li><li>Confirm deactivation</li><li>User loses access immediately</li><li>Can be reactivated later if needed</li></ol><p><strong>What Happens</strong>:</p><ul><li>User cannot log in</li><li>Assigned cases remain</li><li>Audit history preserved</li><li>No data is deleted</li><li>Subscription seat freed (Pro/Enterprise)</li></ul><h3 id="remove-team-member" tabindex="-1">Remove Team Member <a class="header-anchor" href="#remove-team-member" aria-label="Permalink to &quot;Remove Team Member&quot;">​</a></h3><p><strong>Permanently remove</strong>:</p><ol><li>Click on team member</li><li>Select <strong>&quot;Remove from Organization&quot;</strong></li><li>Type member&#39;s email to confirm</li><li>Click <strong>&quot;Remove&quot;</strong></li></ol><p><strong>What Happens</strong>:</p><ul><li>User removed from organization</li><li>Cannot access your data</li><li>Audit logs maintained</li><li>Assigned cases need reassignment</li></ul><h2 id="role-permissions-matrix" tabindex="-1">Role Permissions Matrix <a class="header-anchor" href="#role-permissions-matrix" aria-label="Permalink to &quot;Role Permissions Matrix&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Permission</th><th>Admin</th><th>Org Admin</th><th>Case Handler</th><th>Reviewer</th></tr></thead><tbody><tr><td>View all reports</td><td>✅</td><td>✅</td><td>✅</td><td>❌ View assigned only</td></tr><tr><td>Create/edit reports</td><td>✅</td><td>✅</td><td>✅</td><td>❌</td></tr><tr><td>Delete reports</td><td>✅</td><td>✅</td><td>❌</td><td>❌</td></tr><tr><td>Assign cases</td><td>✅</td><td>✅</td><td>✅</td><td>❌</td></tr><tr><td>Add case notes</td><td>✅</td><td>✅</td><td>✅</td><td>❌</td></tr><tr><td>Send messages</td><td>✅</td><td>✅</td><td>✅</td><td>❌</td></tr><tr><td>View analytics</td><td>✅</td><td>✅</td><td>✅ Limited</td><td>✅ Limited</td></tr><tr><td>Manage team</td><td>✅</td><td>✅</td><td>❌</td><td>❌</td></tr><tr><td>Organization settings</td><td>✅</td><td>✅</td><td>❌</td><td>❌</td></tr><tr><td>Billing</td><td>✅</td><td>❌</td><td>❌</td><td>❌</td></tr><tr><td>Custom branding</td><td>✅</td><td>✅</td><td>❌</td><td>❌</td></tr><tr><td>Custom domains</td><td>✅</td><td>✅</td><td>❌</td><td>❌</td></tr><tr><td>Compliance modules</td><td>✅</td><td>✅</td><td>✅ Limited</td><td>✅ View only</td></tr><tr><td>Audit logs</td><td>✅</td><td>✅</td><td>❌</td><td>✅ View only</td></tr><tr><td>API access</td><td>✅</td><td>❌</td><td>❌</td><td>❌</td></tr></tbody></table><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="onboarding-new-team-members" tabindex="-1">Onboarding New Team Members <a class="header-anchor" href="#onboarding-new-team-members" aria-label="Permalink to &quot;Onboarding New Team Members&quot;">​</a></h3><ol><li><strong>Send invitation</strong> with role appropriate to duties</li><li><strong>Provide training</strong> on platform usage</li><li><strong>Assign test case</strong> for practice</li><li><strong>Share documentation</strong> links</li><li><strong>Set up 1-on-1</strong> to answer questions</li><li><strong>Monitor first cases</strong> for quality</li></ol><h3 id="team-structure-recommendations" tabindex="-1">Team Structure Recommendations <a class="header-anchor" href="#team-structure-recommendations" aria-label="Permalink to &quot;Team Structure Recommendations&quot;">​</a></h3><p><strong>Small Organization (1-10 employees)</strong>:</p><ul><li>1 Admin</li><li>1-2 Case Handlers</li></ul><p><strong>Medium Organization (11-100 employees)</strong>:</p><ul><li>1-2 Admins</li><li>1 Org Admin</li><li>2-4 Case Handlers</li><li>1 Reviewer (optional)</li></ul><p><strong>Large Organization (100+ employees)</strong>:</p><ul><li>2-3 Admins</li><li>2-3 Org Admins</li><li>5-10 Case Handlers</li><li>2-3 Reviewers</li></ul><h3 id="security-best-practices" tabindex="-1">Security Best Practices <a class="header-anchor" href="#security-best-practices" aria-label="Permalink to &quot;Security Best Practices&quot;">​</a></h3><p>✅ <strong>Review team access quarterly</strong> ✅ <strong>Remove access immediately upon employee departure</strong> ✅ <strong>Use principle of least privilege</strong> (minimum necessary access) ✅ <strong>Require strong passwords</strong> ✅ <strong>Enable MFA for administrators</strong> ✅ <strong>Monitor login activity</strong> ✅ <strong>Regular training on data handling</strong></p><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><p><strong>Invitation not received</strong>:</p><ul><li>Check spam/junk folder</li><li>Verify email address is correct</li><li>Resend invitation</li><li>Try different email address</li></ul><p><strong>Can&#39;t change someone&#39;s role</strong>:</p><ul><li>Ensure you have Admin or Org Admin rights</li><li>User must not be only Admin (keep one Admin always)</li><li>User must be active</li></ul><p><strong>Team member can&#39;t access cases</strong>:</p><ul><li>Verify their role permissions</li><li>Check if cases are assigned to them</li><li>Ensure account is active</li><li>Check for system-wide access issues</li></ul><h2 id="billing-impact" tabindex="-1">Billing Impact <a class="header-anchor" href="#billing-impact" aria-label="Permalink to &quot;Billing Impact&quot;">​</a></h2><p><strong>Pro Plan</strong>:</p><ul><li>Seats included: 5 team members</li><li>Additional seats: £5/user/month</li><li>Add/remove users anytime</li><li>Prorated billing</li></ul><p><strong>Enterprise Plan</strong>:</p><ul><li>Custom seat count</li><li>Volume discounts available</li><li>Annual billing options</li><li>Dedicated support</li></ul><p>See <a href="/docs/admin/subscription-billing">Subscription &amp; Billing</a> for details.</p><hr><p><strong>Next Steps:</strong></p><ul><li><a href="/docs/admin/organization-settings">Organization Settings</a></li><li><a href="/docs/guides/administrators">User Guides</a></li><li><a href="/docs/security/access-control">Security &amp; Access Control</a></li></ul>',84)])])}const p=t(r,[["render",s]]);export{u as __pageData,p as default};
