import{_ as e,c as t,o as a,ag as l}from"./chunks/framework.4QX5UEtQ.js";const g=JSON.parse('{"title":"Analytics Dashboard - Disclosurely Real-Time Insights","description":"Comprehensive guide to the Disclosurely analytics dashboard with real-time metrics, interactive charts, filtering capabilities, and data export features.","frontmatter":{"title":"Analytics Dashboard - Disclosurely Real-Time Insights","description":"Comprehensive guide to the Disclosurely analytics dashboard with real-time metrics, interactive charts, filtering capabilities, and data export features.","head":[["meta",{"property":"og:title","content":"Analytics Dashboard Guide - Disclosurely"}],["meta",{"property":"og:description","content":"Comprehensive guide to the Disclosurely analytics dashboard with real-time metrics, interactive charts, filtering capabilities, and data export features."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Analytics Dashboard - Disclosurely"}],["meta",{"name":"twitter:description","content":"Comprehensive guide to the Disclosurely analytics dashboard with real-time metrics, interactive charts, filtering capabilities, and data export features."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"analytics/dashboard.md","filePath":"analytics/dashboard.md"}'),r={name:"analytics/dashboard.md"};function o(s,i,n,c,d,u){return a(),t("div",null,[...i[0]||(i[0]=[l('<h1 id="analytics-dashboard" tabindex="-1">Analytics Dashboard <a class="header-anchor" href="#analytics-dashboard" aria-label="Permalink to &quot;Analytics Dashboard&quot;">‚Äã</a></h1><p>Your central command center for monitoring and analyzing your whistleblowing program&#39;s performance in real-time.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">‚Äã</a></h2><p>The Analytics Dashboard provides a comprehensive, visual overview of your organization&#39;s whistleblowing program. Monitor case volumes, track resolution times, analyze trends, and identify patterns‚Äîall from a single, intuitive interface that updates in real-time as your team works.</p><p><strong>Access:</strong> Dashboard &gt; Analytics</p><h2 id="dashboard-layout" tabindex="-1">Dashboard Layout <a class="header-anchor" href="#dashboard-layout" aria-label="Permalink to &quot;Dashboard Layout&quot;">‚Äã</a></h2><h3 id="header-section" tabindex="-1">Header Section <a class="header-anchor" href="#header-section" aria-label="Permalink to &quot;Header Section&quot;">‚Äã</a></h3><p><strong>Key Performance Indicators (KPIs)</strong></p><p>Four primary metrics displayed at the top of the dashboard:</p><ol><li><p><strong>Total Reports</strong></p><ul><li>All-time count of submitted reports</li><li>Quick filter: Click to view all reports</li><li>Updated in real-time as new reports arrive</li></ul></li><li><p><strong>Active Cases</strong></p><ul><li>Currently under investigation</li><li>Excludes closed, resolved, and archived</li><li>Color-coded: <ul><li>üü¢ Green: Normal workload</li><li>üü° Yellow: High volume</li><li>üî¥ Red: Overwhelming volume (may need more resources)</li></ul></li></ul></li><li><p><strong>Average Response Time</strong></p><ul><li>Time from submission to first acknowledgment</li><li>Measured in hours and days</li><li>SLA indicator: <ul><li>‚úÖ Within target</li><li>‚ö†Ô∏è Approaching limit</li><li>‚ùå Breached SLA</li></ul></li></ul></li><li><p><strong>Resolution Rate</strong></p><ul><li>Percentage of cases successfully closed</li><li>Target benchmark displayed</li><li>Trend indicator (‚Üë improving, ‚Üì declining, ‚Üí stable)</li></ul></li></ol><h3 id="main-chart-area" tabindex="-1">Main Chart Area <a class="header-anchor" href="#main-chart-area" aria-label="Permalink to &quot;Main Chart Area&quot;">‚Äã</a></h3><p><strong>Case Volume Trends</strong></p><p>Interactive line chart showing report submissions over time.</p><p><strong>Time Period Options:</strong></p><ul><li>Daily (last 30 days)</li><li>Weekly (last 12 weeks)</li><li>Monthly (last 12 months)</li><li>Yearly (all years)</li></ul><p><strong>Features:</strong></p><ul><li>Hover to see exact counts for each period</li><li>Click data points to drill down into specific dates</li><li>Identify spikes or unusual patterns</li><li>Compare multiple time periods</li></ul><p><strong>Use Cases:</strong></p><ul><li>Detect seasonal trends</li><li>Identify reporting spikes that may indicate systemic issues</li><li>Monitor program adoption and usage</li><li>Plan resource allocation</li></ul><h3 id="category-distribution" tabindex="-1">Category Distribution <a class="header-anchor" href="#category-distribution" aria-label="Permalink to &quot;Category Distribution&quot;">‚Äã</a></h3><p><strong>Pie/Doughnut Chart</strong></p><p>Visual breakdown of cases by category.</p><p><strong>Default Categories:</strong></p><ul><li>Harassment &amp; Discrimination</li><li>Financial Misconduct</li><li>Health &amp; Safety</li><li>Data Privacy</li><li>Conflicts of Interest</li><li>Retaliation</li><li>Other</li></ul><p><strong>Features:</strong></p><ul><li>Percentage and count for each category</li><li>Click slice to filter dashboard to that category</li><li>Hover for detailed breakdown</li><li>Color-coded for easy identification</li></ul><p><strong>Insights:</strong></p><ul><li>Identify most common issue types</li><li>Detect emerging risk areas</li><li>Guide training and policy development</li><li>Allocate investigation resources</li></ul><h3 id="priority-breakdown" tabindex="-1">Priority Breakdown <a class="header-anchor" href="#priority-breakdown" aria-label="Permalink to &quot;Priority Breakdown&quot;">‚Äã</a></h3><p><strong>Bar Chart</strong></p><p>Distribution of cases across priority levels.</p><p><strong>Priority Levels:</strong></p><ul><li>üî¥ Critical - Immediate action required</li><li>üü† High - Urgent attention needed</li><li>üü° Medium - Normal processing</li><li>üü¢ Low - Routine handling</li></ul><p><strong>Features:</strong></p><ul><li>Count and percentage for each level</li><li>Click bar to filter by priority</li><li>Compare against historical averages</li><li>Identify workload balance</li></ul><h3 id="status-overview" tabindex="-1">Status Overview <a class="header-anchor" href="#status-overview" aria-label="Permalink to &quot;Status Overview&quot;">‚Äã</a></h3><p><strong>Stacked Bar or Area Chart</strong></p><p>Case flow through different investigation stages.</p><p><strong>Statuses Tracked:</strong></p><ul><li>New/Unassigned</li><li>Assigned</li><li>In Progress</li><li>Pending Information</li><li>Under Review</li><li>Resolved</li><li>Closed</li><li>Archived</li></ul><p><strong>Insights:</strong></p><ul><li>Identify bottlenecks in investigation process</li><li>Monitor case progression</li><li>Detect cases stuck in specific statuses</li><li>Improve workflow efficiency</li></ul><h2 id="advanced-filtering" tabindex="-1">Advanced Filtering <a class="header-anchor" href="#advanced-filtering" aria-label="Permalink to &quot;Advanced Filtering&quot;">‚Äã</a></h2><h3 id="date-range-selector" tabindex="-1">Date Range Selector <a class="header-anchor" href="#date-range-selector" aria-label="Permalink to &quot;Date Range Selector&quot;">‚Äã</a></h3><p><strong>Predefined Ranges:</strong></p><ul><li>Today</li><li>Last 7 days</li><li>Last 30 days</li><li>Last 90 days</li><li>Last 12 months</li><li>Year to Date</li><li>All Time</li></ul><p><strong>Custom Range:</strong></p><ul><li>Click &quot;Custom&quot; to select specific start/end dates</li><li>Compare two different time periods</li><li>Analyze specific incident timeframes</li></ul><h3 id="smart-filters" tabindex="-1">Smart Filters <a class="header-anchor" href="#smart-filters" aria-label="Permalink to &quot;Smart Filters&quot;">‚Äã</a></h3><p><strong>Available Filters:</strong></p><p><strong>Category</strong></p><ul><li>Select one or multiple categories</li><li>Exclude specific categories</li><li>View uncategorized reports</li></ul><p><strong>Priority</strong></p><ul><li>Filter by urgency level</li><li>Combine with other filters</li><li>Track high-priority case resolution</li></ul><p><strong>Status</strong></p><ul><li>View cases in specific investigation stages</li><li>Track workflow progression</li><li>Identify stalled cases</li></ul><p><strong>Assignment</strong></p><ul><li>Filter by assigned user</li><li>View unassigned cases</li><li>Monitor team workload distribution</li></ul><p><strong>Department/Location</strong></p><ul><li>Analyze specific departments</li><li>Geographic patterns</li><li>Organizational unit comparison</li></ul><p><strong>Report Type</strong></p><ul><li>Anonymous vs. Confidential</li><li>With/without attachments</li><li>Risk level</li></ul><p><strong>AI Metrics</strong></p><ul><li>Risk score range (1-10)</li><li>Pattern-detected cases</li><li>AI-flagged issues</li></ul><h3 id="saving-filter-combinations" tabindex="-1">Saving Filter Combinations <a class="header-anchor" href="#saving-filter-combinations" aria-label="Permalink to &quot;Saving Filter Combinations&quot;">‚Äã</a></h3><p><strong>Create Custom Views:</strong></p><ol><li>Apply your desired filters</li><li>Click <strong>Save View</strong></li><li>Name your view (e.g., &quot;High Priority Financial Cases&quot;)</li><li>Access quickly from <strong>Saved Views</strong> dropdown</li></ol><p><strong>Pre-Built Views:</strong></p><ul><li>High Risk Cases</li><li>Overdue Responses</li><li>Unassigned Reports</li><li>Recently Closed</li><li>Long-Running Investigations</li></ul><h2 id="interactive-features" tabindex="-1">Interactive Features <a class="header-anchor" href="#interactive-features" aria-label="Permalink to &quot;Interactive Features&quot;">‚Äã</a></h2><h3 id="drill-down-analysis" tabindex="-1">Drill-Down Analysis <a class="header-anchor" href="#drill-down-analysis" aria-label="Permalink to &quot;Drill-Down Analysis&quot;">‚Äã</a></h3><p><strong>Click any chart element to:</strong></p><ul><li>Filter entire dashboard to that subset</li><li>View detailed case list</li><li>Explore underlying data</li><li>Export filtered results</li></ul><p><strong>Example Workflows:</strong></p><p><strong>Analyze Category Spike:</strong></p><ol><li>Notice unusual increase in &quot;Harassment&quot; category</li><li>Click on harassment slice in pie chart</li><li>Dashboard filters to show only harassment cases</li><li>Review timeline to identify when spike occurred</li><li>Examine individual cases for patterns</li></ol><p><strong>Monitor Team Performance:</strong></p><ol><li>Filter by specific team member</li><li>Review their assigned case count</li><li>Check average resolution time</li><li>Compare to team average</li><li>Identify training needs or recognition opportunities</li></ol><h3 id="chart-interactions" tabindex="-1">Chart Interactions <a class="header-anchor" href="#chart-interactions" aria-label="Permalink to &quot;Chart Interactions&quot;">‚Äã</a></h3><p><strong>Hover for Details:</strong></p><ul><li>Exact values and percentages</li><li>Trend comparisons</li><li>Additional metadata</li></ul><p><strong>Click to Filter:</strong></p><ul><li>All charts support click-to-filter</li><li>Multiple selections with Ctrl/Cmd+Click</li><li>Right-click for additional options</li></ul><p><strong>Zoom and Pan:</strong></p><ul><li>Timeline charts support zooming</li><li>Pan across long time periods</li><li>Reset zoom with double-click</li></ul><h2 id="export-and-reporting" tabindex="-1">Export and Reporting <a class="header-anchor" href="#export-and-reporting" aria-label="Permalink to &quot;Export and Reporting&quot;">‚Äã</a></h2><h3 id="export-options" tabindex="-1">Export Options <a class="header-anchor" href="#export-options" aria-label="Permalink to &quot;Export Options&quot;">‚Äã</a></h3><p><strong>Dashboard Snapshot (PDF)</strong></p><ul><li>Captures current view with all charts</li><li>Includes header with date range and filters</li><li>Branded with organization logo</li><li>Perfect for board presentations</li></ul><p><strong>Data Export (CSV)</strong></p><ul><li>Raw data behind current view</li><li>All fields included</li><li>Compatible with Excel, Google Sheets</li><li>Further analysis and manipulation</li></ul><p><strong>Excel Report (XLSX)</strong></p><ul><li>Formatted tables and charts</li><li>Multiple sheets for different metrics</li><li>Formulas and pivot tables included</li><li>Advanced analysis ready</li></ul><h3 id="scheduling-automated-reports" tabindex="-1">Scheduling Automated Reports <a class="header-anchor" href="#scheduling-automated-reports" aria-label="Permalink to &quot;Scheduling Automated Reports&quot;">‚Äã</a></h3><p><strong>Set Up Recurring Reports:</strong></p><ol><li>Navigate to <strong>Analytics &gt; Reports</strong></li><li>Click <strong>Schedule Report</strong></li><li>Configure: <ul><li>Report type (Dashboard, Statistics, Compliance)</li><li>Frequency (Daily, Weekly, Monthly)</li><li>Recipients (email addresses)</li><li>Format (PDF, CSV, Excel)</li><li>Filters to apply</li></ul></li><li>Save schedule</li></ol><p><strong>Automated Report Features:</strong></p><ul><li>Delivered automatically via email</li><li>Always uses latest data</li><li>Can include multiple dashboards</li><li>Maintains consistent formatting</li></ul><h2 id="key-metrics-explained" tabindex="-1">Key Metrics Explained <a class="header-anchor" href="#key-metrics-explained" aria-label="Permalink to &quot;Key Metrics Explained&quot;">‚Äã</a></h2><h3 id="response-time-calculation" tabindex="-1">Response Time Calculation <a class="header-anchor" href="#response-time-calculation" aria-label="Permalink to &quot;Response Time Calculation&quot;">‚Äã</a></h3><p><strong>How it&#39;s measured:</strong></p><ul><li><strong>Start:</strong> Report submission timestamp</li><li><strong>End:</strong> First acknowledgment or status change by team member</li><li><strong>Excludes:</strong> Non-business hours (optional setting)</li></ul><p><strong>Benchmarks:</strong></p><ul><li>‚úÖ Excellent: &lt; 24 hours</li><li>‚ö†Ô∏è Good: 24-48 hours</li><li>‚ùå Needs Improvement: &gt; 48 hours</li></ul><h3 id="resolution-rate-formula" tabindex="-1">Resolution Rate Formula <a class="header-anchor" href="#resolution-rate-formula" aria-label="Permalink to &quot;Resolution Rate Formula&quot;">‚Äã</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Resolution Rate = (Closed Cases / Total Cases) √ó 100</span></span></code></pre></div><p><strong>What counts as &quot;Closed&quot;:</strong></p><ul><li>Investigation completed</li><li>Findings documented</li><li>Reporter notified (if applicable)</li><li>Final status set to &quot;Closed&quot; or &quot;Resolved&quot;</li></ul><p><strong>Industry Benchmarks:</strong></p><ul><li>70-80%: Typical for complex organizations</li><li>80-90%: Well-managed programs</li><li>90%+: Exceptional performance</li></ul><h3 id="escalation-tracking" tabindex="-1">Escalation Tracking <a class="header-anchor" href="#escalation-tracking" aria-label="Permalink to &quot;Escalation Tracking&quot;">‚Äã</a></h3><p><strong>Escalation Indicators:</strong></p><ul><li>Priority increased (e.g., Medium ‚Üí High)</li><li>Reassigned to senior investigator</li><li>Flagged for management review</li><li>Extended beyond standard timeline</li></ul><p><strong>Escalation Rate:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>(Escalated Cases / Total Cases) √ó 100</span></span></code></pre></div><p><strong>What it means:</strong></p><ul><li>&lt; 10%: Normal operations</li><li>10-20%: Some systemic issues present</li><li><blockquote><p>20%: May indicate training needs or policy gaps</p></blockquote></li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">‚Äã</a></h2><h3 id="daily-dashboard-review-5-10-minutes" tabindex="-1">Daily Dashboard Review (5-10 minutes) <a class="header-anchor" href="#daily-dashboard-review-5-10-minutes" aria-label="Permalink to &quot;Daily Dashboard Review (5-10 minutes)&quot;">‚Äã</a></h3><p><strong>Morning Routine:</strong></p><p>‚úÖ <strong>Check New Reports</strong></p><ul><li>Review cases submitted in last 24 hours</li><li>Identify any critical/high-priority cases</li><li>Ensure proper assignment</li></ul><p>‚úÖ <strong>Monitor SLA Compliance</strong></p><ul><li>Check response time indicator</li><li>Identify cases approaching deadlines</li><li>Send reminders to team if needed</li></ul><p>‚úÖ <strong>Review Active Case Count</strong></p><ul><li>Ensure balanced workload</li><li>Reassign if necessary</li><li>Flag resource constraints</li></ul><h3 id="weekly-trend-analysis-15-30-minutes" tabindex="-1">Weekly Trend Analysis (15-30 minutes) <a class="header-anchor" href="#weekly-trend-analysis-15-30-minutes" aria-label="Permalink to &quot;Weekly Trend Analysis (15-30 minutes)&quot;">‚Äã</a></h3><p>‚úÖ <strong>Compare Week-over-Week:</strong></p><ul><li>Case volumes</li><li>Resolution rates</li><li>Response times</li><li>Category distribution</li></ul><p>‚úÖ <strong>Identify Patterns:</strong></p><ul><li>Recurring issues</li><li>Department-specific trends</li><li>Seasonal variations</li></ul><p>‚úÖ <strong>Adjust Resources:</strong></p><ul><li>Reallocate investigators based on workload</li><li>Plan training for frequently reported issues</li><li>Update policies if needed</li></ul><h3 id="monthly-reporting-30-60-minutes" tabindex="-1">Monthly Reporting (30-60 minutes) <a class="header-anchor" href="#monthly-reporting-30-60-minutes" aria-label="Permalink to &quot;Monthly Reporting (30-60 minutes)&quot;">‚Äã</a></h3><p>‚úÖ <strong>Generate Executive Summary:</strong></p><ul><li>Total cases received</li><li>Top categories</li><li>Resolution rate</li><li>Key wins and challenges</li></ul><p>‚úÖ <strong>Benchmark Progress:</strong></p><ul><li>Compare to previous months</li><li>Track towards annual goals</li><li>Identify improvement areas</li></ul><p>‚úÖ <strong>Stakeholder Communication:</strong></p><ul><li>Share dashboard snapshot with audit committee</li><li>Present trends to management</li><li>Report compliance metrics to regulators</li></ul><h2 id="customization-options" tabindex="-1">Customization Options <a class="header-anchor" href="#customization-options" aria-label="Permalink to &quot;Customization Options&quot;">‚Äã</a></h2><h3 id="dashboard-widgets" tabindex="-1">Dashboard Widgets <a class="header-anchor" href="#dashboard-widgets" aria-label="Permalink to &quot;Dashboard Widgets&quot;">‚Äã</a></h3><p><strong>Rearrange Layout:</strong></p><ol><li>Click <strong>Customize Dashboard</strong></li><li>Drag and drop widgets to preferred positions</li><li>Resize widgets by dragging corners</li><li>Hide widgets you don&#39;t need</li><li>Save layout</li></ol><p><strong>Available Widgets:</strong></p><ul><li>KPI Summary Cards</li><li>Case Volume Timeline</li><li>Category Distribution</li><li>Priority Breakdown</li><li>Status Flow Chart</li><li>Recent Activity Feed</li><li>Upcoming Deadlines</li><li>Team Performance</li><li>AI Insights</li></ul><h3 id="color-schemes" tabindex="-1">Color Schemes <a class="header-anchor" href="#color-schemes" aria-label="Permalink to &quot;Color Schemes&quot;">‚Äã</a></h3><p><strong>Accessibility Options:</strong></p><ul><li>Standard (color-coded)</li><li>High contrast</li><li>Colorblind-friendly palettes</li><li>Grayscale for printing</li></ul><h3 id="data-refresh-rate" tabindex="-1">Data Refresh Rate <a class="header-anchor" href="#data-refresh-rate" aria-label="Permalink to &quot;Data Refresh Rate&quot;">‚Äã</a></h3><p><strong>Configure Update Frequency:</strong></p><ul><li>Real-time (instant updates)</li><li>Every 5 minutes</li><li>Every 15 minutes</li><li>Every hour</li><li>Manual refresh only</li></ul><p><strong>Note:</strong> Real-time updates may impact browser performance with large datasets.</p><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">‚Äã</a></h2><p><strong>Dashboard not loading?</strong></p><ul><li>Check internet connection</li><li>Clear browser cache</li><li>Try different browser</li><li>Contact support if issue persists</li></ul><p><strong>Charts showing &quot;No Data&quot;?</strong></p><ul><li>Verify date range isn&#39;t too narrow</li><li>Check applied filters (may be too restrictive)</li><li>Ensure you have cases in the system</li><li>Confirm you have permission to view data</li></ul><p><strong>Export failing?</strong></p><ul><li>Check available storage quota</li><li>Try smaller date range</li><li>Use different format</li><li>Disable browser extensions temporarily</li></ul><p><strong>Slow performance?</strong></p><ul><li>Reduce date range</li><li>Apply more specific filters</li><li>Close unused browser tabs</li><li>Clear browser cache</li></ul><h2 id="privacy-security" tabindex="-1">Privacy &amp; Security <a class="header-anchor" href="#privacy-security" aria-label="Permalink to &quot;Privacy &amp; Security&quot;">‚Äã</a></h2><p><strong>Access Controls:</strong></p><ul><li>Viewers: See only assigned cases</li><li>Case Handlers: See all cases in scope</li><li>Admins: Full analytics access</li><li>Audit trail logs all analytics access</li></ul><p><strong>Data Anonymization:</strong></p><ul><li>PII automatically redacted in charts</li><li>Export options include anonymization</li><li>Aggregate data safe for sharing</li></ul><p><strong>Learn More:</strong> <a href="/docs/security/overview">Security Overview</a></p><h2 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">‚Äã</a></h2><ul><li><a href="/docs/analytics/overview">Analytics Overview</a> - Introduction to analytics</li><li><a href="/docs/analytics/statistics">Statistics &amp; Metrics</a> - Detailed metrics guide</li><li><a href="/docs/analytics/compliance-analytics">Compliance Analytics</a> - Regulatory reporting</li><li><a href="/docs/ai/pattern-detection">Pattern Detection</a> - AI-powered insights</li><li><a href="/docs/features/assignment-rules">Assignment Rules</a> - Automated case routing</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">‚Äã</a></h2><p>Need help with the dashboard?</p><ul><li>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="https://disclosurely.com/tutorials" target="_blank" rel="noreferrer">Video Tutorials</a></li></ul>',173)])])}const h=e(r,[["render",o]]);export{g as __pageData,h as default};
