import{_ as a,c as i,o as t,ag as s}from"./chunks/framework.4QX5UEtQ.js";const g=JSON.parse('{"title":"Analytics Overview - Disclosurely Analytics & Reporting","description":"Comprehensive analytics platform for whistleblowing programs with real-time dashboards, custom reports, trend analysis, and compliance reporting capabilities.","frontmatter":{"title":"Analytics Overview - Disclosurely Analytics & Reporting","description":"Comprehensive analytics platform for whistleblowing programs with real-time dashboards, custom reports, trend analysis, and compliance reporting capabilities.","head":[["meta",{"property":"og:title","content":"Analytics Overview - Disclosurely Platform"}],["meta",{"property":"og:description","content":"Comprehensive analytics platform for whistleblowing programs with real-time dashboards, custom reports, trend analysis, and compliance reporting capabilities."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Analytics Overview - Disclosurely"}],["meta",{"property":"og:description","content":"Comprehensive analytics platform for whistleblowing programs with real-time dashboards, custom reports, trend analysis, and compliance reporting capabilities."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"analytics/overview.md","filePath":"analytics/overview.md"}'),r={name:"analytics/overview.md"};function l(o,e,n,c,p,d){return t(),i("div",null,[...e[0]||(e[0]=[s('<h1 id="analytics-overview" tabindex="-1">Analytics Overview <a class="header-anchor" href="#analytics-overview" aria-label="Permalink to &quot;Analytics Overview&quot;">â€‹</a></h1><p>Transform your whistleblowing data into actionable insights with Disclosurely&#39;s comprehensive analytics and reporting platform.</p><h2 id="what-is-analytics" tabindex="-1">What is Analytics? <a class="header-anchor" href="#what-is-analytics" aria-label="Permalink to &quot;What is Analytics?&quot;">â€‹</a></h2><p>Disclosurely Analytics provides a complete suite of tools to monitor, analyze, and report on your organization&#39;s whistleblowing program. From real-time dashboards to custom compliance reports, our analytics platform helps you understand trends, identify risks, and demonstrate program effectiveness to stakeholders.</p><p><strong>Key Analytics Capabilities:</strong></p><ul><li><strong>Real-Time Dashboards</strong> - Live metrics and KPIs updated as cases progress</li><li><strong>Trend Analysis</strong> - Identify patterns across time periods, departments, and categories</li><li><strong>Compliance Reporting</strong> - Pre-built templates for regulatory requirements</li><li><strong>Custom Report Builder</strong> - Create tailored reports for specific needs</li><li><strong>Visual Charts</strong> - Interactive charts and graphs for data visualization</li><li><strong>Export Capabilities</strong> - Download reports in CSV, PDF, and Excel formats</li><li><strong>AI-Powered Insights</strong> - Pattern detection and risk assessment analytics</li></ul><h2 id="analytics-components" tabindex="-1">Analytics Components <a class="header-anchor" href="#analytics-components" aria-label="Permalink to &quot;Analytics Components&quot;">â€‹</a></h2><h3 id="_1-main-analytics-dashboard" tabindex="-1">1. Main Analytics Dashboard <a class="header-anchor" href="#_1-main-analytics-dashboard" aria-label="Permalink to &quot;1. Main Analytics Dashboard&quot;">â€‹</a></h3><p>The central hub for all your whistleblowing program metrics.</p><p><strong>Access:</strong> Dashboard &gt; Analytics</p><p><strong>Key Features:</strong></p><ul><li>High-level overview of program health</li><li>Case volume trends (daily, weekly, monthly, yearly)</li><li>Category distribution charts</li><li>Priority and urgency breakdowns</li><li>Response time metrics</li><li>Resolution rate tracking</li><li>Active cases monitoring</li><li>Escalation rate analysis</li></ul><p><strong>Learn More:</strong> <a href="/docs/analytics/dashboard">Analytics Dashboard Guide</a></p><h3 id="_2-case-statistics" tabindex="-1">2. Case Statistics <a class="header-anchor" href="#_2-case-statistics" aria-label="Permalink to &quot;2. Case Statistics&quot;">â€‹</a></h3><p>Detailed statistics and metrics for case management.</p><p><strong>Metrics Tracked:</strong></p><ul><li>Total reports received (all-time and filtered periods)</li><li>Average response time to initial acknowledgment</li><li>Average time to resolution</li><li>Case closure rate</li><li>Re-opened cases tracking</li><li>Assignment distribution</li><li>Status distribution</li><li>Department/location analysis</li></ul><p><strong>Learn More:</strong> <a href="/docs/analytics/statistics">Statistics &amp; Metrics</a></p><h3 id="_3-compliance-analytics" tabindex="-1">3. Compliance Analytics <a class="header-anchor" href="#_3-compliance-analytics" aria-label="Permalink to &quot;3. Compliance Analytics&quot;">â€‹</a></h3><p>Specialized analytics for regulatory compliance and audit reporting.</p><p><strong>Compliance Features:</strong></p><ul><li>EU Whistleblowing Directive reporting</li><li>SOX compliance metrics</li><li>GDPR data subject request tracking</li><li>Audit trail reporting</li><li>Policy compliance monitoring</li><li>Deadline tracking and alerts</li><li>Regulatory submission templates</li></ul><p><strong>Learn More:</strong> <a href="/docs/analytics/compliance-analytics">Compliance Analytics</a></p><h2 id="key-metrics-explained" tabindex="-1">Key Metrics Explained <a class="header-anchor" href="#key-metrics-explained" aria-label="Permalink to &quot;Key Metrics Explained&quot;">â€‹</a></h2><h3 id="case-volume-metrics" tabindex="-1">Case Volume Metrics <a class="header-anchor" href="#case-volume-metrics" aria-label="Permalink to &quot;Case Volume Metrics&quot;">â€‹</a></h3><p><strong>Total Reports</strong></p><ul><li>All reports received across all time periods</li><li>Filterable by date range, status, category</li><li>Includes both anonymous and confidential reports</li></ul><p><strong>Active Cases</strong></p><ul><li>Reports currently under investigation</li><li>Excludes closed, resolved, and archived cases</li><li>Real-time updates as statuses change</li></ul><p><strong>New Reports (Period)</strong></p><ul><li>Reports submitted within selected time period</li><li>Compare across different time ranges</li><li>Identify reporting trends and spikes</li></ul><h3 id="performance-metrics" tabindex="-1">Performance Metrics <a class="header-anchor" href="#performance-metrics" aria-label="Permalink to &quot;Performance Metrics&quot;">â€‹</a></h3><p><strong>Average Response Time</strong></p><ul><li>Time from submission to first response/acknowledgment</li><li>Measured in hours or days</li><li>Color-coded based on SLA thresholds: <ul><li>ðŸŸ¢ Green: Within SLA target</li><li>ðŸŸ¡ Yellow: Approaching SLA limit</li><li>ðŸ”´ Red: SLA breached</li></ul></li></ul><p><strong>Average Resolution Time</strong></p><ul><li>Time from submission to case closure</li><li>Broken down by category and priority</li><li>Helps identify bottlenecks in investigation process</li></ul><p><strong>Resolution Rate</strong></p><ul><li>Percentage of cases closed vs. total cases</li><li>Calculated as: (Closed Cases / Total Cases) Ã— 100</li><li>Target: Typically 70-90% depending on case complexity</li></ul><h3 id="risk-metrics" tabindex="-1">Risk Metrics <a class="header-anchor" href="#risk-metrics" aria-label="Permalink to &quot;Risk Metrics&quot;">â€‹</a></h3><p><strong>Escalation Rate</strong></p><ul><li>Percentage of cases escalated to higher priority or management</li><li>Formula: (Escalated Cases / Total Cases) Ã— 100</li><li>High escalation rates may indicate systemic issues</li></ul><p><strong>High-Risk Case Count</strong></p><ul><li>Cases flagged as critical or high severity</li><li>Requires immediate attention and monitoring</li><li>Automatically tracked based on AI risk assessment or manual assignment</li></ul><h3 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">â€‹</a></h3><ul><li><a href="/docs/analytics/dashboard">Analytics Dashboard</a> - Detailed dashboard guide</li><li><a href="/docs/analytics/statistics">Statistics &amp; Metrics</a> - Deep dive into metrics</li><li><a href="/docs/analytics/compliance-analytics">Compliance Analytics</a> - Regulatory reporting</li><li><a href="/docs/ai/pattern-detection">Pattern Detection</a> - AI-powered insights</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">â€‹</a></h2><p>Need help with analytics?</p><ul><li>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="/docs/support/faqs">FAQs</a></li></ul>',48)])])}const u=a(r,[["render",l]]);export{g as __pageData,u as default};
