import{_ as t,c as e,o as a,ag as l}from"./chunks/framework.4QX5UEtQ.js";const g=JSON.parse('{"title":"Report Statistics - Disclosurely Analytics & Metrics","description":"Detailed guide to understanding case statistics, performance metrics, trend analysis, and comparative analytics in your whistleblowing program.","frontmatter":{"title":"Report Statistics - Disclosurely Analytics & Metrics","description":"Detailed guide to understanding case statistics, performance metrics, trend analysis, and comparative analytics in your whistleblowing program.","head":[["meta",{"property":"og:title","content":"Report Statistics & Metrics - Disclosurely"}],["meta",{"property":"og:description","content":"Detailed guide to understanding case statistics, performance metrics, trend analysis, and comparative analytics in your whistleblowing program."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Report Statistics - Disclosurely"}],["meta",{"name":"twitter:description","content":"Detailed guide to understanding case statistics, performance metrics, trend analysis, and comparative analytics in your whistleblowing program."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"analytics/statistics.md","filePath":"analytics/statistics.md"}'),r={name:"analytics/statistics.md"};function s(o,i,n,c,d,u){return a(),e("div",null,[...i[0]||(i[0]=[l('<h1 id="report-statistics" tabindex="-1">Report Statistics <a class="header-anchor" href="#report-statistics" aria-label="Permalink to &quot;Report Statistics&quot;">â€‹</a></h1><p>Deep dive into your whistleblowing program&#39;s metrics, trends, and performance indicators with comprehensive statistical analysis.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">â€‹</a></h2><p>Report Statistics provides detailed metrics and analytics beyond the dashboard&#39;s high-level overview. Analyze performance trends, compare time periods, track investigator productivity, and identify patterns that inform program improvements.</p><p><strong>Access:</strong> Dashboard &gt; Analytics &gt; Statistics</p><h2 id="core-statistics" tabindex="-1">Core Statistics <a class="header-anchor" href="#core-statistics" aria-label="Permalink to &quot;Core Statistics&quot;">â€‹</a></h2><h3 id="volume-metrics" tabindex="-1">Volume Metrics <a class="header-anchor" href="#volume-metrics" aria-label="Permalink to &quot;Volume Metrics&quot;">â€‹</a></h3><p><strong>Total Reports Received</strong></p><ul><li>All-time report count</li><li>Breakdown by anonymous vs. confidential</li><li>New reports per period</li><li>Growth rate over time</li></ul><p><strong>Reports by Time Period</strong></p><ul><li>Daily submissions</li><li>Weekly averages</li><li>Monthly totals</li><li>Yearly comparisons</li><li>Peak submission times (day of week, hour of day)</li></ul><p><strong>Report Sources</strong></p><ul><li>By department or location</li><li>By submission channel (web portal, mobile, phone)</li><li>By reporting link used</li><li>Geographic distribution (if collected)</li></ul><h3 id="case-status-distribution" tabindex="-1">Case Status Distribution <a class="header-anchor" href="#case-status-distribution" aria-label="Permalink to &quot;Case Status Distribution&quot;">â€‹</a></h3><p><strong>Current State:</strong></p><ul><li>New/Unassigned: X cases (X%)</li><li>Assigned: X cases (X%)</li><li>In Progress: X cases (X%)</li><li>Pending Information: X cases (X%)</li><li>Under Review: X cases (X%)</li><li>Resolved: X cases (X%)</li><li>Closed: X cases (X%)</li><li>Archived: X cases (X%)</li></ul><p><strong>Status Flow Analysis:</strong></p><ul><li>Average time in each status</li><li>Bottleneck identification</li><li>Status change frequency</li><li>Cases stuck &gt;30 days in single status</li></ul><h3 id="category-breakdown" tabindex="-1">Category Breakdown <a class="header-anchor" href="#category-breakdown" aria-label="Permalink to &quot;Category Breakdown&quot;">â€‹</a></h3><p><strong>Report Distribution by Category:</strong></p><table tabindex="0"><thead><tr><th>Category</th><th>Count</th><th>Percentage</th><th>Trend</th></tr></thead><tbody><tr><td>Harassment &amp; Discrimination</td><td>45</td><td>28%</td><td>â†‘ +15%</td></tr><tr><td>Financial Misconduct</td><td>32</td><td>20%</td><td>â†’ Stable</td></tr><tr><td>Health &amp; Safety</td><td>25</td><td>15%</td><td>â†“ -10%</td></tr><tr><td>Data Privacy</td><td>22</td><td>14%</td><td>â†‘ +25%</td></tr><tr><td>Conflicts of Interest</td><td>18</td><td>11%</td><td>â†’ Stable</td></tr><tr><td>Retaliation</td><td>12</td><td>7%</td><td>â†‘ +30%</td></tr><tr><td>Other</td><td>8</td><td>5%</td><td>â†“ -5%</td></tr></tbody></table><p><strong>Category Insights:</strong></p><ul><li>Most common allegation types</li><li>Emerging categories</li><li>Declining categories (potential policy improvements)</li><li>Cross-category patterns</li></ul><h3 id="priority-and-urgency" tabindex="-1">Priority and Urgency <a class="header-anchor" href="#priority-and-urgency" aria-label="Permalink to &quot;Priority and Urgency&quot;">â€‹</a></h3><p><strong>Priority Distribution:</strong></p><ul><li>ðŸ”´ Critical: X cases (X%) - Avg resolution: X days</li><li>ðŸŸ  High: X cases (X%) - Avg resolution: X days</li><li>ðŸŸ¡ Medium: X cases (X%) - Avg resolution: X days</li><li>ðŸŸ¢ Low: X cases (X%) - Avg resolution: X days</li></ul><p><strong>Escalation Tracking:</strong></p><ul><li>Cases elevated in priority: X%</li><li>Cases downgraded: X%</li><li>Average time before escalation: X days</li></ul><h2 id="performance-metrics" tabindex="-1">Performance Metrics <a class="header-anchor" href="#performance-metrics" aria-label="Permalink to &quot;Performance Metrics&quot;">â€‹</a></h2><h3 id="response-time-analysis" tabindex="-1">Response Time Analysis <a class="header-anchor" href="#response-time-analysis" aria-label="Permalink to &quot;Response Time Analysis&quot;">â€‹</a></h3><p><strong>First Response Time:</strong></p><ul><li>Average: X hours</li><li>Median: X hours</li><li>Fastest response: X hours</li><li>Slowest response: X hours</li><li>SLA compliance rate: X%</li></ul><p><strong>By Priority Level:</strong></p><ul><li>Critical: Avg X hours (Target: &lt; 4 hours)</li><li>High: Avg X hours (Target: &lt; 24 hours)</li><li>Medium: Avg X hours (Target: &lt; 48 hours)</li><li>Low: Avg X hours (Target: &lt; 72 hours)</li></ul><p><strong>By Category:</strong></p><ul><li>Which categories get fastest response</li><li>Which require improvement</li><li>Correlation between category and response time</li></ul><h3 id="resolution-time-metrics" tabindex="-1">Resolution Time Metrics <a class="header-anchor" href="#resolution-time-metrics" aria-label="Permalink to &quot;Resolution Time Metrics&quot;">â€‹</a></h3><p><strong>Average Time to Resolution:</strong></p><ul><li>Overall: X days</li><li>By category</li><li>By priority</li><li>By assigned investigator</li><li>By department/location</li></ul><p><strong>Resolution Time Distribution:</strong></p><ul><li>&lt; 7 days: X cases (X%)</li><li>7-14 days: X cases (X%)</li><li>14-30 days: X cases (X%)</li><li>30-60 days: X cases (X%)</li><li>60-90 days: X cases (X%)</li><li><blockquote><p>90 days: X cases (X%)</p></blockquote></li></ul><p><strong>Long-Running Investigations:</strong></p><ul><li>Cases open &gt; 90 days</li><li>Oldest open case</li><li>Average age of active cases</li><li>Factors contributing to extended timelines</li></ul><h3 id="resolution-outcomes" tabindex="-1">Resolution Outcomes <a class="header-anchor" href="#resolution-outcomes" aria-label="Permalink to &quot;Resolution Outcomes&quot;">â€‹</a></h3><p><strong>Investigation Findings:</strong></p><ul><li>Substantiated: X cases (X%)</li><li>Partially Substantiated: X cases (X%)</li><li>Unsubstantiated: X cases (X%)</li><li>Insufficient Evidence: X cases (X%)</li><li>Withdrawn by Reporter: X cases (X%)</li><li>Duplicate/Consolidated: X cases (X%)</li></ul><p><strong>Actions Taken:</strong></p><ul><li>Disciplinary action: X cases</li><li>Policy changes: X cases</li><li>Training implemented: X cases</li><li>Process improvements: X cases</li><li>No action required: X cases</li></ul><p><strong>Reporter Satisfaction:</strong></p><ul><li>Feedback provided: X cases</li><li>Positive feedback: X%</li><li>Neutral feedback: X%</li><li>Negative feedback: X%</li></ul><h2 id="team-performance" tabindex="-1">Team Performance <a class="header-anchor" href="#team-performance" aria-label="Permalink to &quot;Team Performance&quot;">â€‹</a></h2><h3 id="investigator-workload" tabindex="-1">Investigator Workload <a class="header-anchor" href="#investigator-workload" aria-label="Permalink to &quot;Investigator Workload&quot;">â€‹</a></h3><p><strong>Cases by Assignee:</strong></p><table tabindex="0"><thead><tr><th>Investigator</th><th>Active</th><th>Closed</th><th>Avg Resolution Days</th><th>SLA Compliance</th></tr></thead><tbody><tr><td>Jane Doe</td><td>8</td><td>45</td><td>12</td><td>95%</td></tr><tr><td>John Smith</td><td>12</td><td>38</td><td>15</td><td>88%</td></tr><tr><td>Sarah Johnson</td><td>6</td><td>52</td><td>10</td><td>98%</td></tr></tbody></table><p><strong>Workload Balance:</strong></p><ul><li>Most assigned investigator</li><li>Least assigned investigator</li><li>Recommended redistributions</li><li>Capacity planning</li></ul><h3 id="team-efficiency" tabindex="-1">Team Efficiency <a class="header-anchor" href="#team-efficiency" aria-label="Permalink to &quot;Team Efficiency&quot;">â€‹</a></h3><p><strong>Productivity Metrics:</strong></p><ul><li>Average cases handled per investigator per month</li><li>Closure rate by investigator</li><li>Quality metrics (re-opened cases, findings accuracy)</li><li>Training effectiveness</li></ul><p><strong>Collaboration Patterns:</strong></p><ul><li>Cases requiring multiple investigators</li><li>Cross-functional investigations</li><li>Consultation frequency</li><li>Expert involvement</li></ul><h2 id="trend-analysis" tabindex="-1">Trend Analysis <a class="header-anchor" href="#trend-analysis" aria-label="Permalink to &quot;Trend Analysis&quot;">â€‹</a></h2><h3 id="historical-comparisons" tabindex="-1">Historical Comparisons <a class="header-anchor" href="#historical-comparisons" aria-label="Permalink to &quot;Historical Comparisons&quot;">â€‹</a></h3><p><strong>Month-over-Month:</strong></p><ul><li>Volume changes: +/- X%</li><li>Resolution time changes: +/- X%</li><li>Category shifts</li><li>Priority distribution changes</li></ul><p><strong>Year-over-Year:</strong></p><ul><li>Annual growth rate</li><li>Seasonal patterns</li><li>Long-term trends</li><li>Program maturity indicators</li></ul><p><strong>Quarter-over-Quarter:</strong></p><ul><li>Quarterly performance</li><li>Goal achievement tracking</li><li>Trend direction (improving, stable, declining)</li></ul><h3 id="pattern-detection" tabindex="-1">Pattern Detection <a class="header-anchor" href="#pattern-detection" aria-label="Permalink to &quot;Pattern Detection&quot;">â€‹</a></h3><p><strong>Recurring Issues:</strong></p><ul><li>Same department multiple reports</li><li>Similar allegations across departments</li><li>Temporal patterns (specific months, days)</li><li>Potential systemic issues</li></ul><p><strong>AI-Detected Patterns:</strong></p><ul><li>Common keywords and themes</li><li>Related case clusters</li><li>Risk prediction indicators</li><li>Anomaly detection</li></ul><p><strong>Learn More:</strong> <a href="/docs/ai/pattern-detection">Pattern Detection</a></p><h3 id="predictive-analytics" tabindex="-1">Predictive Analytics <a class="header-anchor" href="#predictive-analytics" aria-label="Permalink to &quot;Predictive Analytics&quot;">â€‹</a></h3><p><strong>Forecasting:</strong></p><ul><li>Expected case volume next month/quarter</li><li>Resource needs projection</li><li>Peak period anticipation</li><li>Budget planning support</li></ul><p><strong>Risk Prediction:</strong></p><ul><li>High-risk departments</li><li>Likely escalation candidates</li><li>Potential compliance issues</li><li>Training needs identification</li></ul><h2 id="comparative-analytics" tabindex="-1">Comparative Analytics <a class="header-anchor" href="#comparative-analytics" aria-label="Permalink to &quot;Comparative Analytics&quot;">â€‹</a></h2><h3 id="benchmarking" tabindex="-1">Benchmarking <a class="header-anchor" href="#benchmarking" aria-label="Permalink to &quot;Benchmarking&quot;">â€‹</a></h3><p><strong>Industry Comparisons:</strong></p><ul><li>Your organization vs. industry average</li><li>Peer organization benchmarks</li><li>Best-in-class performance indicators</li></ul><p><strong>Metrics to Compare:</strong></p><ul><li>Cases per 1,000 employees</li><li>Average resolution time</li><li>SLA compliance rate</li><li>Resolution outcomes</li><li>Reporter satisfaction</li></ul><p><strong>Internal Benchmarks:</strong></p><ul><li>Department vs. department</li><li>Location vs. location</li><li>Time period vs. time period</li><li>Team vs. team</li></ul><h3 id="goal-tracking" tabindex="-1">Goal Tracking <a class="header-anchor" href="#goal-tracking" aria-label="Permalink to &quot;Goal Tracking&quot;">â€‹</a></h3><p><strong>Set Performance Goals:</strong></p><ul><li>Response time targets</li><li>Resolution rate objectives</li><li>Volume handling capacity</li><li>Quality metrics</li></ul><p><strong>Progress Monitoring:</strong></p><ul><li>Goal achievement percentage</li><li>Trajectory (on track, ahead, behind)</li><li>Required improvement rate</li><li>Projected completion date</li></ul><h2 id="advanced-statistics" tabindex="-1">Advanced Statistics <a class="header-anchor" href="#advanced-statistics" aria-label="Permalink to &quot;Advanced Statistics&quot;">â€‹</a></h2><h3 id="statistical-significance" tabindex="-1">Statistical Significance <a class="header-anchor" href="#statistical-significance" aria-label="Permalink to &quot;Statistical Significance&quot;">â€‹</a></h3><p><strong>Trend Validation:</strong></p><ul><li>Are changes statistically significant?</li><li>Confidence intervals</li><li>Sample size adequacy</li><li>Margin of error</li></ul><p><strong>A/B Testing:</strong></p><ul><li>Compare process changes</li><li>Evaluate policy effectiveness</li><li>Test communication strategies</li><li>Measure intervention impact</li></ul><h3 id="correlation-analysis" tabindex="-1">Correlation Analysis <a class="header-anchor" href="#correlation-analysis" aria-label="Permalink to &quot;Correlation Analysis&quot;">â€‹</a></h3><p><strong>Identifying Relationships:</strong></p><ul><li>Does faster response correlate with better outcomes?</li><li>Category correlation with resolution time</li><li>Department size vs. report volume</li><li>Training impact on allegation types</li></ul><p><strong>Causation Investigation:</strong></p><ul><li>Distinguish correlation from causation</li><li>Control for confounding factors</li><li>Multi-variate analysis</li></ul><h2 id="data-visualization" tabindex="-1">Data Visualization <a class="header-anchor" href="#data-visualization" aria-label="Permalink to &quot;Data Visualization&quot;">â€‹</a></h2><h3 id="chart-types-for-statistics" tabindex="-1">Chart Types for Statistics <a class="header-anchor" href="#chart-types-for-statistics" aria-label="Permalink to &quot;Chart Types for Statistics&quot;">â€‹</a></h3><p><strong>Time Series Charts:</strong></p><ul><li>Line charts for trends over time</li><li>Area charts for cumulative metrics</li><li>Candlestick charts for ranges (min, max, avg, median)</li></ul><p><strong>Distribution Charts:</strong></p><ul><li>Histograms for resolution time distribution</li><li>Box plots for statistical spread</li><li>Violin plots for detailed distributions</li></ul><p><strong>Comparison Charts:</strong></p><ul><li>Bar charts for category comparisons</li><li>Grouped bars for multi-dimensional comparison</li><li>Stacked bars for composition</li></ul><p><strong>Relationship Charts:</strong></p><ul><li>Scatter plots for correlation</li><li>Bubble charts for three dimensions</li><li>Heatmaps for pattern identification</li></ul><h2 id="export-and-reporting" tabindex="-1">Export and Reporting <a class="header-anchor" href="#export-and-reporting" aria-label="Permalink to &quot;Export and Reporting&quot;">â€‹</a></h2><h3 id="statistical-reports" tabindex="-1">Statistical Reports <a class="header-anchor" href="#statistical-reports" aria-label="Permalink to &quot;Statistical Reports&quot;">â€‹</a></h3><p><strong>Pre-Built Report Templates:</strong></p><ul><li>Monthly Statistics Summary</li><li>Quarterly Performance Review</li><li>Annual Program Report</li><li>Team Performance Report</li><li>Category Analysis Report</li><li>Trend Analysis Report</li></ul><p><strong>Custom Reports:</strong></p><ul><li>Select specific metrics</li><li>Choose visualization types</li><li>Apply filters and date ranges</li><li>Save as template for reuse</li></ul><h3 id="export-formats" tabindex="-1">Export Formats <a class="header-anchor" href="#export-formats" aria-label="Permalink to &quot;Export Formats&quot;">â€‹</a></h3><p><strong>Excel (XLSX):</strong></p><ul><li>Multiple worksheets for different metrics</li><li>Pivot tables for dynamic analysis</li><li>Charts and visualizations included</li><li>Formulas for custom calculations</li></ul><p><strong>CSV:</strong></p><ul><li>Raw data export</li><li>Import into other analytics tools</li><li>Database loading</li><li>Custom analysis</li></ul><p><strong>PDF:</strong></p><ul><li>Formatted presentation</li><li>Charts and tables</li><li>Executive summaries</li><li>Board presentations</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">â€‹</a></h2><h3 id="regular-statistical-reviews" tabindex="-1">Regular Statistical Reviews <a class="header-anchor" href="#regular-statistical-reviews" aria-label="Permalink to &quot;Regular Statistical Reviews&quot;">â€‹</a></h3><p>âœ… <strong>Weekly:</strong></p><ul><li>Check volume trends</li><li>Monitor resolution times</li><li>Review new patterns</li><li>Identify urgent issues</li></ul><p>âœ… <strong>Monthly:</strong></p><ul><li>Compare to previous month</li><li>Assess goal progress</li><li>Generate summary report</li><li>Share with stakeholders</li></ul><p>âœ… <strong>Quarterly:</strong></p><ul><li>Deep trend analysis</li><li>Benchmark against industry</li><li>Review and adjust goals</li><li>Strategic planning</li></ul><p>âœ… <strong>Annually:</strong></p><ul><li>Comprehensive program review</li><li>Year-over-year comparison</li><li>Long-term trend identification</li><li>Annual report creation</li></ul><h3 id="data-driven-decision-making" tabindex="-1">Data-Driven Decision Making <a class="header-anchor" href="#data-driven-decision-making" aria-label="Permalink to &quot;Data-Driven Decision Making&quot;">â€‹</a></h3><p>âœ… <strong>Use statistics to:</strong></p><ul><li>Allocate investigator resources</li><li>Identify training needs</li><li>Guide policy development</li><li>Demonstrate program value</li><li>Justify budget requests</li><li>Improve processes</li></ul><p>âœ… <strong>Share insights with:</strong></p><ul><li>Executive leadership</li><li>Audit committee</li><li>Department managers</li><li>HR and compliance teams</li><li>Board of directors</li></ul><h3 id="data-quality" tabindex="-1">Data Quality <a class="header-anchor" href="#data-quality" aria-label="Permalink to &quot;Data Quality&quot;">â€‹</a></h3><p>âœ… <strong>Ensure accuracy:</strong></p><ul><li>Regular data validation</li><li>Correct categorization</li><li>Proper status updates</li><li>Complete case documentation</li><li>Timely data entry</li></ul><p>âœ… <strong>Maintain consistency:</strong></p><ul><li>Standardized definitions</li><li>Uniform processes</li><li>Regular training</li><li>Quality audits</li></ul><h2 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">â€‹</a></h2><ul><li><a href="/docs/analytics/dashboard">Analytics Dashboard</a> - Visual dashboard guide</li><li><a href="/docs/analytics/overview">Analytics Overview</a> - Introduction to analytics</li><li><a href="/docs/analytics/compliance-analytics">Compliance Analytics</a> - Regulatory reporting</li><li><a href="/docs/ai/pattern-detection">Pattern Detection</a> - AI-powered insights</li><li><a href="/docs/cases/workflow">Case Workflow</a> - Investigation process</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">â€‹</a></h2><p>Questions about statistics?</p><ul><li>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="/docs/support/faqs">FAQs</a></li></ul>',152)])])}const h=t(r,[["render",s]]);export{g as __pageData,h as default};
