import{_ as i,c as l,o as t,ag as a}from"./chunks/framework.4QX5UEtQ.js";const p=JSON.parse('{"title":"Case Archiving - Disclosurely Data Retention Guide","description":"Manage case archiving, data retention, and secure deletion. Learn retention policies, legal holds, GDPR compliance, and best practices for long-term case management.","frontmatter":{"title":"Case Archiving - Disclosurely Data Retention Guide","description":"Manage case archiving, data retention, and secure deletion. Learn retention policies, legal holds, GDPR compliance, and best practices for long-term case management.","head":[["meta",{"property":"og:title","content":"Case Archiving & Data Retention - Disclosurely"}],["meta",{"property":"og:description","content":"Manage case archiving, data retention, and secure deletion. Learn retention policies, legal holds, GDPR compliance, and best practices for long-term case management."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Case Archiving - Disclosurely"}],["meta",{"name":"twitter:description","content":"Manage case archiving, data retention, and secure deletion. Learn retention policies, legal holds, GDPR compliance, and best practices for long-term case management."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"cases/archiving.md","filePath":"cases/archiving.md"}'),o={name:"cases/archiving.md"};function n(r,e,s,c,d,u){return t(),l("div",null,[...e[0]||(e[0]=[a('<h1 id="case-archiving" tabindex="-1">Case Archiving <a class="header-anchor" href="#case-archiving" aria-label="Permalink to &quot;Case Archiving&quot;">​</a></h1><p>Long-term case management, data retention, and secure disposal practices.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Proper archiving ensures:</p><ul><li><strong>Compliance</strong>: Meet regulatory retention requirements</li><li><strong>Defensibility</strong>: Preserve evidence for potential litigation</li><li><strong>Efficiency</strong>: Keep active cases list manageable</li><li><strong>Security</strong>: Protect sensitive historical data</li><li><strong>Learning</strong>: Enable analysis of patterns over time</li></ul><h2 id="archive-vs-closed-vs-deleted" tabindex="-1">Archive vs. Closed vs. Deleted <a class="header-anchor" href="#archive-vs-closed-vs-deleted" aria-label="Permalink to &quot;Archive vs. Closed vs. Deleted&quot;">​</a></h2><h3 id="closed-cases" tabindex="-1">Closed Cases <a class="header-anchor" href="#closed-cases" aria-label="Permalink to &quot;Closed Cases&quot;">​</a></h3><p><strong>Status</strong>: Resolved/Closed</p><ul><li>Investigation complete</li><li>Outcome communicated</li><li>Actions implemented</li><li>Still in active system</li><li>Fully searchable</li><li>Included in reports</li><li>Can be reopened easily</li></ul><p><strong>When</strong>: Immediately after resolution</p><h3 id="archived-cases" tabindex="-1">Archived Cases <a class="header-anchor" href="#archived-cases" aria-label="Permalink to &quot;Archived Cases&quot;">​</a></h3><p><strong>Status</strong>: Archived</p><ul><li>Removed from active case list</li><li>Moved to archive storage</li><li>Still accessible but requires extra step</li><li>Preserved according to retention policy</li><li>Not in standard reports</li><li>Harder to reopen</li></ul><p><strong>When</strong>: After retention period begins (typically 6-12 months after resolution)</p><h3 id="deleted-cases" tabindex="-1">Deleted Cases <a class="header-anchor" href="#deleted-cases" aria-label="Permalink to &quot;Deleted Cases&quot;">​</a></h3><p><strong>Status</strong>: Permanently deleted</p><ul><li>Removed from system entirely</li><li>Cannot be recovered</li><li>Evidence destroyed</li><li>Only after retention period expires</li><li>Requires approval</li><li>Audit log remains</li></ul><p><strong>When</strong>: After retention period expires (typically 7+ years)</p><h2 id="retention-requirements" tabindex="-1">Retention Requirements <a class="header-anchor" href="#retention-requirements" aria-label="Permalink to &quot;Retention Requirements&quot;">​</a></h2><h3 id="legal-regulatory-minimums" tabindex="-1">Legal &amp; Regulatory Minimums <a class="header-anchor" href="#legal-regulatory-minimums" aria-label="Permalink to &quot;Legal &amp; Regulatory Minimums&quot;">​</a></h3><p><strong>Employment Law</strong>:</p><ul><li>UK: 6 years after case closure</li><li>EU: Varies by country (typically 5-7 years)</li><li>US: Varies by state and federal law (typically 3-7 years)</li></ul><p><strong>Financial Services</strong>:</p><ul><li>FCA (UK): 7 years minimum</li><li>SEC (US): 7 years minimum</li><li>MiFID II (EU): 7 years minimum</li></ul><p><strong>Healthcare</strong>:</p><ul><li>Medical records related: 7+ years</li><li>HIPAA requirements: 6 years minimum</li></ul><p><strong>Education</strong>:</p><ul><li>Student-related: 6 years typical</li><li>Employee-related: 6 years typical</li></ul><p><strong>General Business</strong>:</p><ul><li>Contract disputes: 6-7 years</li><li>Negligence claims: 6-7 years</li><li>Fraud cases: Indefinite (may never delete)</li></ul><h3 id="industry-best-practices" tabindex="-1">Industry Best Practices <a class="header-anchor" href="#industry-best-practices" aria-label="Permalink to &quot;Industry Best Practices&quot;">​</a></h3><p><strong>Recommended Retention</strong>:</p><ul><li><strong>Minor policy violations</strong>: 5-7 years</li><li><strong>Serious misconduct</strong>: 10-15 years</li><li><strong>Termination cases</strong>: 10-15 years</li><li><strong>Legal settlements</strong>: Indefinite</li><li><strong>Criminal matters</strong>: Indefinite</li><li><strong>Unsubstantiated allegations</strong>: Varies (3-7 years common)</li><li><strong>Withdrawn reports</strong>: Shorter period (1-3 years common)</li></ul><h3 id="setting-retention-policies" tabindex="-1">Setting Retention Policies <a class="header-anchor" href="#setting-retention-policies" aria-label="Permalink to &quot;Setting Retention Policies&quot;">​</a></h3><p><strong>In Disclosurely</strong>:</p><ol><li><p><strong>Navigate to Settings</strong></p><ul><li>Dashboard &gt; <a href="/docs/admin/organization-settings">Organization Settings</a></li><li>&quot;Data Retention &amp; Archiving&quot;</li></ul></li><li><p><strong>Set Default Retention</strong></p><ul><li>All cases: Default period (e.g., 7 years)</li><li>Calculated from resolution date</li></ul></li><li><p><strong>Category-Specific Retention</strong></p><ul><li>Financial misconduct: 10 years</li><li>Harassment: 15 years</li><li>Safety: 10 years</li><li>Minor violations: 5 years</li><li>Custom categories: Set individually</li></ul></li><li><p><strong>Outcome-Specific Retention</strong></p><ul><li>Substantiated: Longer retention</li><li>Unsubstantiated: Shorter retention</li><li>Withdrawn: Shortest retention</li></ul></li><li><p><strong>Legal Holds</strong></p><ul><li>Override retention for litigation</li><li>Preserve indefinitely</li><li>Remove hold when case settles</li></ul></li></ol><p><strong>Automatic Calculation</strong>:</p><ul><li>Resolution date + retention period = deletion eligibility date</li><li>System calculates automatically</li><li>Alerts before deletion</li><li>Requires approval to delete</li></ul><h2 id="archiving-process" tabindex="-1">Archiving Process <a class="header-anchor" href="#archiving-process" aria-label="Permalink to &quot;Archiving Process&quot;">​</a></h2><h3 id="automatic-archiving" tabindex="-1">Automatic Archiving <a class="header-anchor" href="#automatic-archiving" aria-label="Permalink to &quot;Automatic Archiving&quot;">​</a></h3><p><strong>Configure Automatic Archive</strong>:</p><ol><li><p><strong>Set Archive Trigger</strong></p><ul><li>X months after resolution</li><li>Typical: 12 months</li><li>Reduces active case clutter</li></ul></li><li><p><strong>Archive Actions</strong></p><ul><li>Move to archive storage</li><li>Remove from default views</li><li>Maintain full searchability</li><li>Preserve all evidence</li><li>Keep audit trail</li></ul></li><li><p><strong>Notification</strong></p><ul><li>Alert case owner before archiving</li><li>Allow opt-out if still active</li><li>Log archiving event</li></ul></li></ol><p><strong>Benefits</strong>:</p><ul><li>Automatic management</li><li>Consistent approach</li><li>Reduced manual work</li><li>Maintains compliance</li></ul><h3 id="manual-archiving" tabindex="-1">Manual Archiving <a class="header-anchor" href="#manual-archiving" aria-label="Permalink to &quot;Manual Archiving&quot;">​</a></h3><p><strong>Archive Individual Case</strong>:</p><ol><li><p><strong>Open Case</strong></p><ul><li>Navigate to resolved case</li><li>Verify all actions complete</li><li>Check no pending follow-up</li></ul></li><li><p><strong>Click Archive</strong></p><ul><li>Button on case detail page</li><li>Add reason for archiving</li><li>Confirm action</li></ul></li><li><p><strong>Case Archived</strong></p><ul><li>Removed from active list</li><li>Moved to archive section</li><li>Notification sent to team</li><li>Logged in audit trail</li></ul></li></ol><p><strong>Bulk Archiving</strong>:</p><ol><li><p><strong>Filter Cases</strong></p><ul><li>Resolved cases</li><li>Older than X months</li><li>Specific category</li><li>Specific outcome</li></ul></li><li><p><strong>Select Cases</strong></p><ul><li>Checkboxes for bulk selection</li><li>Up to 100 at once</li></ul></li><li><p><strong>Bulk Action</strong></p><ul><li>&quot;Archive Selected Cases&quot;</li><li>Add bulk archive note</li><li>Confirm action</li><li>Processing notification</li></ul></li></ol><p><strong>Use Cases</strong>:</p><ul><li>Year-end cleanup</li><li>Department reorganization</li><li>System migration</li><li>Audit preparation</li></ul><h2 id="accessing-archived-cases" tabindex="-1">Accessing Archived Cases <a class="header-anchor" href="#accessing-archived-cases" aria-label="Permalink to &quot;Accessing Archived Cases&quot;">​</a></h2><h3 id="finding-archived-cases" tabindex="-1">Finding Archived Cases <a class="header-anchor" href="#finding-archived-cases" aria-label="Permalink to &quot;Finding Archived Cases&quot;">​</a></h3><p><strong>Via Archive Section</strong>:</p><ol><li><p><strong>Navigate to Archives</strong></p><ul><li>Dashboard &gt; Cases &gt; Archived Cases</li><li>Separate from active cases</li></ul></li><li><p><strong>Search &amp; Filter</strong></p><ul><li>Same filters as active cases</li><li>Date range</li><li>Category</li><li>Outcome</li><li>Investigator</li><li>Keywords</li></ul></li><li><p><strong>View Case</strong></p><ul><li>Click to open</li><li>Full case details available</li><li>All evidence accessible</li><li>Read-only by default</li></ul></li></ol><p><strong>Via Search</strong>:</p><ul><li>Global search includes archived cases</li><li>Check &quot;Include Archived&quot; box</li><li>Results show archive status</li><li>Can filter results to archived only</li></ul><h3 id="reopening-archived-cases" tabindex="-1">Reopening Archived Cases <a class="header-anchor" href="#reopening-archived-cases" aria-label="Permalink to &quot;Reopening Archived Cases&quot;">​</a></h3><p><strong>When to Reopen</strong>:</p><ul><li>New information emerges</li><li>Related new complaint</li><li>Appeals or legal proceedings</li><li>Follow-up investigation needed</li><li>Errors discovered</li></ul><p><strong>How to Reopen</strong>:</p><ol><li><p><strong>Open Archived Case</strong></p><ul><li>Find case in archives</li><li>Click to view details</li></ul></li><li><p><strong>Request Reopen</strong></p><ul><li>&quot;Reopen Case&quot; button</li><li>Add reason for reopening</li><li>Specify expected duration</li></ul></li><li><p><strong>Approval</strong></p><ul><li>Admin or Org Admin approves</li><li>Case moved back to active</li><li>Status changed to &quot;Under Review&quot;</li><li>Notifications sent</li></ul></li><li><p><strong>Investigation Continues</strong></p><ul><li>All history preserved</li><li>New notes added</li><li>Additional evidence uploaded</li><li>Original timeline visible</li></ul></li></ol><h2 id="data-deletion" tabindex="-1">Data Deletion <a class="header-anchor" href="#data-deletion" aria-label="Permalink to &quot;Data Deletion&quot;">​</a></h2><h3 id="approaching-deletion-date" tabindex="-1">Approaching Deletion Date <a class="header-anchor" href="#approaching-deletion-date" aria-label="Permalink to &quot;Approaching Deletion Date&quot;">​</a></h3><p><strong>System Alerts</strong>:</p><ul><li>90 days before: Warning email</li><li>30 days before: Second warning</li><li>7 days before: Final warning</li><li>Deletion date: Requires approval</li></ul><p><strong>Review Before Deletion</strong>:</p><ul><li>Is retention period correct?</li><li>Any litigation risk?</li><li>Historical significance?</li><li>Related pending cases?</li><li>Regulatory inquiries?</li></ul><p><strong>Extend Retention</strong>:</p><ul><li>Click &quot;Extend Retention&quot;</li><li>Select new period</li><li>Add reason</li><li>Approve extension</li><li>New deletion date calculated</li></ul><h3 id="deletion-process" tabindex="-1">Deletion Process <a class="header-anchor" href="#deletion-process" aria-label="Permalink to &quot;Deletion Process&quot;">​</a></h3><p><strong>Approval Required</strong>:</p><ul><li>Organization Administrator</li><li>Compliance Officer</li><li>Data Protection Officer</li><li>Cannot be delegated</li></ul><p><strong>Deletion Steps</strong>:</p><ol><li><p><strong>Review Deletion Queue</strong></p><ul><li>Dashboard &gt; Data Management &gt; Pending Deletions</li><li>List of cases eligible for deletion</li><li>Review each carefully</li></ul></li><li><p><strong>Bulk or Individual</strong></p><ul><li>Approve deletions individually</li><li>Or approve batch</li><li>Cannot be undone</li></ul></li><li><p><strong>Confirm Deletion</strong></p><ul><li>Final confirmation prompt</li><li>Acknowledges permanent deletion</li><li>Documents compliance with retention policy</li></ul></li><li><p><strong>Deletion Executed</strong></p><ul><li>All case data deleted</li><li>All evidence deleted</li><li>Encryption keys destroyed</li><li>Audit log entry created (preserved)</li><li>Certificate of deletion generated</li></ul></li></ol><p><strong>What Gets Deleted</strong>:</p><ul><li>Report content</li><li>All messages</li><li>All evidence files</li><li>Case notes</li><li>Investigation reports</li><li>Personal data</li><li>All associated records</li></ul><p><strong>What&#39;s Preserved</strong>:</p><ul><li>Audit log (anonymized)</li><li>Aggregate statistics</li><li>Policy compliance metrics</li><li>Retention certificate</li></ul><h3 id="secure-deletion" tabindex="-1">Secure Deletion <a class="header-anchor" href="#secure-deletion" aria-label="Permalink to &quot;Secure Deletion&quot;">​</a></h3><p><strong>Deletion Method</strong>:</p><ul><li>Military-grade data destruction</li><li>Multiple overwrite passes</li><li>Encryption key destruction</li><li>Database record removal</li><li>Backup purging</li><li>Compliance with GDPR &quot;right to erasure&quot;</li></ul><p><strong>Verification</strong>:</p><ul><li>Deletion completion check</li><li>Backup verification</li><li>Certificate generation</li><li>Audit log entry</li><li>Annual deletion report</li></ul><h2 id="legal-holds" tabindex="-1">Legal Holds <a class="header-anchor" href="#legal-holds" aria-label="Permalink to &quot;Legal Holds&quot;">​</a></h2><h3 id="what-is-a-legal-hold" tabindex="-1">What Is a Legal Hold? <a class="header-anchor" href="#what-is-a-legal-hold" aria-label="Permalink to &quot;What Is a Legal Hold?&quot;">​</a></h3><p><strong>Purpose</strong>:</p><ul><li>Preserve evidence for litigation</li><li>Prevent destruction during proceedings</li><li>Comply with discovery obligations</li><li>Avoid spoliation sanctions</li></ul><p><strong>Effect</strong>:</p><ul><li>Overrides retention policy</li><li>Prevents deletion</li><li>Prevents archiving (optional)</li><li>Flags case prominently</li><li>Requires legal team approval to remove</li></ul><h3 id="implementing-legal-hold" tabindex="-1">Implementing Legal Hold <a class="header-anchor" href="#implementing-legal-hold" aria-label="Permalink to &quot;Implementing Legal Hold&quot;">​</a></h3><p><strong>When to Use</strong>:</p><ul><li>Litigation filed or anticipated</li><li>Regulatory investigation</li><li>Subpoena or court order</li><li>Internal investigation into legal matter</li><li>Employment tribunal proceedings</li></ul><p><strong>How to Implement</strong>:</p><ol><li><p><strong>Open Case</strong></p><ul><li>Navigate to relevant case</li><li>Click &quot;Legal Hold&quot; button</li></ul></li><li><p><strong>Complete Hold Form</strong></p><ul><li>Matter name/number</li><li>Reason for hold</li><li>Authorizing attorney/counsel</li><li>Related cases to hold</li><li>Expected duration</li></ul></li><li><p><strong>Activate Hold</strong></p><ul><li>Admin approval required</li><li>Hold icon appears on case</li><li>Deletion disabled</li><li>All users notified</li><li>Audit log entry</li></ul></li></ol><p><strong>Bulk Legal Holds</strong>:</p><ul><li>Hold multiple related cases</li><li>Filter by criteria</li><li>Apply hold to all</li><li>Document scope clearly</li></ul><h3 id="managing-legal-holds" tabindex="-1">Managing Legal Holds <a class="header-anchor" href="#managing-legal-holds" aria-label="Permalink to &quot;Managing Legal Holds&quot;">​</a></h3><p><strong>Monitoring</strong>:</p><ul><li>Dashboard shows all held cases</li><li>Duration of hold</li><li>Responsible attorney</li><li>Hold status</li></ul><p><strong>Notifications</strong>:</p><ul><li>Annual review reminders</li><li>Hold extended notifications</li><li>Hold released notifications</li></ul><p><strong>Releasing Hold</strong>:</p><ul><li>Legal team approval required</li><li>Document reason for release</li><li>Normal retention policy resumes</li><li>Audit log entry created</li></ul><h2 id="retention-reports" tabindex="-1">Retention Reports <a class="header-anchor" href="#retention-reports" aria-label="Permalink to &quot;Retention Reports&quot;">​</a></h2><h3 id="compliance-reports" tabindex="-1">Compliance Reports <a class="header-anchor" href="#compliance-reports" aria-label="Permalink to &quot;Compliance Reports&quot;">​</a></h3><p><strong>Generate Reports</strong>:</p><ul><li>Dashboard &gt; Reports &gt; Data Retention</li><li>Date range</li><li>Case categories</li><li>Retention status</li></ul><p><strong>Report Contents</strong>:</p><ul><li>Cases archived during period</li><li>Cases deleted during period</li><li>Cases under legal hold</li><li>Upcoming deletions</li><li>Retention policy compliance</li><li>Exceptions and extensions</li></ul><p><strong>Use For</strong>:</p><ul><li>Audit preparation</li><li>Board reporting</li><li>Compliance documentation</li><li>Policy review</li><li>Capacity planning</li></ul><h3 id="deletion-certificates" tabindex="-1">Deletion Certificates <a class="header-anchor" href="#deletion-certificates" aria-label="Permalink to &quot;Deletion Certificates&quot;">​</a></h3><p><strong>Auto-Generated</strong>:</p><ul><li>After each deletion</li><li>Lists cases deleted</li><li>Deletion method</li><li>Date and time</li><li>Approver name</li><li>Compliance attestation</li></ul><p><strong>Retention of Certificates</strong>:</p><ul><li>Keep indefinitely</li><li>Proves compliance</li><li>Defensible in litigation</li><li>Required for some audits</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="regular-review" tabindex="-1">Regular Review <a class="header-anchor" href="#regular-review" aria-label="Permalink to &quot;Regular Review&quot;">​</a></h3><p><strong>Quarterly Reviews</strong>:</p><ul><li>Upcoming deletions</li><li>Legal hold status</li><li>Retention policy appropriateness</li><li>Archive size and costs</li><li>Access patterns</li></ul><p><strong>Annual Reviews</strong>:</p><ul><li>Retention policy update</li><li>Regulatory changes</li><li>Storage optimization</li><li>Deletion certificate review</li><li>Audit preparation</li></ul><h3 id="document-exceptions" tabindex="-1">Document Exceptions <a class="header-anchor" href="#document-exceptions" aria-label="Permalink to &quot;Document Exceptions&quot;">​</a></h3><p><strong>Every Time You</strong>:</p><ul><li>Extend retention beyond policy</li><li>Delete before retention period</li><li>Apply legal hold</li><li>Release legal hold</li><li>Reopen archived case</li></ul><p><strong>Document</strong>:</p><ul><li>Why exception needed</li><li>Who approved</li><li>Supporting rationale</li><li>Related cases</li><li>Follow-up required</li></ul><h3 id="maintain-audit-trail" tabindex="-1">Maintain Audit Trail <a class="header-anchor" href="#maintain-audit-trail" aria-label="Permalink to &quot;Maintain Audit Trail&quot;">​</a></h3><p><strong>Audit Log Captures</strong>:</p><ul><li>All archiving actions</li><li>All deletion approvals</li><li>Legal hold implementations</li><li>Retention extensions</li><li>Case reopenings</li><li>Access to archived cases</li></ul><p><strong>Preservation</strong>:</p><ul><li><a href="/docs/compliance/audit-trail">Audit logs</a> never deleted</li><li>Even after case deleted</li><li>Anonymized if necessary</li><li>Meet regulatory requirements</li><li>Available for investigations</li></ul><h3 id="balance-retention-and-privacy" tabindex="-1">Balance Retention and Privacy <a class="header-anchor" href="#balance-retention-and-privacy" aria-label="Permalink to &quot;Balance Retention and Privacy&quot;">​</a></h3><p><strong>Longer Isn&#39;t Always Better</strong>:</p><ul><li>Privacy laws favor deletion</li><li><a href="/docs/compliance/gdpr">GDPR &quot;right to erasure&quot;</a></li><li>Storage costs increase</li><li>Breach exposure grows</li><li>Discovery burdens expand</li></ul><p><strong>Minimum Necessary</strong>:</p><ul><li>Retain only as long as needed</li><li>Delete when legally permissible</li><li>Consider privacy rights</li><li>Balance business needs</li><li>Document decisions</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="cannot-find-archived-case" tabindex="-1">Cannot Find Archived Case <a class="header-anchor" href="#cannot-find-archived-case" aria-label="Permalink to &quot;Cannot Find Archived Case&quot;">​</a></h3><p><strong>Check</strong>:</p><ul><li>Include archived in search</li><li>Correct date range</li><li>May have been deleted</li><li>Check audit log</li><li>Verify access permissions</li></ul><h3 id="case-auto-archived-too-soon" tabindex="-1">Case Auto-Archived Too Soon <a class="header-anchor" href="#case-auto-archived-too-soon" aria-label="Permalink to &quot;Case Auto-Archived Too Soon&quot;">​</a></h3><p><strong>Fix</strong>:</p><ul><li>Reopen case</li><li>Adjust retention settings</li><li>Review archive triggers</li><li>Update auto-archive rules</li></ul><h3 id="cannot-delete-case-past-retention" tabindex="-1">Cannot Delete Case Past Retention <a class="header-anchor" href="#cannot-delete-case-past-retention" aria-label="Permalink to &quot;Cannot Delete Case Past Retention&quot;">​</a></h3><p><strong>Verify</strong>:</p><ul><li>Legal hold in place?</li><li>Retention calculation correct?</li><li>Administrator permissions?</li><li>Related cases pending?</li><li>System error? Contact support</li></ul><h3 id="regulator-requests-deleted-case" tabindex="-1">Regulator Requests Deleted Case <a class="header-anchor" href="#regulator-requests-deleted-case" aria-label="Permalink to &quot;Regulator Requests Deleted Case&quot;">​</a></h3><p><strong>If Properly Deleted</strong>:</p><ul><li>Provide deletion certificate</li><li>Demonstrate retention policy</li><li>Show regulatory compliance</li><li>Provide audit log (anonymized)</li><li>Explain data destruction process</li></ul><p><strong>Lessons Learned</strong>:</p><ul><li>Review retention adequacy</li><li>Consider longer retention</li><li>Implement legal hold earlier</li><li>Better regulatory communication</li></ul><hr><p><strong>Related:</strong></p><ul><li><a href="/docs/cases/resolution">Case Resolution</a></li><li><a href="/docs/compliance/audit-trail">Compliance &amp; Audit Trail</a></li><li><a href="/docs/compliance/retention">Data Retention</a></li><li><a href="/docs/compliance/gdpr">GDPR Compliance</a></li><li><a href="/docs/admin/organization-settings">Organization Settings</a></li></ul>',156)])])}const h=i(o,[["render",n]]);export{p as __pageData,h as default};
