import{_ as e,c as a,o as s,ag as t}from"./chunks/framework.4QX5UEtQ.js";const d=JSON.parse('{"title":"Case Assignment - Disclosurely Team Management Guide","description":"Assign cases efficiently with auto-routing, round-robin distribution, and workload balancing. Configure assignment rules and track team performance metrics.","frontmatter":{"title":"Case Assignment - Disclosurely Team Management Guide","description":"Assign cases efficiently with auto-routing, round-robin distribution, and workload balancing. Configure assignment rules and track team performance metrics.","head":[["meta",{"property":"og:title","content":"Case Assignment & Distribution - Disclosurely"}],["meta",{"property":"og:description","content":"Assign cases efficiently with auto-routing, round-robin distribution, and workload balancing. Configure assignment rules and track team performance metrics."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Case Assignment - Disclosurely"}],["meta",{"name":"twitter:description","content":"Assign cases efficiently with auto-routing, round-robin distribution, and workload balancing. Configure assignment rules and track team performance metrics."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"cases/assignment.md","filePath":"cases/assignment.md"}'),n={name:"cases/assignment.md"};function l(o,i,r,g,c,m){return s(),a("div",null,[...i[0]||(i[0]=[t('<h1 id="case-assignment" tabindex="-1">Case Assignment <a class="header-anchor" href="#case-assignment" aria-label="Permalink to &quot;Case Assignment&quot;">​</a></h1><p>Efficiently assign and distribute cases to the right team members for investigation.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>When a new report is submitted, it needs to be assigned to an investigator or team for review. Proper case assignment ensures:</p><ul><li>Cases reach the right subject matter experts</li><li>Workload is distributed evenly across your team</li><li>Response times are minimized</li><li>Accountability is clear from the start</li></ul><h2 id="automatic-assignment" tabindex="-1">Automatic Assignment <a class="header-anchor" href="#automatic-assignment" aria-label="Permalink to &quot;Automatic Assignment&quot;">​</a></h2><h3 id="rules-based-assignment" tabindex="-1">Rules-Based Assignment <a class="header-anchor" href="#rules-based-assignment" aria-label="Permalink to &quot;Rules-Based Assignment&quot;">​</a></h3><p>Configure automatic assignment rules to route cases based on:</p><p><strong>Category-Based Routing:</strong></p><ul><li>Financial misconduct → Finance team</li><li>HR issues → Human Resources</li><li>Safety concerns → Health &amp; Safety team</li><li>Data breaches → IT Security team</li></ul><p><strong>Priority-Based Routing:</strong></p><ul><li>Critical cases → Senior investigators</li><li>Standard cases → General investigation pool</li><li>Low priority → Junior team members</li></ul><p><strong>Department-Based Routing:</strong></p><ul><li>Route cases to specific departments mentioned in report</li><li>Cross-department issues assigned to compliance team</li><li>Location-specific routing for multi-site organizations</li></ul><h3 id="setting-up-auto-assignment" tabindex="-1">Setting Up Auto-Assignment <a class="header-anchor" href="#setting-up-auto-assignment" aria-label="Permalink to &quot;Setting Up Auto-Assignment&quot;">​</a></h3><ol><li><p><strong>Navigate to Settings</strong></p><ul><li>Go to Dashboard &gt; <a href="/docs/admin/organization-settings">Organization Settings</a></li><li>Click &quot;Case Assignment Rules&quot;</li></ul></li><li><p><strong>Create Assignment Rule</strong></p><ul><li>Click &quot;Add New Rule&quot;</li><li>Select trigger criteria: <ul><li>Report category</li><li>Priority level</li><li>Keywords in report</li><li>Department mentioned</li><li>Reporter location (if available)</li></ul></li></ul></li><li><p><strong>Define Assignment Target</strong></p><ul><li>Assign to specific user</li><li>Assign to team/group</li><li>Round-robin within team</li><li>Load balancing based on current workload</li></ul></li><li><p><strong>Set Rule Priority</strong></p><ul><li>Multiple rules can match one case</li><li>Higher priority rules execute first</li><li>Set fallback assignment if no rules match</li></ul></li><li><p><strong>Save and Activate</strong></p><ul><li>Test rule with sample cases</li><li>Monitor effectiveness</li><li>Adjust as needed</li></ul></li></ol><h3 id="round-robin-assignment" tabindex="-1">Round-Robin Assignment <a class="header-anchor" href="#round-robin-assignment" aria-label="Permalink to &quot;Round-Robin Assignment&quot;">​</a></h3><p>Distribute cases evenly across team members:</p><p><strong>How It Works:</strong></p><ol><li>Define pool of eligible investigators</li><li>System tracks who received last case</li><li>Next case goes to next person in rotation</li><li>Skips users on leave or at capacity</li><li>Ensures fair distribution</li></ol><p><strong>Configuration:</strong></p><ul><li>Go to <a href="/docs/admin/team-management">Team Management</a></li><li>Create &quot;Investigation Team&quot; group</li><li>Enable round-robin for group</li><li>Set maximum concurrent cases per person</li><li>Add/remove members as needed</li></ul><h2 id="manual-assignment" tabindex="-1">Manual Assignment <a class="header-anchor" href="#manual-assignment" aria-label="Permalink to &quot;Manual Assignment&quot;">​</a></h2><h3 id="assigning-from-case-dashboard" tabindex="-1">Assigning from Case Dashboard <a class="header-anchor" href="#assigning-from-case-dashboard" aria-label="Permalink to &quot;Assigning from Case Dashboard&quot;">​</a></h3><p><strong>Quick Assignment:</strong></p><ol><li>Open Cases dashboard</li><li>Find unassigned case</li><li>Click &quot;Assign&quot; button</li><li>Select team member from dropdown</li><li>Add optional note about why assigned</li><li>Click &quot;Assign Case&quot;</li></ol><p><strong>Bulk Assignment:</strong></p><ol><li>Select multiple cases using checkboxes</li><li>Click &quot;Bulk Actions&quot; &gt; &quot;Assign&quot;</li><li>Choose assignee</li><li>Cases assigned with notification sent</li></ol><h3 id="assigning-from-case-detail-page" tabindex="-1">Assigning from Case Detail Page <a class="header-anchor" href="#assigning-from-case-detail-page" aria-label="Permalink to &quot;Assigning from Case Detail Page&quot;">​</a></h3><p>For more detailed assignment:</p><ol><li><p><strong>Open Case</strong></p><ul><li>Click case to view full details</li><li>Review report content, category, priority</li></ul></li><li><p><strong>Choose Assignee</strong></p><ul><li>Click &quot;Assign Case&quot; in top right</li><li>See list of eligible team members</li><li>View each member&#39;s: <ul><li>Current case load</li><li>Availability status</li><li>Relevant expertise</li><li>Average resolution time</li></ul></li></ul></li><li><p><strong>Add Assignment Details</strong></p><ul><li>Optional: Add note explaining assignment</li><li>Set expected response timeframe</li><li>Mark as urgent if needed</li><li>Add case to specific <a href="/docs/cases/workflow">investigation project</a></li></ul></li><li><p><strong>Notify Assignee</strong></p><ul><li>Email notification sent automatically</li><li>In-app notification appears</li><li>SMS alert for urgent cases (Enterprise)</li><li>Slack/Teams integration (if configured)</li></ul></li></ol><h2 id="re-assignment" tabindex="-1">Re-Assignment <a class="header-anchor" href="#re-assignment" aria-label="Permalink to &quot;Re-Assignment&quot;">​</a></h2><p>Cases can be re-assigned when needed:</p><h3 id="when-to-re-assign" tabindex="-1">When to Re-Assign <a class="header-anchor" href="#when-to-re-assign" aria-label="Permalink to &quot;When to Re-Assign&quot;">​</a></h3><p><strong>Workload Balancing:</strong></p><ul><li>Team member overloaded with cases</li><li>Someone has specialized knowledge needed</li><li>Original assignee on leave or unavailable</li></ul><p><strong>Expertise Required:</strong></p><ul><li>Case more complex than initially assessed</li><li>Technical expertise needed</li><li>Legal review required</li><li>External specialist consultation</li></ul><p><strong>Conflict of Interest:</strong></p><ul><li>Assignee has relationship with subject</li><li>Assignee is mentioned in report</li><li>Department conflict identified</li><li>Impartiality concerns</li></ul><h3 id="how-to-re-assign" tabindex="-1">How to Re-Assign <a class="header-anchor" href="#how-to-re-assign" aria-label="Permalink to &quot;How to Re-Assign&quot;">​</a></h3><ol><li><p><strong>Open Case</strong></p><ul><li>Navigate to case detail page</li><li>Current assignee shown at top</li></ul></li><li><p><strong>Click Re-Assign</strong></p><ul><li>Button in top right</li><li>Requires Admin or Org Admin role</li><li>Add reason for re-assignment (recommended)</li></ul></li><li><p><strong>Select New Assignee</strong></p><ul><li>Choose from dropdown</li><li>View availability and workload</li><li>Add handover notes</li></ul></li><li><p><strong>Notify Both Parties</strong></p><ul><li>Original assignee notified of removal</li><li>New assignee notified of assignment</li><li>Case history updated with re-assignment</li></ul></li><li><p><strong>Handover Notes</strong></p><ul><li>Original assignee can add notes</li><li>Share investigation progress</li><li>Highlight key evidence</li><li>Note any reporter communications</li></ul></li></ol><h2 id="team-based-assignment" tabindex="-1">Team-Based Assignment <a class="header-anchor" href="#team-based-assignment" aria-label="Permalink to &quot;Team-Based Assignment&quot;">​</a></h2><p>Assign cases to teams rather than individuals:</p><h3 id="benefits" tabindex="-1">Benefits <a class="header-anchor" href="#benefits" aria-label="Permalink to &quot;Benefits&quot;">​</a></h3><ul><li>Entire team has visibility</li><li>Any team member can work on case</li><li>Built-in backup if someone unavailable</li><li>Collaborative investigation possible</li><li>Knowledge sharing within team</li></ul><h3 id="creating-investigation-teams" tabindex="-1">Creating Investigation Teams <a class="header-anchor" href="#creating-investigation-teams" aria-label="Permalink to &quot;Creating Investigation Teams&quot;">​</a></h3><ol><li><p><strong>Go to Team Management</strong></p><ul><li>Dashboard &gt; Team &gt; Manage Teams</li></ul></li><li><p><strong>Create New Team</strong></p><ul><li>Click &quot;Create Investigation Team&quot;</li><li>Name: e.g., &quot;Financial Investigations&quot;</li><li>Add team members</li><li>Assign team lead</li></ul></li><li><p><strong>Set Team Permissions</strong></p><ul><li>What case types team handles</li><li>Access levels for team members</li><li>Notification preferences</li><li>Collaboration settings</li></ul></li><li><p><strong>Assign Cases to Team</strong></p><ul><li>Cases assigned to team, not individual</li><li>Team members can claim cases</li><li>Team lead can distribute within team</li><li>Workload visible to all team members</li></ul></li></ol><h3 id="claiming-cases" tabindex="-1">Claiming Cases <a class="header-anchor" href="#claiming-cases" aria-label="Permalink to &quot;Claiming Cases&quot;">​</a></h3><p>When case assigned to team:</p><ol><li><p><strong>Team members see available cases</strong></p><ul><li>&quot;Available to Claim&quot; section in dashboard</li><li>Filtered by their team assignments</li></ul></li><li><p><strong>Claim Case</strong></p><ul><li>Click &quot;Claim This Case&quot;</li><li>Becomes your responsibility</li><li>Removed from team pool</li><li>Other team members notified</li></ul></li><li><p><strong>Unclaim if Needed</strong></p><ul><li>Return to team pool</li><li>Add note about why unclaiming</li><li>Team lead notified</li></ul></li></ol><h2 id="notifications" tabindex="-1">Notifications <a class="header-anchor" href="#notifications" aria-label="Permalink to &quot;Notifications&quot;">​</a></h2><h3 id="for-assignees" tabindex="-1">For Assignees <a class="header-anchor" href="#for-assignees" aria-label="Permalink to &quot;For Assignees&quot;">​</a></h3><p>When assigned a case, you receive:</p><p><strong>Immediate Notification:</strong></p><ul><li>Email with case summary</li><li>In-app notification badge</li><li>Push notification (mobile app)</li><li>SMS for urgent cases (Enterprise)</li></ul><p><strong>Notification Includes:</strong></p><ul><li>Case tracking ID</li><li>Report category</li><li>Priority level</li><li>Submission date</li><li>Direct link to case</li><li>Assignment note (if provided)</li></ul><h3 id="for-reporters" tabindex="-1">For Reporters <a class="header-anchor" href="#for-reporters" aria-label="Permalink to &quot;For Reporters&quot;">​</a></h3><p>Reporters are notified when case assigned:</p><p><strong>What They See:</strong></p><ul><li>&quot;Your case has been assigned to an investigator&quot;</li><li>Expected response timeframe</li><li>Next steps in process</li><li>How to send additional information</li></ul><p><strong>What They Don&#39;t See:</strong></p><ul><li>Investigator name (for anonymity)</li><li>Investigator email or contact</li><li>Internal assignment notes</li><li>Team structure</li></ul><h2 id="assignment-metrics" tabindex="-1">Assignment Metrics <a class="header-anchor" href="#assignment-metrics" aria-label="Permalink to &quot;Assignment Metrics&quot;">​</a></h2><h3 id="track-assignment-performance" tabindex="-1">Track Assignment Performance <a class="header-anchor" href="#track-assignment-performance" aria-label="Permalink to &quot;Track Assignment Performance&quot;">​</a></h3><p><strong>Dashboard Metrics:</strong></p><ul><li>Average time to assignment</li><li>Cases currently unassigned</li><li>Assignment distribution across team</li><li>Re-assignment rate</li><li>Cases per investigator</li></ul><p><strong>Reports Available:</strong></p><ul><li>Assignment efficiency report</li><li>Investigator workload report</li><li>Time-to-assignment by category</li><li>Re-assignment analysis</li><li>Team performance comparison</li></ul><h3 id="optimizing-assignment" tabindex="-1">Optimizing Assignment <a class="header-anchor" href="#optimizing-assignment" aria-label="Permalink to &quot;Optimizing Assignment&quot;">​</a></h3><p>Use metrics to improve:</p><p><strong>Identify Bottlenecks:</strong></p><ul><li>Which categories take longest to assign?</li><li>Are certain investigators overloaded?</li><li>Do auto-assignment rules work well?</li><li>Are cases re-assigned frequently?</li></ul><p><strong>Make Improvements:</strong></p><ul><li>Adjust auto-assignment rules</li><li>Rebalance team workloads</li><li>Add more investigators to busy categories</li><li>Provide training on specific case types</li><li>Streamline assignment process</li></ul><h2 id="assignment-permissions" tabindex="-1">Assignment Permissions <a class="header-anchor" href="#assignment-permissions" aria-label="Permalink to &quot;Assignment Permissions&quot;">​</a></h2><h3 id="who-can-assign-cases" tabindex="-1">Who Can Assign Cases <a class="header-anchor" href="#who-can-assign-cases" aria-label="Permalink to &quot;Who Can Assign Cases&quot;">​</a></h3><p><strong>Organization Administrator:</strong></p><ul><li>Assign any case to anyone</li><li>Re-assign cases</li><li>Create assignment rules</li><li>Manage teams</li></ul><p><strong>Case Handler (Admin role):</strong></p><ul><li>Assign unassigned cases</li><li>Re-assign own cases</li><li>Cannot override other assignments (without Org Admin)</li></ul><p><strong>Reviewer:</strong></p><ul><li>Cannot assign cases</li><li>Can only view assigned cases</li><li>Can request re-assignment</li></ul><h3 id="preventing-conflicts" tabindex="-1">Preventing Conflicts <a class="header-anchor" href="#preventing-conflicts" aria-label="Permalink to &quot;Preventing Conflicts&quot;">​</a></h3><p><strong>System Protections:</strong></p><ul><li>Can&#39;t assign to users without proper role</li><li>Can&#39;t assign to inactive accounts</li><li>Can&#39;t assign if user at capacity (if limit set)</li><li>Can&#39;t assign to user on leave status</li></ul><p><strong>Conflict of Interest Checks:</strong></p><ul><li>System flags if assignee in same department as subject</li><li>Warning if assignee previously involved with reporter</li><li>Alert if assignee mentioned in case notes</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="assign-promptly" tabindex="-1">Assign Promptly <a class="header-anchor" href="#assign-promptly" aria-label="Permalink to &quot;Assign Promptly&quot;">​</a></h3><p><strong>Why It Matters:</strong></p><ul><li>Shows reporter their concern is taken seriously</li><li>Meets compliance timeframes</li><li>Prevents cases from being overlooked</li><li>Reduces reporter anxiety</li></ul><p><strong>Target Timeframes:</strong></p><ul><li>Critical cases: Within 1 hour</li><li>High priority: Within 4 hours</li><li>Standard: Within 24 hours</li><li>Low priority: Within 48 hours</li></ul><h3 id="match-expertise-to-case" tabindex="-1">Match Expertise to Case <a class="header-anchor" href="#match-expertise-to-case" aria-label="Permalink to &quot;Match Expertise to Case&quot;">​</a></h3><p><strong>Consider:</strong></p><ul><li>Subject matter expertise needed</li><li>Language skills (for international reports)</li><li>Cultural sensitivity required</li><li>Technical knowledge (IT, financial, legal)</li><li>Investigation experience level</li></ul><h3 id="balance-workload" tabindex="-1">Balance Workload <a class="header-anchor" href="#balance-workload" aria-label="Permalink to &quot;Balance Workload&quot;">​</a></h3><p><strong>Monitor:</strong></p><ul><li>Current case load per investigator</li><li>Average resolution time per investigator</li><li>Complexity of assigned cases</li><li>Upcoming leave or time off</li><li>Other responsibilities outside Disclosurely</li></ul><h3 id="document-assignment-decisions" tabindex="-1">Document Assignment Decisions <a class="header-anchor" href="#document-assignment-decisions" aria-label="Permalink to &quot;Document Assignment Decisions&quot;">​</a></h3><p><strong>Add Notes When:</strong></p><ul><li>Assigning complex cases</li><li>Re-assigning cases</li><li>Choosing specific investigator for reason</li><li>Deviating from standard assignment rules</li></ul><p><strong>Helps With:</strong></p><ul><li>Knowledge sharing</li><li>Audit trail</li><li>Training new administrators</li><li>Defending assignment decisions if questioned</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="case-not-being-auto-assigned" tabindex="-1">Case Not Being Auto-Assigned <a class="header-anchor" href="#case-not-being-auto-assigned" aria-label="Permalink to &quot;Case Not Being Auto-Assigned&quot;">​</a></h3><p><strong>Check:</strong></p><ul><li>Are assignment rules active?</li><li>Does case match any rule criteria?</li><li>Is fallback assignment configured?</li><li>Are all potential assignees at capacity?</li><li>Check rule priority order</li></ul><h3 id="assignee-not-receiving-notifications" tabindex="-1">Assignee Not Receiving Notifications <a class="header-anchor" href="#assignee-not-receiving-notifications" aria-label="Permalink to &quot;Assignee Not Receiving Notifications&quot;">​</a></h3><p><strong>Verify:</strong></p><ul><li>Assignee email address correct</li><li>Notifications enabled in their settings</li><li>Email not in spam folder</li><li>User account is active</li><li>Integration settings (Slack/Teams) correct</li></ul><h3 id="cannot-re-assign-case" tabindex="-1">Cannot Re-Assign Case <a class="header-anchor" href="#cannot-re-assign-case" aria-label="Permalink to &quot;Cannot Re-Assign Case&quot;">​</a></h3><p><strong>Common Causes:</strong></p><ul><li>Insufficient permissions (need Admin role)</li><li>Case already closed</li><li>New assignee lacks required role</li><li>New assignee account inactive</li><li>System conflict of interest flag</li></ul><hr><p><strong>Related:</strong></p><ul><li><a href="/docs/cases/workflow">Investigation Workflow</a></li><li><a href="/docs/admin/team-management">Team Management</a></li><li><a href="/docs/cases/status">Status Management</a></li></ul>',119)])])}const p=e(n,[["render",l]]);export{d as __pageData,p as default};
