import{_ as i,c as l,o as n,ag as t}from"./chunks/framework.4QX5UEtQ.js";const g=JSON.parse('{"title":"Case Notes & Evidence - Disclosurely Investigation Guide","description":"Document investigations effectively with case notes and evidence management. Upload files, maintain chain of custody, and create defensible investigation records.","frontmatter":{"title":"Case Notes & Evidence - Disclosurely Investigation Guide","description":"Document investigations effectively with case notes and evidence management. Upload files, maintain chain of custody, and create defensible investigation records.","head":[["meta",{"property":"og:title","content":"Case Notes & Evidence Management - Disclosurely"}],["meta",{"property":"og:description","content":"Document investigations effectively with case notes and evidence management. Upload files, maintain chain of custody, and create defensible investigation records."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Case Notes & Evidence - Disclosurely"}],["meta",{"name":"twitter:description","content":"Document investigations effectively with case notes and evidence management. Upload files, maintain chain of custody, and create defensible investigation records."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"cases/evidence.md","filePath":"cases/evidence.md"}'),a={name:"cases/evidence.md"};function o(s,e,r,c,d,p){return n(),l("div",null,[...e[0]||(e[0]=[t(`<h1 id="case-notes-and-evidence" tabindex="-1">Case Notes and Evidence <a class="header-anchor" href="#case-notes-and-evidence" aria-label="Permalink to &quot;Case Notes and Evidence&quot;">​</a></h1><p>Comprehensive guide to documenting investigations and managing evidence properly.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Thorough documentation and evidence management are critical for:</p><ul><li><strong>Defensible investigations</strong>: Stand up to legal scrutiny</li><li><strong>Audit compliance</strong>: Meet regulatory requirements</li><li><strong>Knowledge retention</strong>: Maintain institutional memory</li><li><strong>Quality assurance</strong>: Enable peer review</li><li><strong>Accountability</strong>: Demonstrate diligence</li><li><strong>Consistency</strong>: Support fair treatment across cases</li></ul><h2 id="case-notes" tabindex="-1">Case Notes <a class="header-anchor" href="#case-notes" aria-label="Permalink to &quot;Case Notes&quot;">​</a></h2><h3 id="what-are-case-notes" tabindex="-1">What Are Case Notes? <a class="header-anchor" href="#what-are-case-notes" aria-label="Permalink to &quot;What Are Case Notes?&quot;">​</a></h3><p>Internal documentation of investigation activities, not visible to reporters. Use case notes to create a chronological record of:</p><ul><li>Investigation actions taken</li><li>Decisions made and reasoning</li><li>Conversations and interviews</li><li>Observations and insights</li><li>Questions and concerns</li><li>Next steps planned</li></ul><h3 id="when-to-add-notes" tabindex="-1">When to Add Notes <a class="header-anchor" href="#when-to-add-notes" aria-label="Permalink to &quot;When to Add Notes&quot;">​</a></h3><p><strong>After Every Investigation Activity</strong>:</p><ul><li>Reviewing new report</li><li>Interviewing witness</li><li>Collecting evidence</li><li>Making key decision</li><li>Communicating with reporter</li><li>Receiving new information</li><li>Changing investigation direction</li><li>Consulting with colleagues</li><li>Taking any significant action</li></ul><p><strong>Best Practice</strong>: Document immediately while details fresh. Don&#39;t rely on memory.</p><h3 id="writing-effective-case-notes" tabindex="-1">Writing Effective Case Notes <a class="header-anchor" href="#writing-effective-case-notes" aria-label="Permalink to &quot;Writing Effective Case Notes&quot;">​</a></h3><p><strong>Include These Elements</strong>:</p><ol><li><p><strong>Date and Time</strong></p><ul><li>Automatically timestamped</li><li>Helps establish timeline</li><li>Critical for audit trail</li></ul></li><li><p><strong>What You Did</strong></p><ul><li>Not just what you found, but what action you took</li><li>&quot;Interviewed John Smith&quot; not just &quot;John said...&quot;</li><li>&quot;Reviewed emails from March-April&quot; not just &quot;Found evidence&quot;</li></ul></li><li><p><strong>What You Learned</strong></p><ul><li>Key information obtained</li><li>Important quotes (verbatim)</li><li>Facts discovered</li><li>Patterns observed</li></ul></li><li><p><strong>What It Means</strong></p><ul><li>How information relates to allegations</li><li>Whether it supports or contradicts claims</li><li>Significance to investigation</li></ul></li><li><p><strong>What You&#39;ll Do Next</strong></p><ul><li>Follow-up actions needed</li><li>Additional interviews required</li><li>Documents to request</li><li>Questions remaining</li></ul></li></ol><p><strong>Example - Good Case Note</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>2024-03-15 14:30</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Interviewed Jane Doe (Senior Accountant, Finance Dept) regarding</span></span>
<span class="line"><span>allegation of expense fraud by reported subject.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Jane confirmed she noticed unusual expense patterns in Q4 2023:</span></span>
<span class="line"><span>- Multiple expenses just under £500 approval threshold</span></span>
<span class="line"><span>- Receipts that &quot;looked odd&quot; - her words</span></span>
<span class="line"><span>- Same restaurant appearing 3-4 times per week</span></span>
<span class="line"><span></span></span>
<span class="line"><span>She said: &quot;I flagged it to my manager in December but nothing</span></span>
<span class="line"><span>happened. I assumed they dealt with it.&quot;</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Jane provided:</span></span>
<span class="line"><span>- Spreadsheet of flagged expenses (uploaded to evidence)</span></span>
<span class="line"><span>- Email to her manager dated Dec 12, 2023 (uploaded)</span></span>
<span class="line"><span>- Names of 2 colleagues who had similar concerns</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Jane credible and specific. Her evidence corroborates reporter&#39;s</span></span>
<span class="line"><span>allegations about systematic expense fraud.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Next steps:</span></span>
<span class="line"><span>- Interview Jane&#39;s manager about why not escalated</span></span>
<span class="line"><span>- Review all subject&#39;s Q4 2023 expenses</span></span>
<span class="line"><span>- Interview 2 colleagues Jane mentioned</span></span>
<span class="line"><span>- Request restaurant receipts for forensic review</span></span></code></pre></div><p><strong>Example - Poor Case Note</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Talked to Jane. She said expenses were weird. Got some files.</span></span></code></pre></div><h3 id="note-categories-and-tags" tabindex="-1">Note Categories and Tags <a class="header-anchor" href="#note-categories-and-tags" aria-label="Permalink to &quot;Note Categories and Tags&quot;">​</a></h3><p><strong>Organize notes with tags</strong>:</p><ul><li><strong>Interview Notes</strong>: Witness interviews conducted</li><li><strong>Evidence</strong>: Documents and files collected</li><li><strong>Analysis</strong>: Your thinking and conclusions</li><li><strong>Communication</strong>: Messages with reporter/subject via <a href="/docs/reporting/secure-messaging">secure messaging</a></li><li><strong>Administrative</strong>: <a href="/docs/cases/status">Status changes</a>, assignments</li><li><strong>Legal</strong>: Consultations with legal counsel</li><li><strong>External</strong>: Third party involvement</li><li><strong>Decision</strong>: Key choices and reasoning</li></ul><p><strong>Benefits</strong>:</p><ul><li>Find notes quickly</li><li>Filter by note type</li><li>Generate reports</li><li>Support audit review</li></ul><h3 id="private-vs-shared-notes" tabindex="-1">Private vs. Shared Notes <a class="header-anchor" href="#private-vs-shared-notes" aria-label="Permalink to &quot;Private vs. Shared Notes&quot;">​</a></h3><p><strong>Private Notes</strong>:</p><ul><li>Visible only to you</li><li>Use for work-in-progress thoughts</li><li>Preliminary opinions</li><li>Questions for yourself</li><li>Personal reminders</li></ul><p><strong>Shared Notes</strong>:</p><ul><li>Visible to team members on case</li><li>Use for factual information</li><li>Evidence collected</li><li>Actions taken</li><li>Coordination with team</li></ul><p><strong>Upgrade Private to Shared</strong>:</p><ul><li>When thought becomes conclusion</li><li>When team needs to see</li><li>When audit trail required</li><li>Cannot downgrade shared notes (audit integrity)</li></ul><h3 id="ai-generated-notes" tabindex="-1">AI-Generated Notes <a class="header-anchor" href="#ai-generated-notes" aria-label="Permalink to &quot;AI-Generated Notes&quot;">​</a></h3><p>If using <a href="/docs/ai/case-helper">AI Case Helper</a>:</p><p><strong>What AI Can Do</strong>:</p><ul><li>Summarize long interviews</li><li>Extract key points from documents</li><li>Identify patterns across evidence</li><li>Suggest follow-up questions</li><li>Flag inconsistencies</li></ul><p><strong>Your Responsibility</strong>:</p><ul><li>Review all AI-generated content</li><li>Verify accuracy</li><li>Add your analysis</li><li>Make final decisions</li><li>Note: &quot;AI-assisted summary, verified by investigator&quot;</li></ul><p><strong>Never</strong>:</p><ul><li>Copy AI output without review</li><li>Use AI conclusions as your own</li><li>Skip critical thinking</li><li>Rely on AI for final decisions</li></ul><h2 id="evidence-management" tabindex="-1">Evidence Management <a class="header-anchor" href="#evidence-management" aria-label="Permalink to &quot;Evidence Management&quot;">​</a></h2><h3 id="what-counts-as-evidence" tabindex="-1">What Counts as Evidence? <a class="header-anchor" href="#what-counts-as-evidence" aria-label="Permalink to &quot;What Counts as Evidence?&quot;">​</a></h3><p><strong>Direct Evidence</strong>:</p><ul><li>Witness statements</li><li>Documents proving allegations</li><li>Email communications</li><li>Financial records</li><li>Audio/video recordings</li><li>Photos</li><li>Physical items</li></ul><p><strong>Circumstantial Evidence</strong>:</p><ul><li>Behavioral patterns</li><li>Timeline analysis</li><li>Opportunity and motive</li><li>Corroborating details</li><li>Context and background</li></ul><p><strong>Character Evidence</strong>:</p><ul><li>Previous incidents</li><li>Performance reviews</li><li>Disciplinary history</li><li>Reputation (use cautiously)</li></ul><h3 id="uploading-evidence" tabindex="-1">Uploading Evidence <a class="header-anchor" href="#uploading-evidence" aria-label="Permalink to &quot;Uploading Evidence&quot;">​</a></h3><p><strong>How to Upload</strong>:</p><ol><li><p><strong>From Case Page</strong></p><ul><li>Click &quot;Add Evidence&quot;</li><li>Select file(s) from device</li><li>Can drag and drop</li><li>Multiple files at once</li></ul></li><li><p><strong>While Adding Note</strong></p><ul><li>Click attachment icon</li><li>Add files inline with note</li><li>Creates evidence entry linked to note</li></ul></li><li><p><strong>From Reporter</strong></p><ul><li>Files attached to original report automatically added</li><li>Files sent via secure messaging automatically added</li><li>Timestamped and logged</li></ul></li></ol><p><strong>Supported File Types</strong>:</p><ul><li>Documents: PDF, DOC, DOCX, TXT, RTF</li><li>Spreadsheets: XLS, XLSX, CSV</li><li>Images: JPG, PNG, GIF, BMP</li><li>Archives: ZIP (automatically scanned for viruses)</li><li>Audio: MP3, WAV, M4A</li><li>Video: MP4, MOV, AVI (Enterprise plan)</li></ul><p><strong>File Size Limits</strong>:</p><ul><li>Basic plan: 10MB per file</li><li>Pro plan: 50MB per file</li><li>Enterprise plan: 500MB per file</li></ul><p><strong>Security</strong>:</p><ul><li>All files encrypted before upload</li><li>Virus scanned automatically</li><li>Malicious files rejected</li><li>Encrypted at rest and in transit</li><li>Access logged in audit trail</li></ul><h3 id="organizing-evidence" tabindex="-1">Organizing Evidence <a class="header-anchor" href="#organizing-evidence" aria-label="Permalink to &quot;Organizing Evidence&quot;">​</a></h3><p><strong>Categorize Each Item</strong>:</p><ol><li><p><strong>Evidence Type</strong></p><ul><li>Email/communication</li><li>Financial record</li><li>Interview transcript</li><li>Photo/video</li><li>Company document</li><li>External record</li><li>Expert report</li></ul></li><li><p><strong>Relevant Allegation</strong></p><ul><li>Link to specific claim</li><li>Can link to multiple allegations</li><li>Helps filter evidence later</li><li>Supports analysis</li></ul></li><li><p><strong>Source</strong></p><ul><li>Reporter-provided</li><li>Witness-provided</li><li>Company records</li><li>Public records</li><li>Third party</li><li>Self-generated (interview notes)</li></ul></li><li><p><strong>Relevance Level</strong></p><ul><li>Critical: Directly proves/disproves</li><li>High: Strong supporting evidence</li><li>Medium: Contextual information</li><li>Low: Background only</li><li>Uncertain: Need to assess</li></ul></li><li><p><strong>Tags</strong></p><ul><li>Custom tags for searching</li><li>Date ranges</li><li>Departments</li><li>People mentioned</li><li>Topics</li></ul></li></ol><h3 id="evidence-index" tabindex="-1">Evidence Index <a class="header-anchor" href="#evidence-index" aria-label="Permalink to &quot;Evidence Index&quot;">​</a></h3><p><strong>Automatically Generated</strong>:</p><ul><li>List of all evidence on case</li><li>Chronologically ordered</li><li>Filterable and searchable</li><li>Shows who uploaded when</li><li>Links to case notes</li><li>Download as spreadsheet</li></ul><p><strong>Use For</strong>:</p><ul><li>Investigation report appendix</li><li>Audit review</li><li>Legal disclosure</li><li>Quality assurance</li><li>Knowledge transfer</li></ul><h3 id="chain-of-custody" tabindex="-1">Chain of Custody <a class="header-anchor" href="#chain-of-custody" aria-label="Permalink to &quot;Chain of Custody&quot;">​</a></h3><p><strong>For Physical Evidence</strong>:</p><ol><li><p><strong>Document Acquisition</strong></p><ul><li>When received</li><li>From whom</li><li>Condition received</li><li>Where originally located</li></ul></li><li><p><strong>Photo Before Handling</strong></p><ul><li>Original state</li><li>Location</li><li>Identifying marks</li><li>Any damage</li></ul></li><li><p><strong>Secure Storage</strong></p><ul><li>Locked location</li><li>Access logged</li><li>Climate controlled if needed</li><li>Tamper-evident seal</li></ul></li><li><p><strong>Track All Access</strong></p><ul><li>Who handled</li><li>When and why</li><li>What they did</li><li>Returned to storage</li></ul></li><li><p><strong>Document in Disclosurely</strong></p><ul><li>Upload photos</li><li>Log all custody transfers</li><li>Note storage location</li><li>Track until disposition</li></ul></li></ol><p><strong>For Digital Evidence</strong>:</p><ol><li><p><strong>Preserve Original</strong></p><ul><li>Don&#39;t modify source file</li><li>Create forensic copy if important</li><li>Hash file to prove authenticity</li></ul></li><li><p><strong>Document Metadata</strong></p><ul><li>Created date/time</li><li>Modified date/time</li><li>Author</li><li>File properties</li><li>Source location</li></ul></li><li><p><strong>Screenshot Context</strong></p><ul><li>Where file was found</li><li>Surrounding files</li><li>Access permissions</li><li>Full headers (emails)</li></ul></li><li><p><strong>Upload to Disclosurely</strong></p><ul><li>Original file</li><li>Forensic copy if made</li><li>Screenshots of context</li><li>Metadata documentation</li></ul></li></ol><h3 id="evidence-analysis" tabindex="-1">Evidence Analysis <a class="header-anchor" href="#evidence-analysis" aria-label="Permalink to &quot;Evidence Analysis&quot;">​</a></h3><p><strong>Creating Evidence Summary</strong>:</p><ol><li><p><strong>For Each Allegation</strong></p><ul><li>List all relevant evidence</li><li>Note what each item shows</li><li>Identify corroboration</li><li>Note contradictions</li><li>Assess strength</li></ul></li><li><p><strong>Weighing Evidence</strong></p><ul><li><strong>Strong</strong>: Multiple sources, contemporary, credible</li><li><strong>Moderate</strong>: Single source, credible, consistent</li><li><strong>Weak</strong>: Uncorroborated, inconsistent, questionable</li><li><strong>Insufficient</strong>: Gaps, hearsay, unreliable</li></ul></li><li><p><strong>Document Analysis</strong></p><ul><li>Add analysis as case note</li><li>Link to relevant evidence</li><li>Show your reasoning</li><li>Consider alternatives</li><li>Note any doubts</li></ul></li></ol><p><strong>Using AI for Analysis</strong>:</p><ul><li>AI can identify patterns across documents</li><li>Find inconsistencies in statements</li><li>Suggest connections between evidence</li><li>Flag missing information</li><li><strong>Always verify AI findings independently</strong></li></ul><h3 id="interview-transcripts" tabindex="-1">Interview Transcripts <a class="header-anchor" href="#interview-transcripts" aria-label="Permalink to &quot;Interview Transcripts&quot;">​</a></h3><p><strong>Best Practices</strong>:</p><p><strong>During Interview</strong>:</p><ul><li>Take detailed notes in real-time</li><li>Capture exact quotes for key statements</li><li>Note body language, tone (if relevant)</li><li>Record time, date, location</li><li>Note who else present</li></ul><p><strong>After Interview</strong>:</p><ul><li>Transcribe notes while fresh (same day)</li><li>Expand shorthand into full sentences</li><li>Add context you remember</li><li>Note your impressions</li><li>Don&#39;t change or &quot;clean up&quot; quotes</li></ul><p><strong>Creating Transcript</strong>:</p><ul><li>Header: Date, time, location, participants</li><li>Introduction: Explain purpose, confidentiality</li><li>Question and answer format</li><li>Verbatim for key statements</li><li>Paraphrase routine information</li><li>Note breaks, interruptions</li><li>Closing: Thank participant, next steps</li></ul><p><strong>Upload to Evidence</strong>:</p><ul><li>Tag as &quot;Interview Transcript&quot;</li><li>Link to relevant allegations</li><li>Note credibility assessment</li><li>Mark as &quot;Interview Notes&quot; category</li><li>Link to any follow-up needed</li></ul><h3 id="photos-and-videos" tabindex="-1">Photos and Videos <a class="header-anchor" href="#photos-and-videos" aria-label="Permalink to &quot;Photos and Videos&quot;">​</a></h3><p><strong>Taking Photos</strong>:</p><ul><li>Include scale reference if relevant</li><li>Take wide shot and close-ups</li><li>Capture identifying features</li><li>Timestamp (automatic in smartphone)</li><li>Geotag if location relevant</li><li>Multiple angles</li></ul><p><strong>Documenting Videos</strong>:</p><ul><li>Note timestamp of relevant segments</li><li>Transcribe relevant audio</li><li>Screenshot key frames</li><li>Document who, what, where, when</li><li>Maintain original file</li></ul><p><strong>Sensitive Content</strong>:</p><ul><li>Blur faces if privacy concern</li><li>Redact identifying information</li><li>Limit distribution</li><li>Note content warning</li><li>Comply with data protection laws</li></ul><h3 id="document-retention" tabindex="-1">Document Retention <a class="header-anchor" href="#document-retention" aria-label="Permalink to &quot;Document Retention&quot;">​</a></h3><p><strong>How Long to Keep Evidence</strong>:</p><p><strong>Active Cases</strong>:</p><ul><li>All evidence retained</li><li>No deletion</li><li>All versions preserved</li></ul><p><strong>Resolved Cases</strong>:</p><ul><li>Evidence retained per policy</li><li>Typical: 7 years minimum</li><li>Longer for serious cases</li><li>Follow industry standards: <ul><li>Financial services: 7+ years</li><li>Healthcare: 7+ years</li><li>Education: 6+ years</li><li>Government: varies by jurisdiction</li></ul></li></ul><p><strong>Automatic Retention</strong>:</p><ul><li>Disclosurely automatically retains per your settings</li><li>Configure in Organization Settings</li><li>Set by case category</li><li>Set by severity</li><li>Legal holds override deletion</li></ul><p><strong>Destruction</strong>:</p><ul><li>Only after retention period</li><li>Secure deletion (military-grade)</li><li>Audit log maintained</li><li>Certificate of destruction</li><li>Approved by compliance officer</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="document-contemporaneously" tabindex="-1">Document Contemporaneously <a class="header-anchor" href="#document-contemporaneously" aria-label="Permalink to &quot;Document Contemporaneously&quot;">​</a></h3><p><strong>Why</strong>:</p><ul><li>Memory fades quickly</li><li>Details lost over time</li><li>Credibility questioned if delayed</li><li>Legal requirement in some jurisdictions</li></ul><p><strong>Do</strong>:</p><ul><li>Write notes immediately after activities</li><li>Same day minimum</li><li>While impressions fresh</li><li>Before next task</li></ul><p><strong>Don&#39;t</strong>:</p><ul><li>Batch notes at end of week</li><li>Rely on memory days later</li><li>Backdate notes</li><li>Create notes out of sequence</li></ul><h3 id="be-objective-and-factual" tabindex="-1">Be Objective and Factual <a class="header-anchor" href="#be-objective-and-factual" aria-label="Permalink to &quot;Be Objective and Factual&quot;">​</a></h3><p><strong>Do</strong>:</p><ul><li>State facts observed</li><li>Quote exact words</li><li>Note sources</li><li>Distinguish fact from opinion</li><li>Show reasoning</li></ul><p><strong>Don&#39;t</strong>:</p><ul><li>Insert personal biases</li><li>Make assumptions</li><li>Draw premature conclusions</li><li>Use inflammatory language</li><li>Speculate without basis</li></ul><p><strong>Example - Objective</strong>: &quot;Subject&#39;s expense submissions show 47 claims for dinner at same restaurant over 3-month period. Each claim between £480-£495. Company policy requires approval for expenses over £500.&quot;</p><p><strong>Example - Subjective</strong>: &quot;Subject clearly knew he was manipulating the system by keeping expenses just under the limit. Obviously fraudulent.&quot;</p><h3 id="protect-confidentiality" tabindex="-1">Protect Confidentiality <a class="header-anchor" href="#protect-confidentiality" aria-label="Permalink to &quot;Protect Confidentiality&quot;">​</a></h3><p><strong>In Notes</strong>:</p><ul><li>Use role instead of names where possible</li><li>&quot;Reporter&quot; not actual name</li><li>&quot;Subject&quot; not actual name</li><li>Note if identity must be shared (investigation requires)</li></ul><p><strong>In Evidence</strong>:</p><ul><li>Redact unnecessary personal data</li><li>Remove unrelated names</li><li>Protect bystander privacy</li><li>Comply with GDPR/privacy laws</li></ul><p><strong>Access Control</strong>:</p><ul><li>Limit evidence access to need-to-know</li><li>Log all evidence views</li><li>Restrict downloads</li><li>Watermark sensitive documents</li></ul><h3 id="maintain-integrity" tabindex="-1">Maintain Integrity <a class="header-anchor" href="#maintain-integrity" aria-label="Permalink to &quot;Maintain Integrity&quot;">​</a></h3><p><strong>Never</strong>:</p><ul><li>Alter original evidence</li><li>Delete unfavorable information</li><li>Hide evidence that contradicts theory</li><li>Create false documentation</li><li>Backdate or forward-date notes</li><li>Share evidence inappropriately</li></ul><p><strong>Always</strong>:</p><ul><li>Preserve everything</li><li>Document all evidence (even if not using)</li><li>Maintain chronological record</li><li>Create audit trail</li><li>Secure evidence</li><li>Follow procedures</li></ul><h3 id="use-evidence-checklist" tabindex="-1">Use Evidence Checklist <a class="header-anchor" href="#use-evidence-checklist" aria-label="Permalink to &quot;Use Evidence Checklist&quot;">​</a></h3><p><strong>Before Closing Case</strong>:</p><p>✅ All evidence uploaded and categorized ✅ All interviews transcribed ✅ Evidence index complete ✅ All evidence linked to allegations ✅ Chain of custody documented ✅ Sources noted for all evidence ✅ Privileged evidence marked ✅ Redactions appropriate ✅ Retention settings configured ✅ Evidence summary created ✅ Quality review complete</p><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="cannot-upload-file" tabindex="-1">Cannot Upload File <a class="header-anchor" href="#cannot-upload-file" aria-label="Permalink to &quot;Cannot Upload File&quot;">​</a></h3><p><strong>Check</strong>:</p><ul><li>File size under limit for your plan</li><li>File type supported</li><li>Internet connection stable</li><li>Browser up to date</li><li>Not already uploaded (duplicate check)</li></ul><p><strong>Solutions</strong>:</p><ul><li>Compress large files</li><li>Convert to supported format</li><li>Try different browser</li><li>Contact support if persists</li></ul><h3 id="evidence-not-appearing" tabindex="-1">Evidence Not Appearing <a class="header-anchor" href="#evidence-not-appearing" aria-label="Permalink to &quot;Evidence Not Appearing&quot;">​</a></h3><p><strong>Verify</strong>:</p><ul><li>Upload completed successfully</li><li>Correct case selected</li><li>Filters not hiding evidence</li><li>Permissions allow you to view</li><li>Browser cache refreshed</li></ul><h3 id="file-infected-with-virus" tabindex="-1">File Infected with Virus <a class="header-anchor" href="#file-infected-with-virus" aria-label="Permalink to &quot;File Infected with Virus&quot;">​</a></h3><p><strong>What Happens</strong>:</p><ul><li>Automatic virus scan on upload</li><li>Infected files rejected</li><li>Upload fails with error message</li><li>File not saved to system</li></ul><p><strong>What to Do</strong>:</p><ul><li>Scan file on your system</li><li>Clean if possible</li><li>Upload cleaned version</li><li>Consider if evidence still needed</li><li>Document attempt and rejection</li></ul><h3 id="lost-evidence" tabindex="-1">Lost Evidence <a class="header-anchor" href="#lost-evidence" aria-label="Permalink to &quot;Lost Evidence&quot;">​</a></h3><p><strong>Disclosurely Protects Against</strong>:</p><ul><li>Automatic backups</li><li>Version history</li><li>Deleted evidence recoverable</li><li>Audit log shows all actions</li><li>Geographic redundancy</li></ul><p><strong>If Evidence Missing</strong>:</p><ul><li>Check filters and search</li><li>Verify correct case</li><li>Check deleted evidence folder</li><li>Review audit log</li><li>Contact support to recover</li></ul><hr><p><strong>Related:</strong></p><ul><li><a href="/docs/cases/workflow">Investigation Workflow</a></li><li><a href="/docs/cases/resolution">Case Resolution</a></li><li><a href="/docs/ai/case-helper">AI Case Helper</a></li><li><a href="/docs/compliance/audit-trail">Compliance &amp; Audit</a></li></ul>`,154)])])}const h=i(a,[["render",o]]);export{g as __pageData,h as default};
