import{_ as t,c as i,o as a,ag as o}from"./chunks/framework.4QX5UEtQ.js";const c=JSON.parse('{"title":"Status Management - Disclosurely Case Tracking Guide","description":"Track investigation progress with status updates from New to Resolved. Automate notifications, monitor compliance timeframes, and communicate effectively.","frontmatter":{"title":"Status Management - Disclosurely Case Tracking Guide","description":"Track investigation progress with status updates from New to Resolved. Automate notifications, monitor compliance timeframes, and communicate effectively.","head":[["meta",{"property":"og:title","content":"Case Status Management & Tracking - Disclosurely"}],["meta",{"property":"og:description","content":"Track investigation progress with status updates from New to Resolved. Automate notifications, monitor compliance timeframes, and communicate effectively."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Status Management - Disclosurely"}],["meta",{"name":"twitter:description","content":"Track investigation progress with status updates from New to Resolved. Automate notifications, monitor compliance timeframes, and communicate effectively."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"cases/status.md","filePath":"cases/status.md"}'),n={name:"cases/status.md"};function s(r,e,l,u,g,d){return a(),i("div",null,[...e[0]||(e[0]=[o('<h1 id="status-management" tabindex="-1">Status Management <a class="header-anchor" href="#status-management" aria-label="Permalink to &quot;Status Management&quot;">​</a></h1><p>Track and communicate investigation progress through clear, meaningful status updates.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Case status is the single most important indicator of where a case is in the investigation lifecycle. Proper status management helps:</p><ul><li><strong>Reporters</strong> understand what&#39;s happening with their case</li><li><strong>Investigators</strong> track workload and priorities</li><li><strong>Administrators</strong> monitor compliance with timeframes</li><li><strong>Executives</strong> get high-level visibility into investigations</li></ul><h2 id="available-statuses" tabindex="-1">Available Statuses <a class="header-anchor" href="#available-statuses" aria-label="Permalink to &quot;Available Statuses&quot;">​</a></h2><h3 id="new" tabindex="-1">New <a class="header-anchor" href="#new" aria-label="Permalink to &quot;New&quot;">​</a></h3><p><strong>Meaning</strong>: Report just submitted, not yet reviewed by investigator</p><p><strong>Automatic</strong>: Set automatically when report submitted</p><p><strong>Duration</strong>: Should be brief (minutes to hours, not days)</p><p><strong>Actions</strong>:</p><ul><li>System sends acknowledgment to reporter</li><li>Case appears in &quot;Unassigned&quot; queue</li><li>Assignment rules trigger (if configured)</li><li>Compliance clock starts</li></ul><p><strong>Reporter Sees</strong>: &quot;Your report has been received and will be reviewed shortly.&quot;</p><h3 id="under-review" tabindex="-1">Under Review <a class="header-anchor" href="#under-review" aria-label="Permalink to &quot;Under Review&quot;">​</a></h3><p><strong>Meaning</strong>: Initial assessment in progress</p><p><strong>Typical Duration</strong>: 1-3 days</p><p><strong>Investigator Actions</strong>:</p><ul><li>Read report thoroughly</li><li>Assess severity and credibility</li><li>Determine <a href="/docs/cases/workflow">investigation scope</a></li><li>Request additional information if needed</li><li>Categorize and prioritize</li></ul><p><strong>Reporter Sees</strong>: &quot;Your report is being reviewed by our team. We&#39;ll begin investigating shortly.&quot;</p><p><strong>Best Practice</strong>: Move to this status within 24 hours of receipt to show reporter their concern is being taken seriously.</p><h3 id="investigating" tabindex="-1">Investigating <a class="header-anchor" href="#investigating" aria-label="Permalink to &quot;Investigating&quot;">​</a></h3><p><strong>Meaning</strong>: Active evidence gathering and investigation underway</p><p><strong>Typical Duration</strong>: 2-8 weeks (depending on complexity)</p><p><strong>Investigator Actions</strong>:</p><ul><li>Interviewing witnesses</li><li>Collecting documentary evidence</li><li>Reviewing records and communications</li><li>Analyzing findings</li><li><a href="/docs/cases/evidence">Documenting progress in case notes</a></li></ul><p><strong>Reporter Sees</strong>: &quot;We are actively investigating your report. We&#39;ll keep you updated on progress.&quot;</p><p><strong>Updates</strong>: Provide periodic updates to reporter every 1-2 weeks even if just to say &quot;investigation ongoing.&quot;</p><h3 id="pending-information" tabindex="-1">Pending Information <a class="header-anchor" href="#pending-information" aria-label="Permalink to &quot;Pending Information&quot;">​</a></h3><p><strong>Meaning</strong>: Investigation paused awaiting additional information</p><p><strong>Typical Duration</strong>: Days to weeks (set deadline for response)</p><p><strong>When to Use</strong>:</p><ul><li>Waiting for reporter to provide details</li><li>Awaiting witness availability</li><li>Requesting records from external party</li><li>Waiting for legal/HR review</li><li>Third party investigation ongoing</li></ul><p><strong>Investigator Actions</strong>:</p><ul><li>Send clear request for information via <a href="/docs/reporting/secure-messaging">secure messaging</a></li><li>Specify what&#39;s needed and why</li><li>Set reasonable deadline</li><li>Set reminder to follow up</li><li>Continue other investigation activities if possible</li></ul><p><strong>Reporter Sees</strong>: &quot;We need some additional information from you to continue. Please check your messages.&quot;</p><p><strong>Best Practice</strong>:</p><ul><li>Be specific about what you need</li><li>Explain why it&#39;s important</li><li>Give reasonable timeframe (7-14 days typical)</li><li>Send reminder 2 days before deadline</li></ul><h3 id="under-analysis" tabindex="-1">Under Analysis <a class="header-anchor" href="#under-analysis" aria-label="Permalink to &quot;Under Analysis&quot;">​</a></h3><p><strong>Meaning</strong>: Evidence gathered, now analyzing findings and forming conclusions</p><p><strong>Typical Duration</strong>: 1-2 weeks</p><p><strong>Investigator Actions</strong>:</p><ul><li>Reviewing all evidence collected</li><li>Determining if allegations substantiated</li><li>Considering mitigating factors</li><li>Using AI Case Helper for pattern analysis</li><li>Drafting preliminary findings</li><li>Preparing for subject interview</li></ul><p><strong>Reporter Sees</strong>: &quot;We have gathered evidence and are now analyzing our findings.&quot;</p><p><strong>Note</strong>: This status signals you&#39;re approaching resolution, which helps manage reporter expectations.</p><h3 id="subject-response" tabindex="-1">Subject Response <a class="header-anchor" href="#subject-response" aria-label="Permalink to &quot;Subject Response&quot;">​</a></h3><p><strong>Meaning</strong>: Subject has been presented with allegations and given opportunity to respond</p><p><strong>Typical Duration</strong>: 1-2 weeks</p><p><strong>Investigator Actions</strong>:</p><ul><li>Prepare subject interview</li><li>Present allegations to subject</li><li>Allow response and rebuttal</li><li>Collect any additional evidence from subject</li><li>Re-evaluate findings in light of response</li><li>Ensure procedural fairness</li></ul><p><strong>Reporter Sees</strong>: &quot;Our investigation is in its final stages. We&#39;ll have a resolution soon.&quot;</p><p><strong>Privacy Note</strong>: Never reveal to reporter that you&#39;re in &quot;subject response&quot; phase by name - use general language like &quot;final stages.&quot;</p><h3 id="resolved" tabindex="-1">Resolved <a class="header-anchor" href="#resolved" aria-label="Permalink to &quot;Resolved&quot;">​</a></h3><p><strong>Meaning</strong>: Investigation complete, conclusions reached, actions determined</p><p><strong>Typical Duration</strong>: Permanent (or until reopened)</p><p><strong>Investigator Actions</strong>:</p><ul><li>Finalize investigation report</li><li>Implement recommendations</li><li>Communicate outcome to reporter</li><li>Communicate to subject</li><li>Update stakeholders</li><li>Document lessons learned</li></ul><p><strong>Reporter Sees</strong>:</p><ul><li>Summary of findings</li><li>Whether allegations substantiated</li><li>What action was taken (in general terms)</li><li>Thank you for reporting</li></ul><p><strong>Required</strong>: Must provide feedback to reporter within regulatory timeframes (3-6 months depending on jurisdiction).</p><h3 id="closed" tabindex="-1">Closed <a class="header-anchor" href="#closed" aria-label="Permalink to &quot;Closed&quot;">​</a></h3><p><strong>Meaning</strong>: Case archived, all actions complete, no further activity expected</p><p><strong>When to Use</strong>:</p><ul><li>All recommendations implemented</li><li>All follow-up complete</li><li>No appeals pending</li><li>Retention period begins</li></ul><p><strong>Investigator Actions</strong>:</p><ul><li>Final audit check</li><li>Archive all evidence</li><li>Generate compliance reports</li><li>Remove from active caseload</li></ul><p><strong>Reporter Sees</strong>: &quot;This case is now closed. Thank you for reporting.&quot;</p><p><strong>Note</strong>: Closed cases are read-only but can be reopened if new information emerges.</p><h3 id="on-hold" tabindex="-1">On Hold <a class="header-anchor" href="#on-hold" aria-label="Permalink to &quot;On Hold&quot;">​</a></h3><p><strong>Meaning</strong>: Investigation temporarily suspended</p><p><strong>When to Use</strong>:</p><ul><li>Parallel external investigation (police, regulator)</li><li>Legal proceedings underway</li><li>Subject on extended leave</li><li>Organizational restructuring</li><li>Awaiting policy clarification</li><li>Resource constraints (should be rare)</li></ul><p><strong>Investigator Actions</strong>:</p><ul><li>Document reason for hold</li><li>Set review date</li><li>Monitor triggering situation</li><li>Keep reporter informed</li><li>Resume when appropriate</li></ul><p><strong>Reporter Sees</strong>: &quot;Your case investigation is temporarily on hold due to [reason]. We will resume when [condition].&quot;</p><p><strong>Best Practice</strong>: Avoid using this status unless absolutely necessary. Sets concerning message that investigation isn&#39;t a priority.</p><h3 id="withdrawn" tabindex="-1">Withdrawn <a class="header-anchor" href="#withdrawn" aria-label="Permalink to &quot;Withdrawn&quot;">​</a></h3><p><strong>Meaning</strong>: Reporter requested to withdraw report</p><p><strong>When to Use</strong>:</p><ul><li>Reporter explicitly requests withdrawal</li><li>Reporter cannot be contacted (after multiple attempts)</li><li>Report determined to be duplicate</li></ul><p><strong>Process</strong>:</p><ol><li>Reporter submits withdrawal request</li><li>Investigator evaluates if investigation should continue anyway</li><li>If proceeding anyway, change status to &quot;Investigating&quot; and note withdrawal request</li><li>If honoring withdrawal, change to &quot;Withdrawn&quot; and document</li></ol><p><strong>Important</strong>: Some allegations must be investigated even if reporter withdraws (e.g., safety risks, serious misconduct, legal violations).</p><p><strong>Reporter Sees</strong>: &quot;As requested, we have withdrawn your report. You can submit a new report anytime.&quot;</p><h2 id="changing-status" tabindex="-1">Changing Status <a class="header-anchor" href="#changing-status" aria-label="Permalink to &quot;Changing Status&quot;">​</a></h2><h3 id="manual-status-changes" tabindex="-1">Manual Status Changes <a class="header-anchor" href="#manual-status-changes" aria-label="Permalink to &quot;Manual Status Changes&quot;">​</a></h3><ol><li><p><strong>Open Case</strong></p><ul><li>Navigate to case detail page</li></ul></li><li><p><strong>Change Status</strong></p><ul><li>Click current status dropdown at top</li><li>Select new status from list</li><li>Status changes immediately</li></ul></li><li><p><strong>Add Status Note</strong></p><ul><li>Modal appears: &quot;Why are you changing status?&quot;</li><li>Add brief explanation</li><li>Note appears in case timeline</li><li>Helps with audit trail</li></ul></li><li><p><strong>Automatic Notifications</strong></p><ul><li>Reporter notified of status change</li><li>Assigned investigator notified</li><li>Stakeholders notified (if configured)</li></ul></li></ol><h3 id="bulk-status-changes" tabindex="-1">Bulk Status Changes <a class="header-anchor" href="#bulk-status-changes" aria-label="Permalink to &quot;Bulk Status Changes&quot;">​</a></h3><p>Change status for multiple cases:</p><ol><li><p><strong>Select Cases</strong></p><ul><li>Go to Cases dashboard</li><li>Use checkboxes to select multiple cases</li><li>Can select up to 50 at once</li></ul></li><li><p><strong>Bulk Action</strong></p><ul><li>Click &quot;Bulk Actions&quot; &gt; &quot;Change Status&quot;</li><li>Select new status</li><li>Add note (applied to all selected cases)</li><li>Confirm change</li></ul></li><li><p><strong>Notifications</strong></p><ul><li>All reporters notified</li><li>Useful for mass updates (e.g., moving cases to &quot;On Hold&quot; during office closure)</li></ul></li></ol><p><strong>Use Case</strong>:</p><ul><li>Moving group of related cases forward together</li><li>Batch processing of simple cases</li><li>Organization-wide events affecting multiple cases</li></ul><h3 id="automated-status-changes" tabindex="-1">Automated Status Changes <a class="header-anchor" href="#automated-status-changes" aria-label="Permalink to &quot;Automated Status Changes&quot;">​</a></h3><p>Configure automatic status progression:</p><p><strong>Inactivity Rules</strong>:</p><ul><li>&quot;Pending Information&quot; &gt; 30 days → Send reminder</li><li>&quot;Pending Information&quot; &gt; 60 days → Move to &quot;Withdrawn&quot;</li><li>&quot;Investigating&quot; &gt; 90 days → Alert supervisor</li></ul><p><strong>Activity-Based</strong>:</p><ul><li>Reporter sends message → &quot;Pending Information&quot; to &quot;Investigating&quot;</li><li>Investigation report uploaded → &quot;Under Analysis&quot; to &quot;Resolved&quot;</li><li>All evidence reviewed → &quot;Investigating&quot; to &quot;Under Analysis&quot;</li></ul><p><strong>Configure in Settings</strong>:</p><ul><li>Dashboard &gt; Organization Settings</li><li>&quot;Automation Rules&quot;</li><li>Set triggers and actions</li><li>Test before activating</li></ul><h2 id="status-notifications" tabindex="-1">Status Notifications <a class="header-anchor" href="#status-notifications" aria-label="Permalink to &quot;Status Notifications&quot;">​</a></h2><h3 id="what-reporters-see" tabindex="-1">What Reporters See <a class="header-anchor" href="#what-reporters-see" aria-label="Permalink to &quot;What Reporters See&quot;">​</a></h3><p><strong>Automatic Messages</strong>:</p><ul><li><strong>New → Under Review</strong>: &quot;Your report is being reviewed&quot;</li><li><strong>Under Review → Investigating</strong>: &quot;We&#39;ve begun investigating your report&quot;</li><li><strong>Investigating → Pending Information</strong>: &quot;We need more information from you&quot;</li><li><strong>Pending Information → Investigating</strong>: &quot;Thank you, investigation continuing&quot;</li><li><strong>Any → Resolved</strong>: &quot;Investigation complete&quot; + outcome summary</li><li><strong>Resolved → Closed</strong>: &quot;Case closed, thank you&quot;</li></ul><p><strong>Customization</strong>:</p><ul><li>Edit default messages in Settings</li><li>Add organization-specific language</li><li>Translate for different languages</li><li>Include contact information</li><li>Add expected next steps</li></ul><h3 id="internal-notifications" tabindex="-1">Internal Notifications <a class="header-anchor" href="#internal-notifications" aria-label="Permalink to &quot;Internal Notifications&quot;">​</a></h3><p><strong>Investigators Notified When</strong>:</p><ul><li>Case assigned to them</li><li>Reporter sends message</li><li>Status changed by another user</li><li>Case approaching deadline</li><li>Supervisor adds comment</li><li>Evidence uploaded by reporter</li></ul><p><strong>Administrators Notified When</strong>:</p><ul><li>Cases exceed timeframe thresholds</li><li>High-priority cases submitted</li><li>Cases require escalation</li><li>Compliance deadlines approaching</li></ul><p><strong>Configure Notifications</strong>:</p><ul><li>Email, in-app, SMS, Slack/Teams</li><li>Set frequency (immediate, daily digest, weekly)</li><li>Choose what triggers notification</li><li>Quiet hours for non-urgent alerts</li></ul><h2 id="status-reports-analytics" tabindex="-1">Status Reports &amp; Analytics <a class="header-anchor" href="#status-reports-analytics" aria-label="Permalink to &quot;Status Reports &amp; Analytics&quot;">​</a></h2><h3 id="dashboard-metrics" tabindex="-1">Dashboard Metrics <a class="header-anchor" href="#dashboard-metrics" aria-label="Permalink to &quot;Dashboard Metrics&quot;">​</a></h3><p><strong>Overview</strong>:</p><ul><li>Cases by current status (pie chart)</li><li>Status distribution over time (trend)</li><li>Average time in each status</li><li>Cases stuck in status &gt; threshold</li></ul><p><strong>Filtering</strong>:</p><ul><li>By date range</li><li>By category</li><li>By priority</li><li>By assigned investigator</li><li>By department</li></ul><h3 id="status-flow-analysis" tabindex="-1">Status Flow Analysis <a class="header-anchor" href="#status-flow-analysis" aria-label="Permalink to &quot;Status Flow Analysis&quot;">​</a></h3><p><strong>Understand patterns</strong>:</p><ul><li>Which statuses take longest?</li><li>Where do cases get stuck?</li><li>Are you skipping statuses inappropriately?</li><li>Do cases bounce between statuses?</li></ul><p><strong>Common Issues</strong>:</p><ul><li>Too many &quot;Pending Information&quot; - improve initial intake?</li><li>Long &quot;Under Review&quot; time - assign faster?</li><li>Quick &quot;Investigating&quot; - thorough enough?</li><li>Many &quot;On Hold&quot; - resource issue?</li></ul><h3 id="compliance-monitoring" tabindex="-1">Compliance Monitoring <a class="header-anchor" href="#compliance-monitoring" aria-label="Permalink to &quot;Compliance Monitoring&quot;">​</a></h3><p><strong>Track Regulatory Timeframes</strong>:</p><ul><li>7-day acknowledgment (EU Directive)</li><li>3-month feedback requirement (EU Directive)</li><li>Industry-specific deadlines</li></ul><p><strong>Alerts</strong>:</p><ul><li>Cases approaching deadlines</li><li>Cases past deadlines</li><li>Cases at risk of missing deadlines</li></ul><p><strong>Reports</strong>:</p><ul><li>Compliance rate by month</li><li>Cases meeting/missing deadlines</li><li>Average resolution time</li><li>Breakdown by category</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="update-status-regularly" tabindex="-1">Update Status Regularly <a class="header-anchor" href="#update-status-regularly" aria-label="Permalink to &quot;Update Status Regularly&quot;">​</a></h3><p><strong>Why</strong>:</p><ul><li>Keeps reporter informed</li><li>Maintains engagement</li><li>Demonstrates progress</li><li>Supports accountability</li></ul><p><strong>How Often</strong>:</p><ul><li>Change status when investigation phase changes</li><li>Add status note every 1-2 weeks minimum</li><li>Provide updates even if &quot;still investigating&quot;</li></ul><h3 id="use-status-notes" tabindex="-1">Use Status Notes <a class="header-anchor" href="#use-status-notes" aria-label="Permalink to &quot;Use Status Notes&quot;">​</a></h3><p>Every status change should include note:</p><p><strong>Good Status Notes</strong>:</p><ul><li>&quot;Moving to Investigating. Have gathered initial evidence and scheduled witness interviews for next week.&quot;</li><li>&quot;Now in Under Analysis. All evidence collected, reviewing findings before subject interview.&quot;</li><li>&quot;Moved to Pending Information. Requested additional details from reporter about timeline of events.&quot;</li></ul><p><strong>Poor Status Notes</strong>:</p><ul><li>&quot;Status change&quot;</li><li>&quot;Updated&quot;</li><li>(no note)</li></ul><h3 id="be-honest-with-timeline" tabindex="-1">Be Honest with Timeline <a class="header-anchor" href="#be-honest-with-timeline" aria-label="Permalink to &quot;Be Honest with Timeline&quot;">​</a></h3><p><strong>If investigation taking longer than expected</strong>:</p><ul><li>Update status to reflect reality</li><li>Send message to reporter explaining</li><li>Give new expected timeframe</li><li>Apologize for delay</li><li>Explain reason if appropriate</li></ul><p><strong>Don&#39;t</strong>:</p><ul><li>Leave status unchanged hoping to finish soon</li><li>Make up reasons for delay</li><li>Go silent on reporter</li><li>Blame reporter for delays</li></ul><h3 id="match-status-to-reality" tabindex="-1">Match Status to Reality <a class="header-anchor" href="#match-status-to-reality" aria-label="Permalink to &quot;Match Status to Reality&quot;">​</a></h3><p><strong>Status should accurately reflect what&#39;s happening</strong>:</p><p><strong>Don&#39;t</strong>:</p><ul><li>Mark &quot;Investigating&quot; if you haven&#39;t started yet</li><li>Use &quot;Pending Information&quot; to hide inactivity</li><li>Jump to &quot;Resolved&quot; before actions complete</li><li>Keep in &quot;Under Review&quot; for weeks</li></ul><p><strong>Do</strong>:</p><ul><li>Update status when investigation phase actually changes</li><li>Use &quot;On Hold&quot; honestly if paused</li><li>Communicate realistic expectations</li><li>Keep stakeholders informed</li></ul><h3 id="coordinate-status-with-team" tabindex="-1">Coordinate Status with Team <a class="header-anchor" href="#coordinate-status-with-team" aria-label="Permalink to &quot;Coordinate Status with Team&quot;">​</a></h3><p><strong>If working with multiple investigators</strong>:</p><ul><li>Agree who updates status</li><li>Communicate before changing</li><li>Don&#39;t contradict teammate&#39;s updates</li><li>Use internal notes for coordination</li></ul><p><strong>For team-assigned cases</strong>:</p><ul><li>Team lead manages status</li><li>Team members add notes</li><li>Coordinate before major status changes</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="reporter-confused-by-status" tabindex="-1">Reporter Confused by Status <a class="header-anchor" href="#reporter-confused-by-status" aria-label="Permalink to &quot;Reporter Confused by Status&quot;">​</a></h3><p><strong>If reporter messages asking what status means</strong>:</p><ul><li>Explain in plain language</li><li>Tell them what&#39;s happening now</li><li>Tell them what happens next</li><li>Give expected timeframe</li><li>Offer to answer questions</li></ul><p><strong>Consider</strong>: Your status messages may be too technical. Review and simplify.</p><h3 id="status-not-updating" tabindex="-1">Status Not Updating <a class="header-anchor" href="#status-not-updating" aria-label="Permalink to &quot;Status Not Updating&quot;">​</a></h3><p><strong>Check</strong>:</p><ul><li>Do you have permission to change status?</li><li>Is case already closed? (can&#39;t change)</li><li>Browser cache issue? (refresh)</li><li>Integration error? (check logs)</li></ul><h3 id="notifications-not-sending" tabindex="-1">Notifications Not Sending <a class="header-anchor" href="#notifications-not-sending" aria-label="Permalink to &quot;Notifications Not Sending&quot;">​</a></h3><p><strong>Verify</strong>:</p><ul><li>Reporter&#39;s tracking ID valid</li><li>Notification settings enabled</li><li>Email address correct (confidential reports)</li><li>No system-wide notification issue</li><li>Check spam folder</li></ul><h3 id="wrong-status-set" tabindex="-1">Wrong Status Set <a class="header-anchor" href="#wrong-status-set" aria-label="Permalink to &quot;Wrong Status Set&quot;">​</a></h3><p><strong>If status changed by mistake</strong>:</p><ul><li>Change back immediately</li><li>Add status note explaining</li><li>Send message to reporter if they were notified</li><li>Document in case notes</li></ul><hr><p><strong>Related:</strong></p><ul><li><a href="/docs/cases/workflow">Investigation Workflow</a></li><li><a href="/docs/cases/assignment">Case Assignment</a></li><li><a href="/docs/reporting/secure-messaging">Secure Messaging</a></li></ul>',175)])])}const h=t(n,[["render",s]]);export{c as __pageData,h as default};
