import{_ as e,c as l,o as t,ag as n}from"./chunks/framework.4QX5UEtQ.js";const p=JSON.parse('{"title":"Investigation Workflow - Disclosurely Process Guide","description":"Master the complete investigation lifecycle from assessment to resolution. Evidence gathering, analysis, findings, and compliance with regulatory timeframes.","frontmatter":{"title":"Investigation Workflow - Disclosurely Process Guide","description":"Master the complete investigation lifecycle from assessment to resolution. Evidence gathering, analysis, findings, and compliance with regulatory timeframes.","head":[["meta",{"property":"og:title","content":"Investigation Workflow & Process - Disclosurely"}],["meta",{"property":"og:description","content":"Master the complete investigation lifecycle from assessment to resolution. Evidence gathering, analysis, findings, and compliance with regulatory timeframes."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Investigation Workflow - Disclosurely"}],["meta",{"name":"twitter:description","content":"Master the complete investigation lifecycle from assessment to resolution. Evidence gathering, analysis, findings, and compliance with regulatory timeframes."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"cases/workflow.md","filePath":"cases/workflow.md"}'),o={name:"cases/workflow.md"};function s(a,i,r,c,d,g){return t(),l("div",null,[...i[0]||(i[0]=[n('<h1 id="investigation-workflow" tabindex="-1">Investigation Workflow <a class="header-anchor" href="#investigation-workflow" aria-label="Permalink to &quot;Investigation Workflow&quot;">​</a></h1><p>A comprehensive guide to managing the investigation lifecycle from initial review to resolution.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The investigation workflow in Disclosurely follows a structured process to ensure thorough, fair, and compliant investigations. Understanding each stage helps investigators work efficiently and maintain high standards.</p><h2 id="investigation-lifecycle" tabindex="-1">Investigation Lifecycle <a class="header-anchor" href="#investigation-lifecycle" aria-label="Permalink to &quot;Investigation Lifecycle&quot;">​</a></h2><h3 id="stage-1-initial-assessment-days-1-3" tabindex="-1">Stage 1: Initial Assessment (Days 1-3) <a class="header-anchor" href="#stage-1-initial-assessment-days-1-3" aria-label="Permalink to &quot;Stage 1: Initial Assessment (Days 1-3)&quot;">​</a></h3><p><strong>Objectives:</strong></p><ul><li>Understand the allegation</li><li>Assess severity and urgency</li><li>Determine investigation scope</li><li>Identify resources needed</li></ul><p><strong>Actions:</strong></p><ol><li><p><strong>Review Report Content</strong></p><ul><li>Read full report carefully</li><li>Note specific allegations made</li><li>Identify parties involved</li><li>Review any attached evidence</li><li>Check for previous related reports</li></ul></li><li><p><strong>Assess Credibility</strong></p><ul><li>Are allegations specific and detailed?</li><li>Is evidence provided?</li><li>Does report seem genuine?</li><li>Are there inconsistencies?</li><li>Is additional information needed?</li></ul></li><li><p><strong>Categorize &amp; Prioritize</strong></p><ul><li>Confirm category is correct</li><li>Set priority level: <ul><li>Critical: Immediate safety risk, legal violation</li><li>High: Serious misconduct, financial impact</li><li>Medium: Policy violation, team conflict</li><li>Low: Minor procedural issue</li></ul></li></ul></li><li><p><strong>Determine Scope</strong></p><ul><li>What exactly will be investigated?</li><li>What period of time is relevant?</li><li>Which departments involved?</li><li>Who needs to be interviewed?</li><li>What records need review?</li></ul></li><li><p><strong>Request Additional Information</strong></p><ul><li>Use <a href="/docs/reporting/secure-messaging">secure messaging</a> to ask reporter: <ul><li>Specific dates and times</li><li>Names of witnesses</li><li>Location details</li><li>Additional documentation</li></ul></li><li>Set reasonable deadline for response</li></ul></li><li><p><strong>Update Status</strong></p><ul><li>Change from &quot;New&quot; to &quot;Under Review&quot; using <a href="/docs/cases/status">status management</a></li><li>Add initial assessment notes</li><li>Set expected investigation timeline</li><li>Notify reporter of next steps</li></ul></li></ol><h3 id="stage-2-evidence-gathering-days-3-14" tabindex="-1">Stage 2: Evidence Gathering (Days 3-14) <a class="header-anchor" href="#stage-2-evidence-gathering-days-3-14" aria-label="Permalink to &quot;Stage 2: Evidence Gathering (Days 3-14)&quot;">​</a></h3><p><strong>Objectives:</strong></p><ul><li>Collect all relevant evidence</li><li>Interview witnesses and involved parties</li><li>Document findings thoroughly</li><li>Maintain chain of custody</li></ul><p><strong>Actions:</strong></p><ol><li><p><strong>Document Review</strong></p><ul><li>Email communications</li><li>Financial records</li><li>HR files</li><li>CCTV footage</li><li>Access logs</li><li>Meeting minutes</li><li>Previous complaints</li></ul></li><li><p><strong>Witness Interviews</strong></p><p><strong>Preparing for Interviews:</strong></p><ul><li>Identify witnesses to interview</li><li>Prepare interview questions</li><li>Schedule interviews privately</li><li>Ensure confidentiality</li><li>Document consent</li></ul><p><strong>Conducting Interviews:</strong></p><ul><li>Explain purpose and confidentiality</li><li>Ask open-ended questions</li><li>Take detailed notes</li><li>Record key quotes verbatim</li><li>Allow witness to add anything</li><li>Thank them for participation</li></ul><p><strong>After Interviews:</strong></p><ul><li>Transcribe notes while fresh</li><li>Upload to case evidence</li><li>Identify corroboration or conflicts</li><li>Determine if follow-up needed</li></ul></li><li><p><strong>Evidence Collection Best Practices</strong></p><p><strong>For Digital Evidence:</strong></p><ul><li>Screenshot emails with full headers</li><li>Export with metadata intact</li><li>Hash files to prove authenticity</li><li>Maintain original and copy</li><li>Document collection date/time</li></ul><p><strong>For Physical Evidence:</strong></p><ul><li>Photograph in situ if possible</li><li>Label clearly with date/case ID</li><li>Store securely</li><li>Maintain chain of custody log</li><li>Get receipt if taking from location</li></ul></li><li><p><strong>Organize Evidence</strong></p><ul><li>Upload all <a href="/docs/cases/evidence">evidence to case</a></li><li>Tag and categorize each item</li><li>Create evidence index</li><li>Link evidence to specific allegations</li><li>Note relevance and weight</li></ul></li><li><p><strong>Update Case Notes</strong></p><ul><li><a href="/docs/cases/evidence">Document investigation activities</a> daily</li><li>Note who interviewed when</li><li>Record evidence obtained</li><li>Track outstanding actions</li><li>Identify emerging issues</li></ul></li></ol><h3 id="stage-3-analysis-findings-days-14-21" tabindex="-1">Stage 3: Analysis &amp; Findings (Days 14-21) <a class="header-anchor" href="#stage-3-analysis-findings-days-14-21" aria-label="Permalink to &quot;Stage 3: Analysis &amp; Findings (Days 14-21)&quot;">​</a></h3><p><strong>Objectives:</strong></p><ul><li>Analyze all collected evidence</li><li>Determine if allegations substantiated</li><li>Identify policy violations</li><li>Consider mitigating factors</li><li>Draft preliminary findings</li></ul><p><strong>Actions:</strong></p><ol><li><p><strong>Evidence Analysis</strong></p><p><strong>For Each Allegation:</strong></p><ul><li>List evidence supporting</li><li>List evidence contradicting</li><li>Assess credibility of each source</li><li>Identify corroboration</li><li>Note gaps or uncertainties</li></ul></li><li><p><strong>Apply Standards of Proof</strong></p><p><strong>Levels Used:</strong></p><ul><li><strong>Balance of probabilities</strong>: More likely than not (50%+)</li><li><strong>Clear and convincing</strong>: Highly probable (75%+)</li><li><strong>Beyond reasonable doubt</strong>: Almost certain (95%+)</li></ul><p><strong>Typically:</strong></p><ul><li>Employment matters: Balance of probabilities</li><li>Serious misconduct: Clear and convincing</li><li>Criminal matters: Refer to authorities</li></ul></li><li><p><strong>Determine Findings</strong></p><p><strong>For Each Allegation:</strong></p><ul><li><strong>Substantiated</strong>: Evidence supports allegation</li><li><strong>Unsubstantiated</strong>: Insufficient evidence</li><li><strong>False</strong>: Evidence disproves allegation</li><li><strong>Inconclusive</strong>: Conflicting evidence</li></ul></li><li><p><strong>Consider Context</strong></p><ul><li>Were there mitigating circumstances?</li><li>Was conduct intentional or accidental?</li><li>Is there a pattern of behavior?</li><li>Are policies clear and communicated?</li><li>Has training been provided?</li></ul></li><li><p><strong>Identify Policy Violations</strong></p><ul><li>Which policies were breached?</li><li>Severity of each breach</li><li>Previous violations by subject</li><li>Consistency with past cases</li><li>Regulatory implications</li></ul></li><li><p><strong>Document AI Assistance</strong></p><ul><li>If using <a href="/docs/ai/case-helper">AI Case Helper</a> for analysis</li><li>Note which AI insights used</li><li>Verify AI conclusions independently</li><li>Document human review and decision</li><li>Maintain investigator accountability</li></ul></li></ol><h3 id="stage-4-subject-response-days-21-24" tabindex="-1">Stage 4: Subject Response (Days 21-24) <a class="header-anchor" href="#stage-4-subject-response-days-21-24" aria-label="Permalink to &quot;Stage 4: Subject Response (Days 21-24)&quot;">​</a></h3><p><strong>Objectives:</strong></p><ul><li>Ensure procedural fairness</li><li>Give subject opportunity to respond</li><li>Consider their perspective</li><li>Maintain impartiality</li></ul><p><strong>Actions:</strong></p><ol><li><p><strong>Prepare Subject Interview</strong></p><ul><li>Draft allegation summary (no reporter details)</li><li>List evidence to present</li><li>Prepare questions</li><li>Consider subject&#39;s rights</li><li>Arrange witness/representative if allowed</li></ul></li><li><p><strong>Conduct Subject Interview</strong></p><p><strong>Opening:</strong></p><ul><li>Explain investigation process</li><li>Present allegations clearly</li><li>Confirm understanding</li><li>Note they can provide information</li></ul><p><strong>During:</strong></p><ul><li>Allow full response to each allegation</li><li>Listen without judgment</li><li>Ask clarifying questions</li><li>Request any supporting evidence</li><li>Note their version of events</li></ul><p><strong>Closing:</strong></p><ul><li>Summarize their response</li><li>Ask if anything to add</li><li>Explain next steps</li><li>Set timeline for decision</li><li>Reinforce confidentiality</li></ul></li><li><p><strong>Evaluate Subject Response</strong></p><ul><li>Does response change findings?</li><li>Is new evidence provided?</li><li>Are there credible explanations?</li><li>Do you need to re-interview others?</li><li>Should findings be revised?</li></ul></li></ol><h3 id="stage-5-decision-recommendation-days-24-28" tabindex="-1">Stage 5: Decision &amp; Recommendation (Days 24-28) <a class="header-anchor" href="#stage-5-decision-recommendation-days-24-28" aria-label="Permalink to &quot;Stage 5: Decision &amp; Recommendation (Days 24-28)&quot;">​</a></h3><p><strong>Objectives:</strong></p><ul><li>Finalize investigation findings</li><li>Recommend appropriate actions</li><li>Ensure consistency and fairness</li><li>Document thoroughly</li></ul><p>Learn more about <a href="/docs/cases/resolution">determining findings and appropriate actions</a>.</p><p><strong>Actions:</strong></p><ol><li><p><strong>Finalize Findings</strong></p><ul><li>Review all evidence one final time</li><li>Confirm conclusions are supported</li><li>Consider subject&#39;s response</li><li>Check for bias or assumptions</li><li>Validate consistency with past cases</li></ul></li><li><p><strong>Develop Recommendations</strong></p><p><strong>If Substantiated:</strong></p><ul><li>No action (if resolved or minor)</li><li>Coaching/counseling</li><li>Formal warning</li><li>Training required</li><li>Demotion or transfer</li><li>Suspension</li><li>Termination</li><li>Referral to authorities</li></ul><p><strong>Consider:</strong></p><ul><li>Severity of misconduct</li><li>Subject&#39;s role and seniority</li><li>Previous disciplinary record</li><li>Impact on victims/organization</li><li>Mitigating circumstances</li><li>Consistency with past cases</li><li>Legal and regulatory requirements</li></ul></li><li><p><strong>Remedial Actions</strong></p><ul><li>Policy updates needed?</li><li>Additional training required?</li><li>Process improvements?</li><li>Control weaknesses identified?</li><li>Cultural issues to address?</li></ul></li><li><p><strong>Write Investigation Report</strong></p><p><strong>Structure:</strong></p><ul><li>Executive summary</li><li>Background and scope</li><li>Investigation methodology</li><li>Evidence gathered and reviewed</li><li>Findings for each allegation</li><li>Subject response summary</li><li>Conclusions</li><li>Recommendations</li><li>Appendices (evidence index)</li></ul></li><li><p><strong>Internal Review</strong></p><ul><li>Have findings reviewed by: <ul><li>Senior investigator</li><li>Legal counsel (if serious)</li><li>HR director (employment matters)</li><li>Compliance officer</li></ul></li><li>Incorporate feedback</li><li>Ensure recommendations appropriate</li></ul></li></ol><h3 id="stage-6-resolution-communication-days-28-30" tabindex="-1">Stage 6: Resolution &amp; Communication (Days 28-30) <a class="header-anchor" href="#stage-6-resolution-communication-days-28-30" aria-label="Permalink to &quot;Stage 6: Resolution &amp; Communication (Days 28-30)&quot;">​</a></h3><p><strong>Objectives:</strong></p><ul><li>Implement decisions</li><li>Communicate outcomes appropriately</li><li>Close case properly</li><li>Maintain confidentiality</li></ul><p><strong>Actions:</strong></p><ol><li><p><strong>Implement Decisions</strong></p><ul><li>Forward to HR for disciplinary action</li><li>Brief relevant managers</li><li>Monitor implementation</li><li>Set review dates if ongoing actions</li><li>Update policies if needed</li></ul></li><li><p><strong>Communicate to Reporter</strong></p><p><strong>Via Secure Messaging:</strong></p><ul><li>Thank them for reporting</li><li>Confirm investigation completed</li><li>Provide outcome summary: <ul><li>Substantiated/unsubstantiated/inconclusive</li><li>Action taken (general terms)</li><li>Changes implemented</li></ul></li><li><strong>Do Not Share:</strong><ul><li>Specific disciplinary actions</li><li>Names of those interviewed</li><li>Detailed evidence</li><li>Information about subject&#39;s employment</li></ul></li></ul></li><li><p><strong>Communicate to Subject</strong></p><ul><li>Meeting with HR and manager</li><li>Present findings clearly</li><li>Explain disciplinary action</li><li>Provide appeal rights</li><li>Document acknowledgment</li><li>Arrange any support needed</li></ul></li><li><p><strong>Update Stakeholders</strong></p><ul><li>Board/executives (if serious)</li><li>Department heads (if policy changes)</li><li>Legal/compliance teams</li><li>External regulators (if required)</li><li>Keep communications brief and need-to-know</li></ul></li><li><p><strong>Update Case Status</strong></p><ul><li>Change status to &quot;Resolved&quot;</li><li>Add final outcome summary</li><li>Link to investigation report</li><li>Document action items</li><li>Set review dates if applicable</li></ul></li><li><p><strong>Close Case</strong></p><ul><li>Ensure all evidence uploaded</li><li>Complete all required fields</li><li>Run final audit check</li><li>Archive case</li><li>Generate compliance reports if needed</li></ul></li></ol><h2 id="using-disclosurely-s-workflow-tools" tabindex="-1">Using Disclosurely&#39;s Workflow Tools <a class="header-anchor" href="#using-disclosurely-s-workflow-tools" aria-label="Permalink to &quot;Using Disclosurely&#39;s Workflow Tools&quot;">​</a></h2><h3 id="status-progression" tabindex="-1">Status Progression <a class="header-anchor" href="#status-progression" aria-label="Permalink to &quot;Status Progression&quot;">​</a></h3><p>Track investigation progress using statuses:</p><ol><li><strong>New</strong> - Just submitted, not yet reviewed</li><li><strong>Under Review</strong> - Initial assessment underway</li><li><strong>Investigating</strong> - Active evidence gathering</li><li><strong>Pending Information</strong> - Waiting for reporter/witness input</li><li><strong>Under Analysis</strong> - Reviewing evidence and forming conclusions</li><li><strong>Subject Response</strong> - Awaiting or reviewing subject&#39;s response</li><li><strong>Decision Pending</strong> - Recommendations being finalized</li><li><strong>Resolved</strong> - Investigation complete, actions implemented</li><li><strong>Closed</strong> - Case archived, no further action</li></ol><h3 id="case-notes" tabindex="-1">Case Notes <a class="header-anchor" href="#case-notes" aria-label="Permalink to &quot;Case Notes&quot;">​</a></h3><p>Use case notes to document your work:</p><p><strong>Best Practices:</strong></p><ul><li>Date and time each entry</li><li>Note what you did, not just what you found</li><li>Include who you spoke with</li><li>Document decisions made</li><li>Record reasons for key choices</li><li>Note any unusual circumstances</li><li>Add tags for easy searching</li></ul><h3 id="evidence-management" tabindex="-1">Evidence Management <a class="header-anchor" href="#evidence-management" aria-label="Permalink to &quot;Evidence Management&quot;">​</a></h3><p>Upload and organize evidence:</p><p><strong>Upload Files:</strong></p><ul><li>Interview transcripts</li><li>Email screenshots</li><li>Financial records</li><li>Photos and videos</li><li>Audio recordings</li><li>Document scans</li></ul><p><strong>Tag Evidence:</strong></p><ul><li>By allegation</li><li>By source</li><li>By relevance level</li><li>By date</li><li>By custodian</li></ul><h3 id="collaboration" tabindex="-1">Collaboration <a class="header-anchor" href="#collaboration" aria-label="Permalink to &quot;Collaboration&quot;">​</a></h3><p>Work with team members:</p><p><strong>Assign Tasks:</strong></p><ul><li>Request document review</li><li>Delegate interviews</li><li>Ask for specialized analysis</li><li>Get peer review</li></ul><p><strong>Share Notes:</strong></p><ul><li>Make notes visible to team</li><li>Add comments to evidence</li><li>Discuss findings privately</li><li>Get second opinions</li></ul><h2 id="investigation-timeline" tabindex="-1">Investigation Timeline <a class="header-anchor" href="#investigation-timeline" aria-label="Permalink to &quot;Investigation Timeline&quot;">​</a></h2><h3 id="regulatory-timeframes" tabindex="-1">Regulatory Timeframes <a class="header-anchor" href="#regulatory-timeframes" aria-label="Permalink to &quot;Regulatory Timeframes&quot;">​</a></h3><p><strong>EU Whistleblowing Directive:</strong></p><ul><li>Acknowledge receipt: 7 days</li><li>Provide feedback: 3 months (extendable to 6 months)</li></ul><p><strong>UK Regulatory Requirements:</strong></p><ul><li>Prompt investigation required</li><li>&quot;Reasonable time&quot; standard</li><li>Depends on complexity</li></ul><p><strong>US Requirements:</strong></p><ul><li>Varies by industry and allegation type</li><li>SOX: Immediate for financial fraud</li><li>OSHA: Varies by sector</li></ul><h3 id="recommended-timeframes" tabindex="-1">Recommended Timeframes <a class="header-anchor" href="#recommended-timeframes" aria-label="Permalink to &quot;Recommended Timeframes&quot;">​</a></h3><p><strong>Simple Cases:</strong></p><ul><li>Minor policy violations</li><li>Clear evidence</li><li>Few parties involved</li><li>Target: 2-3 weeks</li></ul><p><strong>Standard Cases:</strong></p><ul><li>Moderate complexity</li><li>Multiple witnesses</li><li>Standard evidence gathering</li><li>Target: 4-6 weeks</li></ul><p><strong>Complex Cases:</strong></p><ul><li>Serious allegations</li><li>Multiple jurisdictions</li><li>Extensive evidence</li><li>External parties involved</li><li>Target: 8-12 weeks</li></ul><h3 id="managing-extensions" tabindex="-1">Managing Extensions <a class="header-anchor" href="#managing-extensions" aria-label="Permalink to &quot;Managing Extensions&quot;">​</a></h3><p>If investigation extends beyond initial timeline:</p><ol><li><p><strong>Assess Why</strong></p><ul><li>Additional allegations emerged?</li><li>Key witnesses unavailable?</li><li>Waiting for external information?</li><li>More complex than initially assessed?</li></ul></li><li><p><strong>Communicate Extension</strong></p><ul><li>Update reporter via secure messaging</li><li>Explain reason for delay</li><li>Provide new expected timeline</li><li>Thank them for patience</li></ul></li><li><p><strong>Update Status</strong></p><ul><li>Change to &quot;Pending Information&quot; if waiting</li><li>Add note explaining delay</li><li>Set reminder to follow up</li><li>Keep stakeholders informed</li></ul></li></ol><h2 id="quality-assurance" tabindex="-1">Quality Assurance <a class="header-anchor" href="#quality-assurance" aria-label="Permalink to &quot;Quality Assurance&quot;">​</a></h2><h3 id="investigation-checklist" tabindex="-1">Investigation Checklist <a class="header-anchor" href="#investigation-checklist" aria-label="Permalink to &quot;Investigation Checklist&quot;">​</a></h3><p>Before closing case, verify:</p><p>✅ All allegations addressed ✅ Sufficient evidence gathered ✅ All relevant witnesses interviewed ✅ Subject given opportunity to respond ✅ Evidence properly documented ✅ Findings supported by evidence ✅ Recommendations consistent with past cases ✅ Reporter updated on outcome ✅ Actions implemented ✅ Case notes complete ✅ Audit trail intact ✅ Compliance requirements met</p><h3 id="peer-review" tabindex="-1">Peer Review <a class="header-anchor" href="#peer-review" aria-label="Permalink to &quot;Peer Review&quot;">​</a></h3><p>For serious cases, get peer review:</p><p><strong>What to Review:</strong></p><ul><li>Investigation methodology</li><li>Evidence sufficiency</li><li>Conclusion logic</li><li>Recommendation appropriateness</li><li>Procedural fairness</li><li>Documentation completeness</li></ul><p><strong>Who Should Review:</strong></p><ul><li>Senior investigator</li><li>Legal counsel</li><li>External specialist</li><li>Compliance officer</li></ul><h3 id="continuous-improvement" tabindex="-1">Continuous Improvement <a class="header-anchor" href="#continuous-improvement" aria-label="Permalink to &quot;Continuous Improvement&quot;">​</a></h3><p>Learn from each investigation:</p><p><strong>After Each Case:</strong></p><ul><li>What went well?</li><li>What could improve?</li><li>Were there delays? Why?</li><li>Was process efficient?</li><li>Did you have right resources?</li><li>Update procedures if needed</li></ul><hr><p><strong>Related:</strong></p><ul><li><a href="/docs/cases/assignment">Case Assignment</a></li><li><a href="/docs/cases/status">Status Management</a></li><li><a href="/docs/cases/evidence">Evidence Management</a></li><li><a href="/docs/ai/case-helper">AI Case Helper</a></li></ul>',91)])])}const m=e(o,[["render",s]]);export{p as __pageData,m as default};
