import{_ as e,c as t,o as l,ag as a}from"./chunks/framework.4QX5UEtQ.js";const p=JSON.parse('{"title":"Audit Trail - Disclosurely Tamper-Evident Logging","description":"Tamper-evident audit trails with hash chain integrity for complete compliance. Track all user actions, system events, and investigations with immutable logging.","frontmatter":{"title":"Audit Trail - Disclosurely Tamper-Evident Logging","description":"Tamper-evident audit trails with hash chain integrity for complete compliance. Track all user actions, system events, and investigations with immutable logging.","head":[["meta",{"property":"og:title","content":"Audit Trail & Tamper-Evident Logging - Disclosurely"}],["meta",{"property":"og:description","content":"Tamper-evident audit trails with hash chain integrity for complete compliance. Track all user actions, system events, and investigations with immutable logging."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Audit Trail - Disclosurely"}],["meta",{"name":"twitter:description","content":"Tamper-evident audit trails with hash chain integrity for complete compliance. Track all user actions, system events, and investigations with immutable logging."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"compliance/audit-trail.md","filePath":"compliance/audit-trail.md"}'),o={name:"compliance/audit-trail.md"};function n(r,i,s,c,d,u){return l(),t("div",null,[...i[0]||(i[0]=[a('<h1 id="audit-trail" tabindex="-1">Audit Trail <a class="header-anchor" href="#audit-trail" aria-label="Permalink to &quot;Audit Trail&quot;">​</a></h1><p>Complete, tamper-evident logging of all system activities for compliance and accountability.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Disclosurely&#39;s audit trail provides:</p><ul><li><strong>Complete transparency</strong>: Every action logged</li><li><strong>Tamper-evidence</strong>: Hash chain prevents alteration</li><li><strong>Compliance</strong>: Meets regulatory requirements</li><li><strong>Accountability</strong>: Who did what, when</li><li><strong>Investigation</strong>: Forensic analysis capability</li><li><strong>Trust</strong>: Demonstrable integrity</li></ul><h2 id="what-gets-logged" tabindex="-1">What Gets Logged <a class="header-anchor" href="#what-gets-logged" aria-label="Permalink to &quot;What Gets Logged&quot;">​</a></h2><h3 id="user-actions" tabindex="-1">User Actions <a class="header-anchor" href="#user-actions" aria-label="Permalink to &quot;User Actions&quot;">​</a></h3><p><strong>Account Activities</strong>:</p><ul><li>Login attempts (successful and failed)</li><li>Logout events</li><li>Password changes</li><li>MFA setup and changes</li><li>Email address updates</li><li>Account creation</li><li>Account deactivation</li><li>Permission changes</li></ul><p><strong>Case Activities</strong>:</p><ul><li>Case viewed</li><li>Case assigned/reassigned</li><li>Status changed</li><li>Notes added or edited</li><li>Evidence uploaded</li><li>Evidence downloaded</li><li>Evidence deleted</li><li>Messages sent</li><li>Tags added/removed</li><li>Priority changed</li></ul><p><strong>Administrative Actions</strong>:</p><ul><li>Team member invited</li><li>User role changed</li><li>Organization settings modified</li><li>Branding updated</li><li>Domain configured</li><li>Subscription changed</li><li>Integrations configured</li><li>Retention policies set</li><li>Legal holds applied/released</li></ul><h3 id="system-events" tabindex="-1">System Events <a class="header-anchor" href="#system-events" aria-label="Permalink to &quot;System Events&quot;">​</a></h3><p><strong>Automatic Activities</strong>:</p><ul><li>Report submitted</li><li>Auto-assignment triggered</li><li>Notification sent</li><li>Scheduled tasks run</li><li>Data archiving</li><li>Data deletion</li><li>Backup completion</li><li>System health checks</li></ul><p><strong>Security Events</strong>:</p><ul><li>Failed authentication attempts</li><li>Account lockouts</li><li>Suspicious activity detected</li><li>IP address changes</li><li>Session timeouts</li><li>API access</li><li>Integration connections</li><li>Certificate renewals</li></ul><h2 id="audit-log-structure" tabindex="-1">Audit Log Structure <a class="header-anchor" href="#audit-log-structure" aria-label="Permalink to &quot;Audit Log Structure&quot;">​</a></h2><h3 id="log-entry-fields" tabindex="-1">Log Entry Fields <a class="header-anchor" href="#log-entry-fields" aria-label="Permalink to &quot;Log Entry Fields&quot;">​</a></h3><p>Each audit entry contains:</p><p><strong>Timestamp</strong>:</p><ul><li>Exact date and time (UTC)</li><li>Millisecond precision</li><li>Timezone indicator</li></ul><p><strong>Actor</strong>:</p><ul><li>User who performed action</li><li>User ID (immutable)</li><li>User role at time</li><li>IP address</li><li>Geographic location (approximate)</li><li>Device/browser fingerprint</li></ul><p><strong>Action</strong>:</p><ul><li>What was done</li><li>Standardized action codes</li><li>Human-readable description</li><li>Severity level</li></ul><p><strong>Object</strong>:</p><ul><li>What was acted upon</li><li>Case ID, user ID, setting name, etc.</li><li>Before state (if modified)</li><li>After state (if modified)</li></ul><p><strong>Context</strong>:</p><ul><li>How action was performed (UI, API, automation)</li><li>Related objects</li><li>Parent actions</li><li>Session identifier</li></ul><p><strong>Integrity</strong>:</p><ul><li>Hash of this entry</li><li>Hash of previous entry</li><li>Chain position</li><li>Signature</li></ul><h3 id="hash-chain-integrity" tabindex="-1">Hash Chain Integrity <a class="header-anchor" href="#hash-chain-integrity" aria-label="Permalink to &quot;Hash Chain Integrity&quot;">​</a></h3><p><strong>How It Works</strong>:</p><ol><li><p><strong>First Entry</strong></p><ul><li>Initial hash calculated from entry data</li><li>Becomes baseline for chain</li></ul></li><li><p><strong>Subsequent Entries</strong></p><ul><li>Entry data + previous entry&#39;s hash = new hash</li><li>Creates cryptographic link</li><li>Any tampering breaks chain</li></ul></li><li><p><strong>Verification</strong></p><ul><li>Recalculate all hashes</li><li>Compare to stored hashes</li><li>Any mismatch = tampering detected</li><li>Shows exactly where tampering occurred</li></ul></li></ol><p><strong>Benefits</strong>:</p><ul><li><strong>Tamper-evident</strong>: Cannot alter without detection</li><li><strong>Non-repudiation</strong>: Proves action occurred</li><li><strong>Integrity</strong>: Verifiable by third parties</li><li><strong>Compliance</strong>: Meets SOX, ISO 27001, regulations</li></ul><h2 id="viewing-audit-logs" tabindex="-1">Viewing Audit Logs <a class="header-anchor" href="#viewing-audit-logs" aria-label="Permalink to &quot;Viewing Audit Logs&quot;">​</a></h2><h3 id="accessing-logs" tabindex="-1">Accessing Logs <a class="header-anchor" href="#accessing-logs" aria-label="Permalink to &quot;Accessing Logs&quot;">​</a></h3><p><strong>Permission Required</strong>:</p><ul><li>Organization Administrator</li><li>Compliance Officer (if role exists)</li><li>Auditor (read-only)</li></ul><p><strong>Navigation</strong>:</p><ol><li>Dashboard &gt; Compliance &gt; Audit Trail</li><li>Select date range</li><li>Apply filters</li><li>View log entries</li></ol><h3 id="filtering-logs" tabindex="-1">Filtering Logs <a class="header-anchor" href="#filtering-logs" aria-label="Permalink to &quot;Filtering Logs&quot;">​</a></h3><p><strong>By Time</strong>:</p><ul><li>Last 24 hours</li><li>Last 7 days</li><li>Last 30 days</li><li>Custom date range</li><li>Specific time period</li></ul><p><strong>By User</strong>:</p><ul><li>Specific user</li><li>All users</li><li>Role type</li><li>External users (API)</li><li>System actions</li></ul><p><strong>By Action Type</strong>:</p><ul><li>Case actions</li><li>User management</li><li>Settings changes</li><li>Security events</li><li>System events</li><li>All actions</li></ul><p><strong>By Object</strong>:</p><ul><li>Specific case</li><li>Specific user</li><li>Settings category</li><li>All objects</li></ul><p><strong>By Severity</strong>:</p><ul><li>Critical (security events, deletions)</li><li>High (major changes)</li><li>Medium (standard actions)</li><li>Low (view actions)</li><li>Info (system events)</li></ul><h3 id="search-functionality" tabindex="-1">Search Functionality <a class="header-anchor" href="#search-functionality" aria-label="Permalink to &quot;Search Functionality&quot;">​</a></h3><p><strong>Keyword Search</strong>:</p><ul><li>Search all log entries</li><li>Action descriptions</li><li>User names</li><li>Object IDs</li><li>Context information</li></ul><p><strong>Advanced Search</strong>:</p><ul><li>Combine multiple filters</li><li>Date range + user + action</li><li>Boolean operators</li><li>Regular expressions</li><li>Save search queries</li></ul><h2 id="compliance-uses" tabindex="-1">Compliance Uses <a class="header-anchor" href="#compliance-uses" aria-label="Permalink to &quot;Compliance Uses&quot;">​</a></h2><h3 id="regulatory-compliance" tabindex="-1">Regulatory Compliance <a class="header-anchor" href="#regulatory-compliance" aria-label="Permalink to &quot;Regulatory Compliance&quot;">​</a></h3><p><strong><a href="/docs/compliance/eu-directive">EU Whistleblowing Directive</a></strong>:</p><ul><li>Demonstrates report handling timeline</li><li>Shows acknowledgment within 7 days</li><li>Proves feedback within 3 months</li><li>Documents confidentiality measures</li><li>Audit trail required for compliance</li></ul><p><strong><a href="/docs/compliance/gdpr">GDPR</a></strong>:</p><ul><li>Track data access</li><li>Document data exports</li><li>Verify deletions occurred</li><li>Demonstrate consent handling</li><li>Support data subject requests</li></ul><p><strong><a href="/docs/compliance/sox">SOX (Sarbanes-Oxley)</a></strong>:</p><ul><li>Audit trail of financial report investigations</li><li>Document controls and processes</li><li>Demonstrate segregation of duties</li><li>Prove <a href="/docs/compliance/retention">data retention compliance</a></li></ul><p><strong>ISO 27001</strong>:</p><ul><li>Information security event logging</li><li>Access control verification</li><li>Incident response documentation</li><li>Change management tracking</li></ul><h3 id="internal-audits" tabindex="-1">Internal Audits <a class="header-anchor" href="#internal-audits" aria-label="Permalink to &quot;Internal Audits&quot;">​</a></h3><p><strong>Quarterly Reviews</strong>:</p><ul><li>Case handling efficiency</li><li>User activity patterns</li><li>Compliance with procedures</li><li>Security event analysis</li><li>Process improvements</li></ul><p><strong>Annual Audits</strong>:</p><ul><li>Comprehensive log review</li><li>Retention compliance verification</li><li>Security assessment</li><li>User access review</li><li>Hash chain integrity check</li></ul><p><strong>External Audits</strong>:</p><ul><li>Provide filtered audit logs</li><li>Export for auditor review</li><li>Demonstrate compliance</li><li>Verify integrity</li><li>Support findings</li></ul><h2 id="audit-reports" tabindex="-1">Audit Reports <a class="header-anchor" href="#audit-reports" aria-label="Permalink to &quot;Audit Reports&quot;">​</a></h2><h3 id="pre-built-reports" tabindex="-1">Pre-Built Reports <a class="header-anchor" href="#pre-built-reports" aria-label="Permalink to &quot;Pre-Built Reports&quot;">​</a></h3><p><strong>User Activity Report</strong>:</p><ul><li>Actions by user over period</li><li>Login patterns</li><li>Case access history</li><li>Administrative changes</li><li>Anomaly detection</li></ul><p><strong>Case Activity Report</strong>:</p><ul><li>All actions on specific case</li><li>Timeline view</li><li>User involvement</li><li>Evidence handling</li><li>Status changes</li></ul><p><strong>Security Event Report</strong>:</p><ul><li>Failed login attempts</li><li>Account lockouts</li><li>Suspicious activities</li><li>Permission changes</li><li>Integration access</li></ul><p><strong>Compliance Report</strong>:</p><ul><li>Report handling timeline</li><li>Acknowledgment timeliness</li><li>Feedback timeliness</li><li>Data retention compliance</li><li>Policy adherence</li></ul><p><strong>Change Management Report</strong>:</p><ul><li>Settings changes</li><li>User role modifications</li><li>Policy updates</li><li>Configuration changes</li><li>Administrative actions</li></ul><h3 id="custom-reports" tabindex="-1">Custom Reports <a class="header-anchor" href="#custom-reports" aria-label="Permalink to &quot;Custom Reports&quot;">​</a></h3><p><strong>Build Your Own</strong>:</p><ol><li>Select fields to include</li><li>Choose filters</li><li>Set date range</li><li>Select output format</li><li>Save or export</li></ol><p><strong>Output Formats</strong>:</p><ul><li>PDF (formatted report)</li><li>CSV (spreadsheet analysis)</li><li>JSON (programmatic access)</li><li>Excel (data analysis)</li></ul><p><strong>Scheduling</strong>:</p><ul><li>One-time report</li><li>Daily digest</li><li>Weekly summary</li><li>Monthly compliance report</li><li>Custom schedule</li></ul><h2 id="export-and-integration" tabindex="-1">Export and Integration <a class="header-anchor" href="#export-and-integration" aria-label="Permalink to &quot;Export and Integration&quot;">​</a></h2><h3 id="exporting-audit-logs" tabindex="-1">Exporting Audit Logs <a class="header-anchor" href="#exporting-audit-logs" aria-label="Permalink to &quot;Exporting Audit Logs&quot;">​</a></h3><p><strong>Export Options</strong>:</p><ul><li>Filtered subset</li><li>Complete logs</li><li>Date range</li><li>Specific cases</li><li>User activities</li></ul><p><strong>Formats</strong>:</p><ul><li>CSV (spreadsheet)</li><li>JSON (structured data)</li><li>XML (enterprise systems)</li><li>CEF (SIEM integration)</li><li>Syslog format</li></ul><p><strong>Integrity Verification</strong>:</p><ul><li>Export includes hash chain</li><li>Verification tool provided</li><li>Can validate externally</li><li>Proves authenticity</li></ul><h3 id="siem-integration" tabindex="-1">SIEM Integration <a class="header-anchor" href="#siem-integration" aria-label="Permalink to &quot;SIEM Integration&quot;">​</a></h3><p><strong>Security Information and Event Management</strong>:</p><p><strong>Supported Platforms</strong>:</p><ul><li>Splunk</li><li>IBM QRadar</li><li>LogRhythm</li><li>ArcSight</li><li>Elasticsearch/ELK Stack</li><li>Microsoft Sentinel</li></ul><p><strong>Integration Method</strong>:</p><ul><li>Real-time log streaming</li><li>Webhook delivery</li><li>API polling</li><li>Syslog forwarding</li><li>File-based transfer</li></ul><p><strong>Use Cases</strong>:</p><ul><li>Centralized security monitoring</li><li>Correlation with other systems</li><li>Advanced threat detection</li><li>Compliance reporting</li><li>Incident response</li></ul><h2 id="retention-and-storage" tabindex="-1">Retention and Storage <a class="header-anchor" href="#retention-and-storage" aria-label="Permalink to &quot;Retention and Storage&quot;">​</a></h2><h3 id="audit-log-retention" tabindex="-1">Audit Log Retention <a class="header-anchor" href="#audit-log-retention" aria-label="Permalink to &quot;Audit Log Retention&quot;">​</a></h3><p><strong>Retention Period</strong>:</p><ul><li><strong>Minimum</strong>: 7 years (regulatory requirement)</li><li><strong>Recommended</strong>: 10 years</li><li><strong>Permanent</strong>: Critical security events</li></ul><p><strong>Never Deleted</strong>:</p><ul><li>Security incidents</li><li>Data breaches</li><li>Legal hold events</li><li>Compliance violations</li><li>Administrative sanctions</li></ul><p><strong>Storage</strong>:</p><ul><li><a href="/docs/security/overview">Encrypted at rest</a></li><li>Geographically redundant</li><li>Separate from case data</li><li>Highly available</li><li>Backed up independently</li></ul><h3 id="audit-log-size" tabindex="-1">Audit Log Size <a class="header-anchor" href="#audit-log-size" aria-label="Permalink to &quot;Audit Log Size&quot;">​</a></h3><p><strong>Typical Size</strong>:</p><ul><li>100 cases/month ≈ 1GB logs/year</li><li>1,000 cases/month ≈ 10GB logs/year</li><li>Scales with activity level</li></ul><p><strong>Storage Costs</strong>:</p><ul><li>Included in subscription</li><li>No additional charge</li><li>Unlimited retention</li><li>All plans</li></ul><h2 id="integrity-verification" tabindex="-1">Integrity Verification <a class="header-anchor" href="#integrity-verification" aria-label="Permalink to &quot;Integrity Verification&quot;">​</a></h2><h3 id="verifying-hash-chain" tabindex="-1">Verifying Hash Chain <a class="header-anchor" href="#verifying-hash-chain" aria-label="Permalink to &quot;Verifying Hash Chain&quot;">​</a></h3><p><strong>Automated Verification</strong>:</p><ul><li>Runs automatically daily</li><li>Alerts if tampering detected</li><li>System administrators notified</li><li>Incident logged</li></ul><p><strong>Manual Verification</strong>:</p><ol><li><p><strong>Export Audit Logs</strong></p><ul><li>With hash chain data</li><li>Select period to verify</li><li>Download verification tool</li></ul></li><li><p><strong>Run Verification</strong></p><ul><li>Command-line tool</li><li>Or web-based verifier</li><li>Recalculates hashes</li><li>Compares to originals</li></ul></li><li><p><strong>Review Results</strong></p><ul><li>&quot;Integrity verified&quot; if clean</li><li>&quot;Tampering detected&quot; if compromised</li><li>Shows exact point of break</li><li>Generate verification report</li></ul></li></ol><p><strong>Use Cases</strong>:</p><ul><li>Audit preparation</li><li>Legal proceedings</li><li>Incident investigation</li><li>Compliance demonstration</li><li>Third-party verification</li></ul><h3 id="third-party-verification" tabindex="-1">Third-Party Verification <a class="header-anchor" href="#third-party-verification" aria-label="Permalink to &quot;Third-Party Verification&quot;">​</a></h3><p><strong>Independent Auditors</strong>:</p><ul><li>Provide export with hash chain</li><li>Auditor verifies independently</li><li>Demonstrates integrity</li><li>Cannot be disputed</li><li>Regulatory compliance</li></ul><h2 id="advanced-features" tabindex="-1">Advanced Features <a class="header-anchor" href="#advanced-features" aria-label="Permalink to &quot;Advanced Features&quot;">​</a></h2><h3 id="anomaly-detection" tabindex="-1">Anomaly Detection <a class="header-anchor" href="#anomaly-detection" aria-label="Permalink to &quot;Anomaly Detection&quot;">​</a></h3><p><strong>AI-Powered Analysis</strong>:</p><ul><li>Unusual access patterns</li><li>Off-hours activity</li><li>Excessive downloads</li><li>Rapid status changes</li><li>Geographic anomalies</li></ul><p><strong>Alerts Generated</strong>:</p><ul><li>Real-time notifications</li><li>Security team alerted</li><li>Potential security incidents</li><li>Insider threat detection</li><li>Compliance violations</li></ul><p><strong>Examples</strong>:</p><ul><li>User accessing 100+ cases in 1 hour</li><li>Login from unusual location</li><li>Multiple failed login attempts</li><li>Bulk evidence downloads</li><li>After-hours administrative changes</li></ul><h3 id="correlation-analysis" tabindex="-1">Correlation Analysis <a class="header-anchor" href="#correlation-analysis" aria-label="Permalink to &quot;Correlation Analysis&quot;">​</a></h3><p><strong>Cross-Reference Events</strong>:</p><ul><li>Related actions across time</li><li>Multiple users on same case</li><li>Pattern detection</li><li>Workflow analysis</li><li>Efficiency insights</li></ul><p><strong>Use Cases</strong>:</p><ul><li>Identify bottlenecks</li><li>Optimize processes</li><li>Detect collusion</li><li>Verify segregation of duties</li><li>Improve workflows</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="regular-review" tabindex="-1">Regular Review <a class="header-anchor" href="#regular-review" aria-label="Permalink to &quot;Regular Review&quot;">​</a></h3><p><strong>Who Should Review</strong>:</p><ul><li>Compliance Officer: Monthly</li><li>Security Team: Weekly</li><li>Administrators: Daily (high-level)</li><li>Auditors: Quarterly</li></ul><p><strong>What to Look For</strong>:</p><ul><li>Unusual activity patterns</li><li>Failed access attempts</li><li>Unexpected administrative changes</li><li>After-hours activity</li><li>Bulk data access</li></ul><h3 id="document-review" tabindex="-1">Document Review <a class="header-anchor" href="#document-review" aria-label="Permalink to &quot;Document Review&quot;">​</a></h3><p><strong>Create Review Log</strong>:</p><ul><li>Date of review</li><li>Who performed review</li><li>Findings</li><li>Actions taken</li><li>Follow-up required</li></ul><p><strong>Demonstrate Diligence</strong>:</p><ul><li>Regular oversight</li><li>Active monitoring</li><li>Responsive to issues</li><li>Compliance commitment</li></ul><h3 id="set-up-alerts" tabindex="-1">Set Up Alerts <a class="header-anchor" href="#set-up-alerts" aria-label="Permalink to &quot;Set Up Alerts&quot;">​</a></h3><p><strong>Critical Events</strong>:</p><ul><li>Failed login after 3 attempts</li><li>Account created or deleted</li><li>Legal hold applied/released</li><li>Data deletion</li><li>Settings changes</li><li>Permission elevations</li></ul><p><strong>Notification Method</strong>:</p><ul><li>Email to security team</li><li>SMS for critical events</li><li>Slack/Teams integration</li><li>SIEM alert</li><li>In-app notification</li></ul><h3 id="segregate-duties" tabindex="-1">Segregate Duties <a class="header-anchor" href="#segregate-duties" aria-label="Permalink to &quot;Segregate Duties&quot;">​</a></h3><p><strong>Who Can See What</strong>:</p><ul><li>Investigators: Case audit logs only</li><li>Administrators: User management logs</li><li>Compliance: All logs (read-only)</li><li>External Auditors: Filtered exports</li></ul><p><strong>Prevent Conflicts</strong>:</p><ul><li>Can&#39;t audit own actions comprehensively</li><li>Independent review</li><li>Checks and balances</li><li>Accountability</li></ul><h2 id="compliance-scenarios" tabindex="-1">Compliance Scenarios <a class="header-anchor" href="#compliance-scenarios" aria-label="Permalink to &quot;Compliance Scenarios&quot;">​</a></h2><h3 id="data-subject-request-gdpr" tabindex="-1">Data Subject Request (GDPR) <a class="header-anchor" href="#data-subject-request-gdpr" aria-label="Permalink to &quot;Data Subject Request (GDPR)&quot;">​</a></h3><p><strong>Request</strong>: User wants to know all their data access</p><p><strong>Response</strong>:</p><ol><li>Filter audit log by subject&#39;s case</li><li>Export all view/access events</li><li>Show who accessed when</li><li>Demonstrate encryption and access controls</li><li>Provide report to data subject</li></ol><h3 id="regulatory-investigation" tabindex="-1">Regulatory Investigation <a class="header-anchor" href="#regulatory-investigation" aria-label="Permalink to &quot;Regulatory Investigation&quot;">​</a></h3><p><strong>Request</strong>: Regulator asks about specific case handling</p><p><strong>Response</strong>:</p><ol><li>Export complete audit trail for case</li><li>Show timeline from submission to resolution</li><li>Demonstrate timely acknowledgment</li><li>Prove feedback provided</li><li>Verify integrity with hash chain</li><li>Provide verification certificate</li></ol><h3 id="internal-investigation" tabindex="-1">Internal Investigation <a class="header-anchor" href="#internal-investigation" aria-label="Permalink to &quot;Internal Investigation&quot;">​</a></h3><p><strong>Scenario</strong>: Suspected leak of confidential case information</p><p><strong>Investigation</strong>:</p><ol><li>Filter audit log for case</li><li>View all access events</li><li>Identify who viewed/downloaded evidence</li><li>Check for unusual patterns</li><li>Cross-reference with leak timing</li><li>Document findings</li><li>Take appropriate action</li></ol><h3 id="litigation-discovery" tabindex="-1">Litigation Discovery <a class="header-anchor" href="#litigation-discovery" aria-label="Permalink to &quot;Litigation Discovery&quot;">​</a></h3><p><strong>Scenario</strong>: Employment tribunal requests investigation records</p><p><strong>Response</strong>:</p><ol><li>Apply legal hold to prevent deletion</li><li>Export relevant audit logs</li><li>Demonstrate process followed</li><li>Show procedural fairness</li><li>Verify integrity</li><li>Provide to legal counsel</li><li>Maintain chain of custody</li></ol><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="cannot-access-audit-trail" tabindex="-1">Cannot Access Audit Trail <a class="header-anchor" href="#cannot-access-audit-trail" aria-label="Permalink to &quot;Cannot Access Audit Trail&quot;">​</a></h3><p><strong>Check</strong>:</p><ul><li>Do you have required permissions?</li><li>Organization Administrator or Compliance Officer role needed</li><li>Contact admin to request access</li></ul><h3 id="audit-log-missing-entries" tabindex="-1">Audit Log Missing Entries <a class="header-anchor" href="#audit-log-missing-entries" aria-label="Permalink to &quot;Audit Log Missing Entries&quot;">​</a></h3><p><strong>Verify</strong>:</p><ul><li>Correct date range selected</li><li>Filters not hiding entries</li><li>Time zone settings</li><li>System outage during period (rare)</li><li>Contact support if confirmed gap</li></ul><h3 id="hash-chain-verification-failed" tabindex="-1">Hash Chain Verification Failed <a class="header-anchor" href="#hash-chain-verification-failed" aria-label="Permalink to &quot;Hash Chain Verification Failed&quot;">​</a></h3><p><strong>Immediate Actions</strong>:</p><ol><li>Do not delete or modify logs</li><li>Document verification failure</li><li>Notify security team</li><li>Contact Disclosurely support</li><li>Preserve evidence</li><li>Investigate potential compromise</li></ol><p><strong>Possible Causes</strong>:</p><ul><li>Software bug (most likely)</li><li>Database corruption</li><li>Attempted tampering (rare)</li><li>Export/import error</li></ul><h3 id="export-failed" tabindex="-1">Export Failed <a class="header-anchor" href="#export-failed" aria-label="Permalink to &quot;Export Failed&quot;">​</a></h3><p><strong>Solutions</strong>:</p><ul><li>Reduce date range (less data)</li><li>Try different format</li><li>Check internet connection</li><li>Contact support if persists</li></ul><hr><p><strong>Related:</strong></p><ul><li><a href="/docs/compliance/gdpr">GDPR Compliance</a></li><li><a href="/docs/compliance/retention">Data Retention</a></li><li><a href="/docs/security/overview">Security Overview</a></li><li><a href="/docs/admin/organization-settings">Organization Settings</a></li></ul>',205)])])}const h=e(o,[["render",n]]);export{p as __pageData,h as default};
