import{_ as e,c as t,o as n,ag as o}from"./chunks/framework.4QX5UEtQ.js";const d=JSON.parse('{"title":"EU Whistleblowing Directive - Compliance Guide","description":"Complete EU Directive 2019/1937 compliance guide. 7-day acknowledgment, 3-month feedback, anonymous reporting, confidentiality protection, and anti-retaliation measures.","frontmatter":{"title":"EU Whistleblowing Directive - Compliance Guide","description":"Complete EU Directive 2019/1937 compliance guide. 7-day acknowledgment, 3-month feedback, anonymous reporting, confidentiality protection, and anti-retaliation measures.","head":[["meta",{"property":"og:title","content":"EU Whistleblowing Directive Compliance - Disclosurely"}],["meta",{"property":"og:description","content":"Complete EU Directive 2019/1937 compliance guide. 7-day acknowledgment, 3-month feedback, anonymous reporting, confidentiality protection, and anti-retaliation measures."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"EU Whistleblowing Directive - Disclosurely"}],["meta",{"name":"twitter:description","content":"Complete EU Directive 2019/1937 compliance guide. 7-day acknowledgment, 3-month feedback, anonymous reporting, confidentiality protection, and anti-retaliation measures."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"compliance/eu-directive.md","filePath":"compliance/eu-directive.md"}'),l={name:"compliance/eu-directive.md"};function a(r,i,s,c,p,m){return n(),t("div",null,[...i[0]||(i[0]=[o('<h1 id="eu-whistleblowing-directive-compliance" tabindex="-1">EU Whistleblowing Directive Compliance <a class="header-anchor" href="#eu-whistleblowing-directive-compliance" aria-label="Permalink to &quot;EU Whistleblowing Directive Compliance&quot;">​</a></h1><p>How Disclosurely helps you comply with EU Directive 2019/1937 on the protection of whistleblowers.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The <strong>EU Whistleblowing Directive (2019/1937)</strong> establishes minimum standards for whistleblower protection across all EU member states. All member states were required to transpose the Directive into national law by December 17, 2021.</p><p><strong>Disclosurely is built for full compliance</strong> with the Directive&#39;s requirements.</p><h2 id="who-must-comply" tabindex="-1">Who Must Comply <a class="header-anchor" href="#who-must-comply" aria-label="Permalink to &quot;Who Must Comply&quot;">​</a></h2><h3 id="covered-organizations" tabindex="-1">Covered Organizations <a class="header-anchor" href="#covered-organizations" aria-label="Permalink to &quot;Covered Organizations&quot;">​</a></h3><p><strong>Private Sector</strong>:</p><ul><li><strong>50+ employees</strong>: Must establish internal reporting channels</li><li><strong>Certain sectors</strong>: All entities regardless of size <ul><li>Financial services</li><li>Prevention of money laundering and terrorist financing</li><li>Transport safety</li><li>Environmental protection</li><li>Food safety</li><li>Public health</li><li>Consumer protection</li><li>Privacy and data protection</li><li>Network and information security</li></ul></li></ul><p><strong>Public Sector</strong>:</p><ul><li>All public authorities and institutions</li><li>Regardless of size</li><li>Includes local, regional, and national levels</li></ul><p><strong>Transition Period</strong>:</p><ul><li>Organizations with 50-249 employees: 2-year grace period (until December 17, 2023)</li><li>Organizations with 250+ employees: Immediate compliance required</li></ul><h3 id="member-state-implementation" tabindex="-1">Member State Implementation <a class="header-anchor" href="#member-state-implementation" aria-label="Permalink to &quot;Member State Implementation&quot;">​</a></h3><p>Each EU member state has implemented the Directive through national legislation:</p><ul><li><strong>Ireland</strong>: Protected Disclosures (Amendment) Act 2022</li><li><strong>Germany</strong>: Hinweisgeberschutzgesetz (HinSchG)</li><li><strong>France</strong>: Law No. 2022-401 (Loi Waserman)</li><li><strong>Spain</strong>: Law 2/2023</li><li><strong>Italy</strong>: Legislative Decree No. 24/2023</li><li><strong>Netherlands</strong>: House for Whistleblowers Act (Wet bescherming klokkenluiders)</li><li><strong>And others</strong>: Check your jurisdiction</li></ul><p><strong>Important</strong>: While the Directive sets minimum standards, member states may have additional or stricter requirements.</p><h2 id="key-requirements" tabindex="-1">Key Requirements <a class="header-anchor" href="#key-requirements" aria-label="Permalink to &quot;Key Requirements&quot;">​</a></h2><h3 id="_1-internal-reporting-channels" tabindex="-1">1. Internal Reporting Channels <a class="header-anchor" href="#_1-internal-reporting-channels" aria-label="Permalink to &quot;1. Internal Reporting Channels&quot;">​</a></h3><p><strong>Article 8 - Obligation to Establish Internal Reporting Channels</strong></p><p><strong>Requirements</strong>:</p><ul><li>Secure channels for receiving reports</li><li>Anonymous reporting option recommended</li><li>Clear procedures for handling reports</li><li>Information provided to employees about channels</li></ul><p><strong>Disclosurely Compliance</strong>:</p><ul><li>✅ Secure web-based reporting portal</li><li>✅ <a href="/docs/reporting/report-types">Anonymous reporting</a> via tracking ID</li><li>✅ Confidential reporting with identity protection</li><li>✅ Military-grade AES-256 encryption</li><li>✅ Customizable reporting forms</li><li>✅ Information pages for reporters</li></ul><p><strong>Setup</strong>:</p><ol><li>Configure reporting portal</li><li>Enable anonymous reporting</li><li>Customize branding (optional)</li><li>Set up custom domain</li><li>Add information about reporting process</li><li>Publish portal URL to employees</li></ol><h3 id="_2-acknowledgment-of-receipt" tabindex="-1">2. Acknowledgment of Receipt <a class="header-anchor" href="#_2-acknowledgment-of-receipt" aria-label="Permalink to &quot;2. Acknowledgment of Receipt&quot;">​</a></h3><p><strong>Article 9(1)(b) - Diligent Follow-Up</strong></p><p><strong>Requirement</strong>: Acknowledge receipt of report within <strong>7 days</strong></p><p><strong>Disclosurely Compliance</strong>:</p><ul><li>✅ Automatic acknowledgment email sent immediately upon submission</li><li>✅ Timestamp recorded in audit trail</li><li>✅ Reporter receives confirmation with tracking ID</li><li>✅ No manual action required</li><li>✅ Compliance tracking dashboard</li></ul><p><strong>What Reporters Receive</strong>:</p><ul><li>Immediate confirmation of submission</li><li>Unique tracking ID for follow-up</li><li>Expected timeline for investigation</li><li>How to send additional information</li><li>Contact for questions</li></ul><p><strong>Monitoring Compliance</strong>:</p><ul><li>Dashboard shows all acknowledgments sent</li><li>Filter cases by acknowledgment date</li><li>Alerts if acknowledgment delayed (system issue)</li><li>Audit trail proves compliance</li></ul><h3 id="_3-feedback-to-reporter" tabindex="-1">3. Feedback to Reporter <a class="header-anchor" href="#_3-feedback-to-reporter" aria-label="Permalink to &quot;3. Feedback to Reporter&quot;">​</a></h3><p><strong>Article 9(1)(f) - Providing Feedback</strong></p><p><strong>Requirement</strong>: Provide feedback on investigation outcome within <strong>3 months</strong> (extendable to 6 months for complex cases)</p><p><strong>Disclosurely Compliance</strong>:</p><ul><li>✅ <a href="/docs/reporting/secure-messaging">Secure messaging</a> system for communication</li><li>✅ Case status updates</li><li>✅ Investigation outcome notification</li><li>✅ Timeline tracking with alerts</li><li>✅ Automated compliance monitoring</li></ul><p><strong>Timeline Management</strong>:</p><ol><li><a href="/docs/cases/workflow">Case created</a> → 3-month timer starts</li><li>Alerts at 60 days, 75 days, 85 days</li><li>Extend to 6 months if needed (document reason)</li><li>Provide feedback before deadline</li><li>Audit trail documents compliance</li></ol><p><strong>Feedback Content</strong>:</p><ul><li>Whether allegations substantiated</li><li>General actions taken (not specific discipline)</li><li>Changes implemented to prevent recurrence</li><li>Appreciation for reporting</li></ul><p><strong>What NOT to Share</strong>:</p><ul><li>Specific disciplinary actions</li><li>Subject&#39;s employment status</li><li>Confidential investigation details</li><li>Other employees&#39; information</li></ul><h3 id="_4-impartial-person-responsible" tabindex="-1">4. Impartial Person Responsible <a class="header-anchor" href="#_4-impartial-person-responsible" aria-label="Permalink to &quot;4. Impartial Person Responsible&quot;">​</a></h3><p><strong>Article 8(7) - Designated Person or Department</strong></p><p><strong>Requirement</strong>: Designate impartial person or department to handle reports</p><p><strong>Disclosurely Compliance</strong>:</p><ul><li>✅ Role-based access controls</li><li>✅ Team management</li><li>✅ Assignment workflows</li><li>✅ Conflict of interest tracking</li><li>✅ Segregation of duties</li></ul><p><strong>Best Practices</strong>:</p><ul><li>Designate compliance officer or team</li><li>Provide training on investigation procedures</li><li>Ensure independence from subjects</li><li>Document conflicts of interest</li><li>Rotate assignments if needed</li></ul><p><strong>In Disclosurely</strong>:</p><ol><li>Create &quot;Compliance Team&quot; or designated role</li><li>Assign investigations to impartial investigators</li><li>Document impartiality</li><li>Flag conflicts of interest</li><li>Re-assign if conflict discovered</li></ol><h3 id="_5-confidentiality-protection" tabindex="-1">5. Confidentiality Protection <a class="header-anchor" href="#_5-confidentiality-protection" aria-label="Permalink to &quot;5. Confidentiality Protection&quot;">​</a></h3><p><strong>Article 16 - Protection of Identity</strong></p><p><strong>Requirement</strong>: Protect confidentiality of reporter&#39;s identity</p><p><strong>Disclosurely Compliance</strong>:</p><ul><li>✅ Anonymous reporting option (no identity collected)</li><li>✅ Confidential reporting (identity encrypted)</li><li>✅ Zero-knowledge architecture</li><li>✅ Role-based access to identity information</li><li>✅ Audit trail of identity access</li><li>✅ Confidentiality reminders throughout interface</li></ul><p><strong>Technical Protections</strong>:</p><ul><li>AES-256 encryption</li><li>Identity stored separately from report</li><li>Access logged in audit trail</li><li>Only authorized users can see identity</li><li>Cannot be disclosed without authorization</li></ul><p><strong>Procedural Protections</strong>:</p><ul><li>Confidentiality policies</li><li>Staff training</li><li>Non-disclosure agreements</li><li>Disciplinary consequences for breaches</li><li>Limited &quot;need to know&quot; access</li></ul><p><strong>Exceptions</strong> (when identity may be disclosed):</p><ul><li>Legal obligation (court order)</li><li>Necessary for investigation (with consent)</li><li>Subject&#39;s right to defense (subject may infer)</li></ul><h3 id="_6-data-protection" tabindex="-1">6. Data Protection <a class="header-anchor" href="#_6-data-protection" aria-label="Permalink to &quot;6. Data Protection&quot;">​</a></h3><p><strong>Article 17 - Processing of Personal Data</strong></p><p><strong>Requirement</strong>: Comply with GDPR when processing personal data</p><p><strong>Disclosurely Compliance</strong>:</p><ul><li>✅ GDPR-compliant data processing</li><li>✅ Data minimization</li><li>✅ Purpose limitation</li><li>✅ Retention policies</li><li>✅ Data subject rights support</li><li>✅ Data processing agreements</li></ul><p><strong>See</strong>: <a href="/docs/compliance/gdpr">GDPR Compliance</a> for detailed information</p><p><strong>Key Points</strong>:</p><ul><li>Process only necessary data</li><li>Retain only as long as needed</li><li>Secure storage and transmission</li><li>Support data subject access requests</li><li>Document legal basis for processing</li></ul><h3 id="_7-record-keeping" tabindex="-1">7. Record Keeping <a class="header-anchor" href="#_7-record-keeping" aria-label="Permalink to &quot;7. Record Keeping&quot;">​</a></h3><p><strong>Article 18 - Maintenance of Records</strong></p><p><strong>Requirement</strong>: Maintain records of reports and follow-up actions</p><p><strong>Disclosurely Compliance</strong>:</p><ul><li>✅ Complete case records</li><li>✅ Tamper-evident audit trail</li><li>✅ Evidence management</li><li>✅ Status tracking</li><li>✅ Investigation documentation</li><li>✅ Long-term retention and archiving</li></ul><p><strong>What&#39;s Recorded</strong>:</p><ul><li>Report submission date and time</li><li>Acknowledgment sent</li><li>Investigation activities</li><li>Status changes</li><li>Communications with reporter</li><li>Investigation outcome</li><li>Actions taken</li><li>Feedback provided</li></ul><p><strong>Audit Trail</strong>:</p><ul><li>Hash chain integrity</li><li>Cannot be altered</li><li>Proves compliance with timelines</li><li>Available for regulatory inspection</li><li>Retained per retention policy</li></ul><h3 id="_8-information-and-training" tabindex="-1">8. Information and Training <a class="header-anchor" href="#_8-information-and-training" aria-label="Permalink to &quot;8. Information and Training&quot;">​</a></h3><p><strong>Article 8(6) &amp; Recital 55 - Information About Channels</strong></p><p><strong>Requirement</strong>: Provide clear, accessible information about internal reporting channels</p><p><strong>Disclosurely Compliance</strong>:</p><ul><li>✅ Customizable information pages</li><li>✅ Reporting procedures documented</li><li>✅ Multi-language support</li><li>✅ Accessible design</li><li>✅ Clear explanations of process</li></ul><p><strong>What to Communicate</strong>:</p><ul><li>How to submit a report</li><li>Anonymous vs. confidential options</li><li>What information to include</li><li>Timeline expectations</li><li>Protection from retaliation</li><li>Follow-up process</li><li>External reporting options</li></ul><p><strong>Communication Methods</strong>:</p><ul><li>Employee handbook</li><li>Intranet/internal website</li><li>Email announcements</li><li>Training sessions</li><li>Posters in workplace</li><li>Onboarding for new employees</li></ul><h3 id="_9-prohibition-of-retaliation" tabindex="-1">9. Prohibition of Retaliation <a class="header-anchor" href="#_9-prohibition-of-retaliation" aria-label="Permalink to &quot;9. Prohibition of Retaliation&quot;">​</a></h3><p><strong>Articles 19-22 - Protection from Retaliation</strong></p><p><strong>Requirement</strong>: Prohibit and prevent retaliation against whistleblowers</p><p><strong>Types of Retaliation Prohibited</strong>:</p><ul><li>Suspension, dismissal, demotion</li><li>Withholding promotion or training</li><li>Negative performance evaluations</li><li>Coercion, intimidation, harassment</li><li>Discrimination</li><li>Unfavorable treatment</li><li>Damage to reputation</li></ul><p><strong>Disclosurely Features</strong>:</p><ul><li>✅ Anonymous reporting prevents identification</li><li>✅ Confidential reporting protects identity</li><li>✅ Retaliation flagging</li><li>✅ Monitoring capability</li><li>✅ Documentation of protections</li></ul><p><strong>Organizational Measures</strong>:</p><ul><li>Written anti-retaliation policy</li><li>Training for managers</li><li>Monitoring for retaliation signs</li><li>Separate retaliation reporting channel</li><li>Swift investigation of retaliation claims</li><li>Disciplinary action for retaliators</li><li>Remedies for affected reporters</li></ul><p><strong>Burden of Proof</strong>:</p><ul><li>Directive shifts burden to employer</li><li>Employer must prove adverse action was not retaliation</li><li>Protects reporters from having to prove retaliation</li></ul><h2 id="compliance-checklist" tabindex="-1">Compliance Checklist <a class="header-anchor" href="#compliance-checklist" aria-label="Permalink to &quot;Compliance Checklist&quot;">​</a></h2><h3 id="initial-setup" tabindex="-1">Initial Setup <a class="header-anchor" href="#initial-setup" aria-label="Permalink to &quot;Initial Setup&quot;">​</a></h3><p>✅ Establish internal reporting channel (Disclosurely portal) ✅ Enable anonymous reporting option ✅ Designate impartial person/team to handle reports ✅ Configure automatic acknowledgment ✅ Set up case assignment workflows ✅ Configure timeline alerts (7 days, 3 months) ✅ Create feedback templates ✅ Customize information pages for reporters ✅ Set up encryption and access controls ✅ Configure audit trail ✅ Establish data retention policies ✅ Create GDPR-compliant processing records</p><h3 id="policy-and-procedures" tabindex="-1">Policy and Procedures <a class="header-anchor" href="#policy-and-procedures" aria-label="Permalink to &quot;Policy and Procedures&quot;">​</a></h3><p>✅ Draft whistleblowing policy ✅ Document investigation procedures ✅ Create anti-retaliation policy ✅ Establish confidentiality protocols ✅ Define escalation procedures ✅ Set retention and deletion policies ✅ Document GDPR compliance measures</p><h3 id="communication-and-training" tabindex="-1">Communication and Training <a class="header-anchor" href="#communication-and-training" aria-label="Permalink to &quot;Communication and Training&quot;">​</a></h3><p>✅ Communicate reporting channels to all employees ✅ Provide information on reporting process ✅ Train designated personnel on investigation procedures ✅ Train managers on anti-retaliation ✅ Include in employee handbook ✅ Include in new employee onboarding ✅ Periodic reminders to staff</p><h3 id="ongoing-compliance" tabindex="-1">Ongoing Compliance <a class="header-anchor" href="#ongoing-compliance" aria-label="Permalink to &quot;Ongoing Compliance&quot;">​</a></h3><p>✅ Acknowledge reports within 7 days (automatic) ✅ Provide feedback within 3 months (or 6 if extended) ✅ Maintain confidentiality ✅ Document all investigation activities ✅ Protect against retaliation ✅ Maintain audit trail ✅ Conduct regular compliance reviews ✅ Update procedures as needed ✅ Respond to data subject requests ✅ Cooperate with authorities</p><h2 id="monitoring-compliance" tabindex="-1">Monitoring Compliance <a class="header-anchor" href="#monitoring-compliance" aria-label="Permalink to &quot;Monitoring Compliance&quot;">​</a></h2><h3 id="compliance-dashboard" tabindex="-1">Compliance Dashboard <a class="header-anchor" href="#compliance-dashboard" aria-label="Permalink to &quot;Compliance Dashboard&quot;">​</a></h3><p><strong>Disclosurely Provides</strong>:</p><p><strong>Acknowledgment Compliance</strong>:</p><ul><li>All reports acknowledged within 7 days</li><li>Late acknowledgments flagged (if system issue)</li><li>100% compliance rate typical (automatic)</li></ul><p><strong>Feedback Compliance</strong>:</p><ul><li>Cases approaching 3-month deadline</li><li>Cases past 3-month deadline (alert)</li><li>Cases extended to 6 months</li><li>Compliance rate by month</li><li>Average feedback time</li></ul><p><strong>Overall Metrics</strong>:</p><ul><li>Total reports received</li><li>Reports acknowledged timely</li><li>Feedback provided timely</li><li>Average investigation time</li><li>Compliance rate (%)</li></ul><p><strong>Access Dashboard</strong>:</p><ol><li>Dashboard &gt; Compliance &gt; EU Directive</li><li>View compliance metrics</li><li>Filter by date range</li><li>Export reports</li><li>Address any compliance gaps</li></ol><h3 id="alerts-and-reminders" tabindex="-1">Alerts and Reminders <a class="header-anchor" href="#alerts-and-reminders" aria-label="Permalink to &quot;Alerts and Reminders&quot;">​</a></h3><p><strong>Automatic Alerts</strong>:</p><p><strong>7-Day Acknowledgment</strong>:</p><ul><li>Acknowledgment sent automatically</li><li>Alert only if system failure (rare)</li></ul><p><strong>3-Month Feedback</strong>:</p><ul><li>60 days: First reminder</li><li>75 days: Second reminder</li><li>85 days: Urgent reminder</li><li>90 days: Overdue alert</li></ul><p><strong>Recipients</strong>:</p><ul><li>Assigned investigator</li><li>Compliance officer</li><li>Case handler</li><li>Administrator</li></ul><p><strong>Actions</strong>:</p><ul><li>Provide feedback to reporter</li><li>Extend to 6 months (document reason)</li><li>Escalate case if stuck</li></ul><h3 id="reporting" tabindex="-1">Reporting <a class="header-anchor" href="#reporting" aria-label="Permalink to &quot;Reporting&quot;">​</a></h3><p><strong>Generate Compliance Reports</strong>:</p><ol><li>Dashboard &gt; Reports &gt; Compliance</li><li>Select &quot;EU Directive Compliance Report&quot;</li><li>Choose date range</li><li>Include: <ul><li>Total reports</li><li>Acknowledgment compliance</li><li>Feedback compliance</li><li>Average timelines</li><li>Compliance rate</li></ul></li><li>Export PDF or CSV</li></ol><p><strong>Use For</strong>:</p><ul><li>Board reporting</li><li>Compliance committee meetings</li><li>Internal audits</li><li>External audits</li><li>Regulatory inquiries</li><li>Annual reviews</li></ul><h2 id="external-reporting-channels" tabindex="-1">External Reporting Channels <a class="header-anchor" href="#external-reporting-channels" aria-label="Permalink to &quot;External Reporting Channels&quot;">​</a></h2><h3 id="when-whistleblowers-may-go-external" tabindex="-1">When Whistleblowers May Go External <a class="header-anchor" href="#when-whistleblowers-may-go-external" aria-label="Permalink to &quot;When Whistleblowers May Go External&quot;">​</a></h3><p><strong>Article 10 - External Reporting Channels</strong></p><p>Reporters may go directly to authorities if:</p><ul><li>No internal channels available</li><li>Internal channels not functioning properly</li><li>Reasonable belief that retaliation will occur</li><li>Reasonable belief that breach may constitute imminent or manifest danger to public interest</li><li>Reasonable belief that investigation won&#39;t be handled appropriately</li><li>Previous internal report not followed up</li></ul><p><strong>Implication</strong>: Organizations should ensure internal channels are effective to encourage internal reporting first.</p><h3 id="competent-authorities" tabindex="-1">Competent Authorities <a class="header-anchor" href="#competent-authorities" aria-label="Permalink to &quot;Competent Authorities&quot;">​</a></h3><p>Each member state must designate authorities to receive external reports:</p><p><strong>Examples</strong>:</p><ul><li>Financial regulators</li><li>Data protection authorities</li><li>Environmental agencies</li><li>Health and safety authorities</li><li>Anti-corruption bodies</li></ul><p><strong>Your Responsibility</strong>:</p><ul><li>Inform employees about external reporting options</li><li>Provide contact information for relevant authorities</li><li>Include in whistleblowing policy</li><li>Do not discourage external reporting</li></ul><p><strong>In Disclosurely</strong>:</p><ul><li>Customize information pages to include external channels</li><li>Provide links to national authorities</li><li>Explain when external reporting appropriate</li><li>Demonstrate internal channels are effective</li></ul><h2 id="member-state-variations" tabindex="-1">Member State Variations <a class="header-anchor" href="#member-state-variations" aria-label="Permalink to &quot;Member State Variations&quot;">​</a></h2><h3 id="check-your-jurisdiction" tabindex="-1">Check Your Jurisdiction <a class="header-anchor" href="#check-your-jurisdiction" aria-label="Permalink to &quot;Check Your Jurisdiction&quot;">​</a></h3><p>While the Directive sets minimums, member states may:</p><ul><li>Extend protection to more categories of workers</li><li>Lower the 50-employee threshold</li><li>Add reporting channel requirements</li><li>Impose stricter timelines</li><li>Require additional measures</li><li>Impose penalties for non-compliance</li></ul><p><strong>Examples of Variations</strong>:</p><p><strong>Germany (HinSchG)</strong>:</p><ul><li>Requires external &quot;ombudsperson&quot; option for some organizations</li><li>Stricter penalties for non-compliance</li><li>Specific requirements for verbal reporting</li></ul><p><strong>France (Loi Waserman)</strong>:</p><ul><li>Applies to all organizations with 50+ employees (no sector exceptions)</li><li>Specific requirements for report handling</li><li>Integration with existing alert systems</li></ul><p><strong>Spain (Law 2/2023)</strong>:</p><ul><li>Detailed requirements for investigation procedures</li><li>Specific timeline for initiating investigation</li><li>Obligation to inform reporter of investigation start</li></ul><p><strong>Netherlands (House for Whistleblowers Act)</strong>:</p><ul><li>Strong focus on external authority</li><li>Specific investigation requirements</li><li>Integration with existing whistleblower infrastructure</li></ul><h3 id="consult-local-counsel" tabindex="-1">Consult Local Counsel <a class="header-anchor" href="#consult-local-counsel" aria-label="Permalink to &quot;Consult Local Counsel&quot;">​</a></h3><p><strong>Important</strong>: This documentation provides general guidance on the EU Directive. Always consult local employment and compliance counsel to ensure full compliance with your member state&#39;s specific implementation.</p><h2 id="penalties-for-non-compliance" tabindex="-1">Penalties for Non-Compliance <a class="header-anchor" href="#penalties-for-non-compliance" aria-label="Permalink to &quot;Penalties for Non-Compliance&quot;">​</a></h2><h3 id="member-state-penalties" tabindex="-1">Member State Penalties <a class="header-anchor" href="#member-state-penalties" aria-label="Permalink to &quot;Member State Penalties&quot;">​</a></h3><p>Each member state establishes penalties for:</p><ul><li>Failure to establish internal channels</li><li>Breaching confidentiality</li><li>Retaliating against whistleblowers</li><li>Obstructing reporting</li><li>Not following up on reports</li></ul><p><strong>Examples</strong>:</p><ul><li>Fines (amounts vary by member state)</li><li>Criminal penalties in some jurisdictions</li><li>Personal liability for managers</li><li>Injunctions to establish channels</li><li>Reputational damage</li></ul><h3 id="consequences" tabindex="-1">Consequences <a class="header-anchor" href="#consequences" aria-label="Permalink to &quot;Consequences&quot;">​</a></h3><p><strong>Beyond Regulatory Penalties</strong>:</p><ul><li>Damage to reputation</li><li>Loss of employee trust</li><li>Litigation from whistleblowers</li><li>Employment tribunal claims</li><li>Regulatory investigations</li><li>Media attention</li><li>Investor concerns</li></ul><p><strong>Best Practice</strong>: Proactive compliance is far less costly than reactive remediation.</p><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="go-beyond-minimum-requirements" tabindex="-1">Go Beyond Minimum Requirements <a class="header-anchor" href="#go-beyond-minimum-requirements" aria-label="Permalink to &quot;Go Beyond Minimum Requirements&quot;">​</a></h3><p><strong>Recommended</strong>:</p><ul><li>Encourage speak-up culture</li><li>Multiple reporting channels (in addition to Disclosurely)</li><li>Train all employees, not just investigators</li><li>Regular communication about channels</li><li>Anonymous reporting as default option</li><li>Faster timelines than required</li><li>Comprehensive anti-retaliation measures</li></ul><h3 id="regular-reviews" tabindex="-1">Regular Reviews <a class="header-anchor" href="#regular-reviews" aria-label="Permalink to &quot;Regular Reviews&quot;">​</a></h3><p><strong>Quarterly</strong>:</p><ul><li>Review compliance metrics</li><li>Address any overdue feedback</li><li>Check acknowledgment compliance</li><li>Assess reporter satisfaction</li><li>Update procedures if needed</li></ul><p><strong>Annually</strong>:</p><ul><li>Comprehensive policy review</li><li>Regulatory changes check</li><li>Training effectiveness assessment</li><li>Communicate with employees</li><li>Board reporting</li><li>External audit preparation</li></ul><h3 id="document-everything" tabindex="-1">Document Everything <a class="header-anchor" href="#document-everything" aria-label="Permalink to &quot;Document Everything&quot;">​</a></h3><p><strong>Maintain Records of</strong>:</p><ul><li>Whistleblowing policy</li><li>Procedures and workflows</li><li>Training provided</li><li>Communications to employees</li><li>Compliance reports</li><li>Policy reviews and updates</li><li>Regulatory consultations</li></ul><p><strong>Demonstrates</strong>:</p><ul><li>Good faith compliance effort</li><li>Proactive approach</li><li>Continuous improvement</li><li>Accountability</li></ul><h3 id="culture-of-speaking-up" tabindex="-1">Culture of Speaking Up <a class="header-anchor" href="#culture-of-speaking-up" aria-label="Permalink to &quot;Culture of Speaking Up&quot;">​</a></h3><p><strong>Beyond Compliance</strong>:</p><ul><li>Leadership commitment</li><li>Speak-up encouragement</li><li>No tolerance for retaliation</li><li>Recognition for reporters (appropriately)</li><li>Learning from reports</li><li>Continuous improvement</li><li>Transparency about outcomes (aggregated)</li></ul><hr><p><strong>Related:</strong></p><ul><li><a href="/docs/compliance/gdpr">GDPR Compliance</a></li><li><a href="/docs/compliance/retention">Data Retention</a></li><li><a href="/docs/admin/initial-setup">Initial Setup</a></li><li><a href="/docs/compliance/anti-retaliation">Anti-Retaliation Measures</a></li></ul>',197)])])}const g=e(l,[["render",a]]);export{d as __pageData,g as default};
