import{_ as e,c as t,o as l,ag as a}from"./chunks/framework.4QX5UEtQ.js";const d=JSON.parse('{"title":"GDPR Compliance - Disclosurely Data Protection Guide","description":"Complete GDPR compliance guide for whistleblowing platforms. Data subject rights, retention policies, audit trails, and privacy by design for EU data protection regulations.","frontmatter":{"title":"GDPR Compliance - Disclosurely Data Protection Guide","description":"Complete GDPR compliance guide for whistleblowing platforms. Data subject rights, retention policies, audit trails, and privacy by design for EU data protection regulations.","head":[["meta",{"property":"og:title","content":"GDPR Compliance for Whistleblowing - Disclosurely"}],["meta",{"property":"og:description","content":"Complete GDPR compliance guide for whistleblowing platforms. Data subject rights, retention policies, audit trails, and privacy by design for EU data protection regulations."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"GDPR Compliance - Disclosurely"}],["meta",{"name":"twitter:description","content":"Complete GDPR compliance guide for whistleblowing platforms. Data subject rights, retention policies, audit trails, and privacy by design for EU data protection regulations."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"compliance/gdpr.md","filePath":"compliance/gdpr.md"}'),o={name:"compliance/gdpr.md"};function r(n,i,s,c,u,p){return l(),t("div",null,[...i[0]||(i[0]=[a('<h1 id="gdpr-compliance" tabindex="-1">GDPR Compliance <a class="header-anchor" href="#gdpr-compliance" aria-label="Permalink to &quot;GDPR Compliance&quot;">​</a></h1><p>How Disclosurely helps you meet General Data Protection Regulation requirements.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Disclosurely is built for GDPR compliance:</p><ul><li><strong>Privacy by design</strong>: GDPR principles built-in</li><li><strong>Data minimization</strong>: Collect only what&#39;s needed</li><li><strong>Purpose limitation</strong>: Use data only for investigations</li><li><strong>Storage limitation</strong>: <a href="/docs/compliance/retention">Automated retention and deletion</a></li><li><strong>Integrity and confidentiality</strong>: <a href="/docs/security/overview">Military-grade encryption</a></li><li><strong>Accountability</strong>: <a href="/docs/compliance/audit-trail">Comprehensive audit trails</a></li></ul><h2 id="gdpr-principles-in-disclosurely" tabindex="-1">GDPR Principles in Disclosurely <a class="header-anchor" href="#gdpr-principles-in-disclosurely" aria-label="Permalink to &quot;GDPR Principles in Disclosurely&quot;">​</a></h2><h3 id="lawfulness-fairness-and-transparency" tabindex="-1">Lawfulness, Fairness, and Transparency <a class="header-anchor" href="#lawfulness-fairness-and-transparency" aria-label="Permalink to &quot;Lawfulness, Fairness, and Transparency&quot;">​</a></h3><p><strong>Lawful Basis</strong>:</p><ul><li><strong>Legitimate interest</strong>: Conducting workplace investigations</li><li><strong>Legal obligation</strong>: Compliance with whistleblowing laws</li><li><strong>Consent</strong>: Where required (confidential reports)</li><li><strong>Public interest</strong>: Preventing crime and misconduct</li></ul><p><strong>Transparency</strong>:</p><ul><li>Clear privacy notices</li><li>Explain data processing</li><li>Inform data subjects of their rights</li><li>Document legal basis</li></ul><p><strong>Fairness</strong>:</p><ul><li>No hidden processing</li><li>Expected use of data</li><li>Balanced interests</li><li>Proportionate collection</li></ul><h3 id="purpose-limitation" tabindex="-1">Purpose Limitation <a class="header-anchor" href="#purpose-limitation" aria-label="Permalink to &quot;Purpose Limitation&quot;">​</a></h3><p><strong>Data Used Only For</strong>:</p><ul><li>Investigating reported allegations</li><li>Communicating with reporters</li><li>Compliance with legal obligations</li><li>Statistical analysis (anonymized)</li><li>Improving investigation processes</li></ul><p><strong>Not Used For</strong>:</p><ul><li>Marketing</li><li>Unrelated purposes</li><li>Profiling</li><li>Automated decision-making (except AI assistance with human oversight)</li></ul><h3 id="data-minimization" tabindex="-1">Data Minimization <a class="header-anchor" href="#data-minimization" aria-label="Permalink to &quot;Data Minimization&quot;">​</a></h3><p><strong>Collect Only</strong>:</p><ul><li>Information necessary for investigation</li><li>Reporter identity (confidential reports only)</li><li>Relevant evidence</li><li>Communication history</li><li>Investigation notes</li></ul><p><strong>Do Not Collect</strong>:</p><ul><li>Unnecessary personal details</li><li>Special category data (unless relevant)</li><li>Disproportionate information</li><li>Irrelevant background</li></ul><p><strong>Anonymous Reports</strong>:</p><ul><li>No personal data collected</li><li>Maximum data minimization</li><li>Tracking ID only identifier</li><li>Cannot re-identify reporter</li></ul><h3 id="accuracy" tabindex="-1">Accuracy <a class="header-anchor" href="#accuracy" aria-label="Permalink to &quot;Accuracy&quot;">​</a></h3><p><strong>Ensuring Accuracy</strong>:</p><ul><li>Reporters control their information</li><li>Can update via secure messaging</li><li>Investigators verify facts</li><li>Corrections logged in audit trail</li><li>Outdated data flagged</li></ul><p><strong>Update Mechanisms</strong>:</p><ul><li>Reporter can correct information</li><li>Subject can dispute allegations</li><li>Evidence must be verified</li><li>Interview transcripts verified</li></ul><h3 id="storage-limitation" tabindex="-1">Storage Limitation <a class="header-anchor" href="#storage-limitation" aria-label="Permalink to &quot;Storage Limitation&quot;">​</a></h3><p><strong>Retention Periods</strong>:</p><ul><li>Configurable by case type</li><li>Default: 7 years</li><li>Automatic deletion after retention</li><li>Legal holds override deletion</li><li>Anonymization option</li></ul><p><strong>Automated Deletion</strong>:</p><ul><li>System calculates deletion date</li><li><a href="/docs/cases/archiving">Alerts before deletion</a></li><li>Requires approval</li><li>Secure destruction</li><li>Deletion certificate generated</li></ul><h3 id="integrity-and-confidentiality" tabindex="-1">Integrity and Confidentiality <a class="header-anchor" href="#integrity-and-confidentiality" aria-label="Permalink to &quot;Integrity and Confidentiality&quot;">​</a></h3><p><strong>Encryption</strong>:</p><ul><li><a href="/docs/reporting/encryption">AES-256 encryption at rest</a></li><li>TLS 1.3 for data in transit</li><li>Zero-knowledge architecture</li><li>End-to-end encryption</li><li>Encrypted backups</li></ul><p><strong>Access Controls</strong>:</p><ul><li>Role-based permissions</li><li>Least privilege principle</li><li>Multi-factor authentication</li><li>Session management</li><li>Access logged in <a href="/docs/compliance/audit-trail">audit trail</a></li></ul><p><strong>Security Measures</strong>:</p><ul><li>ISO 27001 certified</li><li>SOC 2 Type II compliant</li><li>Regular penetration testing</li><li>Vulnerability scanning</li><li>Security monitoring</li></ul><h3 id="accountability" tabindex="-1">Accountability <a class="header-anchor" href="#accountability" aria-label="Permalink to &quot;Accountability&quot;">​</a></h3><p><strong>Demonstrate Compliance</strong>:</p><ul><li>Data processing records</li><li>Privacy impact assessments</li><li>Data protection policies</li><li>Audit trails</li><li>Compliance reports</li><li>Training records</li></ul><h2 id="data-subject-rights" tabindex="-1">Data Subject Rights <a class="header-anchor" href="#data-subject-rights" aria-label="Permalink to &quot;Data Subject Rights&quot;">​</a></h2><h3 id="right-of-access-article-15" tabindex="-1">Right of Access (Article 15) <a class="header-anchor" href="#right-of-access-article-15" aria-label="Permalink to &quot;Right of Access (Article 15)&quot;">​</a></h3><p><strong>What Data Subjects Can Request</strong>:</p><ul><li>Copy of their personal data</li><li>How data is being processed</li><li>Who has accessed their data</li><li>Retention period</li><li>Rights to rectify or erase</li></ul><p><strong>Disclosurely Features</strong>:</p><p><strong>For Confidential Reporters</strong>:</p><ol><li>Log in to account</li><li>Navigate to &quot;My Data&quot;</li><li>View all personal data held</li><li>Download data export (JSON/PDF)</li><li>View access log (who viewed case)</li></ol><p><strong>For Anonymous Reporters</strong>:</p><ul><li>Cannot identify reporter to respond</li><li>No personal data collected (by design)</li></ul><p><strong>For Subjects of Investigation</strong>:</p><ul><li>Submit data access request</li><li>Verify identity</li><li>Receive data held about them</li><li>Subject to exemptions (investigation in progress)</li></ul><p><strong>Timeline</strong>: 1 month to respond</p><p><strong>Exemptions</strong>:</p><ul><li>Ongoing investigation may delay response</li><li>Cannot disclose reporter identity</li><li>Cannot compromise investigation</li><li>Document exemption reasoning</li></ul><h3 id="right-to-rectification-article-16" tabindex="-1">Right to Rectification (Article 16) <a class="header-anchor" href="#right-to-rectification-article-16" aria-label="Permalink to &quot;Right to Rectification (Article 16)&quot;">​</a></h3><p><strong>What Can Be Corrected</strong>:</p><ul><li>Factual errors in report</li><li>Personal details</li><li>Evidence annotations</li><li>Contact information</li></ul><p><strong>How to Request</strong>:</p><p><strong>Confidential Reporters</strong>:</p><ul><li>Send message via secure messaging</li><li>Specify what needs correction</li><li>Investigator reviews and updates</li><li>Logged in audit trail</li></ul><p><strong>Subjects</strong>:</p><ul><li>Submit rectification request</li><li>Provide correct information</li><li>Investigator reviews</li><li>Update if appropriate</li><li>Note in case file if disputed</li></ul><p><strong>Not Applicable To</strong>:</p><ul><li>Opinions (subject can dispute)</li><li>Allegations themselves</li><li>Investigation findings</li><li>Subjective assessments</li></ul><h3 id="right-to-erasure-right-to-be-forgotten-article-17" tabindex="-1">Right to Erasure / &quot;Right to be Forgotten&quot; (Article 17) <a class="header-anchor" href="#right-to-erasure-right-to-be-forgotten-article-17" aria-label="Permalink to &quot;Right to Erasure / &quot;Right to be Forgotten&quot; (Article 17)&quot;">​</a></h3><p><strong>When Erasure Required</strong>:</p><ul><li>Data no longer necessary</li><li>Consent withdrawn (if consent was basis)</li><li>Objection and no overriding legitimate interest</li><li>Processed unlawfully</li><li>Legal obligation to erase</li></ul><p><strong>When Erasure NOT Required</strong>:</p><ul><li>Compliance with legal obligation (investigations)</li><li>Public interest (serious misconduct)</li><li>Legal claims (potential litigation)</li><li>During retention period</li></ul><p><strong>Disclosurely Process</strong>:</p><ol><li><p><strong>Request Received</strong></p><ul><li>Via email or in-app</li><li>Identity verified</li><li>Logged in system</li></ul></li><li><p><strong>Evaluation</strong></p><ul><li>Legal team reviews</li><li>Assess legal basis for processing</li><li>Check exemptions</li><li>Consider retention obligations</li></ul></li><li><p><strong>Decision</strong></p><ul><li>Erase if required</li><li>Explain if exemption applies</li><li>Partial erasure if appropriate</li><li>Document reasoning</li></ul></li><li><p><strong>Implementation</strong></p><ul><li>Secure deletion if approved</li><li>Anonymization alternative</li><li>Update audit trail</li><li>Notify data subject</li></ul></li></ol><p><strong>Timeline</strong>: 1 month to respond</p><h3 id="right-to-restriction-of-processing-article-18" tabindex="-1">Right to Restriction of Processing (Article 18) <a class="header-anchor" href="#right-to-restriction-of-processing-article-18" aria-label="Permalink to &quot;Right to Restriction of Processing (Article 18)&quot;">​</a></h3><p><strong>When Applicable</strong>:</p><ul><li>Accuracy disputed (restrict while verifying)</li><li>Processing unlawful but subject prefers restriction to erasure</li><li>No longer need data but subject needs it for legal claims</li><li>Objection lodged (restrict while assessing)</li></ul><p><strong>Effect</strong>:</p><ul><li>Data stored but not processed</li><li>Only with subject&#39;s consent or for legal claims</li><li>Must inform before lifting restriction</li></ul><p><strong>Disclosurely Implementation</strong>:</p><ul><li>&quot;Restricted Processing&quot; flag on case</li><li>Data viewable but not used</li><li>Cannot update case</li><li>Alerts when accessed</li><li>Subject notified before restriction lifted</li></ul><h3 id="right-to-data-portability-article-20" tabindex="-1">Right to Data Portability (Article 20) <a class="header-anchor" href="#right-to-data-portability-article-20" aria-label="Permalink to &quot;Right to Data Portability (Article 20)&quot;">​</a></h3><p><strong>Scope</strong>:</p><ul><li>Data provided by subject</li><li>Automated processing</li><li>Based on consent or contract</li><li>Technically feasible</li></ul><p><strong>Disclosurely Export</strong>:</p><ul><li>Structured format (JSON)</li><li>Machine-readable</li><li>Commonly used format</li><li>Direct transfer where possible</li></ul><p><strong>Export Contents</strong>:</p><ul><li>Report content</li><li>Messages sent</li><li>Files uploaded</li><li>Account information</li><li>Not investigation notes (not provided by subject)</li></ul><p><strong>How to Export</strong>:</p><ol><li>Account settings &gt; &quot;Export My Data&quot;</li><li>Select data to include</li><li>Choose format (JSON, CSV)</li><li>Download file</li><li>Transfer to another service if desired</li></ol><h3 id="right-to-object-article-21" tabindex="-1">Right to Object (Article 21) <a class="header-anchor" href="#right-to-object-article-21" aria-label="Permalink to &quot;Right to Object (Article 21)&quot;">​</a></h3><p><strong>Grounds for Objection</strong>:</p><ul><li>Processing based on legitimate interests</li><li>Direct marketing (not applicable)</li><li>Scientific/historical research</li><li>Particular situation of data subject</li></ul><p><strong>Disclosurely Response</strong>:</p><ol><li><p><strong>Receive Objection</strong></p><ul><li>Via email or in-app</li><li>Document grounds</li></ul></li><li><p><strong>Assess</strong></p><ul><li>Compelling legitimate grounds?</li><li>Legal claims potential?</li><li>Public interest?</li><li>Overriding interests?</li></ul></li><li><p><strong>Decision</strong></p><ul><li>Stop processing if no compelling grounds</li><li>Continue if legal obligation or legitimate grounds</li><li>Explain decision</li><li>Inform of right to complain to supervisory authority</li></ul></li></ol><h3 id="rights-related-to-automated-decision-making-article-22" tabindex="-1">Rights Related to Automated Decision-Making (Article 22) <a class="header-anchor" href="#rights-related-to-automated-decision-making-article-22" aria-label="Permalink to &quot;Rights Related to Automated Decision-Making (Article 22)&quot;">​</a></h3><p><strong>Disclosurely&#39;s Approach</strong>:</p><ul><li>No solely automated decisions</li><li>AI assists but humans decide</li><li>Meaningful human oversight</li><li>Can request human review</li><li>Can express point of view</li><li>Can contest decision</li></ul><p><strong>AI Case Helper</strong>:</p><ul><li>Provides recommendations only</li><li>Investigator makes final decision</li><li>Can override AI suggestions</li><li>Subject can challenge findings</li><li>Human review always available</li></ul><h2 id="data-processing-records" tabindex="-1">Data Processing Records <a class="header-anchor" href="#data-processing-records" aria-label="Permalink to &quot;Data Processing Records&quot;">​</a></h2><h3 id="article-30-records" tabindex="-1">Article 30 Records <a class="header-anchor" href="#article-30-records" aria-label="Permalink to &quot;Article 30 Records&quot;">​</a></h3><p><strong>Required Documentation</strong>:</p><p><strong>Controller Information</strong>:</p><ul><li>Organization name and contact</li><li>Data Protection Officer contact</li><li>Processing purposes</li><li>Data subject categories</li><li>Personal data categories</li><li>Recipient categories</li><li>International transfers</li><li>Retention periods</li><li>Security measures</li></ul><p><strong>Disclosurely Maintains</strong>:</p><ul><li>Organization profile</li><li>DPO designation</li><li>Processing purposes (investigations)</li><li>Categories (reporters, subjects, witnesses)</li><li>Data types (reports, evidence, messages)</li><li>Recipients (investigators, management)</li><li>Transfers (if applicable)</li><li>Retention policies</li><li>Security documentation</li></ul><p><strong>Access</strong>:</p><ul><li>Dashboard &gt; Compliance &gt; GDPR Records</li><li>Generate Article 30 report</li><li>Export for supervisory authority</li><li>Update as needed</li></ul><h3 id="data-protection-impact-assessment-dpia" tabindex="-1">Data Protection Impact Assessment (DPIA) <a class="header-anchor" href="#data-protection-impact-assessment-dpia" aria-label="Permalink to &quot;Data Protection Impact Assessment (DPIA)&quot;">​</a></h3><p><strong>When Required</strong>:</p><ul><li>High risk processing</li><li>Systematic monitoring</li><li>Large scale special category data</li><li>New technologies</li></ul><p><strong>Disclosurely Use Cases Requiring DPIA</strong>:</p><ul><li>Implementing Disclosurely initially</li><li>Processing sensitive allegations</li><li>Using AI Case Helper</li><li>Custom integrations</li></ul><p><strong>Disclosurely Provides</strong>:</p><ul><li>DPIA template</li><li>Risk assessment guide</li><li>Mitigation recommendations</li><li>Regular review prompts</li></ul><p><strong>DPIA Process</strong>:</p><ol><li>Describe processing operations</li><li>Assess necessity and proportionality</li><li>Identify risks to data subjects</li><li>Evaluate measures to address risks</li><li>Document and review</li></ol><h3 id="data-processing-agreements-dpas" tabindex="-1">Data Processing Agreements (DPAs) <a class="header-anchor" href="#data-processing-agreements-dpas" aria-label="Permalink to &quot;Data Processing Agreements (DPAs)&quot;">​</a></h3><p><strong>Disclosurely as Processor</strong>:</p><ul><li>You (customer) are the controller</li><li>Disclosurely is the processor</li><li>DPA required by Article 28</li></ul><p><strong>DPA Includes</strong>:</p><ul><li>Subject matter and duration</li><li>Nature and purpose of processing</li><li>Type of personal data</li><li>Categories of data subjects</li><li>Controller and processor obligations</li><li>Security measures</li><li>Sub-processor arrangements</li><li>Data subject rights assistance</li><li>Audit rights</li><li>Deletion obligations</li></ul><p><strong>Access DPA</strong>:</p><ul><li>Dashboard &gt; Legal &gt; Data Processing Agreement</li><li>Review and accept</li><li>Download signed copy</li><li>Attached to subscription</li></ul><h2 id="international-data-transfers" tabindex="-1">International Data Transfers <a class="header-anchor" href="#international-data-transfers" aria-label="Permalink to &quot;International Data Transfers&quot;">​</a></h2><h3 id="data-location" tabindex="-1">Data Location <a class="header-anchor" href="#data-location" aria-label="Permalink to &quot;Data Location&quot;">​</a></h3><p><strong>Disclosurely Hosting</strong>:</p><ul><li>EU/UK customers: Data in EU</li><li>US customers: Data in US</li><li>Choice of region</li><li>No transfers without consent</li></ul><p><strong>Sub-Processors</strong>:</p><ul><li>Vetted and approved</li><li>Data processing agreements</li><li>Adequate safeguards</li><li>Listed in documentation</li></ul><h3 id="transfer-mechanisms" tabindex="-1">Transfer Mechanisms <a class="header-anchor" href="#transfer-mechanisms" aria-label="Permalink to &quot;Transfer Mechanisms&quot;">​</a></h3><p><strong>If Transfer Needed</strong>:</p><p><strong>EU to Third Countries</strong>:</p><ul><li>Adequacy decision (UK, Switzerland)</li><li>Standard Contractual Clauses (SCCs)</li><li>Binding Corporate Rules</li><li>Subject consent (rare)</li></ul><p><strong>UK Post-Brexit</strong>:</p><ul><li>UK considered adequate by EU</li><li>EU considered adequate by UK</li><li>Seamless transfers</li></ul><p><strong>Disclosurely Provides</strong>:</p><ul><li>SCCs if needed</li><li>Transfer impact assessment</li><li>Alternative safeguards</li><li>Documentation</li></ul><h2 id="data-breaches" tabindex="-1">Data Breaches <a class="header-anchor" href="#data-breaches" aria-label="Permalink to &quot;Data Breaches&quot;">​</a></h2><h3 id="breach-notification-obligations" tabindex="-1">Breach Notification Obligations <a class="header-anchor" href="#breach-notification-obligations" aria-label="Permalink to &quot;Breach Notification Obligations&quot;">​</a></h3><p><strong>Timeline</strong>:</p><ul><li>Discover breach</li><li>Assess severity</li><li>Notify supervisory authority within 72 hours (if high risk)</li><li>Notify data subjects without undue delay (if high risk to rights)</li></ul><p><strong>Disclosurely Process</strong>:</p><ol><li><p><strong>Detection</strong></p><ul><li>Security monitoring</li><li>Automated alerts</li><li>User reports</li><li>Regular audits</li></ul></li><li><p><strong>Containment</strong></p><ul><li>Immediate action to stop breach</li><li>Preserve evidence</li><li>Prevent further damage</li></ul></li><li><p><strong>Assessment</strong></p><ul><li>What data affected?</li><li>How many data subjects?</li><li>Risk to rights and freedoms?</li><li>Notification required?</li></ul></li><li><p><strong>Notification</strong></p><ul><li>Supervisory authority (if required)</li><li>Affected data subjects (if high risk)</li><li>Customer organizations</li><li>Documentation</li></ul></li><li><p><strong>Remediation</strong></p><ul><li>Fix vulnerability</li><li>Implement additional controls</li><li>Monitor for recurrence</li><li>Document lessons learned</li></ul></li></ol><p><strong>Customer Responsibilities</strong>:</p><ul><li>Assess impact on your processing</li><li>Notify your supervisory authority if controller</li><li>Notify affected data subjects if required</li><li>Cooperate with Disclosurely on investigation</li></ul><h3 id="breach-prevention" tabindex="-1">Breach Prevention <a class="header-anchor" href="#breach-prevention" aria-label="Permalink to &quot;Breach Prevention&quot;">​</a></h3><p><strong>Disclosurely Security</strong>:</p><ul><li>Encryption at rest and in transit</li><li>Access controls and MFA</li><li>Security monitoring</li><li>Regular penetration testing</li><li>Vulnerability management</li><li>Incident response plan</li><li>Staff training</li><li>ISO 27001 certified</li></ul><h2 id="gdpr-compliance-tools" tabindex="-1">GDPR Compliance Tools <a class="header-anchor" href="#gdpr-compliance-tools" aria-label="Permalink to &quot;GDPR Compliance Tools&quot;">​</a></h2><h3 id="automated-data-deletion" tabindex="-1">Automated Data Deletion <a class="header-anchor" href="#automated-data-deletion" aria-label="Permalink to &quot;Automated Data Deletion&quot;">​</a></h3><p><strong>Configuration</strong>:</p><ol><li>Set retention period by case type</li><li>System calculates deletion date</li><li>Alerts 90, 30, 7 days before deletion</li><li>Approve or extend</li><li>Automatic secure deletion</li><li>Certificate of deletion</li></ol><p><strong>GDPR Benefit</strong>: Demonstrates storage limitation principle</p><h3 id="access-request-portal" tabindex="-1">Access Request Portal <a class="header-anchor" href="#access-request-portal" aria-label="Permalink to &quot;Access Request Portal&quot;">​</a></h3><p><strong>Data Subject Requests</strong>:</p><ol><li>Submit request via form</li><li>Identity verification</li><li>Automated data gathering</li><li>Legal review for exemptions</li><li>Response generated</li><li>30-day timeline tracking</li></ol><p><strong>Reduces Burden</strong>: Automates most of GDPR rights requests</p><h3 id="privacy-notices" tabindex="-1">Privacy Notices <a class="header-anchor" href="#privacy-notices" aria-label="Permalink to &quot;Privacy Notices&quot;">​</a></h3><p><strong>Customizable Templates</strong>:</p><ul><li>Reporting page privacy notice</li><li>Reporter privacy notice</li><li>Subject privacy notice</li><li>Staff privacy notice</li></ul><p><strong>Required Content</strong>:</p><ul><li>Controller identity</li><li>Processing purposes</li><li>Legal basis</li><li>Recipients</li><li>Retention</li><li>Data subject rights</li><li>Right to complain</li></ul><p><strong>Access</strong>:</p><ul><li>Dashboard &gt; Settings &gt; Privacy Notices</li><li>Edit templates</li><li>Multiple languages</li><li>Version control</li></ul><h3 id="consent-management" tabindex="-1">Consent Management <a class="header-anchor" href="#consent-management" aria-label="Permalink to &quot;Consent Management&quot;">​</a></h3><p><strong>For Confidential Reports</strong>:</p><ul><li>Optional consent for identity disclosure</li><li>Granular consent options</li><li>Withdrawal mechanism</li><li>Audit trail of consent</li><li>Time-stamped</li></ul><p><strong>Not Required for</strong>:</p><ul><li>Anonymous reports (no personal data)</li><li>Legal obligation processing</li><li>Legitimate interest processing</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="designate-a-dpo" tabindex="-1">Designate a DPO <a class="header-anchor" href="#designate-a-dpo" aria-label="Permalink to &quot;Designate a DPO&quot;">​</a></h3><p><strong>When Required</strong>:</p><ul><li>Public authority processing</li><li>Large scale systematic monitoring</li><li>Large scale special category data</li></ul><p><strong>DPO Responsibilities</strong>:</p><ul><li>Monitor GDPR compliance</li><li>Advise on DPIAs</li><li>Cooperate with supervisory authority</li><li>Contact point for data subjects</li></ul><p><strong>In Disclosurely</strong>:</p><ul><li>Assign DPO role to user</li><li>DPO dashboard access</li><li>Compliance reports</li><li>Alert notifications</li></ul><h3 id="regular-compliance-reviews" tabindex="-1">Regular Compliance Reviews <a class="header-anchor" href="#regular-compliance-reviews" aria-label="Permalink to &quot;Regular Compliance Reviews&quot;">​</a></h3><p><strong>Quarterly</strong>:</p><ul><li>Review data processing records</li><li>Check retention and deletion</li><li>Assess data subject requests</li><li>Review privacy notices</li><li>Staff training check</li></ul><p><strong>Annually</strong>:</p><ul><li>Update DPIAs</li><li>Review DPAs</li><li>Audit security measures</li><li>Assess new processing activities</li><li>Train staff on GDPR</li></ul><h3 id="document-everything" tabindex="-1">Document Everything <a class="header-anchor" href="#document-everything" aria-label="Permalink to &quot;Document Everything&quot;">​</a></h3><p><strong>Maintain Records</strong>:</p><ul><li>Legal basis for processing</li><li>DPIA outcomes</li><li>Data subject requests and responses</li><li>Breach assessments</li><li>Deletion certificates</li><li>Staff training</li><li>Policy updates</li></ul><p><strong>Demonstrates Accountability</strong>: Can show supervisory authority</p><h3 id="training" tabindex="-1">Training <a class="header-anchor" href="#training" aria-label="Permalink to &quot;Training&quot;">​</a></h3><p><strong>Who Needs Training</strong>:</p><ul><li>All Disclosurely users</li><li>Investigators especially</li><li>HR team</li><li>Compliance team</li><li>Senior management</li></ul><p><strong>Topics</strong>:</p><ul><li>GDPR principles</li><li>Data subject rights</li><li>Breach notification</li><li>Secure handling of data</li><li>Retention and deletion</li><li>Using Disclosurely compliantly</li></ul><p><strong>Disclosurely Resources</strong>:</p><ul><li>Training videos</li><li>Documentation</li><li>Best practice guides</li><li>Compliance checklists</li></ul><h2 id="supervisory-authorities" tabindex="-1">Supervisory Authorities <a class="header-anchor" href="#supervisory-authorities" aria-label="Permalink to &quot;Supervisory Authorities&quot;">​</a></h2><h3 id="uk-information-commissioner-s-office-ico" tabindex="-1">UK Information Commissioner&#39;s Office (ICO) <a class="header-anchor" href="#uk-information-commissioner-s-office-ico" aria-label="Permalink to &quot;UK Information Commissioner&#39;s Office (ICO)&quot;">​</a></h3><p><strong>Contact</strong>:</p><ul><li>Website: ico.org.uk</li><li>Telephone: 0303 123 1113</li><li>Report concerns online</li></ul><p><strong>Responsibilities</strong>:</p><ul><li>Enforce GDPR in UK</li><li>Investigate complaints</li><li>Issue guidance</li><li>Impose fines</li></ul><h3 id="eu-data-protection-authorities" tabindex="-1">EU Data Protection Authorities <a class="header-anchor" href="#eu-data-protection-authorities" aria-label="Permalink to &quot;EU Data Protection Authorities&quot;">​</a></h3><p><strong>Each EU Country</strong>:</p><ul><li>National supervisory authority</li><li>Lead authority for cross-border processing</li><li>Cooperation mechanisms</li></ul><p><strong>Find Your Authority</strong>:</p><ul><li>European Data Protection Board website</li><li>Lists all EU/EEA authorities</li><li>Contact details</li></ul><h3 id="filing-a-complaint" tabindex="-1">Filing a Complaint <a class="header-anchor" href="#filing-a-complaint" aria-label="Permalink to &quot;Filing a Complaint&quot;">​</a></h3><p><strong>Data Subjects Can</strong>:</p><ul><li>Complain to supervisory authority</li><li>Disclosurely must cooperate</li><li>May result in investigation</li><li>Can appeal decisions</li></ul><p><strong>How to Avoid</strong>:</p><ul><li>Respond promptly to requests</li><li>Respect data subject rights</li><li>Be transparent</li><li>Document compliance</li><li>Seek legal advice when uncertain</li></ul><hr><p><strong>Related:</strong></p><ul><li><a href="/docs/compliance/audit-trail">Audit Trail</a></li><li><a href="/docs/compliance/retention">Data Retention</a></li><li><a href="/docs/security/overview">Security Overview</a></li><li><a href="/docs/reporting/encryption">Privacy &amp; Encryption</a></li></ul>',217)])])}const h=e(o,[["render",r]]);export{d as __pageData,h as default};
