import{_ as t,c as i,o as a,ag as r}from"./chunks/framework.4QX5UEtQ.js";const g=JSON.parse('{"title":"Draft Management - Save & Resume Reports | Disclosurely","description":"Save partially completed whistleblowing reports as drafts, resume from any device using draft codes, automatic 30-day expiration, and secure cross-device access.","frontmatter":{"title":"Draft Management - Save & Resume Reports | Disclosurely","description":"Save partially completed whistleblowing reports as drafts, resume from any device using draft codes, automatic 30-day expiration, and secure cross-device access.","head":[["meta",{"property":"og:title","content":"Draft Management - Save & Resume Reports | Disclosurely"}],["meta",{"property":"og:description","content":"Save partially completed whistleblowing reports as drafts, resume from any device using draft codes, automatic 30-day expiration, and secure cross-device access."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Draft Management - Disclosurely"}],["meta",{"name":"twitter:description","content":"Save partially completed whistleblowing reports as drafts, resume from any device using draft codes, automatic 30-day expiration, and secure cross-device access."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"features/draft-management.md","filePath":"features/draft-management.md"}'),o={name:"features/draft-management.md"};function n(s,e,l,d,p,c){return a(),i("div",null,[...e[0]||(e[0]=[r('<h1 id="draft-management" tabindex="-1">Draft Management <a class="header-anchor" href="#draft-management" aria-label="Permalink to &quot;Draft Management&quot;">​</a></h1><p>Save partially completed reports as drafts and resume from any device using secure draft codes—ensuring reporters never lose their progress.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Draft Management allows whistleblowers to save incomplete reports and return to finish them later, even from different devices. This is especially valuable for complex reports requiring evidence gathering, witness consultation, or simply more time to compose thoughtful allegations.</p><p><strong>Access:</strong> Available during <a href="/docs/reporting/how-to-submit">report submission</a> process</p><h2 id="how-draft-saving-works" tabindex="-1">How Draft Saving Works <a class="header-anchor" href="#how-draft-saving-works" aria-label="Permalink to &quot;How Draft Saving Works&quot;">​</a></h2><h3 id="saving-a-draft" tabindex="-1">Saving a Draft <a class="header-anchor" href="#saving-a-draft" aria-label="Permalink to &quot;Saving a Draft&quot;">​</a></h3><p><strong>During Report Submission:</strong></p><p>At any point while filling out a report, reporters can save their progress:</p><ol><li>Click <strong>&quot;Save Draft&quot;</strong> button (visible on all form steps)</li><li>System immediately saves all entered information</li><li>Unique draft code is generated (format: DFT-XXXXXXXX)</li><li>Reporter can copy code or receive it via optional email</li><li>Draft stored securely for 30 days</li></ol><p><strong>What Gets Saved:</strong></p><ul><li>Report title and description</li><li>Selected category and priority</li><li>Incident date, time, and location</li><li>Uploaded evidence files</li><li>Witness information</li><li>All custom field entries</li><li>Current form step/progress</li></ul><p><strong>What Doesn&#39;t Get Saved:</strong></p><ul><li>Reporter contact information (for anonymous reports)</li><li>Temporary session data</li><li>Browser-specific settings</li></ul><h3 id="automatic-draftsaving" tabindex="-1">Automatic Draftsaving <a class="header-anchor" href="#automatic-draftsaving" aria-label="Permalink to &quot;Automatic Draftsaving&quot;">​</a></h3><p><strong>Auto-Save Feature:</strong></p><p>For reporter convenience, drafts are automatically saved every 2 minutes while composing:</p><p><strong>Behavior:</strong></p><ul><li>Silent background saving (no interruption)</li><li>Visual indicator: &quot;Draft auto-saved at [time]&quot;</li><li>Uses same draft code if already generated</li><li>Creates new draft code if first auto-save</li><li>Continues until reporter submits or abandons form</li></ul><p><strong>Benefits:</strong></p><ul><li>Protection against browser crashes</li><li>Prevents data loss from network issues</li><li>Reduces reporter frustration</li><li>Encourages thorough reporting</li></ul><p><strong>Disable Auto-Save:</strong> Reporters can disable auto-save in form settings if preferred (not recommended).</p><h3 id="draft-codes" tabindex="-1">Draft Codes <a class="header-anchor" href="#draft-codes" aria-label="Permalink to &quot;Draft Codes&quot;">​</a></h3><p><strong>Format:</strong> DFT-XXXXXXXX (e.g., DFT-K7M9P2N5)</p><p><strong>Characteristics:</strong></p><ul><li>Unique 8-character alphanumeric code</li><li>Case-insensitive (DFT-ABC123 = dft-abc123)</li><li>Cryptographically random (cannot be guessed)</li><li>Single-use after submission</li><li>No personal information encoded</li></ul><p><strong>Security:</strong></p><ul><li>Treat like a password—anyone with code can access draft</li><li>Not stored in browser history</li><li>Encrypted in transit and at rest</li><li>Cannot be recovered if lost</li></ul><h2 id="resuming-a-draft" tabindex="-1">Resuming a Draft <a class="header-anchor" href="#resuming-a-draft" aria-label="Permalink to &quot;Resuming a Draft&quot;">​</a></h2><h3 id="how-to-resume" tabindex="-1">How to Resume <a class="header-anchor" href="#how-to-resume" aria-label="Permalink to &quot;How to Resume&quot;">​</a></h3><p><strong>From Any Device:</strong></p><ol><li>Navigate to reporting portal</li><li>Click <strong>&quot;Resume Draft&quot;</strong> link</li><li>Enter draft code (DFT-XXXXXXXX)</li><li>Click <strong>&quot;Load Draft&quot;</strong></li><li>Form restores to exact state when saved</li><li>Continue editing and submit when ready</li></ol><p><strong>Direct Link:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://yourorg.disclosurely.com/resume-draft</span></span></code></pre></div><p><strong>Alternative Access Points:</strong></p><ul><li>From reporting form: &quot;Have a draft code? Resume here&quot;</li><li>From email notification (if email provided)</li><li>From SMS (if phone number provided, Pro plan)</li></ul><h3 id="draft-validation" tabindex="-1">Draft Validation <a class="header-anchor" href="#draft-validation" aria-label="Permalink to &quot;Draft Validation&quot;">​</a></h3><p><strong>When Resuming:</strong></p><p>System performs several validation checks:</p><p><strong>Code Validation:</strong></p><ul><li>Verifies code format is correct</li><li>Confirms code exists in system</li><li>Checks draft hasn&#39;t expired</li><li>Ensures draft not already submitted</li></ul><p><strong>Security Checks:</strong></p><ul><li>Rate limiting (3 attempts per 15 minutes)</li><li>CAPTCHA after 2 failed attempts</li><li>IP-based abuse detection</li><li>Temporary lockout after 5 failed attempts</li></ul><p><strong>Expiration Check:</strong></p><ul><li>Drafts valid for 30 days from creation</li><li>Expired drafts cannot be resumed</li><li>Warning shown 7 days before expiration (if email provided)</li></ul><h3 id="multi-device-support" tabindex="-1">Multi-Device Support <a class="header-anchor" href="#multi-device-support" aria-label="Permalink to &quot;Multi-Device Support&quot;">​</a></h3><p><strong>Cross-Device Access:</strong></p><p>Drafts can be resumed from any device:</p><p><strong>Supported Platforms:</strong></p><ul><li>Desktop (Windows, Mac, Linux)</li><li>Mobile (iOS, Android)</li><li>Tablets (iPad, Android tablets)</li><li>Any modern web browser</li></ul><p><strong>Automatic Syncing:</strong></p><ul><li>Changes saved in real-time</li><li>No manual sync required</li><li>Always access latest version</li><li>Concurrent editing not supported (last save wins)</li></ul><p><strong>Use Cases:</strong></p><ul><li>Start on work computer, finish on personal device</li><li>Begin on mobile, complete on desktop</li><li>Share draft code with witness for collaborative reporting</li><li>Resume after network interruption</li></ul><h2 id="draft-expiration" tabindex="-1">Draft Expiration <a class="header-anchor" href="#draft-expiration" aria-label="Permalink to &quot;Draft Expiration&quot;">​</a></h2><h3 id="_30-day-retention-period" tabindex="-1">30-Day Retention Period <a class="header-anchor" href="#_30-day-retention-period" aria-label="Permalink to &quot;30-Day Retention Period&quot;">​</a></h3><p><strong>Automatic Expiration:</strong></p><p>Drafts are automatically deleted 30 days after creation:</p><p><strong>Why 30 Days:</strong></p><ul><li>Balances convenience with security</li><li>Prevents indefinite storage of incomplete reports</li><li>Encourages timely reporting</li><li>Reduces data storage and privacy risks</li></ul><p><strong>Expiration Process:</strong></p><ol><li>Draft created on Day 0</li><li>Daily expiration checks run at midnight UTC</li><li>Email warning sent on Day 23 (if email provided)</li><li>Final reminder on Day 28</li><li>Draft deleted on Day 30</li><li>Draft code becomes invalid</li></ol><p><strong>After Expiration:</strong></p><ul><li>Draft code no longer works</li><li>All saved data permanently deleted</li><li>Cannot be recovered</li><li>Reporter must start new report</li></ul><h3 id="extending-draft-expiration" tabindex="-1">Extending Draft Expiration <a class="header-anchor" href="#extending-draft-expiration" aria-label="Permalink to &quot;Extending Draft Expiration&quot;">​</a></h3><p><strong>Not Currently Supported:</strong></p><p>Draft expiration cannot be extended beyond 30 days.</p><p><strong>Workaround:</strong></p><ul><li>Save important details externally (notes app, document)</li><li>Submit draft before expiration</li><li>Start new draft if needed</li></ul><p><strong>Future Enhancement:</strong> Considering adding draft extension feature for complex investigations.</p><h2 id="draft-security-privacy" tabindex="-1">Draft Security &amp; Privacy <a class="header-anchor" href="#draft-security-privacy" aria-label="Permalink to &quot;Draft Security &amp; Privacy&quot;">​</a></h2><h3 id="encryption" tabindex="-1">Encryption <a class="header-anchor" href="#encryption" aria-label="Permalink to &quot;Encryption&quot;">​</a></h3><p><strong>End-to-End Encryption:</strong></p><p>Drafts receive same security as submitted reports:</p><p><strong>Encryption Details:</strong></p><ul><li>AES-256 encryption at rest</li><li>TLS 1.3 for data in transit</li><li>Encrypted before leaving reporter&#39;s device</li><li>Zero-knowledge architecture—server cannot decrypt without code</li></ul><p><strong>Learn More:</strong> <a href="/docs/reporting/encryption">Report Encryption</a></p><h3 id="access-control" tabindex="-1">Access Control <a class="header-anchor" href="#access-control" aria-label="Permalink to &quot;Access Control&quot;">​</a></h3><p><strong>Who Can Access Drafts:</strong></p><p><strong>✅ Reporter (with draft code):</strong></p><ul><li>Can view and edit draft</li><li>Can submit draft</li><li>Can abandon draft</li></ul><p><strong>❌ Organization Administrators:</strong></p><ul><li>Cannot see drafts</li><li>Cannot access draft content</li><li>Cannot retrieve draft codes</li></ul><p><strong>❌ Disclosurely Staff:</strong></p><ul><li>No access to draft data</li><li>Cannot recover lost draft codes</li><li>Cannot extend expiration</li></ul><p><strong>❌ Third Parties:</strong></p><ul><li>No access under any circumstances</li><li>Not shared with law enforcement</li><li>Not subject to subpoena (not submitted yet)</li></ul><h3 id="lost-draft-codes" tabindex="-1">Lost Draft Codes <a class="header-anchor" href="#lost-draft-codes" aria-label="Permalink to &quot;Lost Draft Codes&quot;">​</a></h3><p><strong>Cannot Be Recovered:</strong></p><p>If a reporter loses their draft code, it cannot be retrieved:</p><p><strong>Why:</strong></p><ul><li>No personal information links reporter to draft</li><li>Zero-knowledge architecture prevents recovery</li><li>Security by design—protects reporter anonymity</li></ul><p><strong>Prevention:</strong></p><ul><li>Copy draft code immediately when generated</li><li>Save in password manager</li><li>Email to personal account (if comfortable)</li><li>Screenshot or write down code</li><li>Multiple save locations recommended</li></ul><p><strong>If Lost:</strong></p><ul><li>Reporter must start new report</li><li>Previous draft will auto-expire in 30 days</li><li>No security breach—draft remains encrypted</li></ul><h2 id="managing-multiple-drafts" tabindex="-1">Managing Multiple Drafts <a class="header-anchor" href="#managing-multiple-drafts" aria-label="Permalink to &quot;Managing Multiple Drafts&quot;">​</a></h2><h3 id="creating-multiple-drafts" tabindex="-1">Creating Multiple Drafts <a class="header-anchor" href="#creating-multiple-drafts" aria-label="Permalink to &quot;Creating Multiple Drafts&quot;">​</a></h3><p><strong>Reporters Can Have:</strong></p><ul><li>Unlimited drafts simultaneously</li><li>Each with unique draft code</li><li>Different stages of completion</li><li>Separate 30-day expiration timers</li></ul><p><strong>Use Cases:</strong></p><ul><li>Multiple unrelated incidents to report</li><li>Different categories requiring separate reports</li><li>Collaborative reporting (share code with co-reporter)</li><li>Testing submission process before final report</li></ul><h3 id="distinguishing-drafts" tabindex="-1">Distinguishing Drafts <a class="header-anchor" href="#distinguishing-drafts" aria-label="Permalink to &quot;Distinguishing Drafts&quot;">​</a></h3><p><strong>No Built-In Draft Management:</strong></p><p>System doesn&#39;t track drafts per reporter (by design for anonymity).</p><p><strong>Reporter Best Practices:</strong></p><ul><li>Label draft codes in notes app (e.g., &quot;Harassment Draft - DFT-ABC123&quot;)</li><li>Maintain personal spreadsheet of draft codes</li><li>Email drafts to self with descriptive subjects</li><li>Use password manager with tags</li></ul><h3 id="consolidating-drafts" tabindex="-1">Consolidating Drafts <a class="header-anchor" href="#consolidating-drafts" aria-label="Permalink to &quot;Consolidating Drafts&quot;">​</a></h3><p><strong>Not Supported:</strong></p><p>Cannot merge multiple drafts into single report.</p><p><strong>Workaround:</strong></p><ul><li>Copy/paste between drafts manually</li><li>Complete one draft fully</li><li>Reference other draft codes in &quot;Additional Information&quot; section</li></ul><h2 id="draft-analytics-admin-view" tabindex="-1">Draft Analytics (Admin View) <a class="header-anchor" href="#draft-analytics-admin-view" aria-label="Permalink to &quot;Draft Analytics (Admin View)&quot;">​</a></h2><h3 id="organizational-insights" tabindex="-1">Organizational Insights <a class="header-anchor" href="#organizational-insights" aria-label="Permalink to &quot;Organizational Insights&quot;">​</a></h3><p><strong>Administrators Can See:</strong></p><p>Aggregated draft statistics (no individual draft access):</p><p><strong>Metrics Available:</strong></p><ul><li>Total drafts created</li><li>Active drafts (not expired, not submitted)</li><li>Draft submission rate (drafts → reports)</li><li>Average time from draft creation to submission</li><li>Draft abandonment rate</li><li>Average draft completion percentage</li></ul><p><strong>Use Cases:</strong></p><ul><li>Understand reporter behavior</li><li>Identify friction in reporting process</li><li>Optimize form complexity</li><li>Measure campaign effectiveness</li></ul><p><strong>Access:</strong> Dashboard &gt; Analytics &gt; Drafts</p><p><strong>Privacy:</strong></p><ul><li>No access to draft content</li><li>No reporter identification possible</li><li>Aggregate data only</li><li>Complies with <a href="/docs/compliance/gdpr">GDPR</a> and privacy regulations</li></ul><h3 id="improving-form-design" tabindex="-1">Improving Form Design <a class="header-anchor" href="#improving-form-design" aria-label="Permalink to &quot;Improving Form Design&quot;">​</a></h3><p><strong>Using Draft Data:</strong></p><p>Analyze where reporters abandon drafts:</p><p><strong>Common Patterns:</strong></p><ul><li>High abandonment on Step 6 → simplify that step</li><li>Low submission rate → form too complex</li><li>Long draft-to-submission time → consider multi-part form</li></ul><p><strong>Actions:</strong></p><ul><li>Reduce required fields</li><li>Clarify confusing questions</li><li>Break long steps into smaller parts</li><li>Add more helpful tooltips</li><li>Improve mobile experience</li></ul><h2 id="best-practices-for-reporters" tabindex="-1">Best Practices for Reporters <a class="header-anchor" href="#best-practices-for-reporters" aria-label="Permalink to &quot;Best Practices for Reporters&quot;">​</a></h2><h3 id="when-to-save-a-draft" tabindex="-1">When to Save a Draft <a class="header-anchor" href="#when-to-save-a-draft" aria-label="Permalink to &quot;When to Save a Draft&quot;">​</a></h3><p>✅ <strong>Good Reasons to Save:</strong></p><ul><li>Need to gather additional evidence (documents, photos)</li><li>Want to consult with colleague or witness</li><li>Require time to recall specific dates/details</li><li>Prefer to compose thoughtfully over multiple sessions</li><li>Concerned about browser crashes or network issues</li><li>Need to submit from different device for privacy</li></ul><p>❌ <strong>When Not to Save:</strong></p><ul><li>Report is essentially complete (just submit it!)</li><li>Only minor details missing (better to submit and add via messaging)</li><li>Concerns about draft expiration (30 days is firm)</li></ul><h3 id="protecting-your-draft-code" tabindex="-1">Protecting Your Draft Code <a class="header-anchor" href="#protecting-your-draft-code" aria-label="Permalink to &quot;Protecting Your Draft Code&quot;">​</a></h3><p>✅ <strong>Do:</strong></p><ul><li>Copy code immediately when generated</li><li>Save in multiple secure locations</li><li>Use password manager for storage</li><li>Email to personal account (if safe to do so)</li><li>Write down code and store securely</li></ul><p>❌ <strong>Don&#39;t:</strong></p><ul><li>Share code publicly or with untrusted parties</li><li>Save in work email (if reporting work-related issues)</li><li>Post to social media or public forums</li><li>Store in unsecured cloud storage</li><li>Rely on browser autofill alone</li></ul><h3 id="completing-drafts" tabindex="-1">Completing Drafts <a class="header-anchor" href="#completing-drafts" aria-label="Permalink to &quot;Completing Drafts&quot;">​</a></h3><p>✅ <strong>Tips for Completion:</strong></p><ul><li>Set personal deadline before 30-day expiration</li><li>Block time to finish comprehensively</li><li>Review all fields before submitting</li><li>Attach all relevant evidence</li><li>Proofread descriptions for clarity</li><li>Use <a href="/docs/reporting/secure-messaging">secure messaging</a> for follow-ups after submission</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="draft-code-not-working" tabindex="-1">Draft Code Not Working <a class="header-anchor" href="#draft-code-not-working" aria-label="Permalink to &quot;Draft Code Not Working&quot;">​</a></h3><p><strong>Possible Causes:</strong></p><ol><li><p><strong>Incorrect Code Format</strong></p><ul><li>Verify format: DFT-XXXXXXXX</li><li>Check for typos or extra spaces</li><li>Confirm case (code is case-insensitive)</li></ul></li><li><p><strong>Expired Draft</strong></p><ul><li>Drafts expire after 30 days</li><li>Check creation date if known</li><li>No recovery possible—start new report</li></ul></li><li><p><strong>Already Submitted</strong></p><ul><li>Draft codes deactivate after submission</li><li>Check <a href="/docs/reporting/tracking-report">report tracking</a> with report ID instead</li></ul></li><li><p><strong>Rate Limiting</strong></p><ul><li>Too many failed attempts trigger lockout</li><li>Wait 15 minutes and try again</li><li>Contact support if persistent issues</li></ul></li></ol><h3 id="draft-not-saving" tabindex="-1">Draft Not Saving <a class="header-anchor" href="#draft-not-saving" aria-label="Permalink to &quot;Draft Not Saving&quot;">​</a></h3><p><strong>Troubleshooting Steps:</strong></p><ol><li><p><strong>Check Internet Connection</strong></p><ul><li>Verify stable connection</li><li>Try refreshing page</li><li>Reconnect to WiFi if needed</li></ul></li><li><p><strong>Browser Issues</strong></p><ul><li>Clear browser cache</li><li>Disable ad blockers temporarily</li><li>Try incognito/private mode</li><li>Update browser to latest version</li></ul></li><li><p><strong>Storage Quota</strong></p><ul><li>Organization may have draft limits (rare)</li><li>Contact support if draft save fails repeatedly</li></ul></li></ol><h3 id="lost-draft-code" tabindex="-1">Lost Draft Code <a class="header-anchor" href="#lost-draft-code" aria-label="Permalink to &quot;Lost Draft Code&quot;">​</a></h3><p><strong>Unfortunately Cannot Be Recovered:</strong></p><p>As stated earlier, lost draft codes cannot be retrieved due to zero-knowledge architecture.</p><p><strong>Next Steps:</strong></p><ol><li>Start new report with fresh draft code</li><li>Reference details from memory or external notes</li><li>Implement better code management going forward</li><li>Consider using password manager</li></ol><h3 id="draft-shows-outdated-information" tabindex="-1">Draft Shows Outdated Information <a class="header-anchor" href="#draft-shows-outdated-information" aria-label="Permalink to &quot;Draft Shows Outdated Information&quot;">​</a></h3><p><strong>Caching Issue:</strong></p><p>Browser may show cached version:</p><p><strong>Solutions:</strong></p><ol><li>Hard refresh page (Ctrl+F5 or Cmd+Shift+R)</li><li>Clear browser cache</li><li>Use incognito/private browsing mode</li><li>Try different browser</li></ol><p><strong>If Persists:</strong></p><ul><li>Data may not have saved previously</li><li>Check &quot;Last saved&quot; timestamp</li><li>Contact support with draft code for investigation</li></ul><h2 id="related-features" tabindex="-1">Related Features <a class="header-anchor" href="#related-features" aria-label="Permalink to &quot;Related Features&quot;">​</a></h2><h3 id="after-submitting-draft" tabindex="-1">After Submitting Draft <a class="header-anchor" href="#after-submitting-draft" aria-label="Permalink to &quot;After Submitting Draft&quot;">​</a></h3><p><strong>Convert to Report:</strong></p><p>When draft is submitted, it becomes a full report:</p><p><strong>What Happens:</strong></p><ol><li>Draft code deactivates</li><li>Report ID generated (DIS-XXXXXXXX format)</li><li>Draft automatically deleted</li><li>Confirmation screen displays report ID</li><li>Email/SMS sent with tracking info (if provided)</li></ol><p><strong>Next Steps:</strong></p><ul><li>Save report ID for <a href="/docs/reporting/tracking-report">tracking</a></li><li>Use <a href="/docs/reporting/secure-messaging">secure messaging</a> for follow-ups</li><li>Monitor status updates</li><li>Await investigation</li></ul><p><strong>Learn More:</strong> <a href="/docs/reporting/how-to-submit">How to Submit a Report</a></p><h3 id="draft-vs-report-tracking" tabindex="-1">Draft vs. Report Tracking <a class="header-anchor" href="#draft-vs-report-tracking" aria-label="Permalink to &quot;Draft vs. Report Tracking&quot;">​</a></h3><p><strong>Key Differences:</strong></p><table tabindex="0"><thead><tr><th>Feature</th><th>Draft (DFT-XXX)</th><th>Report (DIS-XXX)</th></tr></thead><tbody><tr><td>Status</td><td>Incomplete</td><td>Submitted</td></tr><tr><td>Expiration</td><td>30 days</td><td>Never (until archived)</td></tr><tr><td>Editable</td><td>Yes</td><td>No (use messaging)</td></tr><tr><td>Organization Access</td><td>No</td><td>Yes</td></tr><tr><td>Tracking</td><td>No</td><td>Yes</td></tr><tr><td>Messaging</td><td>No</td><td>Yes</td></tr></tbody></table><h3 id="anonymous-draft-management" tabindex="-1">Anonymous Draft Management <a class="header-anchor" href="#anonymous-draft-management" aria-label="Permalink to &quot;Anonymous Draft Management&quot;">​</a></h3><p><strong>Maintaining Anonymity:</strong></p><p><strong>If Submitting Anonymously:</strong></p><ul><li>Don&#39;t provide email when saving draft</li><li>Don&#39;t save draft code in work systems</li><li>Use personal/private device for resuming</li><li>Clear browser history after submission</li><li>Consider VPN for additional privacy</li></ul><p><strong>Learn More:</strong> <a href="/docs/features/anonymous-reporting">Anonymous Reporting</a></p><h2 id="administrator-settings" tabindex="-1">Administrator Settings <a class="header-anchor" href="#administrator-settings" aria-label="Permalink to &quot;Administrator Settings&quot;">​</a></h2><h3 id="draft-configuration" tabindex="-1">Draft Configuration <a class="header-anchor" href="#draft-configuration" aria-label="Permalink to &quot;Draft Configuration&quot;">​</a></h3><p><strong>Administrators Can Configure:</strong></p><p><strong>Draft Retention Period:</strong></p><ul><li>Default: 30 days</li><li>Range: 7-90 days</li><li>Custom per organization</li></ul><p><strong>Auto-Save Settings:</strong></p><ul><li>Enable/disable auto-save</li><li>Auto-save frequency (1-10 minutes)</li><li>Default on or off for reporters</li></ul><p><strong>Draft Limits:</strong></p><ul><li>Maximum drafts per IP (abuse prevention)</li><li>Storage quota for drafts</li><li>Rate limiting configuration</li></ul><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Reporting &gt; Draft Settings</p><h3 id="disabling-drafts" tabindex="-1">Disabling Drafts <a class="header-anchor" href="#disabling-drafts" aria-label="Permalink to &quot;Disabling Drafts&quot;">​</a></h3><p><strong>Organizations Can Disable:</strong></p><p>If draft functionality not desired:</p><p><strong>Settings &gt; Reporting &gt; Draft Management:</strong></p><ul><li>Toggle &quot;Allow Draft Saving&quot; to OFF</li><li>Hides &quot;Save Draft&quot; button from forms</li><li>Existing drafts remain accessible until expiration</li><li>Can re-enable anytime</li></ul><p><strong>Reasons to Disable:</strong></p><ul><li>Encourage immediate reporting</li><li>Reduce storage costs</li><li>Simplify reporting process</li><li>Organizational policy requirements</li></ul><h2 id="privacy-compliance" tabindex="-1">Privacy &amp; Compliance <a class="header-anchor" href="#privacy-compliance" aria-label="Permalink to &quot;Privacy &amp; Compliance&quot;">​</a></h2><h3 id="gdpr-compliance" tabindex="-1">GDPR Compliance <a class="header-anchor" href="#gdpr-compliance" aria-label="Permalink to &quot;GDPR Compliance&quot;">​</a></h3><p><strong>Draft Data Handling:</strong></p><p>Drafts are personal data subject to GDPR:</p><p><strong>Compliance Measures:</strong></p><ul><li>Data minimization (only save necessary fields)</li><li>Purpose limitation (drafts for reporting only)</li><li>Storage limitation (30-day automatic deletion)</li><li>Encryption at rest and in transit</li><li>No unnecessary processing</li></ul><p><strong>Data Subject Rights:</strong></p><ul><li>Cannot exercise rights on drafts (anonymous, no link to identity)</li><li>Rights apply to submitted reports only</li></ul><p><strong>Learn More:</strong> <a href="/docs/compliance/gdpr">GDPR Compliance</a></p><h3 id="data-retention" tabindex="-1">Data Retention <a class="header-anchor" href="#data-retention" aria-label="Permalink to &quot;Data Retention&quot;">​</a></h3><p><strong>Automatic Deletion:</strong></p><p>Drafts deleted automatically after 30 days:</p><p><strong>No Exceptions:</strong></p><ul><li>Even if reporter requests extension</li><li>Even for high-priority incidents</li><li>Consistent application across all drafts</li></ul><p><strong>Retention Policy:</strong></p><ul><li>Aligns with <a href="/docs/compliance/retention">data retention policies</a></li><li>Minimizes privacy risks</li><li>Reduces storage requirements</li></ul><h2 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">​</a></h2><ul><li><a href="/docs/reporting/how-to-submit">How to Submit a Report</a> - Complete reporting guide</li><li><a href="/docs/reporting/tracking-report">Tracking Your Report</a> - Report status lookups</li><li><a href="/docs/features/anonymous-reporting">Anonymous Reporting</a> - Anonymous submission</li><li><a href="/docs/reporting/secure-messaging">Secure Messaging</a> - Communication after submission</li><li><a href="/docs/reporting/encryption">Report Encryption</a> - Security details</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Questions about draft management?</p><ul><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="/docs/support/faqs">FAQs</a></li><li>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li></ul>',218)])])}const f=t(o,[["render",n]]);export{g as __pageData,f as default};
