import{_ as e,c as o,o as l,ag as t}from"./chunks/framework.4QX5UEtQ.js";const c=JSON.parse('{"title":"File Uploads & Evidence Management | Disclosurely","description":"Securely upload documents, images, videos, and audio files as evidence for whistleblowing reports. Support for 50+ file types with automatic metadata stripping and virus scanning.","frontmatter":{"title":"File Uploads & Evidence Management | Disclosurely","description":"Securely upload documents, images, videos, and audio files as evidence for whistleblowing reports. Support for 50+ file types with automatic metadata stripping and virus scanning.","head":[["meta",{"property":"og:title","content":"File Uploads & Evidence Management - Disclosurely"}],["meta",{"property":"og:description","content":"Securely upload documents, images, videos, and audio files as evidence for whistleblowing reports. Support for 50+ file types with automatic metadata stripping and virus scanning."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"File Uploads & Evidence - Disclosurely"}],["meta",{"property":"og:description","content":"Securely upload documents, images, videos, and audio files as evidence for whistleblowing reports. Support for 50+ file types with automatic metadata stripping and virus scanning."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"features/file-uploads.md","filePath":"features/file-uploads.md"}'),r={name:"features/file-uploads.md"};function s(a,i,n,p,g,d){return l(),o("div",null,[...i[0]||(i[0]=[t(`<h1 id="file-uploads-evidence-management" tabindex="-1">File Uploads &amp; Evidence Management <a class="header-anchor" href="#file-uploads-evidence-management" aria-label="Permalink to &quot;File Uploads &amp; Evidence Management&quot;">​</a></h1><p>Securely attach documents, images, videos, and audio files to whistleblowing reports with comprehensive evidence management supporting 50+ file types, automatic metadata stripping, and enterprise-grade security.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Evidence documentation is critical for effective whistleblowing investigations. Disclosurely&#39;s file upload system enables reporters to attach supporting evidence—documents, photos, videos, audio recordings, spreadsheets, and more—while maintaining security, privacy, and anonymity throughout the process.</p><p><strong>Key Features:</strong></p><ul><li>50+ supported file types</li><li>Drag-and-drop upload interface</li><li>Automatic metadata stripping (protects anonymity)</li><li>Virus and malware scanning</li><li>Encrypted storage and transmission</li><li>Preview capabilities</li><li>Version tracking</li></ul><p><strong>Access:</strong> Available during <a href="/docs/reporting/how-to-submit">report submission</a> and via <a href="/docs/reporting/secure-messaging">secure messaging</a></p><p><strong>Storage Limits:</strong> Vary by plan—see <a href="/docs/features/subscription-tiers">Subscription Tiers</a></p><h2 id="supported-file-types" tabindex="-1">Supported File Types <a class="header-anchor" href="#supported-file-types" aria-label="Permalink to &quot;Supported File Types&quot;">​</a></h2><h3 id="documents" tabindex="-1">Documents <a class="header-anchor" href="#documents" aria-label="Permalink to &quot;Documents&quot;">​</a></h3><p><strong>Office Documents:</strong></p><ul><li><strong>Microsoft Word</strong> - .doc, .docx</li><li><strong>Microsoft Excel</strong> - .xls, .xlsx</li><li><strong>Microsoft PowerPoint</strong> - .ppt, .pptx</li><li><strong>Microsoft Visio</strong> - .vsd, .vsdx</li><li><strong>OpenOffice/LibreOffice</strong> - .odt, .ods, .odp</li></ul><p><strong>PDF:</strong></p><ul><li><strong>PDF Documents</strong> - .pdf (all versions)</li><li><strong>PDF/A</strong> - .pdf (archival format)</li></ul><p><strong>Text Files:</strong></p><ul><li><strong>Plain Text</strong> - .txt</li><li><strong>Rich Text</strong> - .rtf</li><li><strong>Markdown</strong> - .md</li><li><strong>CSV</strong> - .csv (comma-separated values)</li><li><strong>Log Files</strong> - .log</li></ul><p><strong>eBooks:</strong></p><ul><li><strong>EPUB</strong> - .epub</li><li><strong>MOBI</strong> - .mobi</li></ul><h3 id="images" tabindex="-1">Images <a class="header-anchor" href="#images" aria-label="Permalink to &quot;Images&quot;">​</a></h3><p><strong>Photographs:</strong></p><ul><li><strong>JPEG</strong> - .jpg, .jpeg</li><li><strong>PNG</strong> - .png</li><li><strong>GIF</strong> - .gif (static and animated)</li><li><strong>WebP</strong> - .webp</li><li><strong>HEIC</strong> - .heic (iPhone photos)</li></ul><p><strong>Professional Graphics:</strong></p><ul><li><strong>TIFF</strong> - .tiff, .tif</li><li><strong>BMP</strong> - .bmp</li><li><strong>SVG</strong> - .svg (vector graphics)</li><li><strong>RAW Formats</strong> - .cr2, .nef, .arw, .dng</li></ul><p><strong>Design Files:</strong></p><ul><li><strong>Adobe Photoshop</strong> - .psd</li><li><strong>Adobe Illustrator</strong> - .ai (requires Pro plan)</li></ul><h3 id="video" tabindex="-1">Video <a class="header-anchor" href="#video" aria-label="Permalink to &quot;Video&quot;">​</a></h3><p><strong>Common Formats:</strong></p><ul><li><strong>MP4</strong> - .mp4 (most common)</li><li><strong>MOV</strong> - .mov (Apple QuickTime)</li><li><strong>AVI</strong> - .avi</li><li><strong>WMV</strong> - .wmv (Windows Media)</li><li><strong>WebM</strong> - .webm</li></ul><p><strong>Professional Video:</strong></p><ul><li><strong>MKV</strong> - .mkv (Matroska)</li><li><strong>FLV</strong> - .flv (Flash video)</li><li><strong>M4V</strong> - .m4v (iTunes)</li><li><strong>MPEG</strong> - .mpeg, .mpg</li></ul><p><strong>Mobile Video:</strong></p><ul><li><strong>3GP</strong> - .3gp (mobile phones)</li><li><strong>iPhone Video</strong> - .mov (HEVC format)</li></ul><p><strong>Maximum Video Duration:</strong> 60 minutes per file</p><h3 id="audio" tabindex="-1">Audio <a class="header-anchor" href="#audio" aria-label="Permalink to &quot;Audio&quot;">​</a></h3><p><strong>Audio Files:</strong></p><ul><li><strong>MP3</strong> - .mp3</li><li><strong>WAV</strong> - .wav (uncompressed)</li><li><strong>AAC</strong> - .aac, .m4a</li><li><strong>FLAC</strong> - .flac (lossless)</li><li><strong>OGG</strong> - .ogg</li></ul><p><strong>Voice Recordings:</strong></p><ul><li><strong>Voice Memos</strong> - .m4a (iPhone)</li><li><strong>AMR</strong> - .amr (Android)</li><li><strong>WMA</strong> - .wma (Windows)</li></ul><p><strong>Maximum Audio Duration:</strong> 4 hours per file</p><h3 id="archives" tabindex="-1">Archives <a class="header-anchor" href="#archives" aria-label="Permalink to &quot;Archives&quot;">​</a></h3><p><strong>Compressed Files:</strong></p><ul><li><strong>ZIP</strong> - .zip</li><li><strong>RAR</strong> - .rar</li><li><strong>7-Zip</strong> - .7z</li><li><strong>TAR</strong> - .tar, .tar.gz, .tgz</li><li><strong>GZIP</strong> - .gz</li></ul><p><strong>Use Cases:</strong></p><ul><li>Multiple files in single upload</li><li>Folder structures preserved</li><li>Large document collections</li><li>Email archive exports (.mbox, .pst)</li></ul><p><strong>Archive Limits:</strong></p><ul><li>Maximum files in archive: 100</li><li>Archive automatically extracted and scanned</li><li>Individual files must meet size limits</li></ul><h3 id="email" tabindex="-1">Email <a class="header-anchor" href="#email" aria-label="Permalink to &quot;Email&quot;">​</a></h3><p><strong>Email Formats:</strong></p><ul><li><strong>EML</strong> - .eml (standard email format)</li><li><strong>MSG</strong> - .msg (Outlook messages)</li><li><strong>PST</strong> - .pst (Outlook archive - Pro plan)</li><li><strong>MBOX</strong> - .mbox (email archives)</li></ul><p><strong>Email Attachments:</strong> Automatically extracted and displayed separately.</p><h3 id="code-development" tabindex="-1">Code &amp; Development <a class="header-anchor" href="#code-development" aria-label="Permalink to &quot;Code &amp; Development&quot;">​</a></h3><p><strong>Source Code:</strong></p><ul><li><strong>JavaScript</strong> - .js, .json</li><li><strong>Python</strong> - .py</li><li><strong>Java</strong> - .java</li><li><strong>C/C++</strong> - .c, .cpp, .h</li><li><strong>PHP</strong> - .php</li><li><strong>HTML/CSS</strong> - .html, .css</li><li><strong>SQL</strong> - .sql</li></ul><p><strong>Configuration:</strong></p><ul><li><strong>YAML</strong> - .yaml, .yml</li><li><strong>XML</strong> - .xml</li><li><strong>INI</strong> - .ini</li><li><strong>ENV</strong> - .env</li></ul><p><strong>Use Cases:</strong> Evidence of code violations, security vulnerabilities, data breaches</p><h3 id="other-formats" tabindex="-1">Other Formats <a class="header-anchor" href="#other-formats" aria-label="Permalink to &quot;Other Formats&quot;">​</a></h3><p><strong>Database Files:</strong></p><ul><li><strong>SQLite</strong> - .sqlite, .db</li><li><strong>CSV Exports</strong> - .csv</li></ul><p><strong>CAD/Engineering:</strong></p><ul><li><strong>AutoCAD</strong> - .dwg, .dxf (Enterprise plan)</li><li><strong>SolidWorks</strong> - .sldprt (Enterprise plan)</li></ul><p><strong>Medical:</strong></p><ul><li><strong>DICOM</strong> - .dcm (medical imaging - Enterprise plan)</li></ul><p><strong>Unsupported File Type?</strong></p><p>Contact <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a> to request additional format support. Enterprise customers receive priority for custom file type additions.</p><h2 id="file-size-limits" tabindex="-1">File Size Limits <a class="header-anchor" href="#file-size-limits" aria-label="Permalink to &quot;File Size Limits&quot;">​</a></h2><h3 id="limits-by-plan" tabindex="-1">Limits by Plan <a class="header-anchor" href="#limits-by-plan" aria-label="Permalink to &quot;Limits by Plan&quot;">​</a></h3><p><strong>Free Plan:</strong></p><ul><li><strong>Per File:</strong> 10 MB maximum</li><li><strong>Per Report:</strong> 50 MB total</li><li><strong>Total Storage:</strong> 1 GB organization-wide</li></ul><p><strong>Starter Plan:</strong></p><ul><li><strong>Per File:</strong> 50 MB maximum</li><li><strong>Per Report:</strong> 250 MB total</li><li><strong>Total Storage:</strong> 25 GB organization-wide</li></ul><p><strong>Pro Plan:</strong></p><ul><li><strong>Per File:</strong> 500 MB maximum</li><li><strong>Per Report:</strong> 2 GB total</li><li><strong>Total Storage:</strong> 100 GB organization-wide</li></ul><p><strong>Enterprise Plan:</strong></p><ul><li><strong>Per File:</strong> 5 GB maximum</li><li><strong>Per Report:</strong> 20 GB total</li><li><strong>Total Storage:</strong> Unlimited</li></ul><p><strong>Need More Storage?</strong></p><p>Upgrade your plan or purchase additional storage:</p><ul><li><strong>+50 GB</strong> - $25/month</li><li><strong>+250 GB</strong> - $100/month</li><li><strong>+1 TB</strong> - $300/month</li></ul><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Billing &gt; Add Storage</p><h3 id="large-file-handling" tabindex="-1">Large File Handling <a class="header-anchor" href="#large-file-handling" aria-label="Permalink to &quot;Large File Handling&quot;">​</a></h3><p><strong>Files Approaching Limits:</strong></p><p>When uploading large files:</p><p><strong>Compression Recommended:</strong></p><ul><li>Compress videos (reduce resolution if needed)</li><li>Compress PDFs (remove embedded fonts, reduce images)</li><li>Archive multiple files together</li><li>Use lossless compression for important evidence</li></ul><p><strong>Chunked Upload:</strong> Large files upload in chunks to prevent timeout:</p><ul><li>10 MB chunks for reliability</li><li>Resume capability if connection interrupted</li><li>Progress bar shows upload status</li><li>Retry failed chunks automatically</li></ul><p><strong>Upload Time Estimates:</strong></p><p><strong>On 10 Mbps connection:</strong></p><ul><li>100 MB file: ~1.5 minutes</li><li>500 MB file: ~7 minutes</li><li>2 GB file: ~30 minutes</li><li>5 GB file: ~75 minutes (Enterprise only)</li></ul><p><strong>Tips for Faster Uploads:</strong></p><ul><li>Use wired connection (not WiFi)</li><li>Close other bandwidth-intensive applications</li><li>Upload during off-peak hours</li><li>Use compression to reduce file size</li></ul><h2 id="how-to-upload-files" tabindex="-1">How to Upload Files <a class="header-anchor" href="#how-to-upload-files" aria-label="Permalink to &quot;How to Upload Files&quot;">​</a></h2><h3 id="during-report-submission" tabindex="-1">During Report Submission <a class="header-anchor" href="#during-report-submission" aria-label="Permalink to &quot;During Report Submission&quot;">​</a></h3><p><strong>Upload Interface:</strong></p><p><strong>Step 1: Access Upload Area</strong></p><ul><li>Navigate to <a href="/docs/reporting/how-to-submit">report submission form</a></li><li>Proceed to &quot;Evidence &amp; Attachments&quot; step</li><li>Click <strong>&quot;Upload Files&quot;</strong> button or drag files into upload zone</li></ul><p><strong>Step 2: Select Files</strong></p><p><strong>Two Methods:</strong></p><p><strong>Method 1: Drag and Drop</strong></p><ol><li>Open file explorer/finder</li><li>Select one or multiple files</li><li>Drag into upload zone</li><li>Drop files</li><li>Upload begins automatically</li></ol><p><strong>Method 2: Browse and Select</strong></p><ol><li>Click <strong>&quot;Choose Files&quot;</strong> button</li><li>Browse to file location</li><li>Select one or multiple files (hold Ctrl/Cmd for multiple)</li><li>Click &quot;Open&quot;</li><li>Upload begins automatically</li></ol><p><strong>Step 3: Monitor Upload</strong></p><ul><li>Progress bar shows upload status</li><li>File name and size displayed</li><li>Estimated time remaining</li><li>Cancel button available if needed</li></ul><p><strong>Step 4: Confirmation</strong></p><ul><li>Green checkmark when upload complete</li><li>Thumbnail preview (images/videos)</li><li>File name confirmation</li><li>Option to add file description</li></ul><p><strong>Multiple Files:</strong> Upload up to 20 files simultaneously. Total report limits still apply.</p><h3 id="via-secure-messaging" tabindex="-1">Via Secure Messaging <a class="header-anchor" href="#via-secure-messaging" aria-label="Permalink to &quot;Via Secure Messaging&quot;">​</a></h3><p><strong>Add Evidence After Submission:</strong></p><p><strong>How to Upload:</strong></p><ol><li>Track your report using <a href="/docs/reporting/tracking-report">report tracking</a></li><li>Access <a href="/docs/reporting/secure-messaging">secure messaging</a></li><li>Type message or go directly to attachment icon</li><li>Click paperclip/attachment icon</li><li>Select file(s) using same methods above</li><li>Add optional description</li><li>Send message with attachment</li></ol><p><strong>Use Cases:</strong></p><ul><li>Additional evidence discovered after submission</li><li>Investigator requests specific documents</li><li>Clarification documents</li><li>Follow-up evidence</li></ul><p><strong>Learn More:</strong> <a href="/docs/reporting/secure-messaging">Secure Messaging</a></p><h3 id="mobile-upload" tabindex="-1">Mobile Upload <a class="header-anchor" href="#mobile-upload" aria-label="Permalink to &quot;Mobile Upload&quot;">​</a></h3><p><strong>Upload from Phone/Tablet:</strong></p><p><strong>Mobile Optimizations:</strong></p><ul><li>Responsive upload interface</li><li>Camera integration (take photo directly)</li><li>Voice memo recording and upload</li><li>Gallery/photos app integration</li><li>Cloud storage integration (iCloud, Google Drive)</li></ul><p><strong>Take Photo Directly:</strong></p><ol><li>In upload interface, tap <strong>&quot;Take Photo&quot;</strong></li><li>Grant camera permission if prompted</li><li>Take photo of evidence</li><li>Review and confirm</li><li>Upload automatically begins</li></ol><p><strong>Record Voice Memo:</strong></p><ol><li>Tap <strong>&quot;Record Audio&quot;</strong></li><li>Grant microphone permission</li><li>Record your evidence/testimony</li><li>Stop recording</li><li>Review and upload</li></ol><p><strong>Access Cloud Files:</strong></p><ul><li>iOS: Access iCloud Drive files</li><li>Android: Access Google Drive files</li><li>File picker integration seamless</li></ul><h2 id="metadata-removal" tabindex="-1">Metadata Removal <a class="header-anchor" href="#metadata-removal" aria-label="Permalink to &quot;Metadata Removal&quot;">​</a></h2><h3 id="protecting-anonymity" tabindex="-1">Protecting Anonymity <a class="header-anchor" href="#protecting-anonymity" aria-label="Permalink to &quot;Protecting Anonymity&quot;">​</a></h3><p><strong>Automatic Metadata Stripping:</strong></p><p>To protect reporter anonymity, Disclosurely automatically removes metadata from all uploaded files:</p><p><strong>Metadata Removed:</strong></p><p><strong>Documents:</strong></p><ul><li>Author name</li><li>Company/organization</li><li>Computer name</li><li>Last modified by user</li><li>Document creation software</li><li>File path information</li><li>Tracked changes history</li><li>Comments and annotations (optional)</li></ul><p><strong>Images:</strong></p><ul><li>EXIF data (camera model, settings)</li><li>GPS location coordinates</li><li>Date/time photograph taken</li><li>Photographer name</li><li>Copyright information</li><li>Camera serial number</li><li>Software used for editing</li></ul><p><strong>Videos:</strong></p><ul><li>Device information</li><li>Recording location</li><li>Date and time recorded</li><li>Editing software details</li><li>Copyright metadata</li></ul><p><strong>Audio:</strong></p><ul><li>Recording device information</li><li>Artist/creator tags</li><li>Album/collection information</li><li>Timestamps</li></ul><p><strong>Emails:</strong></p><ul><li>Sender IP address</li><li>Email client details</li><li>Routing information (headers simplified)</li><li>Internal server paths</li></ul><h3 id="metadata-preview" tabindex="-1">Metadata Preview <a class="header-anchor" href="#metadata-preview" aria-label="Permalink to &quot;Metadata Preview&quot;">​</a></h3><p><strong>Before Upload:</strong></p><p><strong>Optional Preview (Pro/Enterprise):</strong></p><ol><li>Select file for upload</li><li>Click <strong>&quot;Preview Metadata&quot;</strong></li><li>See what metadata exists</li><li>Confirm removal</li><li>Proceed with upload</li></ol><p><strong>Metadata Report:</strong> Shows detected metadata categories:</p><ul><li>✅ Author information (will be removed)</li><li>✅ GPS coordinates (will be removed)</li><li>✅ Device details (will be removed)</li><li>❌ No sensitive metadata detected</li></ul><p><strong>Manual Metadata Removal:</strong></p><p>For extra caution, remove metadata before upload:</p><p><strong>Windows:</strong></p><ol><li>Right-click file &gt; Properties</li><li>Go to &quot;Details&quot; tab</li><li>Click &quot;Remove Properties and Personal Information&quot;</li><li>Select &quot;Remove the following properties&quot;</li><li>Select all &gt; OK</li></ol><p><strong>macOS:</strong> Use third-party tools:</p><ul><li>ImageOptim (images)</li><li>ExifTool (all files)</li><li>Preview.app Export (images)</li></ul><p><strong>Online Tools:</strong></p><ul><li>PDF metadata removers</li><li>Image EXIF removers</li><li>Be cautious uploading sensitive evidence to public online tools</li></ul><h2 id="file-security" tabindex="-1">File Security <a class="header-anchor" href="#file-security" aria-label="Permalink to &quot;File Security&quot;">​</a></h2><h3 id="encryption" tabindex="-1">Encryption <a class="header-anchor" href="#encryption" aria-label="Permalink to &quot;Encryption&quot;">​</a></h3><p><strong>End-to-End Encryption:</strong></p><p>All uploaded files are encrypted:</p><p><strong>Encryption Process:</strong></p><ol><li><strong>Upload:</strong> File encrypted in browser before transmission (TLS 1.3)</li><li><strong>Transit:</strong> Encrypted connection to servers</li><li><strong>Storage:</strong> AES-256 encryption at rest</li><li><strong>Access:</strong> Decrypted only when authorized user views</li></ol><p><strong>Learn More:</strong> <a href="/docs/security/overview">Security Overview</a></p><h3 id="virus-scanning" tabindex="-1">Virus Scanning <a class="header-anchor" href="#virus-scanning" aria-label="Permalink to &quot;Virus Scanning&quot;">​</a></h3><p><strong>Automatic Malware Detection:</strong></p><p>Every uploaded file scanned for viruses and malware:</p><p><strong>Scanning Process:</strong></p><ol><li>File uploaded to secure quarantine</li><li>Multiple antivirus engines scan file</li><li>Malware signatures checked</li><li>Behavioral analysis performed</li><li>If clean: file moved to permanent storage</li><li>If infected: upload rejected, reporter notified</li></ol><p><strong>Scanning Engines:</strong></p><ul><li>ClamAV (open source)</li><li>Commercial antivirus integration</li><li>Signature databases updated daily</li></ul><p><strong>Rejected File:</strong></p><p>If file contains malware:</p><ul><li>Upload immediately stopped</li><li>File deleted from quarantine</li><li>Reporter receives notification: <ul><li>&quot;File rejected due to security concerns&quot;</li><li>Virus/malware name (if identifiable)</li><li>Recommendation to scan file locally</li></ul></li><li>Can attempt upload after cleaning file</li></ul><p><strong>False Positives:</strong></p><p>Occasionally legitimate files flagged:</p><ul><li>Contact <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li>Provide file hash or description</li><li>Manual review possible</li><li>Whitelist if confirmed safe</li></ul><h3 id="access-controls" tabindex="-1">Access Controls <a class="header-anchor" href="#access-controls" aria-label="Permalink to &quot;Access Controls&quot;">​</a></h3><p><strong>Who Can View Files:</strong></p><p><strong>✅ Authorized Personnel:</strong></p><ul><li>Assigned investigators (case team)</li><li>Organization administrators (configurable)</li><li>Compliance officers (audit access)</li></ul><p><strong>❌ Unauthorized Access:</strong></p><ul><li>Other organization employees</li><li>Unassigned investigators</li><li>External parties</li><li>Disclosurely staff (zero-knowledge encryption)</li></ul><p><strong>Access Logging:</strong></p><p>All file access logged in <a href="/docs/features/audit-trail">audit trail</a>:</p><ul><li>Who accessed file</li><li>When accessed</li><li>From where (IP address)</li><li>Action taken (viewed, downloaded)</li><li>Duration of access</li></ul><p><strong>Download Controls:</strong></p><p><strong>Organization Settings:</strong></p><p>Configure file download permissions:</p><ul><li><strong>Prevent Downloads:</strong> Files viewable only (no download)</li><li><strong>Logged Downloads:</strong> Downloads allowed but logged</li><li><strong>Watermarked Downloads:</strong> Files stamped with accessor identity</li><li><strong>Unrestricted:</strong> Downloads allowed (default for most)</li></ul><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Security &gt; File Access</p><p><strong>Learn More:</strong> <a href="/docs/security/access-control">Access Control</a></p><h2 id="file-preview-viewing" tabindex="-1">File Preview &amp; Viewing <a class="header-anchor" href="#file-preview-viewing" aria-label="Permalink to &quot;File Preview &amp; Viewing&quot;">​</a></h2><h3 id="in-browser-preview" tabindex="-1">In-Browser Preview <a class="header-anchor" href="#in-browser-preview" aria-label="Permalink to &quot;In-Browser Preview&quot;">​</a></h3><p><strong>Supported Preview Formats:</strong></p><p><strong>Documents:</strong></p><ul><li>PDFs (inline viewer)</li><li>Office files (converted preview)</li><li>Text files (syntax highlighting)</li></ul><p><strong>Images:</strong></p><ul><li>All formats (lightbox viewer)</li><li>Zoom and pan</li><li>Fullscreen mode</li><li>Image rotation</li></ul><p><strong>Videos:</strong></p><ul><li>Inline video player</li><li>Playback controls</li><li>Fullscreen option</li><li>Frame-by-frame navigation</li></ul><p><strong>Audio:</strong></p><ul><li>Waveform visualization</li><li>Playback controls</li><li>Timestamp markers</li><li>Speed controls (0.5x, 1x, 1.5x, 2x)</li></ul><p><strong>No Download Required:</strong> Files can be reviewed entirely in browser without downloading.</p><h3 id="download-options" tabindex="-1">Download Options <a class="header-anchor" href="#download-options" aria-label="Permalink to &quot;Download Options&quot;">​</a></h3><p><strong>Downloading Files:</strong></p><p><strong>How to Download:</strong></p><ol><li>Navigate to report with evidence</li><li>Locate file in attachments section</li><li>Click <strong>&quot;Download&quot;</strong> button</li><li>File downloads to default folder</li></ol><p><strong>Bulk Download:</strong></p><p>Download all case files at once:</p><ol><li>Open case details</li><li>Click <strong>&quot;Download All Evidence&quot;</strong></li><li>Files packaged as ZIP archive</li><li>Includes folder structure</li><li>Metadata manifest included</li></ol><p><strong>Useful For:</strong></p><ul><li>External legal review</li><li>Archiving closed cases</li><li>Offline analysis</li><li>Evidence preservation</li></ul><p><strong>Download Audit:</strong></p><p>All downloads logged in <a href="/docs/features/audit-trail">audit trail</a> for compliance.</p><h3 id="annotation-tools-pro-enterprise" tabindex="-1">Annotation Tools (Pro/Enterprise) <a class="header-anchor" href="#annotation-tools-pro-enterprise" aria-label="Permalink to &quot;Annotation Tools (Pro/Enterprise)&quot;">​</a></h3><p><strong>Markup Evidence:</strong></p><p><strong>Available Tools:</strong></p><ul><li><strong>Highlight</strong> text or image areas</li><li><strong>Redact</strong> sensitive information</li><li><strong>Add comments</strong> to specific sections</li><li><strong>Draw</strong> arrows and shapes</li><li><strong>Add text boxes</strong></li></ul><p><strong>Use Cases:</strong></p><ul><li>Highlight important passages</li><li>Redact third-party PII before sharing</li><li>Mark specific evidence details</li><li>Annotate investigator notes</li></ul><p><strong>Version Control:</strong></p><ul><li>Original file preserved</li><li>Annotated version saved separately</li><li>Annotation history tracked</li><li>Multiple annotation versions possible</li></ul><p><strong>Access:</strong> Pro and Enterprise plans only</p><h2 id="file-organization" tabindex="-1">File Organization <a class="header-anchor" href="#file-organization" aria-label="Permalink to &quot;File Organization&quot;">​</a></h2><h3 id="folder-structure" tabindex="-1">Folder Structure <a class="header-anchor" href="#folder-structure" aria-label="Permalink to &quot;Folder Structure&quot;">​</a></h3><p><strong>Automatic Organization:</strong></p><p>Files automatically organized by:</p><p><strong>By Case:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/Case DIS-ABC123/</span></span>
<span class="line"><span>  /Initial Submission/</span></span>
<span class="line"><span>    - document1.pdf</span></span>
<span class="line"><span>    - photo1.jpg</span></span>
<span class="line"><span>  /Messages/</span></span>
<span class="line"><span>    - additional-evidence.xlsx</span></span>
<span class="line"><span>    - screenshot.png</span></span>
<span class="line"><span>  /Investigation/</span></span>
<span class="line"><span>    - investigator-notes.docx</span></span></code></pre></div><p><strong>By File Type:</strong></p><ul><li>Documents</li><li>Images</li><li>Videos</li><li>Audio</li><li>Other</li></ul><p><strong>By Date:</strong></p><ul><li>Chronological order</li><li>Upload timestamp preserved</li></ul><h3 id="search-filtering" tabindex="-1">Search &amp; Filtering <a class="header-anchor" href="#search-filtering" aria-label="Permalink to &quot;Search &amp; Filtering&quot;">​</a></h3><p><strong>Finding Files:</strong></p><p><strong>Access:</strong> Case &gt; Evidence &gt; Search Files</p><p><strong>Search Options:</strong></p><ul><li><strong>File name</strong> search</li><li><strong>File type</strong> filter (documents, images, etc.)</li><li><strong>Upload date</strong> range</li><li><strong>Uploaded by</strong> (reporter vs. investigator)</li><li><strong>File size</strong> range</li></ul><p><strong>Advanced Search (Pro/Enterprise):</strong></p><ul><li><strong>Full-text search</strong> within documents</li><li><strong>OCR search</strong> in images (scanned documents)</li><li><strong>Metadata search</strong> (if preserved for internal files)</li></ul><p><strong>Learn More:</strong> <a href="/docs/analytics/dashboard">Analytics Dashboard</a></p><h2 id="file-retention" tabindex="-1">File Retention <a class="header-anchor" href="#file-retention" aria-label="Permalink to &quot;File Retention&quot;">​</a></h2><h3 id="retention-policies" tabindex="-1">Retention Policies <a class="header-anchor" href="#retention-policies" aria-label="Permalink to &quot;Retention Policies&quot;">​</a></h3><p><strong>Evidence Retention:</strong></p><p>Files retained according to <a href="/docs/compliance/retention">data retention policies</a>:</p><p><strong>Default Retention:</strong></p><ul><li><strong>Active Cases:</strong> Retained until case closed</li><li><strong>Closed Cases:</strong> Retained per organization policy (typically 7 years)</li><li><strong>Archived Cases:</strong> Moved to long-term storage</li><li><strong>Deleted Cases:</strong> Permanent deletion after retention period</li></ul><p><strong>Legal Holds:</strong></p><p>Files under legal hold cannot be deleted:</p><ul><li>Retention period extended</li><li>Deletion blocked automatically</li><li><a href="/docs/features/audit-trail">Audit trail</a> logs hold status</li><li>Released when legal matter resolved</li></ul><p><strong>GDPR Considerations:</strong></p><p>Evidence in erasure requests:</p><ul><li>Cannot delete if legal obligation to retain</li><li>Partial erasure possible (reporter data, not evidence)</li><li>Documented justification required</li></ul><p><strong>Learn More:</strong></p><ul><li><a href="/docs/compliance/retention">Data Retention Policies</a></li><li><a href="/docs/features/gdpr-requests">GDPR Requests</a></li></ul><h3 id="storage-management" tabindex="-1">Storage Management <a class="header-anchor" href="#storage-management" aria-label="Permalink to &quot;Storage Management&quot;">​</a></h3><p><strong>Monitor Storage Usage:</strong></p><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Storage</p><p><strong>Storage Metrics:</strong></p><ul><li>Total storage used</li><li>Storage by file type</li><li>Storage by case</li><li>Storage growth trends</li><li>Projected storage needs</li></ul><p><strong>Storage Alerts:</strong></p><ul><li>80% capacity warning</li><li>90% capacity critical alert</li><li>Automatic email to administrators</li><li>Upgrade prompts</li></ul><p><strong>Storage Cleanup:</strong></p><p><strong>Reclaim Space:</strong></p><ul><li>Archive old cases</li><li>Delete draft reports</li><li>Remove duplicate files</li><li>Compress large videos</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="upload-failures" tabindex="-1">Upload Failures <a class="header-anchor" href="#upload-failures" aria-label="Permalink to &quot;Upload Failures&quot;">​</a></h3><p><strong>Common Issues:</strong></p><p><strong>1. File Too Large</strong></p><ul><li><strong>Error:</strong> &quot;File exceeds maximum size&quot;</li><li><strong>Solution:</strong> Compress file or upgrade plan</li><li><strong>Compression tools:</strong> WinZip, 7-Zip, online compressors</li></ul><p><strong>2. Unsupported File Type</strong></p><ul><li><strong>Error:</strong> &quot;File type not supported&quot;</li><li><strong>Solution:</strong> Convert to supported format or contact support</li><li><strong>Conversion tools:</strong> CloudConvert, Zamzar, Adobe Acrobat</li></ul><p><strong>3. Network Timeout</strong></p><ul><li><strong>Error:</strong> &quot;Upload failed - connection lost&quot;</li><li><strong>Solution:</strong><ul><li>Retry upload (resumable from where it stopped)</li><li>Use wired connection</li><li>Upload smaller files</li><li>Check internet stability</li></ul></li></ul><p><strong>4. Virus Detected</strong></p><ul><li><strong>Error:</strong> &quot;File rejected - security threat detected&quot;</li><li><strong>Solution:</strong><ul><li>Scan file locally with antivirus</li><li>Clean or replace file</li><li>Contact support if false positive</li></ul></li></ul><p><strong>5. Browser Issues</strong></p><ul><li><strong>Error:</strong> Upload stuck at 0% or 100%</li><li><strong>Solution:</strong><ul><li>Hard refresh page (Ctrl+F5)</li><li>Clear browser cache</li><li>Try different browser</li><li>Disable browser extensions</li><li>Update browser to latest version</li></ul></li></ul><h3 id="file-not-displaying" tabindex="-1">File Not Displaying <a class="header-anchor" href="#file-not-displaying" aria-label="Permalink to &quot;File Not Displaying&quot;">​</a></h3><p><strong>Preview Issues:</strong></p><p><strong>1. Corrupted File</strong></p><ul><li>Try downloading and opening locally</li><li>Re-upload file if corrupted</li></ul><p><strong>2. Unsupported Preview</strong></p><ul><li>Some files can&#39;t preview in browser</li><li>Download file to view locally</li></ul><p><strong>3. Browser Compatibility</strong></p><ul><li>Update browser to latest version</li><li>Try Chrome, Firefox, or Edge</li><li>Clear browser cache</li></ul><p><strong>4. Slow Loading</strong></p><ul><li>Large files take time to generate preview</li><li>Wait 30-60 seconds</li><li>Refresh if needed</li></ul><h3 id="download-problems" tabindex="-1">Download Problems <a class="header-anchor" href="#download-problems" aria-label="Permalink to &quot;Download Problems&quot;">​</a></h3><p><strong>Can&#39;t Download File:</strong></p><p><strong>1. Permissions</strong></p><ul><li>Verify you have download permissions</li><li>Contact administrator if access denied</li></ul><p><strong>2. Browser Blocking</strong></p><ul><li>Check browser download settings</li><li>Disable pop-up blockers temporarily</li><li>Allow downloads from disclosurely.com</li></ul><p><strong>3. Network Issues</strong></p><ul><li>Large files may timeout</li><li>Try again later</li><li>Use wired connection for large downloads</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="for-reporters" tabindex="-1">For Reporters <a class="header-anchor" href="#for-reporters" aria-label="Permalink to &quot;For Reporters&quot;">​</a></h3><p>✅ <strong>Organize Before Uploading:</strong></p><ul><li>Rename files descriptively (not &quot;IMG_1234.jpg&quot;)</li><li>Group related evidence in folders (then ZIP)</li><li>Create file list documenting each attachment</li></ul><p>✅ <strong>File Quality:</strong></p><ul><li>High enough quality to be useful evidence</li><li>Not so high that files are unnecessarily large</li><li>Compress videos without losing critical detail</li></ul><p>✅ <strong>Privacy Protection:</strong></p><ul><li>Review files for unintended sensitive information</li><li>Redact third-party personal information</li><li>Use <a href="/docs/features/anonymous-reporting">anonymous reporting</a> if needed</li><li>Don&#39;t include your name in file names</li></ul><p>✅ <strong>Descriptions:</strong></p><ul><li>Add description to each file</li><li>Explain what file shows</li><li>Note date/context if not obvious</li><li>Reference in report narrative</li></ul><h3 id="for-investigators" tabindex="-1">For Investigators <a class="header-anchor" href="#for-investigators" aria-label="Permalink to &quot;For Investigators&quot;">​</a></h3><p>✅ <strong>Evidence Integrity:</strong></p><ul><li>Download files immediately upon receipt</li><li>Create backup copy for legal purposes</li><li>Document chain of custody</li><li>Note any file modifications in <a href="/docs/features/audit-trail">audit trail</a></li></ul><p>✅ <strong>File Analysis:</strong></p><ul><li>Review all evidence thoroughly</li><li>Use annotation tools to mark key items</li><li>Cross-reference evidence with report details</li><li>Note discrepancies or questions</li></ul><p>✅ <strong>Sharing Evidence:</strong></p><ul><li>Redact before sharing externally</li><li>Use <a href="/docs/security/access-control">access controls</a> appropriately</li><li>Share via secure methods only</li><li>Log all evidence sharing</li></ul><h2 id="related-features" tabindex="-1">Related Features <a class="header-anchor" href="#related-features" aria-label="Permalink to &quot;Related Features&quot;">​</a></h2><h3 id="anonymous-reporting" tabindex="-1">Anonymous Reporting <a class="header-anchor" href="#anonymous-reporting" aria-label="Permalink to &quot;Anonymous Reporting&quot;">​</a></h3><p><strong>Anonymous File Uploads:</strong></p><p>Files uploaded in anonymous reports:</p><ul><li>Metadata automatically stripped</li><li>No reporter identification preserved</li><li>File access limited to case team</li><li><a href="/docs/features/audit-trail">Audit trail</a> doesn&#39;t link to reporter identity</li></ul><p><strong>Learn More:</strong> <a href="/docs/features/anonymous-reporting">Anonymous Reporting</a></p><h3 id="secure-messaging" tabindex="-1">Secure Messaging <a class="header-anchor" href="#secure-messaging" aria-label="Permalink to &quot;Secure Messaging&quot;">​</a></h3><p><strong>Evidence via Messages:</strong></p><p>Upload additional evidence through <a href="/docs/reporting/secure-messaging">secure messaging</a>:</p><ul><li>Same security as initial submission</li><li>Supports all file types</li><li>Integrated with case timeline</li><li>Notifies investigators automatically</li></ul><p><strong>Learn More:</strong> <a href="/docs/reporting/secure-messaging">Secure Messaging</a></p><h3 id="audit-trail" tabindex="-1">Audit Trail <a class="header-anchor" href="#audit-trail" aria-label="Permalink to &quot;Audit Trail&quot;">​</a></h3><p><strong>File Access Logging:</strong></p><p>All file activity logged:</p><ul><li>Uploads</li><li>Views</li><li>Downloads</li><li>Modifications</li><li>Deletions</li><li>Access attempts (authorized and unauthorized)</li></ul><p><strong>Learn More:</strong> <a href="/docs/features/audit-trail">Audit Trail</a></p><h2 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">​</a></h2><ul><li><a href="/docs/reporting/how-to-submit">How to Submit a Report</a> - Full submission guide</li><li><a href="/docs/reporting/secure-messaging">Secure Messaging</a> - Post-submission uploads</li><li><a href="/docs/features/anonymous-reporting">Anonymous Reporting</a> - Anonymous file uploads</li><li><a href="/docs/security/overview">Security Overview</a> - File encryption details</li><li><a href="/docs/features/audit-trail">Audit Trail</a> - File access logging</li><li><a href="/docs/features/subscription-tiers">Subscription Tiers</a> - Storage limits by plan</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Questions about file uploads?</p><ul><li>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="/docs/support/faqs">FAQs</a></li><li>File type requests: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li></ul>`,331)])])}const m=e(r,[["render",s]]);export{c as __pageData,m as default};
