import{_ as e,c as i,o as n,ag as t}from"./chunks/framework.4QX5UEtQ.js";const d=JSON.parse('{"title":"Internationalization & Multi-Language Support | Disclosurely","description":"Serve global workforces with comprehensive multi-language whistleblowing portals supporting 12+ languages with auto-detection, language-specific links, and real-time translation.","frontmatter":{"title":"Internationalization & Multi-Language Support | Disclosurely","description":"Serve global workforces with comprehensive multi-language whistleblowing portals supporting 12+ languages with auto-detection, language-specific links, and real-time translation.","head":[["meta",{"property":"og:title","content":"Internationalization & Multi-Language Support - Disclosurely"}],["meta",{"property":"og:description","content":"Serve global workforces with comprehensive multi-language whistleblowing portals supporting 12+ languages with auto-detection, language-specific links, and real-time translation."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Internationalization - Disclosurely"}],["meta",{"name":"twitter:description","content":"Serve global workforces with comprehensive multi-language whistleblowing portals supporting 12+ languages with auto-detection, language-specific links, and real-time translation."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"features/internationalization.md","filePath":"features/internationalization.md"}'),l={name:"features/internationalization.md"};function o(r,a,s,g,u,c){return n(),i("div",null,[...a[0]||(a[0]=[t(`<h1 id="internationalization-multi-language-support" tabindex="-1">Internationalization &amp; Multi-Language Support <a class="header-anchor" href="#internationalization-multi-language-support" aria-label="Permalink to &quot;Internationalization &amp; Multi-Language Support&quot;">â€‹</a></h1><p>Empower global workforces to report in their native language with comprehensive internationalization features supporting 12+ languages, automatic detection, and seamless translation.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">â€‹</a></h2><p>Disclosurely&#39;s internationalization (i18n) system ensures that whistleblowers worldwide can submit reports comfortably in their preferred language. This removes language barriers that might otherwise prevent important disclosures and demonstrates your organization&#39;s commitment to inclusivity.</p><p><strong>Supported Languages:</strong> 12+ languages with continuous expansion</p><p><strong>Access:</strong> Automatically available on all plans</p><p><strong>Learn More:</strong> <a href="/docs/admin/link-generator">Link Generator</a> for language-specific portals</p><h2 id="supported-languages" tabindex="-1">Supported Languages <a class="header-anchor" href="#supported-languages" aria-label="Permalink to &quot;Supported Languages&quot;">â€‹</a></h2><h3 id="currently-available" tabindex="-1">Currently Available <a class="header-anchor" href="#currently-available" aria-label="Permalink to &quot;Currently Available&quot;">â€‹</a></h3><p>Disclosurely supports the following languages with full translations:</p><p><strong>Western European:</strong></p><ul><li>ğŸ‡¬ğŸ‡§ <strong>English</strong> - Default language</li><li>ğŸ‡ªğŸ‡¸ <strong>Spanish (EspaÃ±ol)</strong> - Spain and Latin America variants</li><li>ğŸ‡«ğŸ‡· <strong>French (FranÃ§ais)</strong> - France and Canadian French</li><li>ğŸ‡©ğŸ‡ª <strong>German (Deutsch)</strong> - Germany, Austria, Switzerland</li><li>ğŸ‡®ğŸ‡¹ <strong>Italian (Italiano)</strong></li><li>ğŸ‡µğŸ‡¹ <strong>Portuguese (PortuguÃªs)</strong> - Portugal and Brazilian variants</li><li>ğŸ‡³ğŸ‡± <strong>Dutch (Nederlands)</strong> - Netherlands and Belgian variants</li></ul><p><strong>Nordic:</strong></p><ul><li>ğŸ‡¸ğŸ‡ª <strong>Swedish (Svenska)</strong></li><li>ğŸ‡³ğŸ‡´ <strong>Norwegian (Norsk)</strong> - BokmÃ¥l variant</li><li>ğŸ‡©ğŸ‡° <strong>Danish (Dansk)</strong></li></ul><p><strong>Eastern European:</strong></p><ul><li>ğŸ‡µğŸ‡± <strong>Polish (Polski)</strong></li></ul><p><strong>Mediterranean:</strong></p><ul><li>ğŸ‡¬ğŸ‡· <strong>Greek (Î•Î»Î»Î·Î½Î¹ÎºÎ¬)</strong></li></ul><h3 id="coming-soon" tabindex="-1">Coming Soon <a class="header-anchor" href="#coming-soon" aria-label="Permalink to &quot;Coming Soon&quot;">â€‹</a></h3><p><strong>Priority Languages (Q2 2025):</strong></p><ul><li>Finnish (Suomi)</li><li>Czech (ÄŒeÅ¡tina)</li><li>Romanian (RomÃ¢nÄƒ)</li><li>Hungarian (Magyar)</li></ul><p><strong>Planned Expansion (2025-2026):</strong></p><ul><li>Croatian (Hrvatski)</li><li>Slovak (SlovenÄina)</li><li>Bulgarian (Ğ‘ÑŠĞ»Ğ³Ğ°Ñ€ÑĞºĞ¸)</li><li>Estonian (Eesti)</li><li>Latvian (LatvieÅ¡u)</li><li>Lithuanian (LietuviÅ³)</li></ul><p><strong>Global Expansion:</strong></p><ul><li>Japanese (æ—¥æœ¬èª)</li><li>Chinese Simplified (ç®€ä½“ä¸­æ–‡)</li><li>Chinese Traditional (ç¹é«”ä¸­æ–‡)</li><li>Korean (í•œêµ­ì–´)</li><li>Arabic (Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©)</li></ul><h3 id="request-additional-languages" tabindex="-1">Request Additional Languages <a class="header-anchor" href="#request-additional-languages" aria-label="Permalink to &quot;Request Additional Languages&quot;">â€‹</a></h3><p><strong>Need a language not listed?</strong></p><p>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a> with:</p><ul><li>Language requested</li><li>Number of employees speaking this language</li><li>Business justification</li><li>Timeline requirements</li></ul><p><strong>Enterprise customers</strong> receive priority for custom language additions.</p><h2 id="how-language-selection-works" tabindex="-1">How Language Selection Works <a class="header-anchor" href="#how-language-selection-works" aria-label="Permalink to &quot;How Language Selection Works&quot;">â€‹</a></h2><h3 id="automatic-language-detection" tabindex="-1">Automatic Language Detection <a class="header-anchor" href="#automatic-language-detection" aria-label="Permalink to &quot;Automatic Language Detection&quot;">â€‹</a></h3><p><strong>Intelligent Detection:</strong></p><p>When a reporter accesses your reporting portal, Disclosurely automatically detects their preferred language using:</p><p><strong>Detection Methods (Priority Order):</strong></p><ol><li><strong>URL Parameter</strong> - <code>?lang=es</code> overrides all other detection</li><li><strong>Link-Specific Language</strong> - Language configured on <a href="/docs/admin/link-generator">custom reporting link</a></li><li><strong>Browser Language</strong> - Reads <code>Accept-Language</code> header from browser</li><li><strong>Geographic Location</strong> - IP-based country detection (fallback only)</li><li><strong>Organization Default</strong> - Configured default language</li></ol><p><strong>Example:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Reporter in Spain with Spanish browser:</span></span>
<span class="line"><span>â†’ Automatically loads Spanish interface</span></span>
<span class="line"><span>â†’ No manual selection required</span></span></code></pre></div><p><strong>Override Anytime:</strong> Reporters can manually change language using language selector in portal header.</p><h3 id="manual-language-selection" tabindex="-1">Manual Language Selection <a class="header-anchor" href="#manual-language-selection" aria-label="Permalink to &quot;Manual Language Selection&quot;">â€‹</a></h3><p><strong>Language Selector:</strong></p><p>Visible on all reporting portal pages:</p><p><strong>Location:</strong> Top-right corner of navigation bar</p><p><strong>How to Use:</strong></p><ol><li>Click globe icon or language name</li><li>Dropdown shows all available languages</li><li>Select preferred language</li><li>Entire interface instantly switches</li><li>Selection saved for session</li><li>Cookie preserves selection for future visits</li></ol><p><strong>Persists Across:</strong></p><ul><li>Form navigation (multi-step forms)</li><li>Draft saving and resuming</li><li>Report tracking lookups</li><li>Secure messaging</li></ul><h3 id="language-specific-reporting-links" tabindex="-1">Language-Specific Reporting Links <a class="header-anchor" href="#language-specific-reporting-links" aria-label="Permalink to &quot;Language-Specific Reporting Links&quot;">â€‹</a></h3><p><strong>Enforce Specific Languages:</strong></p><p>Using the <a href="/docs/admin/link-generator">Link Generator</a>, create reporting links that force a specific language:</p><p><strong>Use Cases:</strong></p><ul><li>Regional office links (French for Paris office)</li><li>Department-specific portals (Spanish for Latin America division)</li><li>Training materials (match training language)</li><li>Compliance campaigns (target specific populations)</li></ul><p><strong>How to Configure:</strong></p><ol><li>Navigate to <strong>Dashboard &gt; Links &gt; Create New Link</strong></li><li>Expand <strong>&quot;Language Settings&quot;</strong> section</li><li>Select <strong>&quot;Force Specific Language&quot;</strong></li><li>Choose language from dropdown</li><li>Save link configuration</li></ol><p><strong>Generated Link:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://yourorg.disclosurely.com/report?link=DEPT001&amp;lang=es</span></span></code></pre></div><p><strong>Behavior:</strong></p><ul><li>Reporter cannot change language (selector hidden)</li><li>Entire experience in specified language</li><li>Emails and notifications in that language</li><li>Prevents accidental language switching</li></ul><p><strong>Learn More:</strong> <a href="/docs/admin/link-generator">Link Generator &amp; Management</a></p><h2 id="what-gets-translated" tabindex="-1">What Gets Translated <a class="header-anchor" href="#what-gets-translated" aria-label="Permalink to &quot;What Gets Translated&quot;">â€‹</a></h2><h3 id="full-interface-translation" tabindex="-1">Full Interface Translation <a class="header-anchor" href="#full-interface-translation" aria-label="Permalink to &quot;Full Interface Translation&quot;">â€‹</a></h3><p><strong>Reporting Portal:</strong></p><ul><li>Navigation menus</li><li>Form labels and placeholders</li><li>Field descriptions and tooltips</li><li>Error messages and validation</li><li>Success confirmations</li><li>Help text and instructions</li><li>Privacy policy and terms</li><li>FAQ and support pages</li></ul><p><strong>Report Submission:</strong></p><ul><li>Category names and descriptions</li><li>Priority level descriptions</li><li>Incident type options</li><li>Custom field labels</li><li>File upload instructions</li><li>Draft saving messages</li><li>Submission confirmation</li></ul><p><strong>Report Tracking:</strong></p><ul><li>Status descriptions</li><li>Timeline labels</li><li>Case updates</li><li>Investigation progress messages</li><li>Resolution notifications</li></ul><p><strong>Secure Messaging:</strong></p><ul><li>Messaging interface</li><li>System-generated messages</li><li>Placeholder text</li><li>Attachment instructions</li><li>Read receipts</li></ul><h3 id="email-notifications" tabindex="-1">Email Notifications <a class="header-anchor" href="#email-notifications" aria-label="Permalink to &quot;Email Notifications&quot;">â€‹</a></h3><p><strong>Automated Emails:</strong></p><p>All system-generated emails are translated:</p><p><strong>Reporter Emails:</strong></p><ul><li>Submission confirmation</li><li>Report ID and tracking instructions</li><li>Status change notifications</li><li>New message alerts</li><li>Investigation updates</li><li>Case resolution notices</li></ul><p><strong>Administrator Emails:</strong></p><ul><li>New case notifications</li><li>Assignment alerts</li><li>SLA breach warnings</li><li>Message from reporter alerts</li></ul><p><strong>Email Language Determination:</strong></p><ol><li>Reporter&#39;s selected language (if known)</li><li>Link-specific language configuration</li><li>Organization default language</li></ol><p><strong>Custom Templates:</strong></p><p>Organizations can customize email templates per language:</p><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Notifications &gt; Email Templates</p><p><strong>Customization:</strong></p><ul><li>Edit subject lines per language</li><li>Customize email body content</li><li>Add language-specific legal disclaimers</li><li>Include regional contact information</li><li>Adjust tone for cultural appropriateness</li></ul><h3 id="administrator-interface" tabindex="-1">Administrator Interface <a class="header-anchor" href="#administrator-interface" aria-label="Permalink to &quot;Administrator Interface&quot;">â€‹</a></h3><p><strong>Dashboard Language:</strong></p><p>Administrators can select their preferred dashboard language independently:</p><p><strong>Setting Location:</strong> Dashboard &gt; Profile &gt; Language Preference</p><p><strong>Available For:</strong></p><ul><li>Dashboard navigation</li><li>Case management interface</li><li>Analytics and reporting</li><li>Settings and configuration</li><li>Help documentation</li></ul><p><strong>Note:</strong> Administrator language does not affect reporter-facing content.</p><h2 id="content-that-remains-untranslated" tabindex="-1">Content That Remains Untranslated <a class="header-anchor" href="#content-that-remains-untranslated" aria-label="Permalink to &quot;Content That Remains Untranslated&quot;">â€‹</a></h2><h3 id="user-generated-content" tabindex="-1">User-Generated Content <a class="header-anchor" href="#user-generated-content" aria-label="Permalink to &quot;User-Generated Content&quot;">â€‹</a></h3><p><strong>Not Automatically Translated:</strong></p><p>The following content remains in the original language submitted:</p><p><strong>Report Content:</strong></p><ul><li>Report titles and descriptions</li><li>Incident details</li><li>Evidence file names and descriptions</li><li>Witness information</li><li>Additional comments</li></ul><p><strong>Secure Messages:</strong></p><ul><li>Messages from reporters</li><li>Responses from investigators</li><li>Case notes and comments</li></ul><p><strong>Rationale:</strong></p><ul><li>Preserves original meaning and context</li><li>Avoids translation errors affecting investigations</li><li>Maintains legal accuracy</li><li>Respects reporter&#39;s language choice</li></ul><h3 id="real-time-translation-coming-soon" tabindex="-1">Real-Time Translation (Coming Soon) <a class="header-anchor" href="#real-time-translation-coming-soon" aria-label="Permalink to &quot;Real-Time Translation (Coming Soon)&quot;">â€‹</a></h3><p><strong>Planned Feature (Q3 2025):</strong></p><p>Optional AI-powered translation for secure messages:</p><p><strong>How It Will Work:</strong></p><ol><li>Reporter submits message in Spanish</li><li>System offers translation to English</li><li>Investigator sees original + translation</li><li>Investigator responds in English</li><li>Reporter sees original + Spanish translation</li></ol><p><strong>Benefits:</strong></p><ul><li>Cross-language communication</li><li>Preserve original messages</li><li>No message meaning lost in translation</li><li>Voluntary (can view original anytime)</li></ul><p><strong>Privacy:</strong> Translations performed in-memory, not stored permanently</p><p><strong>Interest List:</strong> Email <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a> to join beta</p><h3 id="custom-content" tabindex="-1">Custom Content <a class="header-anchor" href="#custom-content" aria-label="Permalink to &quot;Custom Content&quot;">â€‹</a></h3><p><strong>Organization-Specific Content:</strong></p><p>Requires manual translation:</p><p><strong>Custom Fields:</strong></p><ul><li>Custom form field labels</li><li>Custom categories</li><li>Custom workflows</li><li>Custom email templates</li><li>Custom legal disclaimers</li></ul><p><strong>How to Add Translations:</strong></p><ol><li>Navigate to <strong>Dashboard &gt; Settings &gt; Custom Content</strong></li><li>Select content item to translate</li><li>Click <strong>&quot;Add Translation&quot;</strong></li><li>Select language</li><li>Enter translated text</li><li>Save and publish</li></ol><p><strong>Professional Translation:</strong></p><p>For critical content, we recommend:</p><ul><li>Professional translation services</li><li>Legal review for compliance language</li><li>Native speaker verification</li><li>Cultural appropriateness review</li></ul><p><strong>Learn More:</strong> <a href="/docs/admin/organization-settings">Organization Settings</a></p><h2 id="regional-variants" tabindex="-1">Regional Variants <a class="header-anchor" href="#regional-variants" aria-label="Permalink to &quot;Regional Variants&quot;">â€‹</a></h2><h3 id="dialect-support" tabindex="-1">Dialect Support <a class="header-anchor" href="#dialect-support" aria-label="Permalink to &quot;Dialect Support&quot;">â€‹</a></h3><p><strong>Language Variants Supported:</strong></p><p><strong>Spanish:</strong></p><ul><li>ğŸ‡ªğŸ‡¸ Spain (EspaÃ±ol de EspaÃ±a)</li><li>ğŸ‡²ğŸ‡½ Latin America (EspaÃ±ol Latinoamericano)</li></ul><p><strong>Key Differences:</strong></p><ul><li>Formal vs. informal address (tÃº vs. usted)</li><li>Vocabulary differences (ordenador vs. computadora)</li><li>Regional terminology preferences</li></ul><p><strong>French:</strong></p><ul><li>ğŸ‡«ğŸ‡· France (FranÃ§ais)</li><li>ğŸ‡¨ğŸ‡¦ Canada (FranÃ§ais Canadien)</li></ul><p><strong>Key Differences:</strong></p><ul><li>Terminology (courriel vs. email)</li><li>Formality levels</li><li>Regional expressions</li></ul><p><strong>Portuguese:</strong></p><ul><li>ğŸ‡µğŸ‡¹ Portugal (PortuguÃªs Europeu)</li><li>ğŸ‡§ğŸ‡· Brazil (PortuguÃªs Brasileiro)</li></ul><p><strong>Key Differences:</strong></p><ul><li>Spelling variations</li><li>Vocabulary differences</li><li>Formal address conventions</li></ul><p><strong>Dutch:</strong></p><ul><li>ğŸ‡³ğŸ‡± Netherlands (Nederlands)</li><li>ğŸ‡§ğŸ‡ª Belgium (Vlaams)</li></ul><p><strong>Key Differences:</strong></p><ul><li>Formality and politeness</li><li>Terminology preferences</li></ul><h3 id="date-time-and-number-formatting" tabindex="-1">Date, Time, and Number Formatting <a class="header-anchor" href="#date-time-and-number-formatting" aria-label="Permalink to &quot;Date, Time, and Number Formatting&quot;">â€‹</a></h3><p><strong>Automatic Localization:</strong></p><p>Dates, times, and numbers format according to language/region:</p><p><strong>Date Formats:</strong></p><ul><li><strong>US English:</strong> 03/15/2025 (MM/DD/YYYY)</li><li><strong>UK English:</strong> 15/03/2025 (DD/MM/YYYY)</li><li><strong>German:</strong> 15.03.2025 (DD.MM.YYYY)</li><li><strong>Swedish:</strong> 2025-03-15 (YYYY-MM-DD)</li></ul><p><strong>Time Formats:</strong></p><ul><li><strong>US:</strong> 3:30 PM (12-hour)</li><li><strong>Most European:</strong> 15:30 (24-hour)</li></ul><p><strong>Number Formats:</strong></p><ul><li><strong>English:</strong> 1,234.56 (comma separator, period decimal)</li><li><strong>German/Spanish:</strong> 1.234,56 (period separator, comma decimal)</li><li><strong>French:</strong> 1 234,56 (space separator, comma decimal)</li></ul><p><strong>Currency:</strong></p><ul><li>Displays in organization&#39;s configured currency</li><li>Symbol placement varies by language (â‚¬100 vs 100â‚¬)</li></ul><h2 id="language-configuration" tabindex="-1">Language Configuration <a class="header-anchor" href="#language-configuration" aria-label="Permalink to &quot;Language Configuration&quot;">â€‹</a></h2><h3 id="organization-default-language" tabindex="-1">Organization Default Language <a class="header-anchor" href="#organization-default-language" aria-label="Permalink to &quot;Organization Default Language&quot;">â€‹</a></h3><p><strong>Setting Default:</strong></p><p>Configure your organization&#39;s primary language:</p><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Organization &gt; Default Language</p><p><strong>Purpose:</strong></p><ul><li>Fallback when detection fails</li><li>Default for administrator interface</li><li>Email template defaults</li><li>New user account defaults</li></ul><p><strong>Recommendation:</strong></p><ul><li>Choose language of majority workforce</li><li>Match corporate communications language</li><li>Consider regulatory requirements</li></ul><h3 id="language-availability" tabindex="-1">Language Availability <a class="header-anchor" href="#language-availability" aria-label="Permalink to &quot;Language Availability&quot;">â€‹</a></h3><p><strong>Enable/Disable Languages:</strong></p><p>Control which languages are available to reporters:</p><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Organization &gt; Available Languages</p><p><strong>Options:</strong></p><ul><li><strong>All Languages</strong> - Allow any supported language (recommended)</li><li><strong>Specific Languages</strong> - Limit to selected subset</li><li><strong>Single Language</strong> - Force one language only</li></ul><p><strong>Use Cases for Restrictions:</strong></p><ul><li>Small regional organizations (single country)</li><li>Reduce complexity for investigators</li><li>Compliance with local regulations</li><li>Match available support capabilities</li></ul><p><strong>Warning:</strong> Restricting languages may reduce reporting comfort and accessibility.</p><h3 id="link-specific-language-settings" tabindex="-1">Link-Specific Language Settings <a class="header-anchor" href="#link-specific-language-settings" aria-label="Permalink to &quot;Link-Specific Language Settings&quot;">â€‹</a></h3><p><strong>Per-Link Configuration:</strong></p><p>Each <a href="/docs/admin/link-generator">custom reporting link</a> can have unique language settings:</p><p><strong>Configuration Options:</strong></p><p><strong>1. Language Detection (Default)</strong></p><ul><li>Auto-detects reporter&#39;s language</li><li>Allows manual switching</li><li>Most flexible option</li></ul><p><strong>2. Suggested Language</strong></p><ul><li>Auto-selects specific language</li><li>Allows reporter to change</li><li>Gentle guidance</li></ul><p><strong>3. Forced Language</strong></p><ul><li>Enforces specific language</li><li>Hides language selector</li><li>Guarantees language consistency</li></ul><p><strong>4. Restricted Languages</strong></p><ul><li>Limit to subset (e.g., only French and English)</li><li>Useful for bilingual campaigns</li><li>Reduces choice complexity</li></ul><p><strong>Access:</strong> Dashboard &gt; Links &gt; Edit Link &gt; Language Settings</p><h2 id="translation-quality" tabindex="-1">Translation Quality <a class="header-anchor" href="#translation-quality" aria-label="Permalink to &quot;Translation Quality&quot;">â€‹</a></h2><h3 id="professional-translation" tabindex="-1">Professional Translation <a class="header-anchor" href="#professional-translation" aria-label="Permalink to &quot;Professional Translation&quot;">â€‹</a></h3><p><strong>Translation Process:</strong></p><p>All Disclosurely interface translations undergo rigorous quality assurance:</p><p><strong>Steps:</strong></p><ol><li><strong>Initial Translation</strong> - Professional translators (native speakers)</li><li><strong>Legal Review</strong> - Compliance terminology verification</li><li><strong>Cultural Adaptation</strong> - Localization for regional appropriateness</li><li><strong>User Testing</strong> - Native speaker usability testing</li><li><strong>Iterative Refinement</strong> - Continuous improvement based on feedback</li></ol><p><strong>Quality Standards:</strong></p><ul><li>Native speaker translators</li><li>Subject matter expertise (legal, compliance)</li><li>Consistency across all content</li><li>Regular updates and improvements</li></ul><h3 id="terminology-consistency" tabindex="-1">Terminology Consistency <a class="header-anchor" href="#terminology-consistency" aria-label="Permalink to &quot;Terminology Consistency&quot;">â€‹</a></h3><p><strong>Glossary Management:</strong></p><p>Key terms translated consistently throughout platform:</p><p><strong>Examples:</strong></p><ul><li>&quot;Whistleblower&quot; â†’ &quot;Informante&quot; (Spanish), &quot;Lanceur d&#39;alerte&quot; (French)</li><li>&quot;Investigation&quot; â†’ &quot;InvestigaciÃ³n&quot; (Spanish), &quot;EnquÃªte&quot; (French)</li><li>&quot;Confidential&quot; â†’ &quot;Confidencial&quot; (Spanish), &quot;Confidentiel&quot; (French)</li></ul><p><strong>Legal Terminology:</strong></p><p>Special attention to legal and compliance terms:</p><ul><li>GDPR terminology (official EU translations)</li><li>Regulatory compliance terms</li><li>Legal rights and obligations</li><li>Privacy and data protection</li></ul><h3 id="translation-feedback" tabindex="-1">Translation Feedback <a class="header-anchor" href="#translation-feedback" aria-label="Permalink to &quot;Translation Feedback&quot;">â€‹</a></h3><p><strong>Report Translation Issues:</strong></p><p>Help improve translation quality:</p><p><strong>How to Report:</strong></p><ol><li>Email: <a href="mailto:translations@disclosurely.com" target="_blank" rel="noreferrer">translations@disclosurely.com</a></li><li>Include: <ul><li>Language and page/feature</li><li>Current translation</li><li>Suggested improvement</li><li>Context/explanation</li></ul></li><li>Screenshots helpful</li></ol><p><strong>Community Contribution:</strong></p><p>Enterprise customers can contribute to translation improvements:</p><ul><li>Review and suggest edits</li><li>Provide cultural context</li><li>Validate terminology</li><li>Test new features in their language</li></ul><p><strong>Response Time:</strong> Translation issues typically addressed within 2-4 weeks</p><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">â€‹</a></h2><h3 id="for-global-organizations" tabindex="-1">For Global Organizations <a class="header-anchor" href="#for-global-organizations" aria-label="Permalink to &quot;For Global Organizations&quot;">â€‹</a></h3><p>âœ… <strong>Enable All Languages:</strong></p><ul><li>Don&#39;t restrict language availability</li><li>Workforce language diversity often underestimated</li><li>Low cost to offering more options</li></ul><p>âœ… <strong>Create Language-Specific Links:</strong></p><ul><li>French link for French offices</li><li>Spanish link for Latin America division</li><li>Improves user experience and adoption</li></ul><p>âœ… <strong>Translate Custom Content:</strong></p><ul><li>Custom categories in all languages</li><li>Custom email templates localized</li><li>Legal disclaimers professionally translated</li></ul><p>âœ… <strong>Monitor Language Usage:</strong></p><ul><li>Track which languages used most</li><li>Identify underserved populations</li><li>Adjust communication strategies</li></ul><p>âœ… <strong>Train Investigators:</strong></p><ul><li>Ensure multilingual support capability</li><li>Use translation services if needed</li><li>Respect reporter&#39;s language choice</li></ul><h3 id="for-regional-organizations" tabindex="-1">For Regional Organizations <a class="header-anchor" href="#for-regional-organizations" aria-label="Permalink to &quot;For Regional Organizations&quot;">â€‹</a></h3><p>âœ… <strong>Set Appropriate Default:</strong></p><ul><li>Match primary workforce language</li><li>Reduces friction for most users</li></ul><p>âœ… <strong>Consider Bilingual Portals:</strong></p><ul><li>Offer 2-3 most relevant languages</li><li>Common in multilingual countries</li><li>Demonstrates inclusivity</li></ul><p>âœ… <strong>Cultural Sensitivity:</strong></p><ul><li>Understand reporting cultural norms</li><li>Adjust messaging tone accordingly</li><li>Consider anonymity preferences by culture</li></ul><h2 id="analytics-insights" tabindex="-1">Analytics &amp; Insights <a class="header-anchor" href="#analytics-insights" aria-label="Permalink to &quot;Analytics &amp; Insights&quot;">â€‹</a></h2><h3 id="language-usage-metrics" tabindex="-1">Language Usage Metrics <a class="header-anchor" href="#language-usage-metrics" aria-label="Permalink to &quot;Language Usage Metrics&quot;">â€‹</a></h3><p><strong>Track Language Preferences:</strong></p><p>Understand your workforce&#39;s language needs:</p><p><strong>Available Metrics:</strong></p><p><strong>Dashboard &gt; Analytics &gt; Languages:</strong></p><ul><li>Reports submitted by language</li><li>Language distribution (pie chart)</li><li>Language trends over time</li><li>Most common language pairs</li><li>Geographic distribution</li></ul><p><strong>Insights:</strong></p><ul><li>Identify underserved language groups</li><li>Validate language availability decisions</li><li>Measure effectiveness of language-specific campaigns</li><li>Optimize link distribution strategies</li></ul><p><strong>Privacy:</strong> Language analytics are aggregated onlyâ€”no individual reporter identification</p><h3 id="campaign-effectiveness" tabindex="-1">Campaign Effectiveness <a class="header-anchor" href="#campaign-effectiveness" aria-label="Permalink to &quot;Campaign Effectiveness&quot;">â€‹</a></h3><p><strong>Language-Specific Campaigns:</strong></p><p>Measure impact of targeted language outreach:</p><p><strong>Tracking:</strong></p><ul><li>Report submissions per language link</li><li>Conversion rates by language</li><li>Engagement metrics by language</li><li>Regional adoption patterns</li></ul><p><strong>Use Cases:</strong></p><ul><li>Assess training effectiveness in different languages</li><li>Optimize communication strategies</li><li>Allocate resources to high-need languages</li><li>Demonstrate inclusivity metrics to leadership</li></ul><p><strong>Learn More:</strong> <a href="/docs/analytics/dashboard">Analytics Dashboard</a></p><h2 id="accessibility-considerations" tabindex="-1">Accessibility Considerations <a class="header-anchor" href="#accessibility-considerations" aria-label="Permalink to &quot;Accessibility Considerations&quot;">â€‹</a></h2><h3 id="right-to-left-rtl-languages" tabindex="-1">Right-to-Left (RTL) Languages <a class="header-anchor" href="#right-to-left-rtl-languages" aria-label="Permalink to &quot;Right-to-Left (RTL) Languages&quot;">â€‹</a></h3><p><strong>Planned Support (2026):</strong></p><p>Arabic and Hebrew RTL language support:</p><p><strong>Features:</strong></p><ul><li>Full RTL interface</li><li>Mirrored layout</li><li>RTL-aware form fields</li><li>Correct text direction</li></ul><p><strong>Current Workaround:</strong></p><ul><li>Arabic speakers can type in text fields (correct RTL display)</li><li>Interface remains LTR (English) for navigation</li><li>Not ideal but functional</li></ul><p><strong>Interest List:</strong> Email <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a> for RTL language priority access</p><h3 id="screen-reader-compatibility" tabindex="-1">Screen Reader Compatibility <a class="header-anchor" href="#screen-reader-compatibility" aria-label="Permalink to &quot;Screen Reader Compatibility&quot;">â€‹</a></h3><p><strong>Multilingual Screen Reader Support:</strong></p><p>All translations are screen reader accessible:</p><p><strong>Standards:</strong></p><ul><li>WCAG 2.1 Level AA compliance</li><li>Proper language tagging (<code>lang</code> attribute)</li><li>Semantic HTML across all languages</li><li>Keyboard navigation support</li></ul><p><strong>Testing:</strong> Languages tested with popular screen readers:</p><ul><li>JAWS (Windows)</li><li>NVDA (Windows)</li><li>VoiceOver (macOS, iOS)</li><li>TalkBack (Android)</li></ul><p><strong>Learn More:</strong> <a href="/docs/features/accessibility">Accessibility Features</a></p><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">â€‹</a></h2><h3 id="wrong-language-displayed" tabindex="-1">Wrong Language Displayed <a class="header-anchor" href="#wrong-language-displayed" aria-label="Permalink to &quot;Wrong Language Displayed&quot;">â€‹</a></h3><p><strong>Reporter Sees Unexpected Language:</strong></p><p><strong>Possible Causes:</strong></p><ol><li><p><strong>Browser Language Setting</strong></p><ul><li>Check browser&#39;s preferred language</li><li>Go to browser settings &gt; Languages</li><li>Ensure correct language at top of list</li></ul></li><li><p><strong>Link-Specific Configuration</strong></p><ul><li>Link may enforce specific language</li><li>Check link URL for <code>?lang=</code> parameter</li><li>Contact administrator if incorrect</li></ul></li><li><p><strong>Cookie/Cache Issue</strong></p><ul><li>Previous session saved different language</li><li>Clear browser cookies</li><li>Hard refresh page (Ctrl+F5 / Cmd+Shift+R)</li></ul></li><li><p><strong>VPN/Location Detection</strong></p><ul><li>VPN may cause incorrect geographic detection</li><li>Manually select language using language selector</li><li>Selection will persist for session</li></ul></li></ol><p><strong>Solution:</strong> Use language selector (globe icon) in portal header to manually change language.</p><h3 id="language-selector-missing" tabindex="-1">Language Selector Missing <a class="header-anchor" href="#language-selector-missing" aria-label="Permalink to &quot;Language Selector Missing&quot;">â€‹</a></h3><p><strong>Cannot Find Language Options:</strong></p><p><strong>Possible Reasons:</strong></p><ol><li><p><strong>Language Restricted on Link</strong></p><ul><li>Administrator forced specific language</li><li>Language selector intentionally hidden</li><li>Contact organization if need different language</li></ul></li><li><p><strong>Single Language Organization</strong></p><ul><li>Organization only enabled one language</li><li>No selector needed if only one option</li><li>Request additional languages from administrator</li></ul></li><li><p><strong>Browser Issue</strong></p><ul><li>JavaScript disabled or error</li><li>Try different browser</li><li>Clear cache and cookies</li><li>Update browser to latest version</li></ul></li></ol><h3 id="incomplete-translation" tabindex="-1">Incomplete Translation <a class="header-anchor" href="#incomplete-translation" aria-label="Permalink to &quot;Incomplete Translation&quot;">â€‹</a></h3><p><strong>Some Text Still in English:</strong></p><p><strong>Expected Behavior:</strong></p><p>The following content is intentionally not translated:</p><ul><li>User-submitted report content</li><li>Secure messages (original language)</li><li>Custom organization content (if not manually translated)</li><li>Evidence file names</li><li>Some technical error messages</li></ul><p><strong>Report Translation Gaps:</strong></p><p>If interface elements (buttons, labels, menus) appear in wrong language:</p><ol><li>Take screenshot</li><li>Email: <a href="mailto:translations@disclosurely.com" target="_blank" rel="noreferrer">translations@disclosurely.com</a></li><li>Include language and page/feature</li><li>We&#39;ll address promptly</li></ol><h3 id="translation-seems-incorrect" tabindex="-1">Translation Seems Incorrect <a class="header-anchor" href="#translation-seems-incorrect" aria-label="Permalink to &quot;Translation Seems Incorrect&quot;">â€‹</a></h3><p><strong>Noticed Awkward or Wrong Translation:</strong></p><p><strong>How to Report:</strong></p><ol><li>Email: <a href="mailto:translations@disclosurely.com" target="_blank" rel="noreferrer">translations@disclosurely.com</a></li><li>Subject: &quot;Translation Issue - [Language] - [Feature]&quot;</li><li>Include: <ul><li>Current translation</li><li>Why it seems incorrect</li><li>Suggested correction</li><li>Your language expertise/background</li></ul></li></ol><p><strong>Response:</strong></p><ul><li>Acknowledged within 3 business days</li><li>Reviewed by native translator</li><li>Fixed in next release if confirmed</li><li>Credit given for contribution (if desired)</li></ul><h2 id="related-features" tabindex="-1">Related Features <a class="header-anchor" href="#related-features" aria-label="Permalink to &quot;Related Features&quot;">â€‹</a></h2><h3 id="custom-domains" tabindex="-1">Custom Domains <a class="header-anchor" href="#custom-domains" aria-label="Permalink to &quot;Custom Domains&quot;">â€‹</a></h3><p><strong>Multilingual Custom Domains:</strong></p><p>Use language-specific custom domains:</p><p><strong>Examples:</strong></p><ul><li><code>reports.yourcompany.com</code> (English default)</li><li><code>rapports.votreentreprise.com</code> (French)</li><li><code>informes.suempresa.com</code> (Spanish)</li></ul><p><strong>Benefits:</strong></p><ul><li>Language immediately clear from URL</li><li>Increased trust (familiar domain)</li><li>Clearer internal communication</li></ul><p><strong>Setup:</strong> Each custom domain requires separate configuration</p><p><strong>Learn More:</strong> <a href="/docs/admin/custom-domains">Custom Domains</a></p><h3 id="link-generator" tabindex="-1">Link Generator <a class="header-anchor" href="#link-generator" aria-label="Permalink to &quot;Link Generator&quot;">â€‹</a></h3><p><strong>Language-Specific Link Creation:</strong></p><p>Create targeted reporting portals by language:</p><p><strong>Features:</strong></p><ul><li>Force specific language</li><li>Pre-select language with override allowed</li><li>Restrict to language subset</li><li>Track usage by language</li></ul><p><strong>Use Cases:</strong></p><ul><li>Regional office portals</li><li>Department-specific links</li><li>Campaign targeting specific populations</li><li>Training materials in native languages</li></ul><p><strong>Learn More:</strong> <a href="/docs/admin/link-generator">Link Generator &amp; Management</a></p><h3 id="email-notifications-1" tabindex="-1">Email Notifications <a class="header-anchor" href="#email-notifications-1" aria-label="Permalink to &quot;Email Notifications&quot;">â€‹</a></h3><p><strong>Multilingual Email System:</strong></p><p>Automatic email translation based on reporter language:</p><p><strong>Translated Emails:</strong></p><ul><li>Submission confirmations</li><li>Status updates</li><li>Message notifications</li><li>Resolution notices</li></ul><p><strong>Custom Templates:</strong></p><ul><li>Create templates per language</li><li>Customize for regional appropriateness</li><li>Include local contact information</li></ul><p><strong>Learn More:</strong> <a href="/docs/features/notifications">Notification System</a></p><h2 id="compliance-considerations" tabindex="-1">Compliance Considerations <a class="header-anchor" href="#compliance-considerations" aria-label="Permalink to &quot;Compliance Considerations&quot;">â€‹</a></h2><h3 id="regulatory-requirements" tabindex="-1">Regulatory Requirements <a class="header-anchor" href="#regulatory-requirements" aria-label="Permalink to &quot;Regulatory Requirements&quot;">â€‹</a></h3><p><strong>Language Requirements by Region:</strong></p><p><strong>European Union:</strong></p><ul><li>Workers&#39; rights to report in native language</li><li>EU Whistleblowing Directive encourages multilingual channels</li><li>Official EU languages should be available</li></ul><p><strong>Recommendation:</strong> Enable all EU official languages for EU operations.</p><p><strong>United Kingdom:</strong></p><ul><li>English primary</li><li>Welsh available for Wales operations</li><li>Consider other languages for diverse workforce</li></ul><p><strong>Canada:</strong></p><ul><li>English and French legally required for federal institutions</li><li>Bilingual portals standard practice</li></ul><p><strong>Switzerland:</strong></p><ul><li>German, French, Italian commonly required</li><li>Depends on canton and workforce</li></ul><p><strong>Multinational:</strong></p><ul><li>Offer languages matching operational countries</li><li>Demonstrates good faith compliance</li><li>Reduces barriers to reporting</li></ul><p><strong>Learn More:</strong> <a href="/docs/compliance/overview">Compliance Overview</a></p><h3 id="data-protection" tabindex="-1">Data Protection <a class="header-anchor" href="#data-protection" aria-label="Permalink to &quot;Data Protection&quot;">â€‹</a></h3><p><strong>Language and GDPR:</strong></p><p>Language selection constitutes personal data processing:</p><p><strong>Compliance Measures:</strong></p><ul><li>Language preference stored temporarily</li><li>Cookie consent for language persistence</li><li>No linking language to reporter identity</li><li>Anonymity preserved regardless of language</li></ul><p><strong>Data Subject Rights:</strong></p><ul><li>Access requests available in any language</li><li>GDPR request portal multilingual</li><li>Data exports include language metadata</li></ul><p><strong>Learn More:</strong> <a href="/docs/compliance/gdpr">GDPR Compliance</a></p><h2 id="future-enhancements" tabindex="-1">Future Enhancements <a class="header-anchor" href="#future-enhancements" aria-label="Permalink to &quot;Future Enhancements&quot;">â€‹</a></h2><h3 id="planned-features" tabindex="-1">Planned Features <a class="header-anchor" href="#planned-features" aria-label="Permalink to &quot;Planned Features&quot;">â€‹</a></h3><p><strong>2025 Roadmap:</strong></p><p><strong>Q2 2025:</strong></p><ul><li>Additional Nordic languages (Finnish, Icelandic)</li><li>Eastern European expansion (Czech, Romanian, Hungarian)</li><li>Improved dialect handling</li></ul><p><strong>Q3 2025:</strong></p><ul><li>Real-time message translation (beta)</li><li>Voice-to-text in multiple languages</li><li>Enhanced custom content translation tools</li></ul><p><strong>Q4 2025:</strong></p><ul><li>Asian language support (Japanese, Korean, Chinese)</li><li>RTL language support (Arabic, Hebrew)</li><li>AI-assisted translation quality improvement</li></ul><p><strong>2026:</strong></p><ul><li>Automatic report content translation (optional)</li><li>Multilingual voice reporting</li><li>Regional compliance templates</li><li>Advanced cultural adaptation features</li></ul><p><strong>Enterprise Preview:</strong> Enterprise customers can request early access to upcoming language features.</p><p><strong>Feedback Welcome:</strong> Email: <a href="mailto:product@disclosurely.com" target="_blank" rel="noreferrer">product@disclosurely.com</a> with language feature requests</p><h2 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">â€‹</a></h2><ul><li><a href="/docs/admin/link-generator">Link Generator</a> - Language-specific reporting links</li><li><a href="/docs/admin/organization-settings">Organization Settings</a> - Language configuration</li><li><a href="/docs/admin/custom-domains">Custom Domains</a> - Multilingual domain setup</li><li><a href="/docs/features/notifications">Notification System</a> - Multilingual emails</li><li><a href="/docs/analytics/dashboard">Analytics Dashboard</a> - Language usage metrics</li><li><a href="/docs/compliance/overview">Compliance Overview</a> - Regulatory language requirements</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">â€‹</a></h2><p>Questions about language support?</p><ul><li>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li>Translation issues: <a href="mailto:translations@disclosurely.com" target="_blank" rel="noreferrer">translations@disclosurely.com</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="mailto:support@disclosurely.com?subject=Language%20Request" target="_blank" rel="noreferrer">Request New Language</a></li></ul>`,351)])])}const m=e(l,[["render",o]]);export{d as __pageData,m as default};
