import{_ as i,c as t,o as a,ag as s}from"./chunks/framework.4QX5UEtQ.js";const m=JSON.parse('{"title":"Notification System - Email Alerts & Updates | Disclosurely","description":"Configure email notifications for case updates, assignments, deadlines, and system alerts. Customize notification preferences and manage alert frequency.","frontmatter":{"title":"Notification System - Email Alerts & Updates | Disclosurely","description":"Configure email notifications for case updates, assignments, deadlines, and system alerts. Customize notification preferences and manage alert frequency."},"headers":[],"relativePath":"features/notifications.md","filePath":"features/notifications.md"}'),n={name:"features/notifications.md"};function o(r,e,l,c,p,d){return a(),t("div",null,[...e[0]||(e[0]=[s('<h1 id="notification-system" tabindex="-1">Notification System <a class="header-anchor" href="#notification-system" aria-label="Permalink to &quot;Notification System&quot;">​</a></h1><p>Stay informed with real-time email notifications and in-app alerts for case updates, assignments, deadlines, and important system events.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Disclosurely&#39;s notification system ensures team members and reporters never miss critical updates. Configure personalized notification preferences, manage alert frequency, and receive timely updates via email and in-app notifications.</p><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Notifications</p><h2 id="notification-types" tabindex="-1">Notification Types <a class="header-anchor" href="#notification-types" aria-label="Permalink to &quot;Notification Types&quot;">​</a></h2><h3 id="for-case-handlers-administrators" tabindex="-1">For Case Handlers &amp; Administrators <a class="header-anchor" href="#for-case-handlers-administrators" aria-label="Permalink to &quot;For Case Handlers &amp; Administrators&quot;">​</a></h3><p><strong>Case Assignments:</strong></p><ul><li>New case assigned to you</li><li>Case reassigned to different handler</li><li>Case escalated to your attention</li><li>Bulk assignments completed</li></ul><p><strong>Status Updates:</strong></p><ul><li>Case status changed (any case you&#39;re involved in)</li><li>Case priority elevated or reduced</li><li>Case marked as resolved</li><li>Case reopened after closure</li></ul><p><strong>Messaging:</strong></p><ul><li>New message from reporter</li><li>Reporter responded to your question</li><li>Internal note added by team member</li><li>File uploaded by reporter</li></ul><p><strong>Deadlines &amp; SLAs:</strong></p><ul><li>SLA deadline approaching (24h warning)</li><li>SLA deadline breached</li><li>Response time exceeding target</li><li>Investigation milestone approaching</li></ul><p><strong>System Alerts:</strong></p><ul><li>New high-priority case requires immediate attention</li><li>Pattern detected across multiple cases</li><li>Security alert or unusual activity</li><li>System maintenance scheduled</li></ul><h3 id="for-reporters" tabindex="-1">For Reporters <a class="header-anchor" href="#for-reporters" aria-label="Permalink to &quot;For Reporters&quot;">​</a></h3><p><strong>Submission Confirmation:</strong></p><ul><li>Report successfully submitted</li><li>Tracking ID provided (DIS-XXXXXXXX)</li><li>Expected response timeline</li><li>Next steps information</li></ul><p><strong>Status Updates:</strong></p><ul><li>Investigation status changed</li><li>Case assigned to investigator</li><li>Additional information requested</li><li>Investigation completed</li><li>Resolution communicated</li></ul><p><strong>Messaging:</strong></p><ul><li>New message from investigator</li><li>Question requires your response</li><li>Investigation update available</li><li>Case closed notification</li></ul><p><strong>Reminders:</strong></p><ul><li>Pending messages requiring response</li><li>Draft expiring soon (if applicable)</li><li>Follow-up requested</li></ul><h2 id="managing-notification-preferences" tabindex="-1">Managing Notification Preferences <a class="header-anchor" href="#managing-notification-preferences" aria-label="Permalink to &quot;Managing Notification Preferences&quot;">​</a></h2><h3 id="email-notifications" tabindex="-1">Email Notifications <a class="header-anchor" href="#email-notifications" aria-label="Permalink to &quot;Email Notifications&quot;">​</a></h3><p><strong>Configure Email Settings:</strong></p><ol><li>Navigate to Dashboard &gt; Settings &gt; Notifications</li><li>Select notification categories to enable/disable</li><li>Choose email frequency (instant, daily digest, weekly summary)</li><li>Set quiet hours (no emails during specified times)</li><li>Save preferences</li></ol><p><strong>Email Frequency Options:</strong></p><ul><li><strong>Instant:</strong> Email sent immediately when event occurs</li><li><strong>Hourly Digest:</strong> Batched updates sent once per hour</li><li><strong>Daily Digest:</strong> Single email at chosen time (e.g., 9 AM)</li><li><strong>Weekly Summary:</strong> Weekly roundup of all activity</li><li><strong>Disabled:</strong> No email notifications</li></ul><p><strong>Recommended Settings:</strong></p><ul><li>High-priority cases: Instant</li><li>Status changes: Daily digest</li><li>Team updates: Weekly summary</li><li>System alerts: Instant</li></ul><h3 id="in-app-notifications" tabindex="-1">In-App Notifications <a class="header-anchor" href="#in-app-notifications" aria-label="Permalink to &quot;In-App Notifications&quot;">​</a></h3><p><strong>Real-Time Alerts:</strong></p><p>Notifications appear in the application:</p><p><strong>Notification Bell:</strong></p><ul><li>Icon in top navigation bar</li><li>Badge shows unread count</li><li>Click to view notification center</li><li>Mark individual or all as read</li></ul><p><strong>Notification Center:</strong></p><ul><li>Chronological list of all notifications</li><li>Filter by type or date</li><li>Quick actions (view case, reply, dismiss)</li><li>Search notifications</li></ul><p><strong>Desktop Notifications:</strong></p><ul><li>Browser push notifications (opt-in)</li><li>Appear even when tab not active</li><li>Click to navigate directly to case</li><li>Requires browser permission</li></ul><p><strong>Learn More:</strong> <a href="/docs/admin/team-management">Team Management</a></p><h2 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">​</a></h2><ul><li><a href="/docs/reporting/secure-messaging">Secure Messaging</a></li><li><a href="/docs/features/assignment-rules">Assignment Rules</a></li><li><a href="/docs/features/sla-management">SLA Management</a></li><li><a href="/docs/admin/team-management">Team Management</a></li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a><br><a href="/docs/support/contact">Contact Support</a></p>',48)])])}const g=i(n,[["render",o]]);export{m as __pageData,g as default};
