import{_ as i,c as a,o as t,ag as o}from"./chunks/framework.4QX5UEtQ.js";const h=JSON.parse('{"title":"PII Detection & Redaction - Privacy Protection | Disclosurely","description":"Automatic detection and redaction of personally identifiable information (PII) before AI analysis. Protect privacy while leveraging AI insights.","frontmatter":{"title":"PII Detection & Redaction - Privacy Protection | Disclosurely","description":"Automatic detection and redaction of personally identifiable information (PII) before AI analysis. Protect privacy while leveraging AI insights."},"headers":[],"relativePath":"features/pii-detection.md","filePath":"features/pii-detection.md"}'),r={name:"features/pii-detection.md"};function l(n,e,s,c,d,p){return t(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="pii-detection-redaction" tabindex="-1">PII Detection &amp; Redaction <a class="header-anchor" href="#pii-detection-redaction" aria-label="Permalink to &quot;PII Detection &amp; Redaction&quot;">​</a></h1><p>Automatically detect and redact personally identifiable information to protect privacy during AI analysis and case review.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Disclosurely&#39;s PII detection system automatically identifies and redacts sensitive personal information before AI analysis, ensuring compliance with privacy regulations like <a href="/docs/compliance/gdpr">GDPR</a> while still enabling powerful AI insights.</p><p><strong>How It Works:</strong></p><ol><li>Reporter submits case</li><li>System scans content for PII</li><li>Detected PII automatically redacted</li><li>AI analysis performed on redacted version</li><li>Original unredacted version securely stored</li><li>Case handlers see redacted or full version (configurable)</li></ol><h2 id="what-pii-is-detected" tabindex="-1">What PII Is Detected <a class="header-anchor" href="#what-pii-is-detected" aria-label="Permalink to &quot;What PII Is Detected&quot;">​</a></h2><h3 id="personal-identifiers" tabindex="-1">Personal Identifiers <a class="header-anchor" href="#personal-identifiers" aria-label="Permalink to &quot;Personal Identifiers&quot;">​</a></h3><p><strong>Names:</strong></p><ul><li>Full names (first + last)</li><li>Partial names in context</li><li>Nicknames when identifiable</li><li>Maiden names</li></ul><p><strong>Contact Information:</strong></p><ul><li>Email addresses</li><li>Phone numbers (all formats)</li><li>Physical addresses</li><li>IP addresses</li></ul><p><strong>Identification Numbers:</strong></p><ul><li>Social Security Numbers (SSN)</li><li>National ID numbers</li><li>Passport numbers</li><li>Driver&#39;s license numbers</li><li>Employee ID numbers</li></ul><h3 id="financial-information" tabindex="-1">Financial Information <a class="header-anchor" href="#financial-information" aria-label="Permalink to &quot;Financial Information&quot;">​</a></h3><p><strong>Payment Data:</strong></p><ul><li>Credit card numbers</li><li>Bank account numbers</li><li>IBAN codes</li><li>Sort codes</li><li>Routing numbers</li></ul><p><strong>Other Financial:</strong></p><ul><li>Tax identification numbers</li><li>Payroll information</li><li>Salary details</li></ul><h3 id="additional-sensitive-data" tabindex="-1">Additional Sensitive Data <a class="header-anchor" href="#additional-sensitive-data" aria-label="Permalink to &quot;Additional Sensitive Data&quot;">​</a></h3><ul><li>Dates of birth</li><li>Medical information</li><li>Biometric data references</li><li>Geographic coordinates</li><li>Vehicle registration numbers</li></ul><h2 id="redaction-methods" tabindex="-1">Redaction Methods <a class="header-anchor" href="#redaction-methods" aria-label="Permalink to &quot;Redaction Methods&quot;">​</a></h2><h3 id="placeholder-replacement" tabindex="-1">Placeholder Replacement <a class="header-anchor" href="#placeholder-replacement" aria-label="Permalink to &quot;Placeholder Replacement&quot;">​</a></h3><p>PII replaced with consistent placeholders:</p><p><strong>Examples:</strong></p><ul><li>&quot;John Smith&quot; → <code>[PERSON_1]</code></li><li>&quot;<a href="mailto:jane.doe@company.com" target="_blank" rel="noreferrer">jane.doe@company.com</a>&quot; → <code>[EMAIL_1]</code></li><li>&quot;555-123-4567&quot; → <code>[PHONE_1]</code></li><li>&quot;123 Main St&quot; → <code>[ADDRESS_1]</code></li></ul><p><strong>Consistency:</strong> Same person referenced multiple times uses same placeholder throughout case.</p><h3 id="preview-before-ai-analysis" tabindex="-1">Preview Before AI Analysis <a class="header-anchor" href="#preview-before-ai-analysis" aria-label="Permalink to &quot;Preview Before AI Analysis&quot;">​</a></h3><p><strong>PII Preview Modal:</strong></p><p>Before submitting to AI, reporters/handlers see:</p><ul><li>Original text</li><li>Redacted version (side-by-side)</li><li>List of detected PII types</li><li>Option to proceed or cancel</li></ul><p><strong>Learn More:</strong> <a href="/docs/ai/case-helper">AI Case Helper</a></p><h2 id="privacy-compliance" tabindex="-1">Privacy &amp; Compliance <a class="header-anchor" href="#privacy-compliance" aria-label="Permalink to &quot;Privacy &amp; Compliance&quot;">​</a></h2><p><strong>GDPR Compliance:</strong></p><ul><li>Data minimization</li><li>Purpose limitation</li><li>Privacy by design</li></ul><p><strong>Security:</strong></p><ul><li>Original data encrypted</li><li>Redacted versions for AI</li><li>Audit trail of PII access</li></ul><p><strong>Learn More:</strong> <a href="/docs/compliance/gdpr">GDPR Compliance</a> | <a href="/docs/security/overview">Security Overview</a></p><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a><br><a href="/docs/support/contact">Contact Support</a></p>',40)])])}const m=i(r,[["render",l]]);export{h as __pageData,m as default};
