import{_ as r,c as a,o as i,ag as n}from"./chunks/framework.4QX5UEtQ.js";const d=JSON.parse('{"title":"Referral Program - Earn Rewards | Disclosurely","description":"Earn account credits, free subscription months, and premium features by referring organizations to Disclosurely. Track referrals, monitor rewards, and grow your network.","frontmatter":{"title":"Referral Program - Earn Rewards | Disclosurely","description":"Earn account credits, free subscription months, and premium features by referring organizations to Disclosurely. Track referrals, monitor rewards, and grow your network.","head":[["meta",{"property":"og:title","content":"Referral Program - Disclosurely"}],["meta",{"property":"og:description","content":"Earn account credits, free subscription months, and premium features by referring organizations to Disclosurely. Track referrals, monitor rewards, and grow your network."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Referral Program - Disclosurely"}],["meta",{"name":"twitter:description","content":"Earn account credits, free subscription months, and premium features by referring organizations to Disclosurely. Track referrals, monitor rewards, and grow your network."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"features/referral-program.md","filePath":"features/referral-program.md"}'),t={name:"features/referral-program.md"};function l(s,e,o,p,c,u){return i(),a("div",null,[...e[0]||(e[0]=[n(`<h1 id="referral-program" tabindex="-1">Referral Program <a class="header-anchor" href="#referral-program" aria-label="Permalink to &quot;Referral Program&quot;">​</a></h1><p>Earn valuable rewards by referring other organizations to Disclosurely. Share your unique referral link and unlock account credits, free subscription months, extended storage, and premium features.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The Disclosurely Referral Program rewards you for helping other organizations implement effective whistleblowing solutions. When you refer a colleague, partner, or another organization that subscribes to Disclosurely, both you and the referred organization benefit.</p><p><strong>How It Works:</strong></p><ol><li>Generate your unique referral link from your dashboard</li><li>Share with colleagues, partners, and professional network</li><li>Earn rewards when they subscribe</li><li>Track referrals and reward status in real-time</li><li>Redeem rewards for account credits, features, and more</li></ol><p><strong>Access:</strong> Dashboard &gt; Account &gt; Referral Program</p><p><strong>Availability:</strong> All plan levels (Free, Starter, Pro, Enterprise)</p><p><strong>Learn More:</strong> <a href="/docs/features/subscription-tiers">Subscription Tiers</a></p><h2 id="how-the-referral-program-works" tabindex="-1">How the Referral Program Works <a class="header-anchor" href="#how-the-referral-program-works" aria-label="Permalink to &quot;How the Referral Program Works&quot;">​</a></h2><h3 id="getting-your-referral-link" tabindex="-1">Getting Your Referral Link <a class="header-anchor" href="#getting-your-referral-link" aria-label="Permalink to &quot;Getting Your Referral Link&quot;">​</a></h3><p><strong>Generate Unique Link:</strong></p><p>Every Disclosurely account receives a unique referral link:</p><p><strong>How to Access:</strong></p><ol><li>Log in to your dashboard</li><li>Navigate to <strong>Account &gt; Referral Program</strong></li><li>Find your unique referral link</li><li>Copy link or share directly</li></ol><p><strong>Your Referral Link Format:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://disclosurely.com/signup?ref=YOUR_UNIQUE_CODE</span></span></code></pre></div><p><strong>Unique Referral Code:</strong></p><ul><li>Automatically generated for your account</li><li>Permanent (doesn&#39;t change)</li><li>Tracks all referred signups</li><li>Cannot be shared or transferred</li></ul><p><strong>Multiple Links:</strong> You can create multiple tracking links for different campaigns:</p><ul><li>Link for LinkedIn posts</li><li>Link for email campaigns</li><li>Link for webinars</li><li>Link for partner websites</li></ul><p><strong>Access:</strong> Dashboard &gt; Referral Program &gt; Create Campaign Link</p><h3 id="sharing-your-referral-link" tabindex="-1">Sharing Your Referral Link <a class="header-anchor" href="#sharing-your-referral-link" aria-label="Permalink to &quot;Sharing Your Referral Link&quot;">​</a></h3><p><strong>Sharing Methods:</strong></p><p><strong>1. Direct Link Sharing:</strong></p><ul><li>Copy and paste in emails</li><li>Share in Slack/Teams channels</li><li>Include in email signatures</li><li>Post on social media</li></ul><p><strong>2. Email Invitation:</strong></p><ul><li>Send pre-written invitation emails</li><li>Customize message with your details</li><li>Track email opens and clicks</li><li>Bulk send to multiple contacts</li></ul><p><strong>Access:</strong> Dashboard &gt; Referral Program &gt; Send Email Invitation</p><p><strong>3. Social Media:</strong></p><ul><li>One-click share to LinkedIn, Twitter, Facebook</li><li>Pre-populated share text (customizable)</li><li>Includes engaging graphics</li><li>Hashtags and mentions</li></ul><p><strong>4. QR Code:</strong></p><ul><li>Generate QR code for print materials</li><li>Include in presentations</li><li>Add to business cards</li><li>Display at conferences</li></ul><p><strong>Download:</strong> Dashboard &gt; Referral Program &gt; Generate QR Code</p><p><strong>5. Promotional Materials:</strong></p><ul><li>Pre-designed social media graphics</li><li>Email templates</li><li>PowerPoint slide decks</li><li>One-pagers and brochures</li><li>Case study templates</li></ul><p><strong>Access:</strong> Dashboard &gt; Referral Program &gt; Marketing Assets</p><h3 id="who-can-you-refer" tabindex="-1">Who Can You Refer? <a class="header-anchor" href="#who-can-you-refer" aria-label="Permalink to &quot;Who Can You Refer?&quot;">​</a></h3><p><strong>Eligible Referrals:</strong></p><p>✅ <strong>Organizations that can be referred:</strong></p><ul><li>Companies implementing compliance programs</li><li>Non-profits establishing whistleblowing channels</li><li>Educational institutions</li><li>Government agencies</li><li>Professional service firms</li><li>Industry associations</li><li>Any organization not currently using Disclosurely</li></ul><p>✅ <strong>Referral relationships:</strong></p><ul><li>Professional colleagues and contacts</li><li>Business partners and vendors</li><li>Industry peers and competitors</li><li>Conference connections</li><li>Online community members</li><li>Former colleagues</li></ul><p>❌ <strong>Ineligible referrals:</strong></p><ul><li>Existing Disclosurely customers</li><li>Organizations you&#39;re affiliated with as employee/owner</li><li>Referrals from prohibited countries (sanctions compliance)</li><li>Self-referrals or fake organizations</li></ul><p><strong>Verification:</strong> All referrals are verified before rewards are issued to prevent fraud.</p><h3 id="referral-tracking" tabindex="-1">Referral Tracking <a class="header-anchor" href="#referral-tracking" aria-label="Permalink to &quot;Referral Tracking&quot;">​</a></h3><p><strong>How Referrals Are Tracked:</strong></p><p><strong>Attribution Methods:</strong></p><ol><li><strong>Unique Link Click</strong> - Cookie stored in browser (30-day window)</li><li><strong>Referral Code Entry</strong> - Manual code entry during signup</li><li><strong>Email Domain Matching</strong> - Organization domain from invitation email</li><li><strong>Admin Verification</strong> - Manual attribution if technical issues</li></ol><p><strong>Tracking Dashboard:</strong></p><p>Monitor your referral activity in real-time:</p><p><strong>Access:</strong> Dashboard &gt; Referral Program &gt; My Referrals</p><p><strong>Metrics Displayed:</strong></p><ul><li><strong>Total Clicks</strong> - How many people clicked your link</li><li><strong>Signups</strong> - How many created free accounts</li><li><strong>Conversions</strong> - How many subscribed to paid plans</li><li><strong>Active Referrals</strong> - Currently subscribed organizations</li><li><strong>Total Rewards Earned</strong> - Lifetime value of rewards</li><li><strong>Pending Rewards</strong> - Awaiting redemption</li></ul><p><strong>Referral Status:</strong></p><ul><li><strong>Clicked</strong> - Someone clicked your link</li><li><strong>Signed Up</strong> - Created free account</li><li><strong>Trial Started</strong> - Started paid plan trial</li><li><strong>Converted</strong> - Subscribed to paid plan</li><li><strong>Active</strong> - Currently paying customer</li><li><strong>Churned</strong> - Canceled subscription</li></ul><p><strong>Detailed View:</strong></p><p>Click on any referral to see:</p><ul><li>Organization name (if public)</li><li>Signup date</li><li>Plan type (Starter, Pro, Enterprise)</li><li>Reward earned</li><li>Reward redemption status</li><li>Days active</li></ul><h2 id="rewards-benefits" tabindex="-1">Rewards &amp; Benefits <a class="header-anchor" href="#rewards-benefits" aria-label="Permalink to &quot;Rewards &amp; Benefits&quot;">​</a></h2><h3 id="reward-structure" tabindex="-1">Reward Structure <a class="header-anchor" href="#reward-structure" aria-label="Permalink to &quot;Reward Structure&quot;">​</a></h3><p><strong>What You Earn:</strong></p><p>Rewards vary based on the plan your referral subscribes to:</p><p><strong>Starter Plan Referrals:</strong></p><ul><li><strong>$50 account credit</strong> (one-time)</li><li><strong>+5 GB storage</strong> added to your account</li><li><strong>1 month free</strong> on your current plan</li></ul><p><strong>Pro Plan Referrals:</strong></p><ul><li><strong>$150 account credit</strong> (one-time)</li><li><strong>+20 GB storage</strong> added to your account</li><li><strong>3 months free</strong> on your current plan</li><li><strong>Early access</strong> to new features (1 quarter)</li></ul><p><strong>Enterprise Plan Referrals:</strong></p><ul><li><strong>$500 account credit</strong> (one-time)</li><li><strong>Unlimited storage</strong> upgrade for 6 months</li><li><strong>6 months free</strong> on your current plan</li><li><strong>Early access</strong> to new features (1 year)</li><li><strong>Dedicated success manager</strong> consultation</li><li><strong>Conference ticket</strong> (select events)</li></ul><p><strong>Annual Subscription Bonus:</strong> If your referral chooses annual billing (vs. monthly):</p><ul><li><strong>+50% bonus</strong> on all rewards</li><li>Example: Pro referral = $225 credit instead of $150</li></ul><h3 id="recurring-rewards" tabindex="-1">Recurring Rewards <a class="header-anchor" href="#recurring-rewards" aria-label="Permalink to &quot;Recurring Rewards&quot;">​</a></h3><p><strong>Lifetime Value:</strong></p><p>Earn ongoing benefits while your referrals remain active customers:</p><p><strong>Monthly Recurring Rewards:</strong></p><ul><li><strong>5% of referral&#39;s monthly subscription</strong> as account credit</li><li>Applied automatically each month</li><li>Continues as long as they remain subscribed</li><li>No limit to total lifetime earnings</li></ul><p><strong>Example:</strong></p><ul><li>Refer 3 organizations on Pro plan ($99/month each)</li><li>Earn: 3 × $99 × 5% = <strong>$14.85/month</strong> recurring credit</li><li>Over 2 years: $14.85 × 24 = <strong>$356.40 total</strong></li></ul><p><strong>Reward Tiers:</strong></p><p>Unlock increasing benefits as you refer more organizations:</p><p><strong>Bronze (1-2 referrals):</strong></p><ul><li>Standard rewards listed above</li><li>Referral dashboard access</li></ul><p><strong>Silver (3-5 referrals):</strong></p><ul><li>+10% bonus on all rewards</li><li>Priority support for your account</li><li>Quarterly product roadmap updates</li></ul><p><strong>Gold (6-10 referrals):</strong></p><ul><li>+25% bonus on all rewards</li><li>Free upgrade to next plan tier (for duration of referrals)</li><li>Monthly product team office hours access</li><li>Named in referral leaderboard</li></ul><p><strong>Platinum (11+ referrals):</strong></p><ul><li>+50% bonus on all rewards</li><li>Free Enterprise features regardless of your plan</li><li>Direct product team contact</li><li>Invitation to exclusive events</li><li>Partnership opportunities</li></ul><h3 id="redemption-options" tabindex="-1">Redemption Options <a class="header-anchor" href="#redemption-options" aria-label="Permalink to &quot;Redemption Options&quot;">​</a></h3><p><strong>How to Use Rewards:</strong></p><p><strong>1. Account Credits:</strong></p><ul><li>Applied automatically to your subscription</li><li>Reduces or eliminates monthly bill</li><li>Credits never expire</li><li>Can accumulate unlimited balance</li></ul><p><strong>2. Plan Upgrades:</strong></p><ul><li>Use credits to upgrade to higher plan</li><li>Example: Use $150 credit to upgrade from Starter ($49) to Pro ($99) for 3 months</li><li>Upgrade remains active while credits available</li></ul><p><strong>3. Storage Expansion:</strong></p><ul><li>Redeem credits for additional storage</li><li>$25 credit = 50 GB additional storage</li><li>Storage persists for 1 year from redemption</li></ul><p><strong>4. Feature Unlocks:</strong></p><ul><li>Unlock Enterprise features temporarily</li><li>Example: $100 credit = 1 month Enterprise features</li><li>Access advanced analytics, API, custom branding</li></ul><p><strong>5. Cash Payout (Platinum Tier Only):</strong></p><ul><li>Minimum $500 balance required</li><li>PayPal or bank transfer</li><li>Paid quarterly</li><li>Tax documentation required (IRS Form W-9 for US)</li></ul><p><strong>6. Charity Donation:</strong></p><ul><li>Donate credits to supported charities</li><li>100% of credits donated (Disclosurely matches 10%)</li><li>Tax receipt provided</li><li>Supported causes: transparency, ethics, whistleblower protection</li></ul><p><strong>Redemption Process:</strong></p><ol><li>Navigate to <strong>Dashboard &gt; Referral Program &gt; Rewards</strong></li><li>View available credit balance</li><li>Select redemption option</li><li>Confirm redemption</li><li>Rewards applied immediately or within 24 hours</li></ol><h2 id="best-practices-for-referrals" tabindex="-1">Best Practices for Referrals <a class="header-anchor" href="#best-practices-for-referrals" aria-label="Permalink to &quot;Best Practices for Referrals&quot;">​</a></h2><h3 id="effective-sharing-strategies" tabindex="-1">Effective Sharing Strategies <a class="header-anchor" href="#effective-sharing-strategies" aria-label="Permalink to &quot;Effective Sharing Strategies&quot;">​</a></h3><p>✅ <strong>Target the Right Audience:</strong></p><ul><li>Focus on organizations with 50+ employees</li><li>Compliance-heavy industries (finance, healthcare, public sector)</li><li>Companies with global operations</li><li>Organizations facing regulatory requirements</li><li>Non-profits receiving government funding</li></ul><p>✅ <strong>Provide Value First:</strong></p><ul><li>Explain benefits to their organization (not just your reward)</li><li>Share your positive experience</li><li>Offer to answer questions</li><li>Provide demo or walkthrough</li><li>Share relevant case studies</li></ul><p>✅ <strong>Use Multiple Channels:</strong></p><ul><li>LinkedIn posts and articles</li><li>Industry-specific forums and communities</li><li>Professional association newsletters</li><li>Webinar mentions</li><li>Conference networking</li><li>Email introductions</li></ul><p>✅ <strong>Personalize Your Message:</strong></p><ul><li>Don&#39;t spam generic links</li><li>Explain why Disclosurely specifically helps their situation</li><li>Reference compliance regulations they face</li><li>Share relevant features for their industry</li><li>Offer personal assistance with onboarding</li></ul><h3 id="sample-referral-messages" tabindex="-1">Sample Referral Messages <a class="header-anchor" href="#sample-referral-messages" aria-label="Permalink to &quot;Sample Referral Messages&quot;">​</a></h3><p><strong>LinkedIn Post Example:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>After implementing Disclosurely for our whistleblowing program, we&#39;ve seen a</span></span>
<span class="line"><span>3x increase in reports and significantly improved our compliance posture. The</span></span>
<span class="line"><span>platform&#39;s anonymous reporting and case management features have transformed</span></span>
<span class="line"><span>how we handle employee concerns.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>If your organization is looking to improve compliance, strengthen your ethics</span></span>
<span class="line"><span>program, or meet regulatory requirements like the EU Whistleblowing Directive,</span></span>
<span class="line"><span>I highly recommend checking out Disclosurely.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>[Your Referral Link]</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Happy to chat about our experience and answer any questions!</span></span></code></pre></div><p><strong>Email to Colleague Example:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Subject: Whistleblowing Platform Recommendation</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Hi [Name],</span></span>
<span class="line"><span></span></span>
<span class="line"><span>I know [Company] has been working on compliance initiatives, and I wanted to</span></span>
<span class="line"><span>share a tool that&#39;s been incredibly valuable for us: Disclosurely.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>We implemented it 6 months ago to meet [SOX/EU Directive/GDPR] requirements,</span></span>
<span class="line"><span>and it&#39;s exceeded expectations:</span></span>
<span class="line"><span>- Anonymous reporting with secure messaging</span></span>
<span class="line"><span>- Automated case workflows and SLA tracking</span></span>
<span class="line"><span>- Comprehensive audit trails for regulators</span></span>
<span class="line"><span>- Multi-language support for our global workforce</span></span>
<span class="line"><span></span></span>
<span class="line"><span>I thought it might be helpful for your team as well. Here&#39;s a link to learn</span></span>
<span class="line"><span>more: [Your Referral Link]</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Happy to connect you with our compliance team if you want to hear about our</span></span>
<span class="line"><span>implementation experience.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Best,</span></span>
<span class="line"><span>[Your Name]</span></span></code></pre></div><p><strong>Conference Conversation Example:</strong></p><p>&quot;We recently implemented a whistleblowing platform called Disclosurely, and it&#39;s been a game-changer for our compliance program. If you&#39;re dealing with [specific regulation], I&#39;d be happy to share our learnings. Here&#39;s my card with more info...&quot;</p><h3 id="what-not-to-do" tabindex="-1">What NOT to Do <a class="header-anchor" href="#what-not-to-do" aria-label="Permalink to &quot;What NOT to Do&quot;">​</a></h3><p>❌ <strong>Don&#39;t spam:</strong></p><ul><li>Mass unsolicited emails</li><li>Posting in irrelevant groups</li><li>Aggressive sales tactics</li><li>Repeated messages to same person</li></ul><p>❌ <strong>Don&#39;t misrepresent:</strong></p><ul><li>Exaggerate features or capabilities</li><li>Promise specific results</li><li>Claim affiliation you don&#39;t have</li><li>Hide that it&#39;s a referral link</li></ul><p>❌ <strong>Don&#39;t violate policies:</strong></p><ul><li>Share in prohibited channels (competitor forums)</li><li>Violate employer social media policies</li><li>Breach confidentiality or NDAs</li><li>Use misleading advertising</li></ul><h2 id="compliance-terms" tabindex="-1">Compliance &amp; Terms <a class="header-anchor" href="#compliance-terms" aria-label="Permalink to &quot;Compliance &amp; Terms&quot;">​</a></h2><h3 id="referral-program-rules" tabindex="-1">Referral Program Rules <a class="header-anchor" href="#referral-program-rules" aria-label="Permalink to &quot;Referral Program Rules&quot;">​</a></h3><p><strong>Eligibility:</strong></p><ul><li>Must have active Disclosurely account (any plan)</li><li>Account in good standing (no violations, current on payments)</li><li>Located in permitted jurisdiction</li><li>Comply with program terms</li></ul><p><strong>Prohibited Activities:</strong></p><ul><li>Self-referrals or fake organizations</li><li>Automated/bot traffic</li><li>Trademark violations in promotion</li><li>Spam or unsolicited commercial email</li><li>Misrepresentation of Disclosurely features</li><li>Referral link manipulation or gaming</li></ul><p><strong>Consequences of Violations:</strong></p><ul><li>Referral rewards forfeited</li><li>Account suspension or termination</li><li>Legal action if fraud detected</li><li>Ineligibility for future rewards</li></ul><h3 id="tax-implications" tabindex="-1">Tax Implications <a class="header-anchor" href="#tax-implications" aria-label="Permalink to &quot;Tax Implications&quot;">​</a></h3><p><strong>Tax Reporting:</strong></p><p><strong>United States:</strong></p><ul><li>Rewards over $600/year may require IRS Form 1099</li><li>You&#39;re responsible for reporting as income</li><li>Consult tax professional for guidance</li></ul><p><strong>International:</strong></p><ul><li>Tax treatment varies by country</li><li>Rewards may constitute taxable income</li><li>Disclosurely does not provide tax advice</li><li>Consult local tax professional</li></ul><p><strong>Business vs. Personal:</strong> If referring as business entity:</p><ul><li>Different tax treatment may apply</li><li>May need business tax documentation</li><li>Consult accountant for proper handling</li></ul><h3 id="data-privacy" tabindex="-1">Data Privacy <a class="header-anchor" href="#data-privacy" aria-label="Permalink to &quot;Data Privacy&quot;">​</a></h3><p><strong>Referral Privacy:</strong></p><p><strong>What We Track:</strong></p><ul><li>Referral link clicks (anonymized)</li><li>Signup attribution (organization level)</li><li>Conversion to paid plan</li><li>Subscription status</li></ul><p><strong>What We Don&#39;t Share:</strong></p><ul><li>Specific organization details (unless public)</li><li>Individual names or contacts</li><li>Sensitive account information</li><li>Financial details</li></ul><p><strong>Your Privacy:</strong></p><ul><li>Referral activity is confidential</li><li>Not shared publicly without permission</li><li>Opt-in to leaderboard display</li></ul><p><strong>Learn More:</strong> <a href="/docs/legal/privacy">Privacy Policy</a></p><h2 id="referral-program-analytics" tabindex="-1">Referral Program Analytics <a class="header-anchor" href="#referral-program-analytics" aria-label="Permalink to &quot;Referral Program Analytics&quot;">​</a></h2><h3 id="tracking-performance" tabindex="-1">Tracking Performance <a class="header-anchor" href="#tracking-performance" aria-label="Permalink to &quot;Tracking Performance&quot;">​</a></h3><p><strong>Dashboard Metrics:</strong></p><p><strong>Access:</strong> Dashboard &gt; Referral Program &gt; Analytics</p><p><strong>Available Metrics:</strong></p><p><strong>Traffic Metrics:</strong></p><ul><li>Total link clicks</li><li>Unique visitors</li><li>Click sources (social media, email, direct)</li><li>Geographic distribution</li><li>Device types (desktop, mobile)</li></ul><p><strong>Conversion Metrics:</strong></p><ul><li>Click-to-signup rate</li><li>Signup-to-trial rate</li><li>Trial-to-paid rate</li><li>Overall conversion funnel</li><li>Time to conversion</li></ul><p><strong>Revenue Metrics:</strong></p><ul><li>Total rewards earned</li><li>Pending rewards</li><li>Redeemed rewards</li><li>Projected monthly recurring rewards</li><li>Lifetime value of referrals</li></ul><p><strong>Referral Health:</strong></p><ul><li>Active referrals (currently paying)</li><li>Churned referrals (canceled)</li><li>At-risk referrals (usage declined)</li><li>Retention rate</li><li>Average customer lifetime</li></ul><p><strong>Campaign Performance:</strong></p><p>If using multiple tracking links:</p><ul><li>Performance by campaign</li><li>Best-performing channels</li><li>ROI by source</li><li>A/B test results</li></ul><h3 id="optimization-insights" tabindex="-1">Optimization Insights <a class="header-anchor" href="#optimization-insights" aria-label="Permalink to &quot;Optimization Insights&quot;">​</a></h3><p><strong>Recommendations:</strong></p><p>The dashboard provides personalized suggestions:</p><p><strong>Example Insights:</strong></p><ul><li>&quot;Your LinkedIn posts have 3x higher conversion than email. Try more LinkedIn content.&quot;</li><li>&quot;Referrals from finance industry have 90% retention. Focus on CFO/compliance officer outreach.&quot;</li><li>&quot;Your trial-to-paid conversion is 45% (platform average: 30%). Share your onboarding tips.&quot;</li></ul><p><strong>Benchmarking:</strong></p><ul><li>Compare your performance to anonymized platform averages</li><li>Identify strengths and opportunities</li><li>Learn from top performers (anonymized insights)</li></ul><h2 id="leaderboard-recognition" tabindex="-1">Leaderboard &amp; Recognition <a class="header-anchor" href="#leaderboard-recognition" aria-label="Permalink to &quot;Leaderboard &amp; Recognition&quot;">​</a></h2><h3 id="monthly-referral-leaderboard" tabindex="-1">Monthly Referral Leaderboard <a class="header-anchor" href="#monthly-referral-leaderboard" aria-label="Permalink to &quot;Monthly Referral Leaderboard&quot;">​</a></h3><p><strong>Compete and Win:</strong></p><p>Top referrers each month earn special recognition:</p><p><strong>Leaderboard Categories:</strong></p><ul><li>Most new referrals (month)</li><li>Highest total rewards earned (month)</li><li>Best conversion rate</li><li>Lifetime referral champion</li></ul><p><strong>Monthly Prizes:</strong></p><p><strong>1st Place:</strong></p><ul><li>$500 bonus account credit</li><li>Featured spotlight in newsletter</li><li>Exclusive Disclosurely swag</li><li>Recognition certificate</li></ul><p><strong>2nd-5th Place:</strong></p><ul><li>$100-$250 bonus credits</li><li>Newsletter mention</li><li>Disclosurely swag</li></ul><p><strong>Access:</strong> Dashboard &gt; Referral Program &gt; Leaderboard</p><p><strong>Privacy:</strong> Leaderboard participation is optional (opt-in only). You can compete anonymously or use your name/organization.</p><h3 id="referral-ambassador-program" tabindex="-1">Referral Ambassador Program <a class="header-anchor" href="#referral-ambassador-program" aria-label="Permalink to &quot;Referral Ambassador Program&quot;">​</a></h3><p><strong>Become an Official Ambassador:</strong></p><p>Top referrers (10+ active referrals) can apply for Ambassador status:</p><p><strong>Benefits:</strong></p><ul><li>Special &quot;Ambassador&quot; badge in community</li><li>Exclusive ambassador Slack channel</li><li>Direct line to product team</li><li>Early access to all features</li><li>Speaking opportunities at Disclosurely events</li><li>Increased referral rewards (+25% permanent boost)</li><li>Co-marketing opportunities</li></ul><p><strong>Requirements:</strong></p><ul><li>10+ active paid referrals</li><li>Account in excellent standing</li><li>Active community participation</li><li>Professional representation of brand</li></ul><p><strong>Apply:</strong> Email <a href="mailto:referrals@disclosurely.com" target="_blank" rel="noreferrer">referrals@disclosurely.com</a> with:</p><ul><li>Your referral stats</li><li>Why you want to be an ambassador</li><li>How you plan to promote Disclosurely</li><li>Any relevant marketing/influence experience</li></ul><h2 id="referral-support" tabindex="-1">Referral Support <a class="header-anchor" href="#referral-support" aria-label="Permalink to &quot;Referral Support&quot;">​</a></h2><h3 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-label="Permalink to &quot;Getting Help&quot;">​</a></h3><p><strong>Referral Program Questions:</strong></p><p><strong>Contact:</strong> <a href="mailto:referrals@disclosurely.com" target="_blank" rel="noreferrer">referrals@disclosurely.com</a></p><p><strong>Common Questions:</strong></p><p><strong>Q: When do I receive rewards?</strong> A: Credits applied within 48 hours of referred organization&#39;s first payment. Recurring rewards applied monthly on subscription anniversary.</p><p><strong>Q: What if my referral doesn&#39;t use my link but mentions my name?</strong> A: Contact <a href="mailto:referrals@disclosurely.com" target="_blank" rel="noreferrer">referrals@disclosurely.com</a> with details. We can manually attribute if within 30 days and verifiable.</p><p><strong>Q: Can I refer multiple departments in same organization?</strong> A: No, only one referral reward per organization (based on primary account). Departments should use same organization account.</p><p><strong>Q: Do rewards expire?</strong> A: Account credits never expire. Storage and free months expire as noted at redemption.</p><p><strong>Q: Can I transfer credits to another organization?</strong> A: No, rewards are non-transferable and tied to your account.</p><p><strong>Q: What if my referral cancels?</strong> A: Recurring rewards stop when they cancel. Initial referral reward is not clawed back unless fraud is detected.</p><h3 id="resources" tabindex="-1">Resources <a class="header-anchor" href="#resources" aria-label="Permalink to &quot;Resources&quot;">​</a></h3><p><strong>Referral Resources:</strong></p><p><strong>Access:</strong> Dashboard &gt; Referral Program &gt; Resources</p><p><strong>Available Materials:</strong></p><ul><li>Referral program FAQ</li><li>Email templates (5+ variations)</li><li>Social media post templates</li><li>Presentation slides (PowerPoint, Google Slides)</li><li>One-page overview (PDF)</li><li>Case studies by industry</li><li>ROI calculator</li><li>Compliance checklist</li><li>Video demo links</li></ul><p><strong>Customization:</strong> All templates can be customized with:</p><ul><li>Your organization name</li><li>Your contact information</li><li>Industry-specific examples</li><li>Regional compliance references</li></ul><h2 id="partnership-opportunities" tabindex="-1">Partnership Opportunities <a class="header-anchor" href="#partnership-opportunities" aria-label="Permalink to &quot;Partnership Opportunities&quot;">​</a></h2><h3 id="formal-partnerships" tabindex="-1">Formal Partnerships <a class="header-anchor" href="#formal-partnerships" aria-label="Permalink to &quot;Formal Partnerships&quot;">​</a></h3><p><strong>High-Volume Referrers:</strong></p><p>Organizations referring 20+ customers may qualify for formal partnership:</p><p><strong>Partnership Types:</strong></p><p><strong>1. Reseller Partnership:</strong></p><ul><li>Sell Disclosurely with margin</li><li>White-label options available</li><li>Joint go-to-market support</li><li>Dedicated partner manager</li></ul><p><strong>2. Technology Partnership:</strong></p><ul><li>Integration with your platform</li><li>Co-marketing opportunities</li><li>Joint customer success</li><li>Revenue sharing models</li></ul><p><strong>3. Affiliate Partnership:</strong></p><ul><li>Higher commission rates (10-20%)</li><li>Dedicated landing pages</li><li>Marketing development funds</li><li>Co-branded materials</li></ul><p><strong>4. Consulting Partnership:</strong></p><ul><li>Implement Disclosurely for clients</li><li>Certification and training</li><li>Implementation support</li><li>Recurring revenue share</li></ul><p><strong>Interest:</strong> Email <a href="mailto:partnerships@disclosurely.com" target="_blank" rel="noreferrer">partnerships@disclosurely.com</a></p><h2 id="related-features" tabindex="-1">Related Features <a class="header-anchor" href="#related-features" aria-label="Permalink to &quot;Related Features&quot;">​</a></h2><h3 id="custom-domains" tabindex="-1">Custom Domains <a class="header-anchor" href="#custom-domains" aria-label="Permalink to &quot;Custom Domains&quot;">​</a></h3><p><strong>Branded Referral Experience:</strong></p><p>Enterprise customers can create custom referral landing pages:</p><p><strong>Example:</strong> Instead of <code>disclosurely.com/signup?ref=ABC123</code>, use: <code>compliance.yourcompany.com/disclosurely-partner</code></p><p><strong>Benefits:</strong></p><ul><li>Increased trust and conversion</li><li>Brand consistency</li><li>Professional appearance</li></ul><p><strong>Learn More:</strong> <a href="/docs/admin/custom-domains">Custom Domains</a></p><h3 id="analytics-integration" tabindex="-1">Analytics Integration <a class="header-anchor" href="#analytics-integration" aria-label="Permalink to &quot;Analytics Integration&quot;">​</a></h3><p><strong>Track Referral ROI:</strong></p><p>Connect referral program to your analytics:</p><p><strong>Available Metrics:</strong></p><ul><li>Referral traffic in Google Analytics</li><li>Attribution modeling</li><li>Conversion tracking</li><li>Revenue attribution</li></ul><p><strong>Setup:</strong> Dashboard &gt; Referral Program &gt; Integrations</p><p><strong>Learn More:</strong> <a href="/docs/analytics/dashboard">Analytics Dashboard</a></p><h2 id="success-stories" tabindex="-1">Success Stories <a class="header-anchor" href="#success-stories" aria-label="Permalink to &quot;Success Stories&quot;">​</a></h2><h3 id="top-referrer-highlights" tabindex="-1">Top Referrer Highlights <a class="header-anchor" href="#top-referrer-highlights" aria-label="Permalink to &quot;Top Referrer Highlights&quot;">​</a></h3><p><strong>Case Study: Compliance Consulting Firm</strong></p><p><em>&quot;We&#39;ve referred 47 clients to Disclosurely over 18 months, earning over $15,000 in credits and unlocking Enterprise features permanently. It&#39;s become a core part of our service offering.&quot;</em></p><p><strong>Results:</strong></p><ul><li>47 active referrals</li><li>Platinum tier status</li><li>$15,000+ in credits earned</li><li>Free Enterprise features</li><li>Selected as Ambassador</li></ul><p><strong>Case Study: CFO Network</strong></p><p><em>&quot;As a CFO peer group leader, I recommended Disclosurely to 12 member companies. The recurring rewards now cover my entire subscription cost.&quot;</em></p><p><strong>Results:</strong></p><ul><li>12 active referrals</li><li>$180/month in recurring credits</li><li>Gold tier status</li><li>Free plan for life (credits exceed cost)</li></ul><h2 id="program-updates" tabindex="-1">Program Updates <a class="header-anchor" href="#program-updates" aria-label="Permalink to &quot;Program Updates&quot;">​</a></h2><h3 id="recent-changes" tabindex="-1">Recent Changes <a class="header-anchor" href="#recent-changes" aria-label="Permalink to &quot;Recent Changes&quot;">​</a></h3><p><strong>Current Program Version:</strong> 2.0 (Launched January 2025)</p><p><strong>Recent Updates:</strong></p><ul><li>Increased Pro plan referral reward ($100 → $150)</li><li>Added recurring monthly rewards (new)</li><li>Introduced tier system (Bronze to Platinum)</li><li>Enhanced analytics dashboard</li><li>Added cash payout option (Platinum tier)</li></ul><p><strong>Coming Soon:</strong></p><ul><li>Team referral pools (share rewards across team)</li><li>Gifted subscriptions (give credits to referrals)</li><li>Referral challenges and contests</li><li>Enhanced partnership models</li><li>API for partner integrations</li></ul><h3 id="stay-informed" tabindex="-1">Stay Informed <a class="header-anchor" href="#stay-informed" aria-label="Permalink to &quot;Stay Informed&quot;">​</a></h3><p><strong>Get Program Updates:</strong></p><p><strong>Subscribe:</strong> Dashboard &gt; Referral Program &gt; Notifications</p><p><strong>Update Channels:</strong></p><ul><li>Email digest (monthly)</li><li>In-app notifications</li><li>Referral program changelog</li><li>Newsletter mentions</li></ul><h2 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">​</a></h2><ul><li><a href="/docs/features/subscription-tiers">Subscription Tiers</a> - Plan comparison</li><li><a href="/docs/admin/organization-settings">Organization Settings</a> - Account management</li><li><a href="/docs/analytics/dashboard">Analytics Dashboard</a> - Referral tracking</li><li><a href="/docs/admin/custom-domains">Custom Domains</a> - Branded landing pages</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Questions about the referral program?</p><ul><li>Email: <a href="mailto:referrals@disclosurely.com" target="_blank" rel="noreferrer">referrals@disclosurely.com</a></li><li>Partnership inquiries: <a href="mailto:partnerships@disclosurely.com" target="_blank" rel="noreferrer">partnerships@disclosurely.com</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li><a href="https://disclosurely.com/referrals/faq" target="_blank" rel="noreferrer">Referral Program FAQ</a></li></ul>`,273)])])}const m=r(t,[["render",l]]);export{d as __pageData,m as default};
