import{_ as e,c as t,o as a,ag as n}from"./chunks/framework.4QX5UEtQ.js";const g=JSON.parse('{"title":"System Health & Status Monitoring | Disclosurely","description":"Real-time platform uptime monitoring, performance metrics, incident reporting, and service status updates. Track system health, scheduled maintenance, and historical performance.","frontmatter":{"title":"System Health & Status Monitoring | Disclosurely","description":"Real-time platform uptime monitoring, performance metrics, incident reporting, and service status updates. Track system health, scheduled maintenance, and historical performance.","head":[["meta",{"property":"og:title","content":"System Health & Status Monitoring - Disclosurely"}],["meta",{"property":"og:description","content":"Real-time platform uptime monitoring, performance metrics, incident reporting, and service status updates. Track system health, scheduled maintenance, and historical performance."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"System Health & Status - Disclosurely"}],["meta",{"name":"twitter:description","content":"Real-time platform uptime monitoring, performance metrics, incident reporting, and service status updates. Track system health, scheduled maintenance, and historical performance."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"features/system-health.md","filePath":"features/system-health.md"}'),l={name:"features/system-health.md"};function r(s,i,o,c,p,u){return a(),t("div",null,[...i[0]||(i[0]=[n(`<h1 id="system-health-status-monitoring" tabindex="-1">System Health &amp; Status Monitoring <a class="header-anchor" href="#system-health-status-monitoring" aria-label="Permalink to &quot;System Health &amp; Status Monitoring&quot;">‚Äã</a></h1><p>Monitor Disclosurely&#39;s platform health with real-time uptime tracking, performance metrics, incident reporting, and service status updates‚Äîensuring your whistleblowing channel is always available when needed.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">‚Äã</a></h2><p>System reliability is critical for whistleblowing platforms. Reporters must have confidence that they can submit reports at any time, and organizations need assurance that their compliance infrastructure is operational 24/7. Disclosurely provides comprehensive system health monitoring with transparent status reporting.</p><p><strong>Key Features:</strong></p><ul><li>Real-time uptime monitoring</li><li>Performance metrics dashboard</li><li>Incident notifications</li><li>Scheduled maintenance windows</li><li>Historical uptime reports</li><li>Service level agreement (SLA) tracking</li><li>Regional status monitoring</li></ul><p><strong>Access:</strong> status.disclosurely.com (public) or Dashboard &gt; System Health (authenticated)</p><p><strong>Uptime Target:</strong> 99.9% uptime (SLA for Pro and Enterprise plans)</p><h2 id="status-page" tabindex="-1">Status Page <a class="header-anchor" href="#status-page" aria-label="Permalink to &quot;Status Page&quot;">‚Äã</a></h2><h3 id="public-status-page" tabindex="-1">Public Status Page <a class="header-anchor" href="#public-status-page" aria-label="Permalink to &quot;Public Status Page&quot;">‚Äã</a></h3><p><strong>Real-Time System Status:</strong></p><p><strong>Access:</strong> <a href="https://status.disclosurely.com" target="_blank" rel="noreferrer">https://status.disclosurely.com</a></p><p><strong>Always Available:</strong></p><ul><li>No login required</li><li>Accessible even during incidents</li><li>Mobile-responsive</li><li>Multiple CDN nodes for reliability</li></ul><p><strong>Status Overview:</strong></p><p><strong>Service Components:</strong></p><p><strong>1. Reporting Portal</strong></p><ul><li>‚úÖ Operational</li><li>‚ö†Ô∏è Degraded Performance</li><li>‚è∏Ô∏è Partial Outage</li><li>üî¥ Major Outage</li></ul><p><strong>2. Dashboard (Admin Interface)</strong></p><ul><li>Status indicator</li><li>Response time (ms)</li><li>Last updated timestamp</li></ul><p><strong>3. Secure Messaging</strong></p><ul><li>Messaging service status</li><li>Notification delivery</li><li>Real-time sync status</li></ul><p><strong>4. File Uploads</strong></p><ul><li>Upload service status</li><li>Storage availability</li><li>Processing queue status</li></ul><p><strong>5. Email Notifications</strong></p><ul><li>Email delivery status</li><li>Queue length</li><li>Delivery rate</li></ul><p><strong>6. API Services</strong> (Enterprise)</p><ul><li>API endpoint status</li><li>Rate limit status</li><li>Response times</li></ul><p><strong>7. Database</strong></p><ul><li>Primary database status</li><li>Replica status</li><li>Backup system status</li></ul><p><strong>8. Search &amp; Analytics</strong></p><ul><li>Search service status</li><li>Analytics processing</li><li>Report generation</li></ul><p><strong>Overall Status:</strong></p><p><strong>System-Wide Indicator:</strong></p><ul><li>üü¢ <strong>All Systems Operational</strong> - Everything running normally</li><li>üü° <strong>Degraded Performance</strong> - System slower than usual but functional</li><li>üü† <strong>Partial Outage</strong> - Some services unavailable</li><li>üî¥ <strong>Major Outage</strong> - Critical services down</li></ul><h3 id="current-incidents" tabindex="-1">Current Incidents <a class="header-anchor" href="#current-incidents" aria-label="Permalink to &quot;Current Incidents&quot;">‚Äã</a></h3><p><strong>Active Incidents:</strong></p><p>When issues occur, incidents are posted in real-time:</p><p><strong>Incident Information:</strong></p><ul><li><strong>Title:</strong> Brief description (e.g., &quot;Slow dashboard loading&quot;)</li><li><strong>Status:</strong> Investigating, Identified, Monitoring, Resolved</li><li><strong>Severity:</strong> Minor, Major, Critical</li><li><strong>Affected Services:</strong> Which components impacted</li><li><strong>Started:</strong> Time incident began</li><li><strong>Updates:</strong> Chronological updates as situation evolves</li></ul><p><strong>Example Incident:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>üü† Slow File Upload Processing</span></span>
<span class="line"><span>Status: Monitoring</span></span>
<span class="line"><span>Severity: Major</span></span>
<span class="line"><span>Affected: File Uploads</span></span>
<span class="line"><span>Started: 2025-01-15 14:30 UTC</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Updates:</span></span>
<span class="line"><span>[15:45 UTC] Resolved - Upload processing returned to normal.</span></span>
<span class="line"><span>            Monitoring continues for 1 hour.</span></span>
<span class="line"><span>[15:20 UTC] Identified - High load on processing servers.</span></span>
<span class="line"><span>            Scaling up capacity.</span></span>
<span class="line"><span>[14:45 UTC] Investigating - Reports of slow upload processing.</span></span>
<span class="line"><span>            Team investigating cause.</span></span></code></pre></div><p><strong>Incident Transparency:</strong></p><p>All incidents are publicly disclosed:</p><ul><li>Root cause analysis published (post-resolution)</li><li>Impact duration clearly stated</li><li>Affected user count (anonymized)</li><li>Remediation steps taken</li><li>Preventive measures implemented</li></ul><h3 id="scheduled-maintenance" tabindex="-1">Scheduled Maintenance <a class="header-anchor" href="#scheduled-maintenance" aria-label="Permalink to &quot;Scheduled Maintenance&quot;">‚Äã</a></h3><p><strong>Planned Downtime:</strong></p><p>Maintenance windows scheduled and communicated in advance:</p><p><strong>Maintenance Notice:</strong></p><ul><li><strong>Date &amp; Time:</strong> Exact start time (UTC and local time zones)</li><li><strong>Duration:</strong> Expected maintenance window</li><li><strong>Affected Services:</strong> What will be unavailable</li><li><strong>Impact:</strong> How users will be affected</li><li><strong>Alternative Access:</strong> Workarounds if any</li></ul><p><strong>Example:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>üîß Scheduled Maintenance: Database Upgrades</span></span>
<span class="line"><span>Date: Sunday, January 28, 2025</span></span>
<span class="line"><span>Time: 02:00 - 04:00 UTC (10:00 PM - 12:00 AM EST)</span></span>
<span class="line"><span>Duration: ~2 hours</span></span>
<span class="line"><span>Affected: All services briefly (5-minute window)</span></span>
<span class="line"><span>Impact: Brief interruption during final cutover</span></span>
<span class="line"><span>Status: Scheduled</span></span></code></pre></div><p><strong>Advance Notice:</strong></p><ul><li><strong>Minor Maintenance:</strong> 7 days advance notice</li><li><strong>Major Maintenance:</strong> 14 days advance notice</li><li><strong>Emergency Maintenance:</strong> As much notice as possible (minimum 2 hours)</li></ul><p><strong>Maintenance Schedule:</strong></p><p>Maintenance typically scheduled during:</p><ul><li>Weekend hours (Saturday/Sunday)</li><li>Late night (02:00-05:00 UTC)</li><li>Low-traffic periods</li><li>Regional off-peak hours</li></ul><h2 id="performance-metrics" tabindex="-1">Performance Metrics <a class="header-anchor" href="#performance-metrics" aria-label="Permalink to &quot;Performance Metrics&quot;">‚Äã</a></h2><h3 id="response-time-monitoring" tabindex="-1">Response Time Monitoring <a class="header-anchor" href="#response-time-monitoring" aria-label="Permalink to &quot;Response Time Monitoring&quot;">‚Äã</a></h3><p><strong>Real-Time Performance:</strong></p><p><strong>Access:</strong> Status page &gt; Performance Metrics</p><p><strong>Metrics Displayed:</strong></p><p><strong>1. Page Load Times</strong></p><ul><li>Reporting portal homepage: Target &lt;2 seconds</li><li>Dashboard load: Target ‚ù§Ô∏è seconds</li><li>Report submission: Target &lt;1 second per page</li></ul><p><strong>2. API Response Times</strong> (Enterprise)</p><ul><li>Average response time: Target &lt;200ms</li><li>95th percentile: Target &lt;500ms</li><li>99th percentile: Target &lt;1000ms</li></ul><p><strong>3. Database Query Performance</strong></p><ul><li>Average query time: Target &lt;50ms</li><li>Slow query count: Target &lt;0.1% of queries</li></ul><p><strong>4. File Upload Speed</strong></p><ul><li>Average upload speed: Varies by connection</li><li>Processing time: Target &lt;30 seconds</li><li>Thumbnail generation: Target &lt;10 seconds</li></ul><p><strong>Performance Visualization:</strong></p><p><strong>Graphs Available:</strong></p><ul><li>Last 24 hours (real-time updates)</li><li>Last 7 days (daily averages)</li><li>Last 30 days (weekly trends)</li><li>Last 90 days (monthly trends)</li></ul><p><strong>Color Coding:</strong></p><ul><li>üü¢ <strong>Green:</strong> Normal performance</li><li>üü° <strong>Yellow:</strong> Slower than average but acceptable</li><li>üü† <strong>Orange:</strong> Degraded performance</li><li>üî¥ <strong>Red:</strong> Unacceptable performance</li></ul><h3 id="uptime-percentage" tabindex="-1">Uptime Percentage <a class="header-anchor" href="#uptime-percentage" aria-label="Permalink to &quot;Uptime Percentage&quot;">‚Äã</a></h3><p><strong>Uptime Tracking:</strong></p><p><strong>Current Month:</strong></p><ul><li>Percentage uptime (99.X%)</li><li>Downtime minutes</li><li>Number of incidents</li><li>Mean time to resolution (MTTR)</li></ul><p><strong>Last 90 Days:</strong></p><ul><li>Uptime by month</li><li>Trend analysis</li><li>Incident frequency</li><li>Performance comparison</li></ul><p><strong>Historical Uptime:</strong></p><p><strong>Access:</strong> Status page &gt; Uptime History</p><p><strong>Uptime by Service:</strong></p><ul><li>Reporting Portal: 99.97%</li><li>Dashboard: 99.95%</li><li>Secure Messaging: 99.98%</li><li>File Uploads: 99.93%</li><li>API Services: 99.99%</li></ul><p><strong>Industry Benchmark:</strong></p><p>Disclosurely targets:</p><ul><li><strong>Free Plan:</strong> 99.5% uptime (best effort)</li><li><strong>Starter Plan:</strong> 99.5% uptime (guaranteed)</li><li><strong>Pro Plan:</strong> 99.9% uptime (guaranteed with SLA)</li><li><strong>Enterprise Plan:</strong> 99.95% uptime (guaranteed with SLA and credits)</li></ul><p><strong>Learn More:</strong> <a href="/docs/features/subscription-tiers">Subscription Tiers</a></p><h2 id="incident-management" tabindex="-1">Incident Management <a class="header-anchor" href="#incident-management" aria-label="Permalink to &quot;Incident Management&quot;">‚Äã</a></h2><h3 id="incident-classification" tabindex="-1">Incident Classification <a class="header-anchor" href="#incident-classification" aria-label="Permalink to &quot;Incident Classification&quot;">‚Äã</a></h3><p><strong>Severity Levels:</strong></p><p><strong>Critical (P0):</strong></p><ul><li>Complete service outage</li><li>Data security breach</li><li>Data loss risk</li><li>All users affected</li></ul><p><strong>Response:</strong></p><ul><li>Immediate all-hands response</li><li>Updates every 30 minutes</li><li>Executive notification</li><li>Postmortem required</li></ul><p><strong>Major (P1):</strong></p><ul><li>Significant functionality unavailable</li><li>Many users affected</li><li>Core features degraded</li><li>Workaround available but difficult</li></ul><p><strong>Response:</strong></p><ul><li>Rapid response team engaged</li><li>Updates every hour</li><li>Senior engineering involved</li><li>Postmortem recommended</li></ul><p><strong>Minor (P2):</strong></p><ul><li>Limited functionality impact</li><li>Small user subset affected</li><li>Non-critical features affected</li><li>Easy workaround available</li></ul><p><strong>Response:</strong></p><ul><li>Standard response procedures</li><li>Updates as needed</li><li>Resolution during business hours</li><li>Internal review</li></ul><p><strong>Maintenance (P3):</strong></p><ul><li>Scheduled or planned work</li><li>Minimal user impact</li><li>Communicated in advance</li><li>Brief interruptions only</li></ul><p><strong>Response:</strong></p><ul><li>Standard change management</li><li>Advance communication</li><li>Rollback plan ready</li></ul><h3 id="communication-during-incidents" tabindex="-1">Communication During Incidents <a class="header-anchor" href="#communication-during-incidents" aria-label="Permalink to &quot;Communication During Incidents&quot;">‚Äã</a></h3><p><strong>Notification Channels:</strong></p><p><strong>1. Status Page Updates</strong></p><ul><li>Real-time incident updates</li><li>Chronological timeline</li><li>Resolution status</li></ul><p><strong>2. Email Notifications</strong> (Subscribed Users)</p><ul><li>Incident detected</li><li>Major updates</li><li>Resolution confirmation</li><li>Postmortem publication</li></ul><p><strong>3. In-App Banners</strong></p><ul><li>Active incident notification</li><li>Link to status page</li><li>Expected resolution time</li></ul><p><strong>4. Social Media</strong> (Major Incidents)</p><ul><li>Twitter: @DisclosurelyStatus</li><li>LinkedIn: Company page updates</li></ul><p><strong>5. Direct Contact</strong> (Enterprise Only)</p><ul><li>Dedicated account manager notification</li><li>Phone call for critical incidents</li><li>Slack/Teams integration</li></ul><p><strong>Subscribe to Notifications:</strong></p><p><strong>How to Subscribe:</strong></p><ol><li>Visit status.disclosurely.com</li><li>Click <strong>&quot;Subscribe to Updates&quot;</strong></li><li>Enter email address</li><li>Select notification preferences: <ul><li>All incidents</li><li>Major and critical only</li><li>Scheduled maintenance only</li><li>Specific service subscriptions</li></ul></li><li>Confirm subscription</li></ol><h3 id="incident-postmortems" tabindex="-1">Incident Postmortems <a class="header-anchor" href="#incident-postmortems" aria-label="Permalink to &quot;Incident Postmortems&quot;">‚Äã</a></h3><p><strong>Transparency Reports:</strong></p><p>After major incidents, detailed postmortems published:</p><p><strong>Postmortem Contents:</strong></p><p><strong>1. Incident Summary</strong></p><ul><li>What happened</li><li>When it occurred</li><li>Duration of impact</li><li>Number of users affected</li></ul><p><strong>2. Timeline</strong></p><ul><li>Detection time</li><li>Response milestones</li><li>Resolution time</li><li>Communication timeline</li></ul><p><strong>3. Root Cause Analysis</strong></p><ul><li>Technical cause identified</li><li>Contributing factors</li><li>Why it wasn&#39;t prevented</li></ul><p><strong>4. Impact Assessment</strong></p><ul><li>Services affected</li><li>User experience impact</li><li>Data integrity confirmation</li><li>Financial impact (if SLA credits owed)</li></ul><p><strong>5. Remediation</strong></p><ul><li>Immediate fixes applied</li><li>Long-term improvements planned</li><li>Process changes implemented</li><li>Additional monitoring added</li></ul><p><strong>6. Lessons Learned</strong></p><ul><li>What went well</li><li>What could be improved</li><li>Action items with owners</li><li>Timeline for implementation</li></ul><p><strong>Example:</strong></p><p>View past postmortems: status.disclosurely.com/postmortems</p><h2 id="regional-status" tabindex="-1">Regional Status <a class="header-anchor" href="#regional-status" aria-label="Permalink to &quot;Regional Status&quot;">‚Äã</a></h2><h3 id="multi-region-infrastructure" tabindex="-1">Multi-Region Infrastructure <a class="header-anchor" href="#multi-region-infrastructure" aria-label="Permalink to &quot;Multi-Region Infrastructure&quot;">‚Äã</a></h3><p><strong>Geographic Distribution:</strong></p><p>Disclosurely operates in multiple regions:</p><p><strong>Primary Regions:</strong></p><ul><li>üá∫üá∏ <strong>US East</strong> (Virginia) - Primary</li><li>üá∫üá∏ <strong>US West</strong> (Oregon) - Secondary</li><li>üá™üá∫ <strong>EU West</strong> (Ireland) - EU customers</li><li>üá¨üáß <strong>UK</strong> (London) - UK customers</li></ul><p><strong>Status by Region:</strong></p><p>Each region displays independent status:</p><ul><li>Regional uptime</li><li>Regional response times</li><li>Regional incident history</li><li>Failover status</li></ul><p><strong>Data Residency:</strong></p><p>Customers can choose data region:</p><ul><li>EU customers: Data stored in EU</li><li>UK customers: Data stored in UK</li><li>US customers: Data stored in US</li><li>Other regions: Closest data center</li></ul><p><strong>Learn More:</strong> <a href="/docs/security/overview">Security Overview</a></p><h3 id="failover-redundancy" tabindex="-1">Failover &amp; Redundancy <a class="header-anchor" href="#failover-redundancy" aria-label="Permalink to &quot;Failover &amp; Redundancy&quot;">‚Äã</a></h3><p><strong>High Availability:</strong></p><p><strong>Automatic Failover:</strong></p><ul><li>Primary region failure triggers automatic failover</li><li>Traffic routed to healthy region</li><li>User sessions preserved</li><li>Seamless experience (typically &lt;30 seconds)</li></ul><p><strong>Database Replication:</strong></p><ul><li>Real-time replication across regions</li><li>Multi-region backups</li><li>Point-in-time recovery</li><li>Zero data loss (RPO: 0 seconds)</li></ul><p><strong>Load Balancing:</strong></p><ul><li>Intelligent traffic routing</li><li>Health checks every 10 seconds</li><li>Automatic removal of unhealthy servers</li><li>Geographic load distribution</li></ul><p><strong>Status Indicators:</strong></p><p><strong>Access:</strong> Status page &gt; Infrastructure</p><p><strong>Components:</strong></p><ul><li>Load balancers: Status and traffic distribution</li><li>Application servers: Count of healthy servers</li><li>Database clusters: Primary and replica status</li><li>Cache servers: Hit rate and availability</li><li>File storage: Availability and capacity</li></ul><h2 id="service-level-agreements-sla" tabindex="-1">Service Level Agreements (SLA) <a class="header-anchor" href="#service-level-agreements-sla" aria-label="Permalink to &quot;Service Level Agreements (SLA)&quot;">‚Äã</a></h2><h3 id="uptime-guarantees" tabindex="-1">Uptime Guarantees <a class="header-anchor" href="#uptime-guarantees" aria-label="Permalink to &quot;Uptime Guarantees&quot;">‚Äã</a></h3><p><strong>Plan-Based SLAs:</strong></p><p><strong>Pro Plan SLA:</strong></p><ul><li><strong>Guarantee:</strong> 99.9% monthly uptime</li><li><strong>Downtime Allowance:</strong> ~43 minutes/month</li><li><strong>Measurement:</strong> Reporting portal availability</li><li><strong>Credits:</strong> 5% monthly fee credit per 0.1% below guarantee</li></ul><p><strong>Enterprise Plan SLA:</strong></p><ul><li><strong>Guarantee:</strong> 99.95% monthly uptime</li><li><strong>Downtime Allowance:</strong> ~22 minutes/month</li><li><strong>Measurement:</strong> All critical services</li><li><strong>Credits:</strong> 10% monthly fee credit per 0.1% below guarantee</li><li><strong>Additional:</strong> Priority support during incidents</li></ul><p><strong>SLA Exclusions:</strong></p><p>Downtime not counted toward SLA:</p><ul><li>Scheduled maintenance (with advance notice)</li><li>Incidents caused by customer actions</li><li>Force majeure events (natural disasters, etc.)</li><li>Third-party service failures (DNS, CDN, etc.)</li><li>DDoS attacks (we mitigate but don&#39;t guarantee)</li></ul><p><strong>Learn More:</strong> <a href="/docs/features/subscription-tiers">Subscription Tiers</a></p><h3 id="sla-credit-requests" tabindex="-1">SLA Credit Requests <a class="header-anchor" href="#sla-credit-requests" aria-label="Permalink to &quot;SLA Credit Requests&quot;">‚Äã</a></h3><p><strong>How to Request Credits:</strong></p><p>If SLA not met:</p><p><strong>Process:</strong></p><ol><li>Verify downtime exceeded SLA allowance</li><li>Email: <a href="mailto:sla@disclosurely.com" target="_blank" rel="noreferrer">sla@disclosurely.com</a></li><li>Provide: <ul><li>Your organization name</li><li>Incident dates/times</li><li>Affected services</li><li>Impact description</li></ul></li><li>Review within 5 business days</li><li>Credits applied to next invoice</li></ol><p><strong>Credit Calculation:</strong></p><p><strong>Example:</strong></p><ul><li>Pro plan: $99/month</li><li>SLA: 99.9% uptime</li><li>Actual: 99.7% uptime (0.2% below SLA)</li><li>Credits: 2 √ó 5% = 10% of monthly fee = $9.90</li></ul><p><strong>Maximum Credits:</strong></p><ul><li>Pro plan: Maximum 50% of monthly fee</li><li>Enterprise plan: Maximum 100% of monthly fee</li></ul><p><strong>Credits Do Not:</strong></p><ul><li>Waive termination fees</li><li>Extend service beyond agreement</li><li>Compensate for data loss (separate policy)</li><li>Apply to free or starter plans</li></ul><h2 id="health-monitoring-dashboard" tabindex="-1">Health Monitoring Dashboard <a class="header-anchor" href="#health-monitoring-dashboard" aria-label="Permalink to &quot;Health Monitoring Dashboard&quot;">‚Äã</a></h2><h3 id="internal-health-dashboard" tabindex="-1">Internal Health Dashboard <a class="header-anchor" href="#internal-health-dashboard" aria-label="Permalink to &quot;Internal Health Dashboard&quot;">‚Äã</a></h3><p><strong>For Administrators:</strong></p><p><strong>Access:</strong> Dashboard &gt; System Health (authenticated users only)</p><p><strong>Organization-Specific Metrics:</strong></p><p><strong>1. Your Reporting Portal Status</strong></p><ul><li>Custom domain health</li><li>SSL certificate status</li><li>Link availability</li><li>Form submission success rate</li></ul><p><strong>2. File Upload Performance</strong></p><ul><li>Average upload speed (your organization)</li><li>Failed uploads count</li><li>Storage usage and limits</li><li>Processing queue for your files</li></ul><p><strong>3. Email Delivery</strong></p><ul><li>Notification delivery rate</li><li>Bounce rate</li><li>Email queue status</li><li>Failed deliveries</li></ul><p><strong>4. API Usage</strong> (Enterprise)</p><ul><li>API calls per day</li><li>Rate limit status</li><li>Error rate</li><li>Endpoint performance</li></ul><p><strong>5. Integration Health</strong></p><ul><li>Webhook delivery status</li><li>SSO authentication success rate</li><li>Third-party integration status</li></ul><p><strong>Custom Alerts:</strong></p><p><strong>Set Up Alerts:</strong></p><p>Configure notifications for your organization:</p><p><strong>Alert Conditions:</strong></p><ul><li>Report submission failures</li><li>Email delivery failures</li><li>Storage capacity warnings</li><li>API rate limit approaching</li><li>Custom domain SSL expiration</li><li>Unusual activity patterns</li></ul><p><strong>Notification Methods:</strong></p><ul><li>Email to administrators</li><li>In-app notifications</li><li>Slack/Teams integration (Enterprise)</li><li>SMS alerts (Enterprise, critical only)</li></ul><p><strong>Access:</strong> Dashboard &gt; Settings &gt; Alerts</p><h3 id="diagnostic-tools" tabindex="-1">Diagnostic Tools <a class="header-anchor" href="#diagnostic-tools" aria-label="Permalink to &quot;Diagnostic Tools&quot;">‚Äã</a></h3><p><strong>Self-Service Diagnostics:</strong></p><p><strong>Access:</strong> Dashboard &gt; System Health &gt; Diagnostics</p><p><strong>Available Tools:</strong></p><p><strong>1. Connection Test</strong></p><ul><li>Test connection to Disclosurely servers</li><li>Measure latency</li><li>Check firewall/proxy issues</li><li>DNS resolution check</li></ul><p><strong>2. Email Deliverability Test</strong></p><ul><li>Send test notification</li><li>Verify email receiving</li><li>Check spam folder</li><li>SPF/DKIM verification</li></ul><p><strong>3. File Upload Test</strong></p><ul><li>Test file upload functionality</li><li>Measure upload speed</li><li>Verify processing</li><li>Check browser compatibility</li></ul><p><strong>4. API Health Check</strong> (Enterprise)</p><ul><li>Test API authentication</li><li>Verify endpoint access</li><li>Check rate limits</li><li>Sample request/response</li></ul><p><strong>5. Custom Domain Test</strong></p><ul><li>Verify DNS configuration</li><li>Check SSL certificate</li><li>Test routing</li><li>Validate CNAME records</li></ul><p><strong>Test Results:</strong></p><p>Each test provides:</p><ul><li>‚úÖ Pass/‚ùå Fail status</li><li>Detailed error messages if failed</li><li>Recommendations for fixes</li><li>Support contact if assistance needed</li></ul><h2 id="monitoring-best-practices" tabindex="-1">Monitoring Best Practices <a class="header-anchor" href="#monitoring-best-practices" aria-label="Permalink to &quot;Monitoring Best Practices&quot;">‚Äã</a></h2><h3 id="for-organizations" tabindex="-1">For Organizations <a class="header-anchor" href="#for-organizations" aria-label="Permalink to &quot;For Organizations&quot;">‚Äã</a></h3><p>‚úÖ <strong>Subscribe to Status Updates:</strong></p><ul><li>Monitor platform status proactively</li><li>Receive incident notifications</li><li>Plan around scheduled maintenance</li></ul><p>‚úÖ <strong>Monitor Your Metrics:</strong></p><ul><li>Review internal health dashboard weekly</li><li>Track report submission success rate</li><li>Monitor email delivery rate</li><li>Watch storage usage trends</li></ul><p>‚úÖ <strong>Set Up Alerts:</strong></p><ul><li>Configure critical alerts</li><li>Ensure multiple administrators notified</li><li>Test alert delivery quarterly</li><li>Review alert history</li></ul><p>‚úÖ <strong>Review SLA Performance:</strong></p><ul><li>Track monthly uptime</li><li>Compare to SLA guarantees</li><li>Request credits when appropriate</li><li>Escalate concerns early</li></ul><p>‚úÖ <strong>Test Integrations:</strong></p><ul><li>Verify webhooks delivering</li><li>Test SSO authentication monthly</li><li>Check API access regularly</li><li>Validate email routing</li></ul><h3 id="for-reporters" tabindex="-1">For Reporters <a class="header-anchor" href="#for-reporters" aria-label="Permalink to &quot;For Reporters&quot;">‚Äã</a></h3><p>‚úÖ <strong>Check Status Before Reporting:</strong></p><ul><li>Visit status page if experiencing issues</li><li>Verify it&#39;s not a known incident</li><li>Check scheduled maintenance calendar</li></ul><p>‚úÖ <strong>Understand Incident Status:</strong></p><ul><li>&quot;Investigating&quot; = Team aware, working on it</li><li>&quot;Identified&quot; = Root cause known</li><li>&quot;Monitoring&quot; = Fix applied, watching for stability</li><li>&quot;Resolved&quot; = Issue fully resolved</li></ul><p>‚úÖ <strong>Use Alternative Channels:</strong></p><ul><li>If online reporting unavailable, check for: <ul><li>Phone hotline (if configured)</li><li>Email reporting (if configured)</li><li>In-person reporting (if available)</li></ul></li></ul><h2 id="historical-performance" tabindex="-1">Historical Performance <a class="header-anchor" href="#historical-performance" aria-label="Permalink to &quot;Historical Performance&quot;">‚Äã</a></h2><h3 id="performance-reports" tabindex="-1">Performance Reports <a class="header-anchor" href="#performance-reports" aria-label="Permalink to &quot;Performance Reports&quot;">‚Äã</a></h3><p><strong>Monthly Performance Reports:</strong></p><p><strong>Access:</strong> status.disclosurely.com/reports</p><p><strong>Report Contents:</strong></p><p><strong>Uptime Summary:</strong></p><ul><li>Overall uptime percentage</li><li>Uptime by service</li><li>Downtime breakdown</li><li>Incident count and severity</li></ul><p><strong>Performance Metrics:</strong></p><ul><li>Average response times</li><li>Performance compared to targets</li><li>Improvement trends</li><li>Regional performance</li></ul><p><strong>Incident Analysis:</strong></p><ul><li>Number of incidents</li><li>Mean time to detection (MTTD)</li><li>Mean time to resolution (MTTR)</li><li>Repeat incidents</li></ul><p><strong>Maintenance Summary:</strong></p><ul><li>Scheduled maintenance windows</li><li>Maintenance impact</li><li>Upgrades and improvements</li><li>Upcoming planned work</li></ul><p><strong>Customer Impact:</strong></p><ul><li>User-reported issues</li><li>Support ticket volume</li><li>Feature requests</li><li>Satisfaction metrics</li></ul><h3 id="transparency-commitment" tabindex="-1">Transparency Commitment <a class="header-anchor" href="#transparency-commitment" aria-label="Permalink to &quot;Transparency Commitment&quot;">‚Äã</a></h3><p><strong>Our Commitment:</strong></p><p><strong>Always Public:</strong></p><ul><li>Real-time status information</li><li>Honest incident reporting</li><li>Detailed postmortems</li><li>Historical performance data</li></ul><p><strong>Never Hidden:</strong></p><ul><li>Minor incidents disclosed</li><li>Performance degradation reported</li><li>Maintenance impact communicated</li><li>SLA breaches acknowledged</li></ul><p><strong>Continuous Improvement:</strong></p><ul><li>Regular infrastructure upgrades</li><li>Proactive monitoring enhancements</li><li>Incident prevention focus</li><li>Performance optimization</li></ul><h2 id="third-party-dependencies" tabindex="-1">Third-Party Dependencies <a class="header-anchor" href="#third-party-dependencies" aria-label="Permalink to &quot;Third-Party Dependencies&quot;">‚Äã</a></h2><h3 id="external-service-status" tabindex="-1">External Service Status <a class="header-anchor" href="#external-service-status" aria-label="Permalink to &quot;External Service Status&quot;">‚Äã</a></h3><p><strong>Services We Rely On:</strong></p><p>Disclosurely depends on third-party services:</p><p><strong>Infrastructure:</strong></p><ul><li><strong>AWS</strong> (Amazon Web Services) - Hosting</li><li><strong>Cloudflare</strong> - CDN and DDoS protection</li><li><strong>Auth0</strong> - Authentication (Enterprise SSO)</li></ul><p><strong>Communications:</strong></p><ul><li><strong>SendGrid</strong> - Email delivery</li><li><strong>Twilio</strong> - SMS notifications (if enabled)</li></ul><p><strong>Monitoring:</strong></p><ul><li><strong>Datadog</strong> - System monitoring</li><li><strong>PagerDuty</strong> - Incident management</li></ul><p><strong>Third-Party Status:</strong></p><p>Links to third-party status pages:</p><ul><li>AWS Status: status.aws.amazon.com</li><li>Cloudflare Status: www.cloudflarestatus.com</li><li>SendGrid Status: status.sendgrid.com</li></ul><p><strong>Impact Handling:</strong></p><p>When third-party outages occur:</p><ul><li>Status page updated immediately</li><li>Incident attributed to third-party</li><li>Mitigation steps communicated</li><li>Alternative services activated when possible</li></ul><h2 id="support-during-incidents" tabindex="-1">Support During Incidents <a class="header-anchor" href="#support-during-incidents" aria-label="Permalink to &quot;Support During Incidents&quot;">‚Äã</a></h2><h3 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-label="Permalink to &quot;Getting Help&quot;">‚Äã</a></h3><p><strong>During Active Incidents:</strong></p><p><strong>1. Check Status Page First</strong></p><ul><li>Verify if known incident</li><li>Review posted updates</li><li>Check estimated resolution</li></ul><p><strong>2. If Not Listed:</strong></p><ul><li>Contact <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li>Describe issue in detail</li><li>Include error messages</li><li>Note time issue started</li></ul><p><strong>3. Urgent/Critical Issues:</strong></p><ul><li>Pro/Enterprise: Priority support line</li><li>Enterprise: Dedicated account manager</li><li>Include &quot;URGENT&quot; in email subject</li><li>Follow up with phone call if critical</li></ul><p><strong>Response Times:</strong></p><p><strong>Status Page Updates:</strong></p><ul><li>Critical incidents: Every 30 minutes</li><li>Major incidents: Every hour</li><li>Minor incidents: As needed</li><li>Maintenance: Before, during, after</li></ul><p><strong>Support Tickets:</strong></p><ul><li>Critical issues: &lt;1 hour response</li><li>High priority: &lt;4 hours response</li><li>Normal priority: &lt;24 hours response</li><li>Low priority: &lt;48 hours response</li></ul><p><strong>Enterprise Support:</strong></p><ul><li>Phone support available 24/7</li><li>Dedicated Slack/Teams channel</li><li>Account manager direct line</li><li>Escalation procedures defined</li></ul><h3 id="communicating-with-reporters" tabindex="-1">Communicating with Reporters <a class="header-anchor" href="#communicating-with-reporters" aria-label="Permalink to &quot;Communicating with Reporters&quot;">‚Äã</a></h3><p><strong>During Platform Issues:</strong></p><p><strong>Best Practices:</strong></p><p>‚úÖ <strong>Inform Reporters Proactively:</strong></p><ul><li>Post notice on reporting portal (if accessible)</li><li>Send email to known reporters with active cases</li><li>Provide status page link</li><li>Offer alternative reporting methods</li></ul><p>‚úÖ <strong>Extend Deadlines:</strong></p><ul><li>If SLA timers affected by outage</li><li>Communicate extension clearly</li><li>Document in <a href="/docs/features/audit-trail">audit trail</a></li></ul><p>‚úÖ <strong>Preserve Trust:</strong></p><ul><li>Be transparent about issues</li><li>Apologize for inconvenience</li><li>Reassure about data security</li><li>Confirm no data loss</li></ul><h2 id="related-features" tabindex="-1">Related Features <a class="header-anchor" href="#related-features" aria-label="Permalink to &quot;Related Features&quot;">‚Äã</a></h2><h3 id="audit-trail" tabindex="-1">Audit Trail <a class="header-anchor" href="#audit-trail" aria-label="Permalink to &quot;Audit Trail&quot;">‚Äã</a></h3><p><strong>System Events Logged:</strong></p><p>All system health events recorded in <a href="/docs/features/audit-trail">audit trail</a>:</p><ul><li>Incident start/end times</li><li>Maintenance windows</li><li>Performance degradation</li><li>Failover events</li><li>SLA breaches</li></ul><p><strong>Learn More:</strong> <a href="/docs/features/audit-trail">Audit Trail</a></p><h3 id="notifications" tabindex="-1">Notifications <a class="header-anchor" href="#notifications" aria-label="Permalink to &quot;Notifications&quot;">‚Äã</a></h3><p><strong>System Health Notifications:</strong></p><p>Integrated with <a href="/docs/features/notifications">notification system</a>:</p><ul><li>Incident alerts</li><li>Maintenance reminders</li><li>Performance warnings</li><li>SLA breach notifications</li></ul><p><strong>Learn More:</strong> <a href="/docs/features/notifications">Notification System</a></p><h3 id="security-monitoring" tabindex="-1">Security Monitoring <a class="header-anchor" href="#security-monitoring" aria-label="Permalink to &quot;Security Monitoring&quot;">‚Äã</a></h3><p><strong>Security Incidents:</strong></p><p>Security-related incidents handled separately:</p><ul><li>Reported on status page</li><li>Detailed disclosure (after mitigation)</li><li>User notification if data affected</li><li>Regulatory reporting as required</li></ul><p><strong>Learn More:</strong> <a href="/docs/security/overview">Security Overview</a></p><h2 id="related-pages" tabindex="-1">Related Pages <a class="header-anchor" href="#related-pages" aria-label="Permalink to &quot;Related Pages&quot;">‚Äã</a></h2><ul><li><a href="/docs/security/overview">Security Overview</a> - Platform security details</li><li><a href="/docs/features/audit-trail">Audit Trail</a> - System event logging</li><li><a href="/docs/features/notifications">Notification System</a> - Alert configuration</li><li><a href="/docs/features/subscription-tiers">Subscription Tiers</a> - SLA by plan</li><li><a href="/docs/analytics/dashboard">Analytics Dashboard</a> - Usage metrics</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">‚Äã</a></h2><p>Questions about system health?</p><ul><li>Status page: status.disclosurely.com</li><li>Email: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li>SLA inquiries: <a href="mailto:sla@disclosurely.com" target="_blank" rel="noreferrer">sla@disclosurely.com</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li>Emergency hotline (Enterprise): Available in account dashboard</li></ul>`,333)])])}const m=e(l,[["render",r]]);export{g as __pageData,m as default};
