import{_ as i,c as t,o as a,ag as r}from"./chunks/framework.4QX5UEtQ.js";const g=JSON.parse('{"title":"Platform Overview - Disclosurely Whistleblowing Platform","description":"Enterprise whistleblowing and compliance platform with anonymous reporting, case management, and AI-powered risk assessment. Built for GDPR, SOX, and EU Directive compliance.","frontmatter":{"title":"Platform Overview - Disclosurely Whistleblowing Platform","description":"Enterprise whistleblowing and compliance platform with anonymous reporting, case management, and AI-powered risk assessment. Built for GDPR, SOX, and EU Directive compliance.","head":[["meta",{"property":"og:title","content":"Disclosurely Platform Overview - Complete Whistleblowing Solution"}],["meta",{"property":"og:description","content":"Enterprise whistleblowing and compliance platform with anonymous reporting, case management, and AI-powered risk assessment. Built for GDPR, SOX, and EU Directive compliance."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Disclosurely Platform Overview"}],["meta",{"name":"twitter:description","content":"Enterprise whistleblowing and compliance platform with anonymous reporting, case management, and AI-powered risk assessment. Built for GDPR, SOX, and EU Directive compliance."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"introduction/platform-overview.md","filePath":"introduction/platform-overview.md"}'),o={name:"introduction/platform-overview.md"};function n(s,e,l,c,d,u){return a(),t("div",null,[...e[0]||(e[0]=[r('<h1 id="platform-overview" tabindex="-1">Platform Overview <a class="header-anchor" href="#platform-overview" aria-label="Permalink to &quot;Platform Overview&quot;">​</a></h1><p>Disclosurely is an enterprise-grade whistleblowing and compliance management platform designed to help organizations protect whistleblowers, maintain regulatory compliance, and manage organizational risk effectively. Built with security and privacy at its core, Disclosurely provides a comprehensive suite of tools for modern compliance programs.</p><h2 id="what-is-disclosurely" tabindex="-1">What is Disclosurely? <a class="header-anchor" href="#what-is-disclosurely" aria-label="Permalink to &quot;What is Disclosurely?&quot;">​</a></h2><p>Disclosurely is a complete whistleblowing and compliance solution that enables organizations to:</p><ul><li><strong>Protect Whistleblowers</strong>: Provide secure, anonymous reporting channels with end-to-end encryption</li><li><strong>Stay Compliant</strong>: Meet requirements for EU Whistleblowing Directive, GDPR, SOX, ISO 27001, and more</li><li><strong>Manage Cases</strong>: Centralized platform for managing, investigating, and resolving whistleblower reports</li><li><strong>Assess Risk</strong>: AI-powered risk assessment and pattern detection across your organization</li><li><strong>Maintain Audit Trails</strong>: Tamper-evident audit logs for all activities and actions</li><li><strong>Automate Compliance</strong>: Automated workflows, reminders, and GDPR request processing</li></ul><h2 id="core-platform-components" tabindex="-1">Core Platform Components <a class="header-anchor" href="#core-platform-components" aria-label="Permalink to &quot;Core Platform Components&quot;">​</a></h2><h3 id="_1-anonymous-reporting-system" tabindex="-1">1. Anonymous Reporting System <a class="header-anchor" href="#_1-anonymous-reporting-system" aria-label="Permalink to &quot;1. Anonymous Reporting System&quot;">​</a></h3><p>Our <a href="/docs/reporting/report-types">anonymous reporting system</a> is built on zero-knowledge architecture, meaning report content is encrypted client-side before it ever reaches our servers. Key features include:</p><ul><li><strong>End-to-End Encryption</strong>: Reports are encrypted using <a href="/docs/reporting/encryption">AES-256 encryption</a> on the user&#39;s device</li><li><strong>Anonymous Submission</strong>: No personal information is collected or stored</li><li><strong>Secure Tracking</strong>: Unique <a href="/docs/reporting/tracking-report">tracking IDs</a> (DIS-XXXXXXXX format) allow whistleblowers to check status</li><li><strong>Two-Way Messaging</strong>: <a href="/docs/reporting/secure-messaging">Encrypted messaging</a> allows dialogue while maintaining anonymity</li><li><strong>File Attachments</strong>: Support for <a href="/docs/reporting/how-to-submit">uploading evidence</a> securely</li><li><strong>Multi-Language Support</strong>: Interface available in multiple languages</li></ul><h3 id="_2-case-management" tabindex="-1">2. Case Management <a class="header-anchor" href="#_2-case-management" aria-label="Permalink to &quot;2. Case Management&quot;">​</a></h3><p>Comprehensive <a href="/docs/cases/workflow">case management tools</a> help your team efficiently handle and resolve reports:</p><ul><li><strong>Centralized Dashboard</strong>: All reports in one place with powerful filtering and search</li><li><strong>Workflow Management</strong>: Customizable workflows with <a href="/docs/cases/status">status tracking</a></li><li><strong>Team Collaboration</strong>: <a href="/docs/cases/assignment">Assign cases</a>, add notes, and collaborate securely</li><li><strong>Priority Management</strong>: Set priority levels and manage workload distribution</li><li><strong>Status Timeline</strong>: Track complete history of case status changes</li><li><strong>Soft Delete</strong>: <a href="/docs/cases/archiving">Archive cases</a> with restore capability</li><li><strong>Export Capabilities</strong>: Export case data for analysis or reporting</li></ul><h3 id="_3-ai-powered-analysis" tabindex="-1">3. AI-Powered Analysis <a class="header-anchor" href="#_3-ai-powered-analysis" aria-label="Permalink to &quot;3. AI-Powered Analysis&quot;">​</a></h3><p>Leverage <a href="/docs/ai/case-helper">artificial intelligence</a> to enhance your compliance program:</p><ul><li><strong>AI Case Helper</strong>: Get AI-powered insights and recommendations for cases</li><li><strong>Pattern Detection</strong>: Automatically identify patterns and trends across reports</li><li><strong>Risk Assessment</strong>: AI-driven risk scoring and prioritization</li><li><strong>Content Analysis</strong>: Analyze case content for key themes and issues</li><li><strong>Document Processing</strong>: Upload and analyze company documents for context</li><li><strong>Smart Suggestions</strong>: Receive action recommendations based on similar cases</li></ul><h3 id="_4-compliance-management" tabindex="-1">4. Compliance Management <a class="header-anchor" href="#_4-compliance-management" aria-label="Permalink to &quot;4. Compliance Management&quot;">​</a></h3><p>Comprehensive compliance tools to manage policies, risks, and obligations:</p><ul><li><strong>Policy Management</strong>: Version-controlled policy library with acknowledgment tracking</li><li><strong>Risk Register</strong>: Centralized risk assessment and mitigation tracking</li><li><strong>Compliance Calendar</strong>: Deadline tracking for compliance obligations</li><li><strong>GDPR Automation</strong>: Automated <a href="/docs/compliance/gdpr">data export and erasure request processing</a></li><li><strong>Audit Logs</strong>: <a href="/docs/compliance/audit-trail">Tamper-evident audit trails</a> with hash chain integrity</li><li><strong>Compliance Scoring</strong>: Track and measure your compliance posture</li></ul><h3 id="_5-security-privacy" tabindex="-1">5. Security &amp; Privacy <a class="header-anchor" href="#_5-security-privacy" aria-label="Permalink to &quot;5. Security &amp; Privacy&quot;">​</a></h3><p>Enterprise-grade <a href="/docs/security/overview">security protecting your most sensitive data</a>:</p><ul><li><strong>Zero-Knowledge Architecture</strong>: We cannot access report content - ever</li><li><strong>End-to-End Encryption</strong>: AES-256 encryption for all sensitive data</li><li><strong>Multi-Factor Authentication</strong>: Optional MFA for all users</li><li><strong>Role-Based Access Control</strong>: Granular permissions and access control</li><li><strong>Session Management</strong>: Automatic timeouts and secure session handling</li><li><strong>Security Monitoring</strong>: Real-time detection of suspicious activity</li><li><strong>Audit Everything</strong>: Complete audit trail of all system activities</li></ul><h2 id="platform-architecture" tabindex="-1">Platform Architecture <a class="header-anchor" href="#platform-architecture" aria-label="Permalink to &quot;Platform Architecture&quot;">​</a></h2><h3 id="multi-tenant-design" tabindex="-1">Multi-Tenant Design <a class="header-anchor" href="#multi-tenant-design" aria-label="Permalink to &quot;Multi-Tenant Design&quot;">​</a></h3><p>Disclosurely is built as a secure multi-tenant platform where each organization&#39;s data is completely isolated:</p><ul><li><strong>Data Isolation</strong>: Organization data is logically separated at the database level</li><li><strong>Custom Branding</strong>: Each organization can customize branding and domains</li><li><strong>Independent Configuration</strong>: Settings, workflows, and policies per organization</li><li><strong>Scalable Infrastructure</strong>: Built on modern serverless architecture for reliability</li></ul><h3 id="technology-stack" tabindex="-1">Technology Stack <a class="header-anchor" href="#technology-stack" aria-label="Permalink to &quot;Technology Stack&quot;">​</a></h3><ul><li><strong>Frontend</strong>: Modern React application with TypeScript</li><li><strong>Backend</strong>: Supabase (PostgreSQL) with Row Level Security (RLS)</li><li><strong>Encryption</strong>: Client-side encryption using Web Crypto API</li><li><strong>Edge Functions</strong>: 42+ serverless functions for specialized operations</li><li><strong>AI Integration</strong>: DeepSeek AI for analysis and insights</li><li><strong>Infrastructure</strong>: Deployed on global edge network for performance</li></ul><h2 id="subscription-tiers" tabindex="-1">Subscription Tiers <a class="header-anchor" href="#subscription-tiers" aria-label="Permalink to &quot;Subscription Tiers&quot;">​</a></h2><p>Disclosurely offers flexible pricing to meet organizational needs:</p><h3 id="basic-tier-free" tabindex="-1">Basic Tier (Free) <a class="header-anchor" href="#basic-tier-free" aria-label="Permalink to &quot;Basic Tier (Free)&quot;">​</a></h3><ul><li>Up to 10 reports per month</li><li>Core reporting features</li><li>Basic case management</li><li>Email notifications</li><li>30-day data retention</li></ul><h3 id="pro-tier-£39-99-month" tabindex="-1">Pro Tier (£39.99/month) <a class="header-anchor" href="#pro-tier-£39-99-month" aria-label="Permalink to &quot;Pro Tier (£39.99/month)&quot;">​</a></h3><ul><li>Unlimited reports</li><li>Advanced case management</li><li>AI-powered analysis</li><li>Custom branding</li><li>Custom domains</li><li>Compliance management suite</li><li>GDPR automation</li><li>Priority support</li><li>Unlimited data retention</li></ul><h3 id="enterprise-custom-pricing" tabindex="-1">Enterprise (Custom Pricing) <a class="header-anchor" href="#enterprise-custom-pricing" aria-label="Permalink to &quot;Enterprise (Custom Pricing)&quot;">​</a></h3><ul><li>Everything in Pro</li><li>Dedicated support</li><li>SLA guarantees</li><li>Custom integrations</li><li>Advanced security features</li><li>Training and onboarding</li><li>Custom contracts</li></ul><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to &quot;Use Cases&quot;">​</a></h2><h3 id="corporate-compliance" tabindex="-1">Corporate Compliance <a class="header-anchor" href="#corporate-compliance" aria-label="Permalink to &quot;Corporate Compliance&quot;">​</a></h3><p>Large enterprises use Disclosurely to:</p><ul><li>Meet EU Whistleblowing Directive requirements</li><li>Maintain SOX compliance</li><li>Provide secure reporting channels</li><li>Manage compliance obligations</li><li>Track and mitigate organizational risk</li></ul><h3 id="hr-employee-relations" tabindex="-1">HR &amp; Employee Relations <a class="header-anchor" href="#hr-employee-relations" aria-label="Permalink to &quot;HR &amp; Employee Relations&quot;">​</a></h3><p>HR departments leverage Disclosurely for:</p><ul><li>Workplace misconduct reporting</li><li>Harassment and discrimination reporting</li><li>Policy violation tracking</li><li>Employee grievances</li><li>Anonymous feedback channels</li></ul><h3 id="financial-services" tabindex="-1">Financial Services <a class="header-anchor" href="#financial-services" aria-label="Permalink to &quot;Financial Services&quot;">​</a></h3><p>Financial institutions use Disclosurely for:</p><ul><li>Market abuse reporting</li><li>Financial misconduct</li><li>Regulatory compliance</li><li>Internal control monitoring</li><li>Audit management</li></ul><h3 id="healthcare" tabindex="-1">Healthcare <a class="header-anchor" href="#healthcare" aria-label="Permalink to &quot;Healthcare&quot;">​</a></h3><p>Healthcare organizations utilize Disclosurely for:</p><ul><li>Patient safety concerns</li><li>Quality of care issues</li><li>Regulatory compliance</li><li>HIPAA privacy concerns</li><li>Medical staff reporting</li></ul><h3 id="government-public-sector" tabindex="-1">Government &amp; Public Sector <a class="header-anchor" href="#government-public-sector" aria-label="Permalink to &quot;Government &amp; Public Sector&quot;">​</a></h3><p>Public sector organizations benefit from:</p><ul><li>Transparency and accountability</li><li>Public interest disclosures</li><li>Corruption reporting</li><li>Ethics and conduct violations</li><li>Citizen feedback channels</li></ul><h2 id="key-differentiators" tabindex="-1">Key Differentiators <a class="header-anchor" href="#key-differentiators" aria-label="Permalink to &quot;Key Differentiators&quot;">​</a></h2><h3 id="_1-true-anonymity" tabindex="-1">1. True Anonymity <a class="header-anchor" href="#_1-true-anonymity" aria-label="Permalink to &quot;1. True Anonymity&quot;">​</a></h3><p>Unlike competitors, Disclosurely uses zero-knowledge encryption. We cannot decrypt or access report content, ensuring true anonymity for whistleblowers.</p><h3 id="_2-ai-powered-intelligence" tabindex="-1">2. AI-Powered Intelligence <a class="header-anchor" href="#_2-ai-powered-intelligence" aria-label="Permalink to &quot;2. AI-Powered Intelligence&quot;">​</a></h3><p>Our AI Case Helper provides insights and recommendations that would take humans hours to compile, helping you resolve cases faster and more effectively.</p><h3 id="_3-comprehensive-compliance" tabindex="-1">3. Comprehensive Compliance <a class="header-anchor" href="#_3-comprehensive-compliance" aria-label="Permalink to &quot;3. Comprehensive Compliance&quot;">​</a></h3><p>More than just whistleblowing - manage policies, risks, GDPR, audits, and more in one integrated platform.</p><h3 id="_4-developer-friendly" tabindex="-1">4. Developer-Friendly <a class="header-anchor" href="#_4-developer-friendly" aria-label="Permalink to &quot;4. Developer-Friendly&quot;">​</a></h3><p>Built with modern technologies and APIs, making it easy to integrate with your existing systems (coming soon: webhooks, Zapier, custom APIs).</p><h3 id="_5-regulatory-expertise" tabindex="-1">5. Regulatory Expertise <a class="header-anchor" href="#_5-regulatory-expertise" aria-label="Permalink to &quot;5. Regulatory Expertise&quot;">​</a></h3><p>Built by compliance professionals who understand EU Whistleblowing Directive, GDPR, SOX, and other regulatory requirements.</p><h2 id="data-residency" tabindex="-1">Data Residency <a class="header-anchor" href="#data-residency" aria-label="Permalink to &quot;Data Residency&quot;">​</a></h2><p>All customer data is stored securely in ISO 27001-certified data centers. We offer:</p><ul><li><strong>EU Data Residency</strong>: Data stored in EU for GDPR compliance</li><li><strong>Data Portability</strong>: Export your data anytime in standard formats</li><li><strong>Data Ownership</strong>: You own your data - we&#39;re just the custodian</li><li><strong>Right to Erasure</strong>: Delete your data completely at any time</li></ul><h2 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h2><p>Ready to get started with Disclosurely? Here&#39;s what to do next:</p><ol><li><strong><a href="https://disclosurely.com" target="_blank" rel="noreferrer">Sign Up</a></strong> - Create your free account</li><li><strong><a href="/docs/admin/initial-setup">Initial Setup</a></strong> - Configure your organization</li><li><strong><a href="/docs/admin/team-management">Invite Your Team</a></strong> - Add team members</li><li><strong><a href="/docs/admin/custom-branding">Customize Branding</a></strong> - Make it yours</li><li><strong><a href="/docs/reporting/how-to-submit">Launch Reporting</a></strong> - Go live with secure reporting</li></ol><h2 id="support-resources" tabindex="-1">Support &amp; Resources <a class="header-anchor" href="#support-resources" aria-label="Permalink to &quot;Support &amp; Resources&quot;">​</a></h2><p>Need help? We&#39;re here for you:</p><ul><li><strong>Email</strong>: <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></li><li><strong>Documentation</strong>: You&#39;re reading it!</li><li><strong>FAQs</strong>: <a href="/docs/support/faqs">Common questions answered</a></li><li><strong>Live Chat</strong>: Available in the application</li></ul><hr><p><strong>Next Steps:</strong></p><ul><li><a href="/docs/introduction/key-concepts">Understanding Key Concepts</a></li><li><a href="/docs/admin/initial-setup">Initial Setup Guide</a></li><li><a href="/docs/security/encryption">Platform Security</a></li></ul>',74)])])}const m=i(o,[["render",n]]);export{g as __pageData,m as default};
