import{_ as i,c as t,o as r,ag as a}from"./chunks/framework.4QX5UEtQ.js";const h=JSON.parse('{"title":"Report Encryption - Disclosurely Zero-Knowledge Security","description":"AES-256 encryption with zero-knowledge architecture protects whistleblower reports. Client-side encryption ensures complete privacy and military-grade security.","frontmatter":{"title":"Report Encryption - Disclosurely Zero-Knowledge Security","description":"AES-256 encryption with zero-knowledge architecture protects whistleblower reports. Client-side encryption ensures complete privacy and military-grade security.","head":[["meta",{"property":"og:title","content":"Report Encryption & Zero-Knowledge Security - Disclosurely"}],["meta",{"property":"og:description","content":"AES-256 encryption with zero-knowledge architecture protects whistleblower reports. Client-side encryption ensures complete privacy and military-grade security."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Report Encryption - Disclosurely"}],["meta",{"name":"twitter:description","content":"AES-256 encryption with zero-knowledge architecture protects whistleblower reports. Client-side encryption ensures complete privacy and military-grade security."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"reporting/encryption.md","filePath":"reporting/encryption.md"}'),o={name:"reporting/encryption.md"};function n(l,e,s,c,p,d){return r(),t("div",null,[...e[0]||(e[0]=[a('<h1 id="report-encryption" tabindex="-1">Report Encryption <a class="header-anchor" href="#report-encryption" aria-label="Permalink to &quot;Report Encryption&quot;">​</a></h1><p>Understanding how Disclosurely protects your reports with military-grade encryption and zero-knowledge architecture.</p><h2 id="zero-knowledge-architecture" tabindex="-1">Zero-Knowledge Architecture <a class="header-anchor" href="#zero-knowledge-architecture" aria-label="Permalink to &quot;Zero-Knowledge Architecture&quot;">​</a></h2><p>Disclosurely uses <strong>zero-knowledge encryption</strong>, meaning:</p><ul><li>Report content encrypted in your browser</li><li>Encryption happens before data leaves your device</li><li>We never have access to encryption keys</li><li>We cannot read your reports</li><li>True end-to-end encryption</li></ul><p><strong>What this means for you</strong>: Your report is completely private. Even if someone hacked our servers, they couldn&#39;t read your report.</p><h2 id="how-encryption-works" tabindex="-1">How Encryption Works <a class="header-anchor" href="#how-encryption-works" aria-label="Permalink to &quot;How Encryption Works&quot;">​</a></h2><h3 id="step-1-report-submission" tabindex="-1">Step 1: Report Submission <a class="header-anchor" href="#step-1-report-submission" aria-label="Permalink to &quot;Step 1: Report Submission&quot;">​</a></h3><p>When you submit a report:</p><ol><li>You write your report in the web form</li><li>JavaScript in your browser generates encryption key</li><li>Report content encrypted using AES-256</li><li>Only encrypted data sent to our servers</li><li>Encryption key hashed and stored separately</li><li>Plaintext never leaves your browser</li></ol><h3 id="step-2-data-storage" tabindex="-1">Step 2: Data Storage <a class="header-anchor" href="#step-2-data-storage" aria-label="Permalink to &quot;Step 2: Data Storage&quot;">​</a></h3><p>On our servers:</p><ul><li>Encrypted report content stored</li><li>Encryption key hash stored separately</li><li>Keys and content never stored together</li><li>Multiple layers of security</li><li>Encrypted database</li><li>Encrypted backups</li></ul><h3 id="step-3-report-access" tabindex="-1">Step 3: Report Access <a class="header-anchor" href="#step-3-report-access" aria-label="Permalink to &quot;Step 3: Report Access&quot;">​</a></h3><p>When authorized case handler views report:</p><ol><li>Handler requests report</li><li>System verifies authorization</li><li>Encrypted content sent to handler&#39;s browser</li><li>Decryption happens in browser</li><li>Plaintext displayed only in memory</li><li>Never stored unencrypted on servers</li></ol><h2 id="encryption-specifications" tabindex="-1">Encryption Specifications <a class="header-anchor" href="#encryption-specifications" aria-label="Permalink to &quot;Encryption Specifications&quot;">​</a></h2><h3 id="algorithm" tabindex="-1">Algorithm <a class="header-anchor" href="#algorithm" aria-label="Permalink to &quot;Algorithm&quot;">​</a></h3><p><strong>AES-256 (Advanced Encryption Standard)</strong></p><ul><li>256-bit key length</li><li>Industry standard encryption</li><li>Used by governments and militaries</li><li>Virtually unbreakable</li></ul><h3 id="key-derivation" tabindex="-1">Key Derivation <a class="header-anchor" href="#key-derivation" aria-label="Permalink to &quot;Key Derivation&quot;">​</a></h3><p><strong>PBKDF2 (Password-Based Key Derivation Function 2)</strong></p><ul><li>100,000 iterations</li><li>SHA-256 hashing</li><li>Random salt generation</li><li>Protection against brute force</li></ul><h3 id="transport-security" tabindex="-1">Transport Security <a class="header-anchor" href="#transport-security" aria-label="Permalink to &quot;Transport Security&quot;">​</a></h3><p><strong>TLS 1.3 (Transport Layer Security)</strong></p><ul><li>All data encrypted in transit</li><li>Perfect forward secrecy</li><li>Modern cipher suites</li><li>Protection against man-in-the-middle attacks</li></ul><h2 id="what-gets-encrypted" tabindex="-1">What Gets Encrypted <a class="header-anchor" href="#what-gets-encrypted" aria-label="Permalink to &quot;What Gets Encrypted&quot;">​</a></h2><h3 id="always-encrypted" tabindex="-1">Always Encrypted <a class="header-anchor" href="#always-encrypted" aria-label="Permalink to &quot;Always Encrypted&quot;">​</a></h3><ul><li>Report description and content</li><li>Messages between you and investigators</li><li>Reporter identity (confidential reports)</li><li>File attachments</li><li>Sensitive metadata</li></ul><h3 id="not-encrypted-metadata" tabindex="-1">Not Encrypted (Metadata) <a class="header-anchor" href="#not-encrypted-metadata" aria-label="Permalink to &quot;Not Encrypted (Metadata)&quot;">​</a></h3><ul><li>Report submission timestamp</li><li>Status changes</li><li>Category selection</li><li>Tracking ID (not sensitive)</li><li>Organization ID</li></ul><p><strong>Why some metadata isn&#39;t encrypted</strong>: Needed for routing, notifications, and analytics. Contains no sensitive information.</p><h2 id="file-encryption" tabindex="-1">File Encryption <a class="header-anchor" href="#file-encryption" aria-label="Permalink to &quot;File Encryption&quot;">​</a></h2><p>Files uploaded with reports are:</p><ul><li>Encrypted before upload</li><li>Stored with same encryption as report</li><li>Decrypted only when authorized user accesses</li><li>Virus scanned before encryption</li></ul><p>Supported files:</p><ul><li>Documents (PDF, DOC, DOCX)</li><li>Images (JPG, PNG, GIF)</li><li>Spreadsheets (XLS, XLSX)</li><li>Archives (ZIP)</li></ul><h2 id="access-keys" tabindex="-1">Access Keys <a class="header-anchor" href="#access-keys" aria-label="Permalink to &quot;Access Keys&quot;">​</a></h2><h3 id="what-are-access-keys" tabindex="-1">What Are Access Keys? <a class="header-anchor" href="#what-are-access-keys" aria-label="Permalink to &quot;What Are Access Keys?&quot;">​</a></h3><p>Access keys are cryptographic keys that allow decryption. Generated automatically when you submit a report.</p><h3 id="who-has-access-keys" tabindex="-1">Who Has Access Keys? <a class="header-anchor" href="#who-has-access-keys" aria-label="Permalink to &quot;Who Has Access Keys?&quot;">​</a></h3><p><strong>You</strong> (Reporter):</p><ul><li>Your tracking ID provides access</li><li>Can view your own report</li><li>Can send/receive messages</li></ul><p><strong>Authorized Case Handlers</strong>:</p><ul><li>Assigned investigators</li><li>Organization administrators</li><li>Access logged in audit trail</li></ul><p><strong>Who Doesn&#39;t</strong>:</p><ul><li>Disclosurely support staff</li><li>Unauthorized employees</li><li>Other organizations</li><li>Anyone without proper authorization</li></ul><h2 id="security-audits" tabindex="-1">Security Audits <a class="header-anchor" href="#security-audits" aria-label="Permalink to &quot;Security Audits&quot;">​</a></h2><p>Disclosurely&#39;s encryption has been:</p><ul><li>Independently audited</li><li>Penetration tested</li><li>Security reviewed</li><li>Compliance certified</li></ul><p><strong>Certifications</strong>:</p><ul><li>ISO 27001 (Information Security)</li><li>SOC 2 Type II</li><li>GDPR Compliant</li><li>Industry best practices</li></ul><h2 id="encryption-limitations" tabindex="-1">Encryption Limitations <a class="header-anchor" href="#encryption-limitations" aria-label="Permalink to &quot;Encryption Limitations&quot;">​</a></h2><p><strong>What Encryption Doesn&#39;t Protect</strong>:</p><ul><li>Your tracking ID (protect it like a password)</li><li>Metadata (submission time, category)</li><li>Communication patterns (when you check status)</li><li>Your identity if you reveal it in messages</li></ul><p><strong>Your Responsibility</strong>:</p><ul><li>Keep tracking ID secure</li><li>Don&#39;t share it with anyone</li><li>Access from secure devices</li><li>Use strong passwords</li><li>Enable MFA on your account (if registered)</li></ul><h2 id="technical-details" tabindex="-1">Technical Details <a class="header-anchor" href="#technical-details" aria-label="Permalink to &quot;Technical Details&quot;">​</a></h2><p>For security professionals:</p><p><strong>Encryption</strong>:</p><ul><li>Algorithm: AES-256-GCM</li><li>Key Size: 256 bits</li><li>Block Mode: Galois/Counter Mode</li><li>IV: Random, 96 bits</li></ul><p><strong>Key Derivation</strong>:</p><ul><li>Function: PBKDF2</li><li>Iterations: 100,000</li><li>Hash: SHA-256</li><li>Salt: Random, 128 bits</li></ul><p><strong>Transport</strong>:</p><ul><li>Protocol: TLS 1.3</li><li>Cipher Suites: Modern, secure only</li><li>Perfect Forward Secrecy: Yes</li><li>HSTS: Enabled</li></ul><h2 id="compliance" tabindex="-1">Compliance <a class="header-anchor" href="#compliance" aria-label="Permalink to &quot;Compliance&quot;">​</a></h2><h3 id="gdpr" tabindex="-1"><a href="/docs/compliance/gdpr">GDPR</a> <a class="header-anchor" href="#gdpr" aria-label="Permalink to &quot;[GDPR](/compliance/gdpr)&quot;">​</a></h3><ul><li>Right to access: Encrypted data exportable</li><li>Right to erasure: Encryption keys destroyed</li><li>Data minimization: Only necessary data collected</li><li>Purpose limitation: Encryption ensures limited access</li></ul><h3 id="iso-27001" tabindex="-1">ISO 27001 <a class="header-anchor" href="#iso-27001" aria-label="Permalink to &quot;ISO 27001&quot;">​</a></h3><ul><li>Information security management</li><li>Risk assessment</li><li>Access controls</li><li>Incident management</li></ul><h3 id="sox" tabindex="-1"><a href="/docs/compliance/sox">SOX</a> <a class="header-anchor" href="#sox" aria-label="Permalink to &quot;[SOX](/compliance/sox)&quot;">​</a></h3><ul><li><a href="/docs/compliance/audit-trail">Audit trails maintained</a></li><li>Access logging</li><li>Data integrity</li><li>Non-repudiation</li></ul><hr><p><strong>Questions about encryption?</strong></p><ul><li><a href="/docs/security/overview">Security Overview</a></li><li><a href="/docs/support/contact">Contact Support</a></li><li>Email: <a href="mailto:security@disclosurely.com" target="_blank" rel="noreferrer">security@disclosurely.com</a></li></ul>',75)])])}const y=i(o,[["render",n]]);export{h as __pageData,y as default};
