import{_ as t,c as i,o,ag as n}from"./chunks/framework.4QX5UEtQ.js";const h=JSON.parse('{"title":"Report Types - Anonymous vs Confidential Reporting","description":"Choose between anonymous or confidential whistleblowing. Compare features, security, and best practices for each reporting type with complete encryption protection.","frontmatter":{"title":"Report Types - Anonymous vs Confidential Reporting","description":"Choose between anonymous or confidential whistleblowing. Compare features, security, and best practices for each reporting type with complete encryption protection.","head":[["meta",{"property":"og:title","content":"Anonymous vs Confidential Whistleblowing - Disclosurely"}],["meta",{"property":"og:description","content":"Choose between anonymous or confidential whistleblowing. Compare features, security, and best practices for each reporting type with complete encryption protection."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Report Types - Disclosurely"}],["meta",{"name":"twitter:description","content":"Choose between anonymous or confidential whistleblowing. Compare features, security, and best practices for each reporting type with complete encryption protection."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"reporting/report-types.md","filePath":"reporting/report-types.md"}'),r={name:"reporting/report-types.md"};function a(s,e,l,c,p,u){return o(),i("div",null,[...e[0]||(e[0]=[n('<h1 id="report-types" tabindex="-1">Report Types <a class="header-anchor" href="#report-types" aria-label="Permalink to &quot;Report Types&quot;">​</a></h1><p>Disclosurely supports two types of report submissions: Anonymous and Confidential. Understanding the difference helps you choose the right approach.</p><h2 id="anonymous-reports" tabindex="-1">Anonymous Reports <a class="header-anchor" href="#anonymous-reports" aria-label="Permalink to &quot;Anonymous Reports&quot;">​</a></h2><p><strong>Complete anonymity guaranteed</strong> - no personal information collected or stored.</p><h3 id="how-it-works" tabindex="-1">How It Works <a class="header-anchor" href="#how-it-works" aria-label="Permalink to &quot;How It Works&quot;">​</a></h3><ul><li>Submit report without providing identity</li><li>Content encrypted client-side before transmission</li><li>Receive unique tracking ID (DIS-XXXXXXXX)</li><li>Track status and communicate via tracking ID</li><li>Organization cannot identify you</li></ul><h3 id="best-for" tabindex="-1">Best For <a class="header-anchor" href="#best-for" aria-label="Permalink to &quot;Best For&quot;">​</a></h3><ul><li>Sensitive issues where retaliation is feared</li><li>Situations requiring maximum privacy</li><li>When you don&#39;t need direct communication</li><li>Reports about senior leadership</li><li>Concerns about investigation impartiality</li></ul><h3 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h3><p>✅ <a href="/docs/reporting/encryption">End-to-end encryption</a> ✅ Zero-knowledge architecture ✅ <a href="/docs/reporting/secure-messaging">Two-way secure messaging</a> ✅ Status tracking ✅ File uploads supported ✅ Cannot be traced to you</p><h3 id="limitations" tabindex="-1">Limitations <a class="header-anchor" href="#limitations" aria-label="Permalink to &quot;Limitations&quot;">​</a></h3><ul><li>Cannot provide direct follow-up (use messaging instead)</li><li>If tracking ID is lost, cannot recover access</li><li>More difficult for investigators to clarify details</li></ul><h2 id="confidential-reports" tabindex="-1">Confidential Reports <a class="header-anchor" href="#confidential-reports" aria-label="Permalink to &quot;Confidential Reports&quot;">​</a></h2><p><strong>Identity known to organization but kept confidential</strong> - easier communication while protecting privacy.</p><h3 id="how-it-works-1" tabindex="-1">How It Works <a class="header-anchor" href="#how-it-works-1" aria-label="Permalink to &quot;How It Works&quot;">​</a></h3><ul><li>Provide your contact information</li><li>Identity known to authorized personnel only</li><li>Direct communication possible</li><li>Protected from unauthorized disclosure</li><li>Still encrypted and secure</li></ul><h3 id="best-for-1" tabindex="-1">Best For <a class="header-anchor" href="#best-for-1" aria-label="Permalink to &quot;Best For&quot;">​</a></h3><ul><li>Issues where you&#39;re comfortable sharing identity</li><li>When direct communication is important</li><li>Situations requiring your specific knowledge</li><li>Follow-up clarification likely needed</li><li>When you want to participate in investigation</li></ul><h3 id="features-1" tabindex="-1">Features <a class="header-anchor" href="#features-1" aria-label="Permalink to &quot;Features&quot;">​</a></h3><p>✅ Secure and encrypted ✅ Direct communication ✅ Easier follow-up ✅ Identity protected from unauthorized access ✅ Can participate in investigation ✅ Receive direct updates</p><h3 id="protections" tabindex="-1">Protections <a class="header-anchor" href="#protections" aria-label="Permalink to &quot;Protections&quot;">​</a></h3><ul><li>Identity shared on need-to-know basis only</li><li>Protected from retaliation</li><li>Audit trail of who accesses your information</li><li>Confidentiality maintained in investigation</li></ul><h2 id="choosing-the-right-type" tabindex="-1">Choosing the Right Type <a class="header-anchor" href="#choosing-the-right-type" aria-label="Permalink to &quot;Choosing the Right Type&quot;">​</a></h2><p><strong>Choose Anonymous If:</strong></p><ul><li>You fear retaliation</li><li>Issue involves senior management</li><li>Maximum privacy is priority</li><li>You&#39;re uncomfortable revealing identity</li><li>Issue is highly sensitive</li></ul><p><strong>Choose Confidential If:</strong></p><ul><li>You&#39;re comfortable sharing identity</li><li>Direct communication is valuable</li><li>You want to participate in process</li><li>Issue requires your specific expertise</li><li>You trust the investigation process</li></ul><h2 id="switching-between-types" tabindex="-1">Switching Between Types <a class="header-anchor" href="#switching-between-types" aria-label="Permalink to &quot;Switching Between Types&quot;">​</a></h2><p><strong>Can I make an anonymous report confidential?</strong> Yes - you can reveal your identity by including it in a message.</p><p><strong>Can I make a confidential report anonymous?</strong> No - once identity is shared, it cannot be undone.</p><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><p><strong>For Anonymous Reports:</strong></p><ul><li>Save tracking ID securely</li><li>Don&#39;t include identifying information</li><li>Use personal devices/networks</li><li>Avoid patterns that might identify you</li><li>Check status from safe locations</li></ul><p><strong>For Confidential Reports:</strong></p><ul><li>Provide accurate contact information</li><li>Respond promptly to follow-up questions</li><li>Document any retaliation attempts</li><li>Keep copies of relevant evidence</li><li>Maintain discretion about investigation</li></ul><hr><p><strong>Next Steps:</strong></p><ul><li><a href="/docs/reporting/how-to-submit">How to Submit a Report</a></li><li><a href="/docs/reporting/tracking-report">Tracking Your Report</a></li><li><a href="/docs/reporting/secure-messaging">Secure Messaging</a></li></ul>',38)])])}const m=t(r,[["render",a]]);export{h as __pageData,m as default};
