import{_ as i,c as t,o as a,ag as s}from"./chunks/framework.4QX5UEtQ.js";const h=JSON.parse('{"title":"Secure Messaging - Disclosurely Anonymous Communication","description":"Encrypted two-way messaging for whistleblowers. Communicate with investigators anonymously using tracking IDs. Complete privacy protection with end-to-end encryption.","frontmatter":{"title":"Secure Messaging - Disclosurely Anonymous Communication","description":"Encrypted two-way messaging for whistleblowers. Communicate with investigators anonymously using tracking IDs. Complete privacy protection with end-to-end encryption.","head":[["meta",{"property":"og:title","content":"Secure Anonymous Messaging for Whistleblowers - Disclosurely"}],["meta",{"property":"og:description","content":"Encrypted two-way messaging for whistleblowers. Communicate with investigators anonymously using tracking IDs. Complete privacy protection with end-to-end encryption."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Secure Messaging - Disclosurely"}],["meta",{"name":"twitter:description","content":"Encrypted two-way messaging for whistleblowers. Communicate with investigators anonymously using tracking IDs. Complete privacy protection with end-to-end encryption."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"reporting/secure-messaging.md","filePath":"reporting/secure-messaging.md"}'),o={name:"reporting/secure-messaging.md"};function n(r,e,l,c,u,g){return a(),t("div",null,[...e[0]||(e[0]=[s('<h1 id="secure-messaging" tabindex="-1">Secure Messaging <a class="header-anchor" href="#secure-messaging" aria-label="Permalink to &quot;Secure Messaging&quot;">​</a></h1><p>Communicate securely with investigators while maintaining your anonymity. Two-way encrypted messaging allows dialogue without compromising privacy.</p><h2 id="how-secure-messaging-works" tabindex="-1">How Secure Messaging Works <a class="header-anchor" href="#how-secure-messaging-works" aria-label="Permalink to &quot;How Secure Messaging Works&quot;">​</a></h2><h3 id="for-anonymous-reports" tabindex="-1">For Anonymous Reports <a class="header-anchor" href="#for-anonymous-reports" aria-label="Permalink to &quot;For Anonymous Reports&quot;">​</a></h3><ul><li>Messages encrypted end-to-end</li><li>Your identity remains protected</li><li>Communicate via tracking ID</li><li>No email or personal information needed</li><li>Cannot be traced back to you</li></ul><h3 id="for-confidential-reports" tabindex="-1">For Confidential Reports <a class="header-anchor" href="#for-confidential-reports" aria-label="Permalink to &quot;For Confidential Reports&quot;">​</a></h3><ul><li>Encrypted communication</li><li>Direct dialogue possible</li><li>Faster clarification</li><li>Email notifications available</li><li>Identity protected from unauthorized access</li></ul><h2 id="sending-messages" tabindex="-1">Sending Messages <a class="header-anchor" href="#sending-messages" aria-label="Permalink to &quot;Sending Messages&quot;">​</a></h2><h3 id="access-messaging" tabindex="-1">Access Messaging <a class="header-anchor" href="#access-messaging" aria-label="Permalink to &quot;Access Messaging&quot;">​</a></h3><ol><li>Visit status lookup page</li><li>Enter your tracking ID</li><li>Scroll to messaging section</li><li>Type your message</li><li>Click &quot;Send&quot;</li></ol><p>Your message is:</p><ul><li>Encrypted before sending</li><li>Delivered immediately</li><li>Visible only to authorized case handlers</li><li>Logged in case timeline</li></ul><h3 id="what-to-include" tabindex="-1">What to Include <a class="header-anchor" href="#what-to-include" aria-label="Permalink to &quot;What to Include&quot;">​</a></h3><p><strong>Be specific</strong>:</p><ul><li>Answer questions directly</li><li>Provide requested information</li><li>Add relevant new details</li><li>Clarify previous statements</li></ul><p><strong>Maintain privacy</strong> (if anonymous):</p><ul><li>Avoid identifying information</li><li>Don&#39;t reveal your identity</li><li>Be vague about unique details</li><li>Protect your anonymity</li></ul><h2 id="receiving-messages" tabindex="-1">Receiving Messages <a class="header-anchor" href="#receiving-messages" aria-label="Permalink to &quot;Receiving Messages&quot;">​</a></h2><h3 id="checking-for-new-messages" tabindex="-1">Checking for New Messages <a class="header-anchor" href="#checking-for-new-messages" aria-label="Permalink to &quot;Checking for New Messages&quot;">​</a></h3><p><strong>Anonymous Reports</strong>:</p><ul><li>Check status page regularly</li><li>Look for &quot;New Messages&quot; badge</li><li>Messages appear in chronological order</li><li>No email notification</li></ul><p><strong>Confidential Reports</strong>:</p><ul><li>Receive email notification</li><li>Click link to view message</li><li>Reply directly from status page</li><li>All messages encrypted</li></ul><h3 id="types-of-messages-you-might-receive" tabindex="-1">Types of Messages You Might Receive <a class="header-anchor" href="#types-of-messages-you-might-receive" aria-label="Permalink to &quot;Types of Messages You Might Receive&quot;">​</a></h3><p><strong>Clarification Questions</strong>:</p><ul><li>&quot;Can you provide more detail about the date?&quot;</li><li>&quot;Which department did this occur in?&quot;</li><li>&quot;Were there any other witnesses?&quot;</li></ul><p><strong>Evidence Requests</strong>:</p><ul><li>&quot;Do you have any documentation?&quot;</li><li>&quot;Can you provide specific examples?&quot;</li><li>&quot;Are there emails or messages related to this?&quot;</li></ul><p><strong>Timeline Confirmation</strong>:</p><ul><li>&quot;Did this happen before or after X event?&quot;</li><li>&quot;How long has this been occurring?&quot;</li><li>&quot;When did you first notice this?&quot;</li></ul><p><strong>Update Messages</strong>:</p><ul><li>&quot;Your report is being investigated&quot;</li><li>&quot;We need more time to complete investigation&quot;</li><li>&quot;Investigation is complete, see status for outcome&quot;</li></ul><h2 id="message-best-practices" tabindex="-1">Message Best Practices <a class="header-anchor" href="#message-best-practices" aria-label="Permalink to &quot;Message Best Practices&quot;">​</a></h2><h3 id="do" tabindex="-1">Do: <a class="header-anchor" href="#do" aria-label="Permalink to &quot;Do:&quot;">​</a></h3><p>✅ Respond within 48 hours when possible ✅ Be clear and specific ✅ Provide requested information ✅ Ask for clarification if needed ✅ Keep messages professional ✅ Save important message threads</p><h3 id="don-t" tabindex="-1">Don&#39;t: <a class="header-anchor" href="#don-t" aria-label="Permalink to &quot;Don&#39;t:&quot;">​</a></h3><p>❌ Include personally identifying information (if anonymous) ❌ Make threats or use inflammatory language ❌ Provide false information ❌ Ignore messages from investigators ❌ Share your tracking ID with others ❌ Discuss investigation with coworkers</p><h2 id="message-security" tabindex="-1">Message Security <a class="header-anchor" href="#message-security" aria-label="Permalink to &quot;Message Security&quot;">​</a></h2><h3 id="encryption" tabindex="-1">Encryption <a class="header-anchor" href="#encryption" aria-label="Permalink to &quot;Encryption&quot;">​</a></h3><p>All messages use:</p><ul><li><a href="/docs/reporting/encryption">AES-256 encryption</a></li><li>Client-side encryption</li><li>Encrypted in transit and at rest</li><li>Zero-knowledge architecture</li><li>Military-grade security</li></ul><p><strong>What this means</strong>: Even Disclosurely cannot read your messages. Only you and authorized case handlers have access.</p><h3 id="access-control" tabindex="-1">Access Control <a class="header-anchor" href="#access-control" aria-label="Permalink to &quot;Access Control&quot;">​</a></h3><p>Messages can only be accessed by:</p><ul><li>Case handlers assigned to your report</li><li>Organization administrators</li><li>You (via tracking ID)</li></ul><p><strong>Who cannot access</strong>:</p><ul><li>Unassigned team members</li><li>Other organizations</li><li>Disclosurely support staff</li><li>Anyone without authorization</li></ul><h3 id="audit-trail" tabindex="-1">Audit Trail <a class="header-anchor" href="#audit-trail" aria-label="Permalink to &quot;Audit Trail&quot;">​</a></h3><p>All message access is logged:</p><ul><li>Who viewed messages</li><li>When they were viewed</li><li>What actions were taken</li><li><a href="/docs/compliance/audit-trail">Tamper-evident audit logs</a></li></ul><h2 id="message-history" tabindex="-1">Message History <a class="header-anchor" href="#message-history" aria-label="Permalink to &quot;Message History&quot;">​</a></h2><h3 id="viewing-past-messages" tabindex="-1">Viewing Past Messages <a class="header-anchor" href="#viewing-past-messages" aria-label="Permalink to &quot;Viewing Past Messages&quot;">​</a></h3><p>All messages are retained and accessible:</p><ul><li>Chronological order</li><li>Timestamped</li><li>Threaded conversation view</li><li>Full history available</li></ul><h3 id="exporting-messages" tabindex="-1">Exporting Messages <a class="header-anchor" href="#exporting-messages" aria-label="Permalink to &quot;Exporting Messages&quot;">​</a></h3><p><strong>Anonymous Reports</strong>:</p><ul><li>Screenshot messages for your records</li><li>Cannot export directly</li><li>Maintains anonymity</li></ul><p><strong>Confidential Reports</strong>:</p><ul><li>Request message export via support</li><li>Provided in PDF format</li><li>Includes full conversation history</li></ul><h2 id="limitations" tabindex="-1">Limitations <a class="header-anchor" href="#limitations" aria-label="Permalink to &quot;Limitations&quot;">​</a></h2><p><strong>Message Notifications</strong>:</p><ul><li>Anonymous reports: No email notifications</li><li>Must check status page manually</li><li>Maintains complete anonymity</li></ul><p><strong>Real-Time Chat</strong>:</p><ul><li>Not a real-time chat system</li><li>Messages checked during business hours</li><li>Expect response within 24-48 hours</li></ul><p><strong>File Attachments in Messages</strong>:</p><ul><li>Cannot attach files to messages</li><li>Upload during initial report submission</li><li>Send new report if additional evidence needed</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><p><strong>Message not sending:</strong></p><ul><li>Check internet connection</li><li>Verify tracking ID is valid</li><li>Try refreshing page</li><li>Contact support if persists</li></ul><p><strong>Not receiving responses:</strong></p><ul><li>Confirm you&#39;re checking correct tracking ID</li><li>Look in messaging section, not just status</li><li>Messages may come during investigation, not immediately</li><li>Wait 48 hours for response</li></ul><p><strong>Can&#39;t see past messages:</strong></p><ul><li>Scroll down in messaging section</li><li>Clear browser cache</li><li>Try different browser</li><li>Verify you&#39;re logged in with correct tracking ID</li></ul><hr><p><strong>Related Topics:</strong></p><ul><li><a href="/docs/reporting/tracking-report">Tracking Your Report</a></li><li><a href="/docs/reporting/encryption">Report Encryption</a></li><li><a href="/docs/reporting/how-to-submit">How to Submit</a></li></ul>',76)])])}const m=i(o,[["render",n]]);export{h as __pageData,m as default};
