import{_ as i,c as t,o as a,ag as s}from"./chunks/framework.4QX5UEtQ.js";const d=JSON.parse('{"title":"Tracking Your Report - Disclosurely Status Lookup","description":"Track whistleblower report status with unique tracking IDs. Check investigation progress, receive updates, and communicate securely with investigators anytime, anywhere.","frontmatter":{"title":"Tracking Your Report - Disclosurely Status Lookup","description":"Track whistleblower report status with unique tracking IDs. Check investigation progress, receive updates, and communicate securely with investigators anytime, anywhere.","head":[["meta",{"property":"og:title","content":"Track Your Whistleblower Report Status - Disclosurely"}],["meta",{"property":"og:description","content":"Track whistleblower report status with unique tracking IDs. Check investigation progress, receive updates, and communicate securely with investigators anytime, anywhere."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Tracking Your Report - Disclosurely"}],["meta",{"name":"twitter:description","content":"Track whistleblower report status with unique tracking IDs. Check investigation progress, receive updates, and communicate securely with investigators anytime, anywhere."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"reporting/tracking-report.md","filePath":"reporting/tracking-report.md"}'),o={name:"reporting/tracking-report.md"};function r(n,e,l,c,u,g){return a(),t("div",null,[...e[0]||(e[0]=[s('<h1 id="tracking-your-report" tabindex="-1">Tracking Your Report <a class="header-anchor" href="#tracking-your-report" aria-label="Permalink to &quot;Tracking Your Report&quot;">​</a></h1><p>Use your tracking ID to check the status of your report, receive updates, and communicate with investigators anytime.</p><h2 id="your-tracking-id" tabindex="-1">Your Tracking ID <a class="header-anchor" href="#your-tracking-id" aria-label="Permalink to &quot;Your Tracking ID&quot;">​</a></h2><p>Every report receives a unique tracking ID in the format: <strong>DIS-XXXXXXXX</strong></p><h3 id="what-it-s-used-for" tabindex="-1">What It&#39;s Used For <a class="header-anchor" href="#what-it-s-used-for" aria-label="Permalink to &quot;What It&#39;s Used For&quot;">​</a></h3><ul><li>Check report status anytime</li><li>Receive updates and messages</li><li>Communicate with investigators</li><li>View investigation progress</li><li>See resolution outcome</li></ul><h3 id="important-security-notes" tabindex="-1">Important Security Notes <a class="header-anchor" href="#important-security-notes" aria-label="Permalink to &quot;Important Security Notes&quot;">​</a></h3><p>⚠️ <strong>Keep your tracking ID secure</strong> - it&#39;s like a password to your report ⚠️ <strong>Don&#39;t share it</strong> - anyone with the ID can access your report ⚠️ <strong>Save it securely</strong> - write it down or use a password manager ⚠️ <strong>Can&#39;t be recovered if lost</strong> (anonymous reports) - this is by design for security</p><h2 id="checking-report-status" tabindex="-1">Checking Report Status <a class="header-anchor" href="#checking-report-status" aria-label="Permalink to &quot;Checking Report Status&quot;">​</a></h2><h3 id="access-the-status-page" tabindex="-1">Access the Status Page <a class="header-anchor" href="#access-the-status-page" aria-label="Permalink to &quot;Access the Status Page&quot;">​</a></h3><ol><li>Visit your organization&#39;s status lookup page</li><li>Or click the link provided after submission</li><li>Enter your tracking ID</li><li>View current status and messages</li></ol><h3 id="report-statuses" tabindex="-1">Report Statuses <a class="header-anchor" href="#report-statuses" aria-label="Permalink to &quot;Report Statuses&quot;">​</a></h3><p>Learn more about <a href="/docs/cases/status">case status management</a>.</p><p><strong>New</strong></p><ul><li>Report just submitted</li><li>Awaiting initial review</li><li>Expected: 24-48 hours for first update</li></ul><p><strong>In Review</strong></p><ul><li>Initial assessment underway</li><li>Case handler reviewing details</li><li>May be assigned to investigator</li></ul><p><strong>Investigating</strong></p><ul><li>Active investigation in progress</li><li>Gathering evidence and interviews</li><li>May receive questions from investigators</li></ul><p><strong>Resolved</strong></p><ul><li>Investigation complete</li><li>Resolution implemented</li><li>Outcome documented</li></ul><p><strong>Closed</strong></p><ul><li>Case officially closed</li><li>Final outcome recorded</li><li>Available in archives</li></ul><p><strong>Archived</strong></p><ul><li>Historical record</li><li>No longer active</li><li>Retained per data policy</li></ul><h2 id="status-timeline" tabindex="-1">Status Timeline <a class="header-anchor" href="#status-timeline" aria-label="Permalink to &quot;Status Timeline&quot;">​</a></h2><p>View complete history of your report:</p><ul><li>When status changed</li><li>Who changed it (role, not name)</li><li>Reason for change</li><li>Messages at each stage</li><li>Time spent in each status</li></ul><p>This transparency helps you understand investigation progress.</p><h2 id="receiving-updates" tabindex="-1">Receiving Updates <a class="header-anchor" href="#receiving-updates" aria-label="Permalink to &quot;Receiving Updates&quot;">​</a></h2><h3 id="automatic-notifications" tabindex="-1">Automatic Notifications <a class="header-anchor" href="#automatic-notifications" aria-label="Permalink to &quot;Automatic Notifications&quot;">​</a></h3><p>If you provided an email (confidential reports):</p><ul><li>Status change notifications</li><li>New message alerts</li><li>Investigation updates</li><li>Resolution notifications</li></ul><h3 id="checking-manually" tabindex="-1">Checking Manually <a class="header-anchor" href="#checking-manually" aria-label="Permalink to &quot;Checking Manually&quot;">​</a></h3><p>For anonymous reports:</p><ul><li>Check status page regularly</li><li>Look for &quot;New Messages&quot; indicator</li><li>Review timeline for updates</li><li>No email notifications (maintains anonymity)</li></ul><h2 id="secure-messaging" tabindex="-1">Secure Messaging <a class="header-anchor" href="#secure-messaging" aria-label="Permalink to &quot;Secure Messaging&quot;">​</a></h2><h3 id="sending-messages" tabindex="-1">Sending Messages <a class="header-anchor" href="#sending-messages" aria-label="Permalink to &quot;Sending Messages&quot;">​</a></h3><ol><li>Access your report via tracking ID</li><li>Scroll to messaging section</li><li>Type your message</li><li>Click &quot;Send&quot;</li></ol><p>Messages are:</p><ul><li>End-to-end encrypted</li><li>Maintain your anonymity</li><li>Delivered immediately</li><li>Notification sent to investigators</li></ul><h3 id="receiving-messages" tabindex="-1">Receiving Messages <a class="header-anchor" href="#receiving-messages" aria-label="Permalink to &quot;Receiving Messages&quot;">​</a></h3><p>Investigators may ask:</p><ul><li>Clarifying questions</li><li>Additional details</li><li>Evidence requests</li><li>Timeline confirmation</li><li>Witness information</li></ul><p><strong>Respond promptly</strong> to avoid investigation delays.</p><h3 id="message-best-practices" tabindex="-1">Message Best Practices <a class="header-anchor" href="#message-best-practices" aria-label="Permalink to &quot;Message Best Practices&quot;">​</a></h3><p>✅ <strong>Be responsive</strong> - check regularly for new messages ✅ <strong>Be specific</strong> - provide detailed answers ✅ <strong>Be honest</strong> - accuracy is critical ✅ <strong>Protect anonymity</strong> - don&#39;t reveal identifying details (if anonymous) ✅ <strong>Ask questions</strong> - seek clarification if needed</p><h2 id="expected-timeframes" tabindex="-1">Expected Timeframes <a class="header-anchor" href="#expected-timeframes" aria-label="Permalink to &quot;Expected Timeframes&quot;">​</a></h2><p><strong>Initial Response</strong>: 24-48 hours after submission</p><p><strong>Investigation Duration</strong>:</p><ul><li>Simple cases: 1-2 weeks</li><li>Moderate complexity: 2-4 weeks</li><li>Complex cases: 4-8 weeks</li></ul><p><strong>Resolution</strong>: Varies by severity and complexity</p><p><strong>Tip</strong>: Check timeline and messages for specific updates about your case.</p><h2 id="if-status-doesn-t-change" tabindex="-1">If Status Doesn&#39;t Change <a class="header-anchor" href="#if-status-doesn-t-change" aria-label="Permalink to &quot;If Status Doesn&#39;t Change&quot;">​</a></h2><p><strong>If no update after 1 week:</strong></p><ol><li>Send a message asking for status update</li><li>Be patient - complex cases take time</li><li>Check messages in case you missed a question</li></ol><p><strong>If no response after 2 weeks:</strong></p><ol><li>Send follow-up message</li><li>If still no response, contact organization&#39;s compliance team directly</li><li>May indicate technical issue or oversight</li></ol><h2 id="viewing-investigation-outcome" tabindex="-1">Viewing Investigation Outcome <a class="header-anchor" href="#viewing-investigation-outcome" aria-label="Permalink to &quot;Viewing Investigation Outcome&quot;">​</a></h2><p>When resolved or closed:</p><ul><li>Final status update provided</li><li>Outcome summary (within confidentiality limits)</li><li>Actions taken (may be limited by privacy/legal requirements)</li><li>Case closure reason</li></ul><p><strong>Note</strong>: Specific disciplinary actions may not be shared due to employment law and privacy requirements.</p><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><p><strong>Can&#39;t access tracking page:</strong></p><ul><li>Check URL is correct</li><li>Clear browser cache</li><li>Try different browser</li><li>Contact support if persists</li></ul><p><strong>Tracking ID not working:</strong></p><ul><li>Verify you entered it correctly (case-sensitive)</li><li>Check for extra spaces</li><li>Confirm you&#39;re using organization&#39;s lookup page</li><li>Contact support with proof of submission</li></ul><p><strong>Lost tracking ID:</strong></p><ul><li>Anonymous reports: Cannot be recovered (by design)</li><li>Confidential reports: Contact support with your email</li><li>Prevention: Save ID immediately after submission</li></ul><hr><p><strong>Next Steps:</strong></p><ul><li><a href="/docs/reporting/secure-messaging">Secure Messaging</a></li><li><a href="/docs/reporting/report-types">Report Types</a></li><li><a href="/docs/reporting/how-to-submit">How to Submit</a></li></ul>',72)])])}const h=i(o,[["render",r]]);export{d as __pageData,h as default};
