import{_ as e,c as t,o as l,ag as r}from"./chunks/framework.4QX5UEtQ.js";const g=JSON.parse('{"title":"Security Overview - Disclosurely Zero-Knowledge Platform","description":"Military-grade security with AES-256 encryption, zero-knowledge architecture, SOC 2 compliance, ISO 27001 certification, and comprehensive access controls.","frontmatter":{"title":"Security Overview - Disclosurely Zero-Knowledge Platform","description":"Military-grade security with AES-256 encryption, zero-knowledge architecture, SOC 2 compliance, ISO 27001 certification, and comprehensive access controls.","head":[["meta",{"property":"og:title","content":"Security Overview - Zero-Knowledge Whistleblowing Platform"}],["meta",{"property":"og:description","content":"Military-grade security with AES-256 encryption, zero-knowledge architecture, SOC 2 compliance, ISO 27001 certification, and comprehensive access controls."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"Security Overview - Disclosurely"}],["meta",{"name":"twitter:description","content":"Military-grade security with AES-256 encryption, zero-knowledge architecture, SOC 2 compliance, ISO 27001 certification, and comprehensive access controls."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"security/overview.md","filePath":"security/overview.md"}'),n={name:"security/overview.md"};function o(a,i,s,c,u,p){return l(),t("div",null,[...i[0]||(i[0]=[r('<h1 id="security-overview" tabindex="-1">Security Overview <a class="header-anchor" href="#security-overview" aria-label="Permalink to &quot;Security Overview&quot;">​</a></h1><p>Comprehensive overview of Disclosurely&#39;s security architecture and practices.</p><h2 id="our-security-commitment" tabindex="-1">Our Security Commitment <a class="header-anchor" href="#our-security-commitment" aria-label="Permalink to &quot;Our Security Commitment&quot;">​</a></h2><p>Disclosurely is built security-first to protect whistleblowers, organizations, and sensitive investigation data. We understand that trust is earned through demonstrable security, not just promises.</p><p><strong>Our Principles</strong>:</p><ul><li><strong>Privacy by design</strong>: Security built into every feature</li><li><strong>Zero-knowledge architecture</strong>: We cannot access your encrypted data</li><li><strong>Defense in depth</strong>: Multiple layers of security</li><li><strong>Transparency</strong>: Open about our security practices</li><li><strong>Continuous improvement</strong>: Regular testing and updates</li><li><strong>Compliance-ready</strong>: Meet regulatory requirements</li></ul><h2 id="security-architecture" tabindex="-1">Security Architecture <a class="header-anchor" href="#security-architecture" aria-label="Permalink to &quot;Security Architecture&quot;">​</a></h2><h3 id="multi-layer-security" tabindex="-1">Multi-Layer Security <a class="header-anchor" href="#multi-layer-security" aria-label="Permalink to &quot;Multi-Layer Security&quot;">​</a></h3><p><strong>Application Layer</strong>:</p><ul><li>Secure coding practices</li><li>Input validation and sanitization</li><li>Output encoding</li><li>CSRF protection</li><li>XSS prevention</li><li>SQL injection prevention</li><li>Regular security audits</li></ul><p><strong>Data Layer</strong>:</p><ul><li><a href="/docs/reporting/encryption">AES-256 encryption at rest</a></li><li><a href="/docs/reporting/encryption">End-to-end encryption for reports</a></li><li>Zero-knowledge encryption for anonymous reports</li><li>Encrypted database</li><li>Encrypted backups</li><li>Secure key management</li></ul><p><strong>Network Layer</strong>:</p><ul><li>TLS 1.3 for data in transit</li><li>HTTPS only (HSTS enabled)</li><li>DDoS protection</li><li>Web Application Firewall (WAF)</li><li>Rate limiting</li><li>Intrusion detection</li></ul><p><strong>Infrastructure Layer</strong>:</p><ul><li>Secure cloud hosting</li><li>Network segmentation</li><li>Firewall protection</li><li>Isolated environments</li><li>Regular security updates</li><li>Infrastructure as code</li></ul><p><strong>Physical Layer</strong> (via cloud provider):</p><ul><li>SOC 2 certified data centers</li><li>24/7 physical security</li><li>Biometric access controls</li><li>Surveillance monitoring</li><li>Redundant power and cooling</li><li>Geographic redundancy</li></ul><h3 id="zero-knowledge-architecture" tabindex="-1">Zero-Knowledge Architecture <a class="header-anchor" href="#zero-knowledge-architecture" aria-label="Permalink to &quot;Zero-Knowledge Architecture&quot;">​</a></h3><p><strong>What Is Zero-Knowledge?</strong></p><p>Zero-knowledge architecture means Disclosurely cannot access the content of encrypted data, even if we wanted to. This provides maximum protection for whistleblowers.</p><p><strong>How It Works</strong>:</p><ol><li><p><strong>Anonymous Reports</strong>:</p><ul><li>Report encrypted in browser before upload</li><li>Encryption key never sent to server</li><li>Key derived from tracking ID (only reporter has)</li><li>Disclosurely servers cannot decrypt</li><li>Only authorized organization users can decrypt</li></ul></li><li><p><strong>Confidential Reports</strong>:</p><ul><li>Reporter identity separately encrypted</li><li>Multiple encryption layers</li><li>Role-based decryption keys</li><li>Investigator can read report, not identity</li><li>Only authorized users can access identity</li></ul></li><li><p><strong>Evidence Files</strong>:</p><ul><li>Files encrypted before upload</li><li>Unique encryption key per file</li><li>Keys encrypted with user keys</li><li>Cannot be decrypted by Disclosurely</li><li>Organization controls decryption</li></ul></li></ol><p><strong>Benefits</strong>:</p><ul><li>Maximum reporter protection</li><li>Cannot be compelled to reveal content</li><li>Breach impact minimized</li><li>Compliance with privacy regulations</li><li>Trust through technology</li></ul><h2 id="encryption" tabindex="-1">Encryption <a class="header-anchor" href="#encryption" aria-label="Permalink to &quot;Encryption&quot;">​</a></h2><h3 id="encryption-standards" tabindex="-1">Encryption Standards <a class="header-anchor" href="#encryption-standards" aria-label="Permalink to &quot;Encryption Standards&quot;">​</a></h3><p><strong>Data at Rest</strong>:</p><ul><li><strong>Algorithm</strong>: AES-256-GCM</li><li><strong>Key length</strong>: 256-bit</li><li><strong>Implementation</strong>: Industry-standard libraries</li><li><strong>Key rotation</strong>: Automatic periodic rotation</li><li><strong>Key storage</strong>: Hardware Security Modules (HSMs)</li></ul><p><strong>Data in Transit</strong>:</p><ul><li><strong>Protocol</strong>: TLS 1.3</li><li><strong>Cipher suites</strong>: Strong ciphers only</li><li><strong>Certificate</strong>: Extended Validation (EV) SSL</li><li><strong>HSTS</strong>: Enabled (forces HTTPS)</li><li><strong>Perfect forward secrecy</strong>: Enabled</li></ul><p><strong><a href="/docs/reporting/encryption">End-to-End Encryption</a></strong>:</p><ul><li><strong>Reports</strong>: Encrypted client-side</li><li><strong>Messages</strong>: Encrypted before transmission</li><li><strong>Evidence</strong>: Encrypted before upload</li><li><strong>Keys</strong>: Never transmitted in plain text</li></ul><h3 id="encryption-key-management" tabindex="-1">Encryption Key Management <a class="header-anchor" href="#encryption-key-management" aria-label="Permalink to &quot;Encryption Key Management&quot;">​</a></h3><p><strong>Key Hierarchy</strong>:</p><ol><li><p><strong>Master Encryption Key (MEK)</strong></p><ul><li>Stored in HSM</li><li>Never exported</li><li>Used to encrypt Data Encryption Keys</li><li>Rotated annually</li></ul></li><li><p><strong>Data Encryption Keys (DEKs)</strong></p><ul><li>Per-organization keys</li><li>Encrypt organization data</li><li>Encrypted by MEK</li><li>Rotated quarterly</li></ul></li><li><p><strong>File Encryption Keys (FEKs)</strong></p><ul><li>Per-file unique keys</li><li>Encrypt individual evidence files</li><li>Encrypted by DEK</li><li>Single use</li></ul></li><li><p><strong>User Keys</strong></p><ul><li>Per-user encryption keys</li><li>Control access to data</li><li>Derived from authentication</li><li>Session-specific</li></ul></li></ol><p><strong>Key Rotation</strong>:</p><ul><li>Automatic rotation schedule</li><li>No service interruption</li><li>Old keys retained for decryption</li><li>New data uses new keys</li><li>Audit trail maintained</li></ul><p><strong>Key Recovery</strong>:</p><ul><li>Administrative key recovery process</li><li>Multi-person authorization required</li><li>All recovery logged</li><li>Emergency procedures documented</li><li>Regular testing</li></ul><h2 id="access-controls" tabindex="-1">Access Controls <a class="header-anchor" href="#access-controls" aria-label="Permalink to &quot;Access Controls&quot;">​</a></h2><h3 id="role-based-access-control-rbac" tabindex="-1">Role-Based Access Control (RBAC) <a class="header-anchor" href="#role-based-access-control-rbac" aria-label="Permalink to &quot;Role-Based Access Control (RBAC)&quot;">​</a></h3><p><strong>Principle of Least Privilege</strong>: Users have only permissions necessary for their role.</p><p><strong>Standard Roles</strong>:</p><p><strong>Organization Administrator</strong>:</p><ul><li>Full system access</li><li>User management</li><li>Settings configuration</li><li>Billing management</li><li>Cannot access report content without reason</li></ul><p><strong>Case Handler (Admin)</strong>:</p><ul><li>View all cases</li><li>Assign cases</li><li>Change status</li><li>Add notes</li><li>View evidence</li><li>Cannot access reporter identity (confidential reports)</li></ul><p><strong>Investigator</strong>:</p><ul><li>View assigned cases</li><li>Add notes and evidence</li><li>Update status</li><li>Communicate with reporter</li><li>Cannot see reporter identity</li><li>Cannot access unassigned cases</li></ul><p><strong>Reviewer (Read-Only)</strong>:</p><ul><li>View assigned cases</li><li>Read case notes</li><li>Download evidence</li><li>Cannot modify cases</li><li>Cannot see reporter identity</li></ul><p><strong>Audit Committee</strong>:</p><ul><li>View financial misconduct cases</li><li>Read-only access</li><li>Cannot modify investigations</li><li>Aggregated reporting</li><li>No identity access</li></ul><p><strong>Reporter</strong>:</p><ul><li>Submit reports</li><li>Track own report</li><li>Send/receive messages</li><li>Upload additional evidence</li><li>No access to other reports</li></ul><h3 id="access-audit-trail" tabindex="-1">Access Audit Trail <a class="header-anchor" href="#access-audit-trail" aria-label="Permalink to &quot;Access Audit Trail&quot;">​</a></h3><p><strong>All Access Logged</strong>:</p><ul><li>Who accessed what</li><li>When accessed</li><li>What actions taken</li><li>IP address and location</li><li>Device and browser</li><li>Duration of access</li></ul><p><strong>Tamper-Evident</strong>:</p><ul><li>Hash chain prevents alteration</li><li>Cannot delete audit logs</li><li>Verifiable integrity</li><li>Retained permanently</li><li><a href="/docs/compliance/audit-trail">Available for compliance</a></li></ul><p><strong>Monitoring</strong>:</p><ul><li>Unusual access patterns detected</li><li>Alerts for suspicious activity</li><li>Regular access reviews</li><li>Compliance reporting</li><li>Anomaly detection</li></ul><h2 id="authentication" tabindex="-1">Authentication <a class="header-anchor" href="#authentication" aria-label="Permalink to &quot;Authentication&quot;">​</a></h2><h3 id="multi-factor-authentication-mfa" tabindex="-1">Multi-Factor Authentication (MFA) <a class="header-anchor" href="#multi-factor-authentication-mfa" aria-label="Permalink to &quot;Multi-Factor Authentication (MFA)&quot;">​</a></h3><p><strong>Supported Methods</strong>:</p><ul><li><strong>Authenticator apps</strong>: Google Authenticator, Authy, etc.</li><li><strong>SMS codes</strong>: Text message verification</li><li><strong>Email codes</strong>: Email verification</li><li><strong>Biometric</strong>: Fingerprint, Face ID (on supported devices)</li><li><strong>Hardware tokens</strong>: YubiKey, etc. (Enterprise)</li></ul><p><strong>MFA Requirements</strong>:</p><ul><li>Required for Organization Administrators</li><li>Recommended for all users</li><li>Configurable per-organization</li><li>Cannot be disabled by user</li><li>Enforced at login</li></ul><p><strong>Setup Process</strong>:</p><ol><li>User logs in with password</li><li>Prompted to enable MFA</li><li>Choose MFA method</li><li>Scan QR code or setup</li><li>Enter verification code</li><li>Backup codes generated</li><li>MFA active for all future logins</li></ol><h3 id="single-sign-on-sso" tabindex="-1">Single Sign-On (SSO) <a class="header-anchor" href="#single-sign-on-sso" aria-label="Permalink to &quot;Single Sign-On (SSO)&quot;">​</a></h3><p><strong>Enterprise Plan Feature</strong></p><p><strong>Supported Protocols</strong>:</p><ul><li>SAML 2.0</li><li>OAuth 2.0</li><li>OpenID Connect</li></ul><p><strong>Identity Providers</strong>:</p><ul><li>Microsoft Azure AD</li><li>Okta</li><li>Google Workspace</li><li>OneLogin</li><li>Auth0</li><li>Any SAML 2.0 compliant IdP</li></ul><p><strong>Benefits</strong>:</p><ul><li>Centralized user management</li><li>Leverage existing authentication</li><li>Enforce organization MFA policies</li><li>Automatic user provisioning</li><li>Simplified user experience</li></ul><p><strong>Setup</strong>:</p><ol><li>Configure IdP connection</li><li>Map user attributes</li><li>Test with test users</li><li>Enable for organization</li><li>Users login via SSO</li></ol><h3 id="password-security" tabindex="-1">Password Security <a class="header-anchor" href="#password-security" aria-label="Permalink to &quot;Password Security&quot;">​</a></h3><p><strong>Password Requirements</strong>:</p><ul><li>Minimum 12 characters</li><li>Mix of upper, lower, numbers, symbols</li><li>No common passwords (dictionary check)</li><li>No user information in password</li><li>Cannot reuse last 10 passwords</li></ul><p><strong>Password Storage</strong>:</p><ul><li>Never stored in plain text</li><li>Bcrypt hashing with salt</li><li>High work factor (cost 12+)</li><li>Cannot be retrieved</li><li>Password reset only</li></ul><p><strong>Session Management</strong>:</p><ul><li>Secure session tokens</li><li>HTTP-only cookies</li><li>SameSite attribute</li><li>Session timeout (configurable)</li><li>Automatic logout on inactivity</li><li>Device/location tracking</li></ul><h2 id="infrastructure-security" tabindex="-1">Infrastructure Security <a class="header-anchor" href="#infrastructure-security" aria-label="Permalink to &quot;Infrastructure Security&quot;">​</a></h2><h3 id="cloud-hosting" tabindex="-1">Cloud Hosting <a class="header-anchor" href="#cloud-hosting" aria-label="Permalink to &quot;Cloud Hosting&quot;">​</a></h3><p><strong>Provider</strong>: Industry-leading cloud infrastructure (AWS/Azure/GCP)</p><p><strong>Certifications</strong>:</p><ul><li>SOC 2 Type II</li><li>ISO 27001</li><li>ISO 27017 (cloud security)</li><li>ISO 27018 (cloud privacy)</li><li>PCI DSS (if applicable)</li></ul><p><strong>Geographic Redundancy</strong>:</p><ul><li>Multi-region deployment</li><li>Automatic failover</li><li>Data replicated across regions</li><li>99.9% uptime SLA</li><li>Disaster recovery tested quarterly</li></ul><h3 id="network-security" tabindex="-1">Network Security <a class="header-anchor" href="#network-security" aria-label="Permalink to &quot;Network Security&quot;">​</a></h3><p><strong>Perimeter Protection</strong>:</p><ul><li>Web Application Firewall (WAF)</li><li>DDoS mitigation (multi-Tbps capacity)</li><li>Rate limiting</li><li>IP allowlisting (optional)</li><li>Geo-blocking (optional)</li></ul><p><strong>Network Segmentation</strong>:</p><ul><li>Application tier isolated</li><li>Database tier isolated</li><li>Management tier isolated</li><li>No direct internet access to data</li><li>Bastion hosts for admin access</li></ul><p><strong>Intrusion Detection</strong>:</p><ul><li>Network intrusion detection (NIDS)</li><li>Host intrusion detection (HIDS)</li><li>Security Information and Event Management (SIEM)</li><li>24/7 monitoring</li><li>Automated alerting</li></ul><h3 id="vulnerability-management" tabindex="-1">Vulnerability Management <a class="header-anchor" href="#vulnerability-management" aria-label="Permalink to &quot;Vulnerability Management&quot;">​</a></h3><p><strong>Regular Scanning</strong>:</p><ul><li>Automated vulnerability scans (weekly)</li><li>Dependency scanning (continuous)</li><li>Code analysis (static and dynamic)</li><li>Container security scanning</li><li>Infrastructure scanning</li></ul><p><strong>Penetration Testing</strong>:</p><ul><li>Annual third-party penetration test</li><li>Scope: entire platform</li><li>Report provided to customers on request</li><li>All findings remediated</li><li>Re-test to verify fixes</li></ul><p><strong>Bug Bounty Program</strong>:</p><ul><li>Responsible disclosure program</li><li>Security researchers invited</li><li>Rewards for valid findings</li><li>Coordinated disclosure</li><li>Continuous security improvement</li></ul><p><strong>Patch Management</strong>:</p><ul><li>Security patches applied within 48 hours</li><li>Critical patches applied within 24 hours</li><li>Staged rollout to production</li><li>Rollback plan ready</li><li>Communicated to customers</li></ul><h2 id="compliance-and-certifications" tabindex="-1">Compliance and Certifications <a class="header-anchor" href="#compliance-and-certifications" aria-label="Permalink to &quot;Compliance and Certifications&quot;">​</a></h2><h3 id="industry-certifications" tabindex="-1">Industry Certifications <a class="header-anchor" href="#industry-certifications" aria-label="Permalink to &quot;Industry Certifications&quot;">​</a></h3><p><strong>ISO 27001</strong>: Information Security Management</p><ul><li>Certified annually</li><li>Comprehensive security controls</li><li>Regular audits</li><li>Continuous improvement</li></ul><p><strong>SOC 2 Type II</strong>: Service Organization Controls</p><ul><li>Annual audit</li><li>Security, availability, confidentiality</li><li>Report available to customers</li><li>Independent attestation</li></ul><p><strong><a href="/docs/compliance/gdpr">GDPR Compliant</a></strong>: General Data Protection Regulation</p><ul><li>Privacy by design</li><li>Data subject rights</li><li>Data protection impact assessments</li><li>Data processing agreements</li></ul><p><strong><a href="/docs/compliance/eu-directive">EU Whistleblowing Directive</a></strong>: Directive 2019/1937</p><ul><li>Built for full compliance</li><li>All requirements met</li><li>Documented controls</li><li>Regular compliance monitoring</li></ul><h3 id="regulatory-compliance-support" tabindex="-1">Regulatory Compliance Support <a class="header-anchor" href="#regulatory-compliance-support" aria-label="Permalink to &quot;Regulatory Compliance Support&quot;">​</a></h3><p><strong>Financial Services</strong>:</p><ul><li><a href="/docs/compliance/sox">SOX compliance features</a></li><li>SEC requirements</li><li>Financial services regulations</li><li>Audit committee oversight</li></ul><p><strong>Healthcare</strong>:</p><ul><li>HIPAA-ready (Enterprise)</li><li>Healthcare data protection</li><li>Business Associate Agreement available</li><li>Encryption and access controls</li></ul><p><strong>Government</strong>:</p><ul><li>FedRAMP authorization (in progress)</li><li>FISMA compliant</li><li>Government security standards</li><li>Secure government deployment</li></ul><h2 id="security-monitoring" tabindex="-1">Security Monitoring <a class="header-anchor" href="#security-monitoring" aria-label="Permalink to &quot;Security Monitoring&quot;">​</a></h2><h3 id="_24-7-security-operations" tabindex="-1">24/7 Security Operations <a class="header-anchor" href="#_24-7-security-operations" aria-label="Permalink to &quot;24/7 Security Operations&quot;">​</a></h3><p><strong>Monitoring</strong>:</p><ul><li>Application performance monitoring</li><li>Security event monitoring</li><li>Anomaly detection</li><li>User behavior analytics</li><li>Threat intelligence feeds</li></ul><p><strong>Incident Response</strong>:</p><ul><li>24/7 on-call security team</li><li>Incident response plan</li><li>Defined escalation procedures</li><li>Quarterly DR drills</li><li>Post-incident reviews</li></ul><p><strong>Alerting</strong>:</p><ul><li>Real-time alerts</li><li>Multiple notification channels</li><li>Escalation procedures</li><li>Alert tuning and refinement</li><li>Regular review of alerts</li></ul><h3 id="threat-detection" tabindex="-1">Threat Detection <a class="header-anchor" href="#threat-detection" aria-label="Permalink to &quot;Threat Detection&quot;">​</a></h3><p><strong>Automated Detection</strong>:</p><ul><li>Failed login attempts</li><li>Brute force attacks</li><li>SQL injection attempts</li><li>XSS attempts</li><li>Unusual access patterns</li><li>Data exfiltration attempts</li><li>Privilege escalation</li><li>Account takeover attempts</li></ul><p><strong>Response Actions</strong>:</p><ul><li>Automatic account lockout</li><li>IP blocking</li><li>Rate limiting</li><li>WAF rule updates</li><li>Security team notification</li><li>User notification (if appropriate)</li></ul><h2 id="data-privacy" tabindex="-1">Data Privacy <a class="header-anchor" href="#data-privacy" aria-label="Permalink to &quot;Data Privacy&quot;">​</a></h2><h3 id="privacy-by-design" tabindex="-1">Privacy by Design <a class="header-anchor" href="#privacy-by-design" aria-label="Permalink to &quot;Privacy by Design&quot;">​</a></h3><p><strong>Principles</strong>:</p><ul><li>Proactive not reactive</li><li>Privacy as default setting</li><li>Embedded into design</li><li>Full functionality (no zero-sum)</li><li>End-to-end security</li><li>Visibility and transparency</li><li>Respect for user privacy</li></ul><p><strong>Implementation</strong>:</p><ul><li>Data minimization</li><li>Purpose limitation</li><li>Storage limitation</li><li>Integrity and confidentiality</li><li>Accountability</li><li>Transparent processing</li></ul><h3 id="data-handling" tabindex="-1">Data Handling <a class="header-anchor" href="#data-handling" aria-label="Permalink to &quot;Data Handling&quot;">​</a></h3><p><strong>Personal Data</strong>:</p><ul><li>Collected only when necessary</li><li>Used only for stated purpose</li><li>Retained only as long as needed</li><li>Secured with encryption</li><li>Access strictly controlled</li><li>Deletion when no longer needed</li></ul><p><strong>Special Categories</strong> (sensitive data):</p><ul><li>Extra protection measures</li><li>Limited access</li><li>Enhanced encryption</li><li>Careful retention</li><li>Explicit consent (if required)</li><li>DPIA conducted</li></ul><h2 id="your-security-responsibilities" tabindex="-1">Your Security Responsibilities <a class="header-anchor" href="#your-security-responsibilities" aria-label="Permalink to &quot;Your Security Responsibilities&quot;">​</a></h2><h3 id="shared-responsibility-model" tabindex="-1">Shared Responsibility Model <a class="header-anchor" href="#shared-responsibility-model" aria-label="Permalink to &quot;Shared Responsibility Model&quot;">​</a></h3><p><strong>Disclosurely&#39;s Responsibility</strong>:</p><ul><li>Platform security</li><li>Infrastructure security</li><li>Application security</li><li>Encryption implementation</li><li>Security monitoring</li><li>Incident response</li><li>Compliance certifications</li></ul><p><strong>Your Responsibility</strong>:</p><ul><li>User account security</li><li>Password management</li><li>MFA enablement</li><li>Access control configuration</li><li>User training</li><li>Security awareness</li><li>Incident reporting</li><li>Data classification</li></ul><h3 id="security-best-practices" tabindex="-1">Security Best Practices <a class="header-anchor" href="#security-best-practices" aria-label="Permalink to &quot;Security Best Practices&quot;">​</a></h3><p><strong>For Administrators</strong>:</p><ul><li>Enable MFA for all users</li><li>Review access regularly</li><li>Use strong, unique passwords</li><li>Configure session timeouts</li><li>Monitor audit logs</li><li>Train users on security</li><li>Report suspicious activity</li><li>Keep contact information current</li></ul><p><strong>For Users</strong>:</p><ul><li>Use strong, unique passwords</li><li>Enable MFA</li><li>Don&#39;t share credentials</li><li>Log out when finished</li><li>Verify URLs before clicking</li><li>Report phishing attempts</li><li>Secure your devices</li><li>Use updated browsers</li></ul><p><strong>For Reporters</strong>:</p><ul><li>Use anonymous reporting if concerned</li><li>Don&#39;t include unnecessary personal details</li><li>Use secure networks (avoid public WiFi)</li><li>Remember your tracking ID</li><li>Use strong passphrases</li><li>Don&#39;t share tracking ID</li><li>Clear browser history if needed</li></ul><h2 id="security-incidents" tabindex="-1">Security Incidents <a class="header-anchor" href="#security-incidents" aria-label="Permalink to &quot;Security Incidents&quot;">​</a></h2><h3 id="if-you-suspect-a-security-issue" tabindex="-1">If You Suspect a Security Issue <a class="header-anchor" href="#if-you-suspect-a-security-issue" aria-label="Permalink to &quot;If You Suspect a Security Issue&quot;">​</a></h3><p><strong>Report Immediately</strong>:</p><ul><li>Email: <a href="mailto:security@disclosurely.com" target="_blank" rel="noreferrer">security@disclosurely.com</a></li><li>Include details of concern</li><li>Don&#39;t investigate yourself</li><li>Don&#39;t share publicly</li><li>Await response</li></ul><p><strong>We Will</strong>:</p><ul><li>Acknowledge within 24 hours</li><li>Investigate promptly</li><li>Keep you informed</li><li>Take corrective action</li><li>Notify if breach confirmed</li><li>Document and learn</li></ul><h3 id="breach-notification" tabindex="-1">Breach Notification <a class="header-anchor" href="#breach-notification" aria-label="Permalink to &quot;Breach Notification&quot;">​</a></h3><p><strong>Our Commitment</strong>:</p><ul><li>Notify within 72 hours (GDPR requirement)</li><li>Transparent communication</li><li>Clear impact assessment</li><li>Remediation steps taken</li><li>Support for affected parties</li><li>Post-incident review</li></ul><p><strong>Customer Notification</strong>:</p><ul><li>Direct notification to administrators</li><li>Description of incident</li><li>Data affected</li><li>Steps taken</li><li>Your recommended actions</li><li>Contact for questions</li><li>Regular updates</li></ul><h2 id="security-documentation" tabindex="-1">Security Documentation <a class="header-anchor" href="#security-documentation" aria-label="Permalink to &quot;Security Documentation&quot;">​</a></h2><h3 id="available-resources" tabindex="-1">Available Resources <a class="header-anchor" href="#available-resources" aria-label="Permalink to &quot;Available Resources&quot;">​</a></h3><p><strong>For Customers</strong>:</p><ul><li>Security whitepaper</li><li>SOC 2 Type II report (NDA required)</li><li>Penetration test summary</li><li>Compliance documentation</li><li>Data Processing Agreement</li><li>Privacy Policy</li><li>Terms of Service</li></ul><p><strong>Request Documentation</strong>:</p><ul><li>Contact: <a href="mailto:security@disclosurely.com" target="_blank" rel="noreferrer">security@disclosurely.com</a></li><li>Or via Dashboard &gt; Support &gt; Security Documentation</li></ul><h3 id="security-questionnaires" tabindex="-1">Security Questionnaires <a class="header-anchor" href="#security-questionnaires" aria-label="Permalink to &quot;Security Questionnaires&quot;">​</a></h3><p>We understand you may need to complete security questionnaires for your procurement or compliance processes.</p><p><strong>We Can Help</strong>:</p><ul><li>Standard questionnaires pre-completed</li><li>Custom questionnaire completion</li><li>Technical security documentation</li><li>Compliance attestations</li><li>Security presentations</li></ul><p><strong>Contact</strong>: <a href="mailto:enterprise@disclosurely.com" target="_blank" rel="noreferrer">enterprise@disclosurely.com</a></p><hr><p><strong>Related:</strong></p><ul><li><a href="/docs/security/encryption">Encryption &amp; Privacy</a></li><li><a href="/docs/security/access-control">Access Controls</a></li><li><a href="/docs/security/authentication">Authentication &amp; MFA</a></li><li><a href="/docs/security/best-practices">Security Best Practices</a></li></ul>',190)])])}const y=e(n,[["render",o]]);export{g as __pageData,y as default};
