import{_ as t,c as o,o as r,ag as n}from"./chunks/framework.4QX5UEtQ.js";const d=JSON.parse(`{"title":"FAQs - Disclosurely Support","description":"Common questions answered about Disclosurely's secure whistleblowing platform, security features, pricing, compliance, reporting, and support options.","frontmatter":{"title":"FAQs - Disclosurely Support","description":"Common questions answered about Disclosurely's secure whistleblowing platform, security features, pricing, compliance, reporting, and support options.","head":[["meta",{"property":"og:title","content":"Frequently Asked Questions - Disclosurely"}],["meta",{"property":"og:description","content":"Common questions answered about Disclosurely's secure whistleblowing platform, security features, pricing, compliance, reporting, and support options."}],["meta",{"property":"og:type","content":"website"}],["meta",{"property":"og:image","content":"https://disclosurely.com/docs/ogimage.png"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:title","content":"FAQs - Disclosurely Support"}],["meta",{"name":"twitter:description","content":"Common questions answered about Disclosurely's secure whistleblowing platform, security features, pricing, compliance, reporting, and support options."}],["meta",{"name":"twitter:image","content":"https://disclosurely.com/docs/ogimage.png"}]]},"headers":[],"relativePath":"support/faqs.md","filePath":"support/faqs.md"}`),a={name:"support/faqs.md"};function s(i,e,l,p,c,u){return r(),o("div",null,[...e[0]||(e[0]=[n('<h1 id="frequently-asked-questions" tabindex="-1">Frequently Asked Questions <a class="header-anchor" href="#frequently-asked-questions" aria-label="Permalink to &quot;Frequently Asked Questions&quot;">​</a></h1><p>Common questions about Disclosurely answered.</p><h2 id="general" tabindex="-1">General <a class="header-anchor" href="#general" aria-label="Permalink to &quot;General&quot;">​</a></h2><p><strong>Q: What is Disclosurely?</strong> A: Disclosurely is an enterprise whistleblowing and compliance management platform with end-to-end encryption, AI-powered analysis, and comprehensive compliance tools.</p><p><strong>Q: Who can use Disclosurely?</strong> A: Any organization that needs secure whistleblowing channels - companies, non-profits, government agencies, schools, healthcare providers, and more.</p><p><strong>Q: How much does it cost?</strong> A: Free Basic plan available. Pro plan is £39.99/month. Enterprise plans have custom pricing. See <a href="https://disclosurely.com/pricing" target="_blank" rel="noreferrer">Pricing</a>.</p><h2 id="security-privacy" tabindex="-1">Security &amp; Privacy <a class="header-anchor" href="#security-privacy" aria-label="Permalink to &quot;Security &amp; Privacy&quot;">​</a></h2><p><strong>Q: How secure is Disclosurely?</strong> A: Military-grade AES-256 encryption, zero-knowledge architecture, ISO 27001 certified. See <a href="/docs/security/encryption">Security</a>.</p><p><strong>Q: Can Disclosurely see my reports?</strong> A: No. Zero-knowledge encryption means we cannot access report content. Only authorized users in your organization can decrypt reports.</p><p><strong>Q: Is anonymity really guaranteed?</strong> A: Yes. Anonymous reports collect no personal information and are encrypted client-side. We cannot identify reporters.</p><p><strong>Q: What if I lose my tracking ID?</strong> A: Anonymous reports cannot be recovered if tracking ID is lost (by design for security). Confidential reports can be recovered by contacting support.</p><h2 id="reporting" tabindex="-1">Reporting <a class="header-anchor" href="#reporting" aria-label="Permalink to &quot;Reporting&quot;">​</a></h2><p><strong>Q: How do I submit a report?</strong> A: Access your organization&#39;s secure reporting link and follow the submission process. See <a href="/docs/reporting/how-to-submit">How to Submit</a>.</p><p><strong>Q: Should I report anonymously or confidentially?</strong> A: Anonymous for maximum privacy. Confidential if you&#39;re comfortable sharing identity for easier communication. See <a href="/docs/reporting/report-types">Report Types</a>.</p><p><strong>Q: How long does investigation take?</strong> A: Typically 2-4 weeks, varies by complexity. Check your tracking ID for updates.</p><p><strong>Q: Will I know the outcome?</strong> A: Yes, you&#39;ll receive status updates and final outcome (within confidentiality limits). Specific disciplinary actions may not be shared.</p><h2 id="for-administrators" tabindex="-1">For Administrators <a class="header-anchor" href="#for-administrators" aria-label="Permalink to &quot;For Administrators&quot;">​</a></h2><p><strong>Q: How do I set up my organization?</strong> A: See <a href="/docs/admin/initial-setup">Initial Setup Guide</a>.</p><p><strong>Q: Can I customize the branding?</strong> A: Yes, on Pro and Enterprise plans. See <a href="/docs/admin/custom-branding">Custom Branding</a>.</p><p><strong>Q: How do I invite team members?</strong> A: Navigate to Dashboard &gt; Team &gt; Invite. See <a href="/docs/admin/team-management">Team Management</a>.</p><p><strong>Q: What user roles are available?</strong> A: Admin, Org Admin, Case Handler, and Reviewer. See <a href="/docs/admin/team-management">Team Management</a>.</p><h2 id="billing" tabindex="-1">Billing <a class="header-anchor" href="#billing" aria-label="Permalink to &quot;Billing&quot;">​</a></h2><p><strong>Q: Can I cancel anytime?</strong> A: Yes, cancel anytime. Access continues until end of billing cycle.</p><p><strong>Q: Do you offer refunds?</strong> A: 14-day money-back guarantee on first Pro subscription.</p><p><strong>Q: Is there a discount for annual billing?</strong> A: Yes, 20% discount for annual billing.</p><h2 id="compliance" tabindex="-1">Compliance <a class="header-anchor" href="#compliance" aria-label="Permalink to &quot;Compliance&quot;">​</a></h2><p><strong>Q: Is Disclosurely GDPR compliant?</strong> A: Yes, fully GDPR compliant with automated data request processing.</p><p><strong>Q: Does it meet EU Whistleblowing Directive requirements?</strong> A: Yes, designed specifically for EU Directive compliance.</p><p><strong>Q: What audit capabilities are included?</strong> A: Tamper-evident audit logs with hash chain integrity for all actions.</p><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p><strong>Q: How do I get help?</strong> A: Email <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a>, use in-app chat, or browse this documentation.</p><p><strong>Q: What are your support hours?</strong> A: Monday-Friday 9am-5pm GMT. Enterprise customers get 24/7 support.</p><p><strong>Q: Do you offer training?</strong> A: Yes, documentation, video tutorials, and live training for Enterprise customers.</p><hr><p><strong>Still have questions?</strong><br><a href="/docs/support/contact">Contact Support</a> or email <a href="mailto:support@disclosurely.com" target="_blank" rel="noreferrer">support@disclosurely.com</a></p>',35)])])}const g=t(a,[["render",s]]);export{d as __pageData,g as default};
